INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`) VALUES
(9962, -1, 'email_start_perpage_text', 'cphelptext', 'This is the number of messages to be sent in a batch. Unless you run your own server, it is recommended this is set to 100 or less. Even if you do run your own server, unless it is not very busy, you should keep this relatively low.', 'vbulletin', 'Jelsoft', 0, ''),
(9963, -1, 'email_start_perpage_title', 'cphelptext', 'Messages to Send at Once', 'vbulletin', 'Jelsoft', 0, ''),
(9964, -1, 'email_start_from_text', 'cphelptext', 'This is the address that the email is to be from. The email address set as the webmaster''s address is pre-filled in, but you can enter any address you wish here.', 'vbulletin', 'Jelsoft', 0, ''),
(9965, -1, 'email_start_from_title', 'cphelptext', 'From', 'vbulletin', 'Jelsoft', 0, ''),
(9966, -1, 'email_start_subject_text', 'cphelptext', 'Enter the subject line of the email here.', 'vbulletin', 'Jelsoft', 0, ''),
(9967, -1, 'email_start_subject_title', 'cphelptext', 'Subject', 'vbulletin', 'Jelsoft', 0, ''),
(9968, -1, 'email_start_message_text', 'cphelptext', 'This is the message to be sent to your users. Within the message, you may use the following special words, or variables, that will be replaced with the correct information for each user when the email is sent.<br />\r\n<b>$userid</b>: This is the user''s userid. Normally this won''t mean much to a user, but if you are, for example, sending an email telling users what their referrer link is, you would need to include the userid like this:<br />\r\nwww.mysite.com/myvb/index.php?referrerid=$userid<br />\r\nwhich would then give the user credit if another user follows that link and registers.<br />\r\n<b>$activateid</b> and <b>$activatelink</b> are only applicable if you have Email Verification turned on and the user has not verified their address yet. $activatelink is the link that the user needs to visit to activate their account. $activateid would be used if you are manually typing out that link, otherwise it is of no use.<br />\r\n<b>$username</b>: If you want to personalize the email, or remind the user what their username is, use this variable.<br />\r\n<b>$email</b>: This is the email address on file for the user. It is useful if you wish to include a line to the effect of<br />\r\nThis email was sent to $emailaddress which was used to register an account at the vBulletin Community Forums.<br />\r\nPlease note you <b>can not</b> send passwords in emails because they are now encrypted in the database. You can, if you wish, direct users to the Lost Password page which is located at<br />\r\nhttp://www.yoursite.com/yourvb/login.php?do=lostpw', 'vbulletin', 'Jelsoft', 1219837553, '3.8.0 Alpha 1'),
(9969, -1, 'email_start_message_title', 'cphelptext', 'Message', 'vbulletin', 'Jelsoft', 0, ''),
(9970, -1, 'faq_text', 'cphelptext', '<b>FAQs:</b><br />\r\nThe vBulletin FAQ system allows you to create infinitely nestable sets of entries, or questions, and additionally allows end-users to search the FAQs.  FAQs are typically a collection of Frequently Asked Questions, while vBulletin''s FAQ system is something of a FAQ / Knowledge Base hybrid because of its searchability.<br /><br />\r\n\r\nvBulletin ships with a number of default sets and entries that are focused on using vBulletin from the end-user''s perspective.  You may wish to add additional FAQs that answer questions about your specific forum configuration - forum permissions for example.<br /><br />\r\n\r\nThe FAQ system also supports multiple languages.  Each FAQ entry can have a translation for each language in the <a href="language.php?do=modify" target="_blank">Language Manager</a>.  When a user loads a FAQ page vBulletin will display its FAQ entries in the language in which the current logged in user is viewing your forums.', 'vbulletin', 'Jelsoft', 1204936623, '3.7.0 Release Candidate 1'),
(9971, -1, 'faq_title', 'cphelptext', 'About FAQs', 'vbulletin', 'Jelsoft', 0, ''),
(9972, -1, 'faq_add_text', 'cphelptext', 'On this screen, you add a new FAQ entry or set. The options are described below.', 'vbulletin', 'Jelsoft', 0, ''),
(9973, -1, 'faq_add_title', 'cphelptext', 'Adding FAQ Entries and Sets', 'vbulletin', 'Jelsoft', 0, ''),
(9974, -1, 'faq_add_product_text', 'cphelptext', 'This controls what product this FAQ is associated with. You do not need to worry about this option unless you are planning on making an addon for vBulletin to be released.', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(9975, -1, 'faq_add_product_title', 'cphelptext', 'Product', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(9976, -1, 'faq_add_faq_text', 'cphelptext', 'This is a short name for the FAQ and is only used internally. It may only contain letters, numerals and the underscore character and must start with a letter.', 'vbulletin', 'Jelsoft', 1205940980, '3.7.0 Release Candidate 1'),
(9977, -1, 'faq_add_faq_title', 'cphelptext', 'Short Name', 'vbulletin', 'Jelsoft', 0, ''),
(9978, -1, 'faq_add_edit_faqparent_text', 'cphelptext', 'Any entry or set in the FAQ can be the parent for another item. Set the parent of this item here.', 'vbulletin', 'Jelsoft', 0, ''),
(9979, -1, 'faq_add_edit_faqparent_title', 'cphelptext', 'Parent FAQ Item', 'vbulletin', 'Jelsoft', 0, ''),
(9980, -1, 'faq_add_edit_deftitle_text', 'cphelptext', 'This is the title of the entry or set.<br /><br />\r\n\r\nFor entries, it can also be thought of as the "Question", which the "Text" answers. It does not have to be unique, but should be unique to this set of entries to minimize confusion.<br /><br />\r\n\r\nFor sets, it should be descriptive of what kind of questions are in the set.', 'vbulletin', 'Jelsoft', 0, ''),
(9981, -1, 'faq_add_edit_deftitle_title', 'cphelptext', 'Title', 'vbulletin', 'Jelsoft', 0, ''),
(9982, -1, 'faq_add_edit_deftext_text', 'cphelptext', 'This is the "answer" to the "question" (aka Title) of the entry. It should be viewed as a template in that you must use HTML for formatting such as line breaks.\r\n\r\nIf this is a set, this is the description of the set.', 'vbulletin', 'Jelsoft', 0, ''),
(9983, -1, 'faq_add_edit_deftext_title', 'cphelptext', 'Text', 'vbulletin', 'Jelsoft', 0, ''),
(9984, -1, 'faq_add_edit_displayorder_text', 'cphelptext', 'This determines the order in which entries for a particular set are displayed. If you use 0, this FAQ entry will not be displayed, though entries within will still be accessible if their URL is known.', 'vbulletin', 'Jelsoft', 1228218349, '4.0.0 Alpha 1'),
(9985, -1, 'faq_add_edit_displayorder_title', 'cphelptext', 'Display Order', 'vbulletin', 'Jelsoft', 0, ''),
(9986, -1, 'faq_add_edit_1_text', 'cphelptext', 'The Title and Text options above are the "default" text for this set or entry. If you have additional languages setup on your forum, you can enter a translation for the Title and Text for each language you have installed. This is optional. If nothing is entered, the default above will be used.', 'vbulletin', 'Jelsoft', 0, ''),
(9987, -1, 'faq_add_edit_1_title', 'cphelptext', 'Languages', 'vbulletin', 'Jelsoft', 0, ''),
(9988, -1, 'faq_delete_text', 'cphelptext', 'This page allows you to delete this FAQ.  Click the "Yes" button below to perform the deletion.<br /><br />\r\n\r\nAny children of this FAQ will be deleted as well.  This action cannot be undone!', 'vbulletin', 'Jelsoft', 0, ''),
(9989, -1, 'faq_delete_title', 'cphelptext', 'Delete FAQ', 'vbulletin', 'Jelsoft', 0, ''),
(9990, -1, 'faq_edit_text', 'cphelptext', 'This screen allows you to modify each option for the selected FAQ entry or set. Options are described below.', 'vbulletin', 'Jelsoft', 0, ''),
(9991, -1, 'faq_edit_title', 'cphelptext', 'Editing FAQ Entries and Sets', 'vbulletin', 'Jelsoft', 0, ''),
(9992, -1, 'faq_modify_text', 'cphelptext', 'To view the existing FAQ sets, select Modify under FAQ Sets &amp; Entries from the left navigation bar.<br /><br />\r\n\r\nYou start at the top level of the FAQ. At the very top it says "FAQ" - this will expand to become a navigation bar as you drill down into the FAQ levels. Below this is a list of the top two levels of your FAQ structure. To view lower levels, click the parent of the level you wish to view. For example, under vBulletin FAQ, click User Maintenance to view all entries associated with that topic, and child sets (if any). Or, click vBulletin FAQ to view the entries associated with all three children of vBulletin FAQ.<br /><br />\r\n\r\nYou can edit the properties of a a set or entry from here by clicking edit across from the item name. (see <a href="help.php?do=answer&amp;page=faq&amp;pageaction=edit">Editing FAQ Entries and Sets</a>)<br /><br />\r\n\r\nYou can add additional sets or sub-sets. To do so, click Add in the left nav bar, or click Add Child across from the item you wish to add an item under. (see <a href="help.php?do=answer&amp;page=faq&amp;pageaction=add">Adding FAQ Entries and Sets</a>)', 'vbulletin', 'Jelsoft', 1205941222, '3.7.0 Release Candidate 1'),
(9993, -1, 'faq_modify_title', 'cphelptext', 'FAQ Administration', 'vbulletin', 'Jelsoft', 0, ''),
(9994, -1, 'forum_text', 'cphelptext', '<b>Related General Help Topics:</b><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=forum&amp;pageaction=&amp;option=">Forums</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=forumpermission&amp;pageaction=&amp;option=">Forum Permissions</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=moderator&amp;pageaction=&amp;option=">Moderators</a><br />\r\n<a href="help.php?s=&amp;do=answer&amp;page=resources&amp;pageaction=&amp;option=">Viewing Permissions</a><br /><br />\r\n\r\n<b>Forums:</b><br />\r\nThere are two kinds of forums, a forum and a category.  Forums can allow new threads and posts while categories cannot.  Categories are used to group forums by topic.  Forums of related topics are typically created as children of a parent category to group them by topic.<br /><br />\r\n\r\nWhen adding / editing a forum there is an option called "Act as Forum".  When this option is set to "Yes" the forum will act as a forum.  When this option is set to "No" the forum will act as a category.', 'vbulletin', 'Jelsoft', 1205431035, '3.7.0 Release Candidate 1'),
(9995, -1, 'forum_title', 'cphelptext', 'Forum Title', 'vbulletin', 'Jelsoft', 1275685613, '4.0.4'),
(9996, -1, 'forum_add_text', 'cphelptext', 'Options on this page are listed below.', 'vbulletin', 'Jelsoft', 0, ''),
(9997, -1, 'forum_add_title', 'cphelptext', 'Adding a Forum', 'vbulletin', 'Jelsoft', 0, ''),
(9998, -1, 'forum_add_edit_title_text', 'cphelptext', 'This is the title of the new forum. You may use HTML in this, but be aware this will also be used as the title of the page. Since HTML formatting is not valid in the title of a page, it will be shown as plain text, and will look odd.', 'vbulletin', 'Jelsoft', 0, ''),
(9999, -1, 'forum_add_edit_title_title', 'cphelptext', 'Title', 'vbulletin', 'Jelsoft', 0, ''),
(10000, -1, 'forum_add_edit_description_text', 'cphelptext', 'This description is shown under the forum name on your forums home page, and on the forums listing page (forumdisplay.php).', 'vbulletin', 'Jelsoft', 0, ''),
(10001, -1, 'forum_add_edit_description_title', 'cphelptext', 'Description', 'vbulletin', 'Jelsoft', 0, ''),
(10002, -1, 'forum_add_edit_link_text', 'cphelptext', 'If you enter a URL in this box, the forum will not be a real forum. Instead it will simply be a link to the URL you enter.', 'vbulletin', 'Jelsoft', 0, ''),
(10003, -1, 'forum_add_edit_link_title', 'cphelptext', 'Link to other page', 'vbulletin', 'Jelsoft', 0, ''),
(10004, -1, 'forum_add_edit_displayorder_text', 'cphelptext', 'This determines the order in which forums are displayed anywhere they are listed. If you set this to 0, the forum will not be displayed to end-users anywhere unless the user manually types in the URL to access it.', 'vbulletin', 'Jelsoft', 0, ''),
(10005, -1, 'forum_add_edit_displayorder_title', 'cphelptext', 'Display Order', 'vbulletin', 'Jelsoft', 0, ''),
(10006, -1, 'forum_add_edit_parentid_text', 'cphelptext', 'If you want this to be a subforum of another forum, select that forum from this list. If not, select (No One).', 'vbulletin', 'Jelsoft', 0, ''),
(10007, -1, 'forum_add_edit_parentid_title', 'cphelptext', 'Parent Forum', 'vbulletin', 'Jelsoft', 0, ''),
(10008, -1, 'forum_add_edit_daysprune_text', 'cphelptext', 'This determines how many days worth of threads are shown on forumdisplay. The value chosen here will be used by guests as well as forum members that leave this option set to "Forum Default" in their settings. Users can also change this temporarily by using the controls at the bottom of forumdisplay.', 'vbulletin', 'Jelsoft', 0, ''),
(10009, -1, 'forum_add_edit_daysprune_title', 'cphelptext', 'Default View Age', 'vbulletin', 'Jelsoft', 0, ''),
(10010, -1, 'forum_add_edit_defaultsortfield_text', 'cphelptext', 'This controls the field that threads are sorted using by default. Users may still selectively override this setting and resort using the controls at the bottom of the thread list.<br />\r\n<br />\r\nNote that to select sorting by thread rating, ratings must be enabled for this forum. If they are not, the forum will be sorted by last post.', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(10011, -1, 'forum_add_edit_defaultsortfield_title', 'cphelptext', 'Default Sort Field', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(10012, -1, 'forum_add_edit_defaultsortorder_text', 'cphelptext', 'This controls the order that threads are shown in by default. Users may still selectively override this setting and resort using the controls at the bottom of the thread list.', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(10013, -1, 'forum_add_edit_defaultsortorder_title', 'cphelptext', 'Default Sort Order', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(10014, -1, 'forum_add_edit_showprivate_text', 'cphelptext', 'Select ''No'' here will hide this forum from users who are not allowed to access it. Users who do have permission to access them will have to log in before they can see these forums too.<br />\r\n<br />\r\nEither of the yes options will allow users to see this forum if they don''t have access to it. The ability to see the forum''s post count is controlled by the two yes options. If the forum is passworded, it is treated as a private forum until the user enters the valid password.<br />\r\n<br />\r\nSetting this option to ''Use Default'' will use the setting in Forum Display Listings Options for this forum.', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(10015, -1, 'forum_add_edit_showprivate_title', 'cphelptext', 'Show Private Forum', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(10016, -1, 'forum_add_edit_newpostemail_text', 'cphelptext', 'Enter the email address(es) of anyone who should be notified when a new post is made in this forum. Separate multiple addresses with a space. It is suggested that you do not enter moderators'' email addresses here - you can select whether or not a moderator receives email notifications when adding that moderator.', 'vbulletin', 'Jelsoft', 0, ''),
(10017, -1, 'forum_add_edit_newpostemail_title', 'cphelptext', 'Email addresses to notify when there is a new post', 'vbulletin', 'Jelsoft', 0, ''),
(10018, -1, 'forum_add_edit_newthreademail_text', 'cphelptext', 'Enter the email address(es) of anyone who should be notified when a new thread is made in this forum. Separate multiple addresses with a space. It is suggested that you do not enter moderators'' email addresses here - you can select whether or not a moderator receives email notifications when adding that moderator. If you add a user''s email address to both the "Post" and "Thread" email notifications, that person will receive two emails when a new thread is started.', 'vbulletin', 'Jelsoft', 0, ''),
(10019, -1, 'forum_add_edit_newthreademail_title', 'cphelptext', 'Email addresses to notify when there is a new thread', 'vbulletin', 'Jelsoft', 0, ''),
(10020, -1, 'forum_add_edit_moderatenewpost_text', 'cphelptext', 'If this option is enabled, then posts will not show up in this forum until a moderator approves them.', 'vbulletin', 'Jelsoft', 0, ''),
(10021, -1, 'forum_add_edit_moderatenewpost_title', 'cphelptext', 'Moderate Posts', 'vbulletin', 'Jelsoft', 0, ''),
(10022, -1, 'forum_add_edit_moderatenewthread_text', 'cphelptext', 'Determines whether threads will require a moderator to approve them before being displayed.', 'vbulletin', 'Jelsoft', 0, ''),
(10023, -1, 'forum_add_edit_moderatenewthread_title', 'cphelptext', 'Thread Moderation Queue', 'vbulletin', 'Jelsoft', 0, ''),
(10024, -1, 'forum_add_edit_moderateattach_text', 'cphelptext', 'Determines whether new attachments will require a moderator to approve them before being displayed. If you have this set to Yes and Moderator Queue set to No, until the attachment is approved the post will be displayed with a message indicating that the attachment has successfully been posted but requires moderation.', 'vbulletin', 'Jelsoft', 0, ''),
(10025, -1, 'forum_add_edit_moderateattach_title', 'cphelptext', 'Attachment Queue', 'vbulletin', 'Jelsoft', 0, ''),
(10026, -1, 'forum_add_edit_styleid_text', 'cphelptext', 'You can select which style to use for this forum. This will override the Default forum style if selected.', 'vbulletin', 'Jelsoft', 0, ''),
