(10153, -1, 'forumpermission_edit_quickforum_caneditpost_title', 'cphelptext', 'Can edit own posts?', 'vbulletin', 'Jelsoft', 0, ''),
(10154, -1, 'forumpermission_edit_quickforum_candeletepost_text', 'cphelptext', 'If this is set to No, users will not be allowed to delete their posts. Note that if Can delete own threads below is set to No, they will not be able to delete their post if it is the first post in a thread, even if this is set to Yes.', 'vbulletin', 'Jelsoft', 0, ''),
