(10163, -1, 'forumpermission_edit_quickforum_canpostpoll_title', 'cphelptext', 'Can post polls?', 'vbulletin', 'Jelsoft', 0, ''),
(10164, -1, 'forumpermission_edit_quickforum_canvote_text', 'cphelptext', 'If this is set to Yes, users will be able to vote on polls in this forum.', 'vbulletin', 'Jelsoft', 0, ''),
