(10218, -1, 'image_addcategory_editcategory_displayorder_text', 'cphelptext', 'This determines in what order categories are displayed to the user.', 'vbulletin', 'Jelsoft', 0, ''),
(10219, -1, 'image_addcategory_editcategory_displayorder_title', 'cphelptext', 'Display Order', 'vbulletin', 'Jelsoft', 0, ''),
(10220, -1, 'image_edit_text', 'cphelptext', 'This page allows you to edit the settings for this image.  If you have any questions about specific settings please see the individual help topics.', 'vbulletin', 'Jelsoft', 0, ''),
(10221, -1, 'image_edit_title', 'cphelptext', 'Edit Avatar / Post Icon / Smilie', 'vbulletin', 'Jelsoft', 0, ''),
(10222, -1, 'image_editcategory_text', 'cphelptext', 'On this screen you can edit an existing image category. Images are displayed by category and you can set permissions per category as to who can use them (except for smilies).', 'vbulletin', 'Jelsoft', 0, ''),
