(11319, -1, 'phrase_add_title', 'cphelptext', 'Adding a new Phrase', 'vbulletin', 'Jelsoft', 0, ''),
(11320, -1, 'phrase_add_edit_phrasetypeid_text', 'cphelptext', 'The phrase type is a way of categorizing your phrases to make it easier to find them later. This consists of several built-in types, as well as any you have added. Select what type your new phrase is from the list.', 'vbulletin', 'Jelsoft', 0, ''),
(11321, -1, 'phrase_add_edit_phrasetypeid_title', 'cphelptext', 'Phrase Type', 'vbulletin', 'Jelsoft', 0, ''),
