INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`) VALUES
(11420, -1, 'profilefield_add_edit_def_text', 'cphelptext', '<b>Select Fields</b><br />\r\nIf you wish to have the first option set as the default choice for this field, set this to Yes. Otherwise, a blank line will be the default. This is useful to force users to select an option at registration if this is a required field.<br />\r\n<br />\r\n<b>Radio Fields</b><br />\r\nIf this is set to Yes, the first radio button will be preselected for the user. Otherwise, none will be selected. This is useful if you wish to force a user to select one if this field is required.', 'vbulletin', 'Jelsoft', 0, ''),
(11421, -1, 'profilefield_add_edit_def_title', 'cphelptext', 'Set Default?', 'vbulletin', 'Jelsoft', 0, ''),
(11422, -1, 'profilefield_add_edit_perline_text', 'cphelptext', 'This setting is how many controls to display per line on forms. Set this to 0 to show all controls on one line.', 'vbulletin', 'Jelsoft', 1199998638, '3.7.0 Beta 4'),
(11423, -1, 'profilefield_add_edit_perline_title', 'cphelptext', 'Items Per Line (<i>Radio, Checkbox only</i>)', 'vbulletin', 'Jelsoft', 1147805177, '3.6.0 Beta 1'),
(11424, -1, 'profilefield_add_edit_boxheight_text', 'cphelptext', 'This setting is how tall the box should be. If you have 12 options and want 3 of them displayed without the user having to use the scrollbar, set this to 3. It is recommended that you set this to at least 3 for user sanity reasons.', 'vbulletin', 'Jelsoft', 0, ''),
(11425, -1, 'profilefield_add_edit_boxheight_title', 'cphelptext', 'Box Height (<i>Select Multiple only</i>)', 'vbulletin', 'Jelsoft', 0, ''),
(11426, -1, 'profilefield_add_edit_displayorder_text', 'cphelptext', 'This sets the order in which fields are displayed. The default is to add it to the end of your current fields. Display order starts at 1 and can be any whole positive number (e.g. don''t use decimals or fractions).', 'vbulletin', 'Jelsoft', 0, ''),
(11427, -1, 'profilefield_add_edit_displayorder_title', 'cphelptext', 'Display Order', 'vbulletin', 'Jelsoft', 0, ''),
(11428, -1, 'profilefield_add_edit_required_text', 'cphelptext', '<b>Yes, at registration and profile updating</b> - User will be required to complete this field at registration and when updating their profile.<br />\r\n<br />\r\n<b>Yes, always</b> - User will be required to complete this field at registration.  Enabling this setting will force all users to complete it before they can continue using your forum. This applies only if the field is shown on the "Edit Your Details" page.<br />\r\n<br />\r\n<b>No</b> - Completion of this field is optional.<br />\r\n<br />\r\n<b>No, but display at registration</b> - Completion of this field is optional but it will appear at registration.', 'vbulletin', 'Jelsoft', 1214221606, '3.8.0 Alpha 1'),
(11429, -1, 'profilefield_add_edit_required_title', 'cphelptext', 'Field Required?', 'vbulletin', 'Jelsoft', 0, ''),
(11430, -1, 'profilefield_add_edit_editable_text', 'cphelptext', '<b>Yes</b> - User may edit this field at any time.<br />\r\n<br />\r\n<b>No</b>  - User may never edit this field.<br />\r\n<br />\r\n<b>Only at registration</b> - User may only edit this field at registration or if it is set to be required and it is blank.', 'vbulletin', 'Jelsoft', 1147803250, '3.6.0 Beta 1'),
(11431, -1, 'profilefield_add_edit_editable_title', 'cphelptext', 'Field Editable by User?', 'vbulletin', 'Jelsoft', 0, ''),
(11432, -1, 'profilefield_add_edit_hidden_text', 'cphelptext', 'If this is set to Yes, this field can be filled in by the user but will only be viewable to those with the proper usergroup permission.', 'vbulletin', 'Jelsoft', 0, ''),
(11433, -1, 'profilefield_add_edit_hidden_title', 'cphelptext', 'Private Field', 'vbulletin', 'Jelsoft', 0, ''),
(11434, -1, 'profilefield_add_edit_searchable_text', 'cphelptext', 'If this is set to No, users will not be able to search using this field on the Member''s List Advanced Search. If this field is set to Private than only those users with the proper usergroup permission will be able to search on it.', 'vbulletin', 'Jelsoft', 0, ''),
