(11639, -1, 'subscriptions_add_varname_title', 'cphelptext', 'Shortname', 'vbulletin', 'Jelsoft', 1142519066, '3.6.0 Beta 1'),
(11640, -1, 'subscriptions_add_edit_adminavatar_text', 'cphelptext', 'This option only applies if this subscription results in the user gaining permission to have custom avatars.\r\n\r\nIf the user selects a custom avatar, then they will be allowed to keep it after this subscription expires if their normal permissions do not allow for custom avatars.', 'vbulletin', 'Jelsoft', 1144702919, '3.6.0 Beta 1'),
