INSERT INTO `phrase` (`phraseid`, `languageid`, `varname`, `fieldname`, `text`, `product`, `username`, `dateline`, `version`) VALUES
(12592, -1, 'usertools_removesentpms_text', 'cphelptext', 'Click the "Yes" button below to remove all private messages sent by this user.  This will delete private messages from this user that are in other members'' inboxes.  This action cannot be undone!', 'vbulletin', 'Jelsoft', 0, ''),
(12593, -1, 'usertools_removesentpms_title', 'cphelptext', 'Remove User''s Sent PMs', 'vbulletin', 'Jelsoft', 0, ''),
(12594, -1, 'usertools_removesubs_text', 'cphelptext', 'Click the "Yes" button below to remove this user''s thread subscriptions.  If this user''s subscriptions are removed, they will no longer receive notifications of new replies in subscribed threads.  This action cannot be undone!', 'vbulletin', 'Jelsoft', 0, ''),
