(12643, -1, 'blog_admin_counters_text', 'cphelptext', 'This page contains several counters that you can update.<br /><br />\r\n\r\n<b>Rebuild Blog Entry Information:</b><br />\r\nThis feature will rebuild the total comments, trackbacks and last post information for each blog entry.<br /><br />\r\n\r\n<b>Rebuild Blog User Information:</b><br />\r\nThis will rebuild the total entry count for categories as well as the total entries, comments, trackbacks and last post information for each blog.<br /><br />\r\n\r\n<b>Rebuild Blog Counters:</b><br />\r\nThis will rebuild the total entry count for categories, total attachments and trackbacks for a blog entry.<br /><br />\r\n\r\n<b>Clear Parsed Text Cache:</b><br />\r\nThis will clear all the current cached text entries, this may be useful if you have added or changed permissions on a bbcode or other settings affecting post output.<br /><br />\r\n\r\n<b>Rebuild Attachment Thumbnails:</b><br />\r\nRebuilding thumbnails is necessary after changing your thumbnail settings in your <a href="options.php?dogroup=attachment&do=options" target="_blank">vBulletin options</a>.<br /><br />', 'vbblog', 'Scott', 1187602780, '1.0.0 Release Candidate 1'),
(12644, -1, 'blog_admin_counters_title', 'cphelptext', 'Update Counters', 'vbblog', 'Scott', 1187108960, '1.0.0 Beta 5'),
(12645, -1, 'blog_admin_editmod_canremovecomments_text', 'cphelptext', 'This permission controls removal of blog comments, if this permission is granted comments can be removed permanently from the database.', 'vbblog', 'freddie', 1187192239, '1.0.0 Beta 5'),
