(12909, -1, 'blog_entry_notify', 'emailbody', 'Dear $touser[username],\r\n\r\n$username has just replied to a blog entry you have subscribed to entitled - $entrytitle - in the $blogtitle blog of $vboptions[bbtitle].\r\n\r\nThis entry is located at:\r\n$vboptions[bburl]/blog.php?blogid=$blogid&goto=newpost\r\n\r\nHere is the message that has just been posted:\r\n***************\r\n$pagetext\r\n***************\r\n\r\n\r\nThere may also be other comments, but you will not receive any more notifications until you visit the forum or blog again.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this blog entry, please visit this page:\r\n$vboptions[bburl]/blog_subscription.php?do=unsubscribe&blogsubscribeentryid=$touser[blogsubscribeentryid]&auth=$touser[auth]\r\n', 'vbblog', 'Ed Sullivan', 1183114822, '1.0.0 Alpha 1'),
(12910, -1, 'blog_group_request_email', 'emailbody', 'Dear $userinfo[username],\r\n\r\n$fromuserinfo[username] has requested that you join his/her blog. You can approve or deny this request by visiting:\r\n\r\n$vboptions[bburl]/blog_usercp.php?do=groups\r\n\r\nAll the best,\r\n$vboptions[bbtitle]', 'vbblog', 'freddie', 1214345510, '2.0.0 Beta 1'),
(12911, -1, 'blog_user_notify', 'emailbody', 'Dear $touser[username],\r\n\r\n$username has posted a new entry in their blog: $blogtitle blog of $vboptions[bbtitle] titled ''$entrytitle''\r\n\r\nThis entry is located at:\r\n$vboptions[bburl]/blog.php?blogid=$blogid\r\n\r\nHere is the message that has just been posted:\r\n***************\r\n$pagetext\r\n***************\r\n\r\nThere may also be other comments, but you will not receive any more notifications until you visit the forum or blog again.\r\n\r\nAll the best,\r\n$vboptions[bbtitle]\r\n\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~\r\nUnsubscription information:\r\n\r\nTo unsubscribe from this user''s blog, please visit this page:\r\n$vboptions[bburl]/blog_subscription.php?do=unsubscribe&blogsubscribeuserid=$touser[blogsubscribeuserid]&auth=$touser[auth]\r\n', 'vbblog', 'Ed Sullivan', 1183115154, '1.0.0 Alpha 1'),
(12912, -1, 'report_blog_comment', 'emailbody', '$reportinfo[rusername] ( mailto:$bbuserinfo[email] ) has reported this blog comment:\r\n\r\n$vboptions[bburl]/blog.php?bt=$reportinfo[blogtextid]\r\n\r\nThis is part of this blog entry:\r\n$reportinfo[entrytitle]\r\n$vboptions[bburl]/blog.php?b=$reportinfo[blogid]\r\n\r\nThis is the reason that the user gave:\r\n$reportinfo[reason]\r\n\r\n$reportinfo[discuss]\r\n\r\nThis message has been sent to all moderators of this blog, or all administrators if there are no moderators.\r\n\r\nPlease respond to this comment as applicable.', 'vbblog', 'freddie', 1221088230, '2.0.0 Beta 1'),
