(13758, -1, 'setting_vbblog_tagcloud_searchcloud_desc', 'vbsettings', 'This option controls whether a tag cloud relating to the frequency of tag searches is shown at the bottom of the advanced search.<br />\r\n<br />\r\nA usage-based tag cloud is always shown <a href="../blog_tag.php" target="_blank">here</a>.', 'vbblog', 'freddie', 1216929997, '2.0.0 Beta 1'),
(13759, -1, 'setting_vbblog_tagcloud_searchcloud_title', 'vbsettings', 'Enable Search Tag Cloud', 'vbblog', 'freddie', 1216929997, '2.0.0 Beta 1'),
(13760, -1, 'setting_vbblog_tagcloud_searchhistory_desc', 'vbsettings', 'Amount of days that the system will keep a record of tag searches for use in the search tag cloud.<br />\r\n<br />\r\n0 means to use all data available.', 'vbblog', 'freddie', 1216929997, '2.0.0 Beta 1'),
(13761, -1, 'setting_vbblog_tagcloud_searchhistory_title', 'vbsettings', 'Search Tag Cloud: History (Days)', 'vbblog', 'freddie', 1216929997, '2.0.0 Beta 1'),
