INSERT INTO `unc_news` (`news_id`, `news_title`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`) VALUES
(105, ' Dân trí và Hội cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An ', ' (Dân trí) - Chiều 29/9 (ngày 14/8 âm lịch), đại diện báo Dân trí và Hội cha mẹ Nhân Ái TPHCM đã đến thị xã Tân An, tỉnh Long An để tổ chức trung thu cho các em nhỏ nơi đây. ', '<div class="fon34 mt3 mr2 fon43"><DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;<IMG alt="Dân trí và cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-22-1a559.JPG"></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Sau khi tổ chức Bữa sáng yêu thương ở BV Nhi đồng 2, chuyến xe từ thiện mới khởi hành đi Long An. Đến địa điểm tập trung là Ban điều hành khu phố 4, phường 2, thị xã Tân An thì đã giữa trưa.</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG style="WIDTH: 445px; HEIGHT: 366px" alt="Dân trí và cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-01-1a559.jpg" width=409 height=336></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Mọi người vội vã bắt tay vào công tác chuẩn bị: bố Sĩ (hội trưởng) dán số thứ tự xuống dưới ghế để chuẩn bị chơi trò chơi, các mẹ thì chuẩn bị quà, các bố khác lo phông nền, bong bóng…</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Dân trí và cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-06-1a559.jpg"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Quà dành cho các bé rất “hoành tráng”: một lốc tập trắng, bộ đồng phục học sinh, một chiếc cặp sách, bánh trung thu, lồng đèn, 1 lít sữa Cô gái Hà Lan, 5kg gạo loại 12.000đ/kg… mỗi phần quà trị giá khoảng 300.000đ</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Áo đồng phục với kiểu dáng dễ thương có nhiều size tùy theo độ tuổi các bé" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-05-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Áo đồng phục với kiểu dáng dễ thương có nhiều size tùy theo độ tuổi các bé</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Áo đồng phục với kiểu dáng dễ thương có nhiều size tùy theo độ tuổi các bé" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-04-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bánh trung thu bằng rau câu (thạch) này là sản phẩm “cây nhà lá vườn” của một thành viên HCMNA có nick name là mẹ Ti Tom</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Bữa trưa vội vàng của mọi người là ổ bánh mì và chai nước suối" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-07-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bữa trưa vội vàng của mọi người là ổ bánh mì và chai nước suối</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Các “mầm non” của HCMNA cũng đến chơi với các bạn ở Long An" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-21-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Các “mầm non” của HCMNA cũng đến chơi với các bạn ở Long An</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="2 giờ chiều, các bé đã kéo đến chật cả khoảng sân của Ban điều hành khu phố" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-08-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">2 giờ chiều, các bé đã kéo đến chật cả khoảng sân của Ban điều hành khu phố</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Hào hứng tham gia trò chơi do mẹ Song Anh, mẹ Xá Xị làm người dẫn chương trình" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-11-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Hào hứng tham gia trò chơi do mẹ Song Anh, mẹ Xá Xị làm người dẫn chương trình</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Dường như các bé chưa bao giờ được tham gia hoạt động vui nhộn thế này" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-02-1a559.jpg"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Dường như các bé chưa bao giờ được tham gia hoạt động vui nhộn thế này</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Ánh mắt háo hức, nụ cười hồn nhiên xua đi cái oi nồng của buổi ban trưa" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-14-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ánh mắt háo hức, nụ cười hồn nhiên xua đi cái oi nồng của buổi ban trưa</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Phụ huynh không thể nhịn cười trước câu trả lời ngộ nghĩnh: “Con năm nay 6 tuổi, học lớp 8” (!!!)" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-12-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Phụ huynh không thể nhịn cười trước câu trả lời ngộ nghĩnh: “Con năm nay 6 tuổi, học lớp 8” (!!!)</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Cuối cùng là tiết mục trao quà" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-19-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Cuối cùng là "tiết mục" trao quà<BR><BR><IMG style="MARGIN: 5px" alt="Chà, quà nặng quá... phụ huynh phải xách giùm các bé thôi" align=center src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-16-1a559.JPG" width=450 _fl="null"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Chà, quà nặng quá... phụ huynh phải xách giùm các bé thôi</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;<SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></DIV>\r\n<DIV align=center><IMG alt="Người mẹ đang đi bán vé số cũng ghé vào xem con gái&nbsp;vui trung thu với các bạn..." src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-17-1a559.JPG"></DIV>\r\n<DIV align=center></SPAN><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Người mẹ đang đi bán vé số cũng ghé vào xem con gái&nbsp;vui trung thu với các bạn...</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG alt="Hẹn gặp lại Long An vào các chuyến từ thiện sau này!" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-20-1a559.JPG"></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Hẹn gặp lại Long An vào các chuyến từ thiện sau này!</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">\r\n<DIV align=center>\r\n<TABLE style="BORDER-BOTTOM: black 1px solid; BORDER-LEFT: black 1px solid; MARGIN: 0px; WIDTH: 550px; BORDER-COLLAPSE: collapse; BACKGROUND: #e6e6fa; HEIGHT: 781px; BORDER-TOP: black 1px solid; BORDER-RIGHT: black 1px solid" cellSpacing=5 cellPadding=0 align=center>\r\n<TBODY>\r\n<TR>\r\n<TD style="TEXT-ALIGN: center; VERTICAL-ALIGN: top">\r\n<P align=center><IMG alt="1.2 tấn sữa Cô gái Hà Lan đã đến tay trẻ em nghèo" src="http://dantri4.vcmedia.vn/t91w87nI95JGqjMM6t4Y/Image/2012/09/Phat-sua-o-BVUB-1-1a559.jpg"></P>\r\n<DIV align=left><SPAN style="FONT-WEIGHT: bold">100 thùng sữa Cô gái Hà Lan đã đến tay trẻ em nghèo </SPAN></DIV>\r\n<P align=left></P>\r\n<P align=left>Đây là phần quà do công ty Friesland Campina Việt Nam trao tặng cho trẻ em nghèo thông qua báo Dân trí. 100 thùng sữa, mỗi thùng gồm 12 hộp sữa loại 1 lít đã được Hội cha mẹ Nhân Ái tiếp nhận để trao tận tay các em. Cụ thể:</P>\r\n<P align=left>1. BV Ung Bướu TPHCM: 35 thùng, ngày 26/9</P>\r\n<P align=left>2. BV Phạm Ngọc Thạch TPHCM: 10 thùng, ngày 29/9</P>\r\n<P align=left>3. Chương trình Trăng rằm Nhân Ái ở Cần Thơ: 10 thùng, ngày 29/9</P>\r\n<P align=left>4. Tổ trợ giúp bệnh nhân nghèo BV Nhi đồng 2: 10 thùng, ngày 29/9</P>\r\n<P align=left>5. Chương trình Trăng rằm Nhân Ái ở Long An: 10 thùng, ngày 29/9</P>\r\n<P align=left>6. Trao cho trẻ em nghèo khu Gò Mả, phường 5, quận 8, TPHCM: 12 thùng, ngày 27/9</P>\r\n<P align=left>7. Còn lại 13 thùng sẽ phát vào Bữa sáng yêu thương tại BV Nhi đồng 2 vào&nbsp;thứ bảy, ngày 6/10.</P></TD></TR></TBODY></TABLE></DIV></SPAN>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<P align=right><SPAN style="FONT-WEIGHT: bold">Hồng Nhung - Phương Hà</SPAN></P><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/></div>\r\n \r\n <div itemscope itemtype="http://schema.org/webpage" style="display: none !important;">\r\n <span itemprop="name">Dân trí và Hội cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An</span>\r\n <img id="ctl00_IDContent_ctl00_imgAvatar_2" itemprop="image" src="http://dantri21.vcmedia.vn/zoom/70_70/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-21-1a559.JPG" alt="Dân trí và Hội cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An" style="border-width:0px;" />\r\n <div id="static_rating" itemprop="aggregateRating" itemscope itemtype="http://schema.org/AggregateRating">\r\n  <span itemprop="ratingValue">8</span>\r\n  <span itemprop="bestRating">10</span>\r\n  <span itemprop="ratingCount">1</span>\r\n </div>\r\n </div>\r\n <div class="hrecipe" style="position: fixed; bottom: -1000px; left: -1000px;">\r\n <span class="item">\r\n  <span class="item">\r\n  <span class="fn">\r\n   Dân trí và Hội cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An</span>\r\n  </span>\r\n  <img id="ctl00_IDContent_ctl00_imgAvatar_3" class="photo" src="http://dantri21.vcmedia.vn/zoom/70_70/t91w87nI95JGqjMM6t4Y/Image/2012/09/Long-An-21-1a559.JPG" alt="Dân trí và Hội cha mẹ Nhân Ái mang trung thu đến với trẻ em nghèo ở Long An" style="border-width:0px;" />\r\n  <span class="review hreview-aggregate">\r\n  <span class="rating">\r\n   <span class="best">10</span>\r\n   <span class="average">8</span> \r\n   <span class="count"> 1</span>\r\n  </span> \r\n  </span>\r\n </span>\r\n </div>\r\n \r\n <div style="text-align:center; padding-top:5px" id="div_tamlongnhanai"></div>\r\n <div style="text-align:center; padding-top:5px"><script type="text/javascript" src="http://admicro1.vcmedia.vn/ads_codes/ads_box_230.ads"></script></div>\r\n <div class="emailprint">\r\n <ul>\r\n  <li><a href="javascript:;" onclick="share_facebook();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/facebook1.gif" alt="Share on Facebook" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/> </a></li>\r\n  <li><a href="javascript:;" onclick="share_twitter();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/twitter1.gif" alt="Share on Twitter" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a href="javascript:;" onclick="share_google();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/google1.gif" alt="Share on Google" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a href="javascript:;" onclick="share_buzz();" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/buzz1.gif" alt="Share on Buzz" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a class="dt-print" href="javascript:;" onclick="return OpenPrint();" rel="nofollow"><span>in</span></a></li>\r\n  <li><a class="dt-respond" href="javascript:;" onclick="ShowHideComment();return false;" rel="nofollow"><span>Xem phản hồi</span></a></li>\r\n  <li><a class="dt-respond" href="javascript:;" onclick="ShowHideSendComment();return false;" rel="nofollow"><span>Gửi phản hồi</span></a></li>\r\n </ul>\r\n </div>\r\n \r\n <div class="neo-dtcontent" id="divComment" style="display: none;">\r\n \r\n <input type="hidden" id="hidSendComment" value="http://comment.dantri.com.vn/default.aspx?newsid=399664"/><input type="hidden" id="hidViewComment" value="http://comment.dantri.com.vn/ListReplyComment.aspx?newsid=399664"/>\r\n <iframe height="670" id="ifViewComment" frameborder="0" width="460" scrolling="no" class="IframeComment"></iframe>\r\n </div>\r\n <div class="neo-dt', '', '2012-10-02 10:59:46', '0000-00-00 00:00:00', 'private', 0, 0, 0),
(106, ' Trăng rằm nhân ái đến với trẻ em nghèo cố đô Huế ', ' (Dân trí) - Trong 2 ngày 29,30 tháng 9 (tức 14 và 15 tháng 8 Âm lịch), hàng trăm em nhỏ của xóm vạn đò Thuỷ Phú (Thị xã Hương Trà) và phường Thuỷ Lương (Thị xã Hương Thuỷ) được đón một cái tết trung thu thật ý nghĩa và ám áp. ', '<div class="fon34 mt3 mr2 fon43"><P class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt">Tại thôn Thuỷ Phú, xã Hương Vinh, Thị xã Hương Trà là nơi duy nhất còn tồn tại xóm vạn đò trên toàn địa bàn thị xã. Báo <SPAN style="FONT-STYLE: italic">Dân Trí</SPAN> đã cùng với Hội Cha mẹ nhân ái tổ chức cho các em thiếu nhi một buổi trung thu đầy ý nghĩa và trao 40 suất quà bao gồm sách vở, bút, đồ chơi và đặc biệt là áo phao cứu sinh đến các em thiếu nhi sống trên sông nước nơi đây</SPAN></P>\r\n<P class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt">Tại bữa tiệc trung thu, các em nhỏ được các tình nguyện viên tổ chức trò chơi như múa lân, rước đèn, phá cỗ... khơi lại không khí trung thu ngày xưa cho các em.</SPAN></P>\r\n<P style="TEXT-ALIGN: center" class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt"></SPAN></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1104-15a61.JPG" width=448 _fl="" alt="Vui Trung thu cùng trẻ em vạn đò thôn Thủy Phú"></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Vui Trung thu cùng trẻ em vạn đò thôn Thủy Phú</SPAN><BR></DIV>\r\n<P></P>\r\n<P class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt">Em Nguyễn Thị Thu Sương ( học sinh lớp 7) tâm sự: "Em rất vui sướng và hạnh phúc vì đây là lầm đầu tiên em được đón trung thu, được nhận quà. Em ước mơ sau này lớn lên sẽ làm bác sĩ để giúp đỡ ba mẹ em".</SPAN></P>\r\n<P class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt">Ông Nguyễn Văn Nguyên, trưởng thôn Thuỷ Phú chia sẻ: “Nơi đây có 17 hộ dân với 90 nhân khẩu, trong đó có gần 40 em nhỏ đang trong độ tuổi cắp sách đến trường. Cuộc sống nghèo khó nên kiếm cái ăn thôi cũng rất khó khăn chứ nói gì đến việc tổ chức trung thu cho con cháu. Tôi xin thay mặt người dân, cám ơn Báo <SPAN style="FONT-STYLE: italic">Dân Trí</SPAN>, cám ơn Hội Cha mẹ bé nhân ái tại Huế đã tổ chức cho các em thiếu nhi vạn đò có một cái tết trung thu ý nghĩa như thế này.</SPAN></P>\r\n<P class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt">Để tổ chức đợt trung thu sẻ chia tình cảm với các em nhỏ vùng sông nước, Quỹ Nhân ái Báo điện tử Dân trí trích 5 triệu đồng phối hợp với Hội Cha mẹ bé Nhân ái ở Huế&nbsp;để tặng&nbsp;gần 300 suất quà cho cả 2 nơi. Trong đó, có 100 suất trị giá hơn 200 nghìn đồng/suất, 100 suất trị giá 150 nghìn đồng/suất và 100 suất trị giá 100 nghìn đồng/suất.</SPAN></P>\r\n<P style="FONT-WEIGHT: bold" class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-STYLE: italic; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt; FONT-WEIGHT: normal">Chùm ảnh ghi lại không khí đón Trung thu của trẻ em vùng sông nước trên địa bàn tỉnh Thừa Thiên Huế do&nbsp;Báo Dân trí và Hội Cha mẹ bé Nhân ái&nbsp;tại Huế tổ chức</SPAN></P>\r\n<P style="TEXT-ALIGN: center" class=MsoNormal><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt; mso-bidi-font-size: 11.0pt"></SPAN></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/11015-15a61.JPG" width=448 _fl="" alt="Mâm cỗ được bày giữa bãi cát ven sông"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Mâm cỗ được bày giữa bãi cát ven sông</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1105-15a61.JPG" width=448 _fl="" alt="Các em chân trần nhảy múa cùng các anh chị tình nguyện viên và hội cha mẹ bé Nhân ái Huế"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Các em chân trần nhảy múa cùng các anh chị tình nguyện viên và hội cha mẹ bé Nhân ái Huế</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1106-15a61.JPG" width=448 _fl="" alt="Các em nhận quà là áo phao và kẹo bánh, lồng đèn, vở viết"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Các em nhận quà là áo phao và kẹo bánh, lồng đèn, vở viết</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1107-15a61.JPG" width=448 _fl="" alt="Trĩu nặng quà trên tay"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Trĩu nặng quà trên tay</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1108-15a61.JPG" width=448 _fl="" alt="Những con thuyền nơi các em và cha mẹ sinh sống vui hẳn trong ngày Trung thu đầy ý nghĩa"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Những con thuyền nơi các em và cha mẹ sinh sống vui hẳn trong ngày Trung thu đầy ý nghĩa</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1109-15a61.JPG" width=448 _fl="" alt="Những con thuyền nơi các em và cha mẹ sinh sống vui hẳn trong ngày Trung thu đầy ý nghĩa"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Những trẻ em sông nước ngoái lại nhìn mọi người thầm cảm ơn đã cho các em một trung thu thật nhiều quà lần đầu trong đời</SPAN><BR style="FONT-FAMILY: Tahoma"></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/11010-15a61.JPG" width=448 _fl="" alt="Tối rằm 15/8 âm lịch, tại phường Thủy Lương - TX Hương Thủy"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Tối rằm 15/8 âm lịch, tại phường Thủy Lương - TX Hương Thủy</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/11011-15a61.JPG" width=448 _fl="" alt="Chương trình Trăng rằm nhân ái tiếp tục đến với nhiều em nghèo khó khăn"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Chương trình Trăng rằm nhân ái tiếp tục đến với nhiều em nghèo khó khăn</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/11014-15a61.JPG" width=421 _fl="" alt="Các tiết mục văn nghệ cây nhà lá vườn"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Các tiết mục văn nghệ "cây nhà lá vườn"</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/11012-15a61.JPG" width=448 _fl="" alt="Và tiếp tục nhiều quà tặng cho các em. Một số em không giấu nổi tò mò đã mở ngay ra xem"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Và tiếp tục nhiều quà tặng cho các em. Một số em không giấu nổi tò mò đã mở ngay ra xem</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/19/11013-15a61.JPG" width=336 _fl="" alt="Niềm vui của một bé gái được nhận quà trung thu"></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Niềm vui của một bé gái được nhận quà trung thu</SPAN><BR></DIV>\r\n<P></P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal align=right><SPAN style="LINE-HEIGHT: 115%; FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt; FONT-WEIGHT: bold">Nguyễn Dũng – Đại Dương</SPAN></P><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/></div>\r\n \r\n <div itemscope itemtype="http://schema.org/webpage" style="display: none !important;">\r\n <span itemprop="name">Trăng rằm nhân ái đến với trẻ em nghèo cố đô Huế</span>\r\n <img id="ctl00_IDContent_ctl00_imgAvatar_2" itemprop="image" src="http://dantri21.vcmedia.vn/zoom/70_70/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1104-15a61.JPG" alt="Trăng rằm nhân ái đến với trẻ em nghèo cố đô Huế" style="border-width:0px;" />\r\n <div id="static_rating" itemprop="aggregateRating" itemscope itemtype="http://schema.org/AggregateRating">\r\n  <span itemprop="ratingValue">6</span>\r\n  <span itemprop="bestRating">10</span>\r\n  <span itemprop="ratingCount">1</span>\r\n </div>\r\n </div>\r\n <div class="hrecipe" style="position: fixed; bottom: -1000px; left: -1000px;">\r\n <span class="item">\r\n  <span class="item">\r\n  <span class="fn">\r\n   Trăng rằm nhân ái đến với trẻ em nghèo cố đô Huế</span>\r\n  </span>\r\n  <img id="ctl00_IDContent_ctl00_imgAvatar_3" class="photo" src="http://dantri21.vcmedia.vn/zoom/70_70/HvrZZiM2C5QLR4u67tfR/Image/2012/19/1104-15a61.JPG" alt="Trăng rằm nhân ái đến với trẻ em nghèo cố đô Huế" style="border-width:0px;" />\r\n  <span class="review hreview-aggregate">\r\n  <span class="rating">\r\n   <span class="best">10</span>\r\n   <span class="average">6</span> \r\n   <span class="count"> 1</span>\r\n  </span> \r\n  </span>\r\n </span>\r\n </div>\r\n \r\n <div style="text-align:center; padding-top:5px" id="div_tamlongnhanai"></div>\r\n <div style="text-align:center; padding-top:5px"><script type="text/javascript" src="http://admicro1.vcmedia.vn/ads_codes/ads_box_230.ads"></script></div>\r\n <div class="emailprint">\r\n <ul>\r\n  <li><a href="javascript:;" onclick="share_facebook();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/facebook1.gif" alt="Share on Facebook" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/> </a></li>\r\n  <li><a href="javascript:;" onclick="share_twitter();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/twitter1.gif" alt="Share on Twitter" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a href="javascript:;" onclick="share_google();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/google1.gif" alt="Share on Google" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a href="javascript:;" onclick="share_buzz();" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/buzz1.gif" alt="Share on Buzz" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a class="dt-print" href="javascript:;" onclick="return OpenPrint();" rel="nofollow"><span>in</span></a></li>\r\n  <li><a class="dt-respond" href="javascript:;" onclick="ShowHideComment();return false;" rel="nofollow"><span>Xem phản hồi</span></a></li>\r\n  <li><a class="dt-respond" href="javascript:;" onclick="ShowHideSendComment();return false;" rel="nofollow"><span>Gửi phản hồi</span></a></li>\r\n </ul>\r\n </div>\r\n \r\n <div class="neo-dtcontent" id="divComment" style="display: none;">\r\n \r\n <input type="hidden" id="hidSendComment" value="http://comment.dantri.com.vn/default.aspx?newsid=399657"/><input type="hidden" id="hidViewComment" value="http://comment.dantri.com.vn/ListReplyComment.aspx?newsid=399657"/>\r\n <iframe height="670" id="ifViewComment" frameborder="0" width="460" scrolling="no" class="IframeComment"></iframe>\r\n </div>\r\n <div class="neo-dtcontent" id="divSendComment" style="display: none;">\r\n <iframe height="250" id="ifSendComment" frameborder="0"', '', '2012-10-02 10:59:46', '0000-00-00 00:00:00', 'private', 0, 0, 0),
(107, ' Quà trung thu kịp thời đến với bé Nhân Hòa ở Kiên Giang ', ' (Dân trí) Sau khi kết thúc “Đêm hội trăng rằm” với 1.000 bé ở Cần Thơ, PV Dân trí đến Kiên Giang thăm và tặng quà trung thu cho bé Nhân Hòa – nhân vật trong bài viết: “Một sinh linh vô thừa nhận rất cần được giúp đỡ” được Dân trí đăng tải tháng 5/2011.<br><a href="http://dantri.com.vn/c167/s167-482292/Mot-sinh-linh-vo-thua-nhan-rat-can-duoc-giup-do.htm"><b>&nbsp;>>&nbsp;Một sinh linh vô thừa nhận rất cần được giúp đỡ</b></a><br><a href="http://dantri.com.vn/c167/s167-493888/Gia-dinh-be-han-Hoa-xin-nhan-lai-chau.htm"><b>&nbsp;>>&nbsp;Gia đình bé Nhân Hòa xin nhận lại cháu</b></a> ', '<div class="fon34 mt3 mr2 fon43"><P style="TEXT-ALIGN: justify" class=MsoNormal>Đến Kiên Giang, chúng tôi bắt xe ôm đến bến đò Ngã Cái, tại đây chúng tôi được ông ngoại bé Nhân Hòa Nguyễn Ngọc Sâm ra đón chúng tôi, vì theo ông Sâm cho biết đường vào nhà quanh co, nhiều ngã rẻ nên khó lòng tìm đến nhà.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/10/h1-d93d2.jpg" width=336 _fl="" alt="Từ khi Nhân Hòa được mẹ đưa về nhà ông ngoại sinh sống, cu cậu khỏe mạnh, tăng cân, ít bị bệnh"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Từ khi Nhân Hòa được mẹ đưa về nhà ông ngoại sinh sống, cu cậu khỏe mạnh, tăng cân, ít bị bệnh<BR></SPAN></DIV><BR>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Như một thói quen tự nhiên, chiếc xe máy của ông Sâm còn ở tận ngoài ngõ, bé Nhân Hòa đã nhận biết, liền chạy ù ra sân đón ông ngoại với tiếng cười ríu rít, nụng nịu đòi ông ngoại bế cho bằng được. Khi gặp chúng tôi, bé Nhân Hòa có chút e dè, nhưng chỉ mất vài phút cậu Hòa đã cho bế, thậm chí nắm tay dẫn chúng tôi đi quanh nhà. Đặc biệt khi ông Sâm hỏi nhà mới của Nhân Hòa ở đâu, bé Hòa chỉ thẳng tay ra đống gạch đá ngoài sân (cạnh trường học -PV) mà ông Sâm định ra riêng cho hai mẹ con chị Hồng.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Ông Sâm cho biết: “Thấy con nó đi làm công nhân vất vả, tiền công chẳng còn bao nhiêu sau khi trừ chi phí nhà trọ, tiền ăn uống và tiền gửi bé Nhân Hòa, … Nên vợ chồng tui bảo mẹ con nó về đây, tui cất cho cái nhà cạnh trường học để bán tạp hóa sinh sống và mẹ con nó đã đồng ý rồi, bây giờ chỉ lo kinh phí xây nhà.”</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/10/h2-d93d2.jpg" width=448 _fl="" alt="Sau phút tinh nghịch,&nbsp;bé Hòa tự vào tủ lạnh lấy sữa uống"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Sau phút tinh nghịch,&nbsp;bé Hòa tự vào tủ lạnh lấy sữa uống</SPAN><BR></DIV><BR>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Theo ông Sâm, cân nhà mà ông dự định cất cho mẹ con chị Hồng có chiều ngang 4m, dài 7m, tổng kinh phí dự trù trên 40 triệu đồng, nhưng gia đình chỉ có hơn một nửa, khoản còn lại ông Sâm định đi vay ngân hàng. Ngoài ra ông Sâm cho biết từ khi chị Hồng đưa cháu Nhân Hòa về quê sinh sống, bé khỏe mạnh, tăng cân, ít bị bệnh. Theo ông có thể ở nhà trọ cháu Hòa ngột ngạt, không có sân chơi nên hay bệnh… </P>Chị Hồng – mẹ ruột bé Nhân Hòa cho biết lần tái khám mới đây (cách nay 1 tháng - PV) các bác sĩ cho biết sức khỏe bé Hòa phát triển tốt, không có dấu hiệu bất thường. Mặc dù vậy, các bác sĩ căn dặn khi đến ngày tái khám gia đình vẫn phải đưa bé Nhân Hòa lên cho các bác sĩ kiểm tra lại.\r\n<P></P>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/10/h3-d93d2.jpg" width=448 _fl="" alt="Bé Nhân Hòa tỏ ra rất thích với hộp bánh trung thu này"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bé Nhân Hòa tỏ ra rất thích với hộp bánh trung thu này</SPAN><BR></DIV>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Tiếp nhận quà trung thu của báo <SPAN style="FONT-STYLE: italic">Dân trí</SPAN>, chị Hồng bảy tỏ lời cám ơn đến Ban Biên tập báo Dân trí, các tấm lòng hảo tâm đã sẻ chia, giúp đỡ gia đình trong thời gian qua, đặc biệt là cho mẹ con chị nhận lại nhau sau quyết định dại dột của thời con gái.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Được biết, cái tên Nhân Hòa do Tổng biên tập&nbsp;Phạm Huy Hoàn<SPAN style="FONT-STYLE: italic">&nbsp;</SPAN>đặt cho cu cậu Nhân Hòa, chấm dứt 12&nbsp;ngày nghiệt ngã sống trong đau đớn và cô độc. Và từ lúc ấy cho đến nay Nhân Hòa không chỉ sống trong tình yêu thương của&nbsp;gia đình mà con sống trong trong vòng tay Nhân ái của <SPAN style="FONT-STYLE: italic">Dân trí </SPAN>và bạn đọc gần xa. </P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal align=right><SPAN style="FONT-WEIGHT: bold">Ngô Nguyễn</SPAN></P><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/></div>\r\n \r\n <div itemscope itemtype="http://schema.org/webpage" style="display: none !important;">\r\n <span itemprop="name">Quà trung thu kịp thời đến với bé Nhân Hòa ở Kiên Giang</span>\r\n <img id="ctl00_IDContent_ctl00_imgAvatar_2" itemprop="image" src="http://dantri21.vcmedia.vn/zoom/70_70/x9pGccccccccccccDacc/Image/2012/10/h3-d93d2.jpg" alt="Quà trung thu kịp thời đến với bé Nhân Hòa ở Kiên Giang" style="border-width:0px;" />\r\n <div id="static_rating" itemprop="aggregateRating" itemscope itemtype="http://schema.org/AggregateRating">\r\n  <span itemprop="ratingValue">9</span>\r\n  <span itemprop="bestRating">10</span>\r\n  <span itemprop="ratingCount">1</span>\r\n </div>\r\n </div>\r\n <div class="hrecipe" style="position: fixed; bottom: -1000px; left: -1000px;">\r\n <span class="item">\r\n  <span class="item">\r\n  <span class="fn">\r\n   Quà trung thu kịp thời đến với bé Nhân Hòa ở Kiên Giang</span>\r\n  </span>\r\n  <img id="ctl00_IDContent_ctl00_imgAvatar_3" class="photo" src="http://dantri21.vcmedia.vn/zoom/70_70/x9pGccccccccccccDacc/Image/2012/10/h3-d93d2.jpg" alt="Quà trung thu kịp thời đến với bé Nhân Hòa ở Kiên Giang" style="border-width:0px;" />\r\n  <span class="review hreview-aggregate">\r\n  <span class="rating">\r\n   <span class="best">10</span>\r\n   <span class="average">9</span> \r\n   <span class="count"> 1</span>\r\n  </span> \r\n  </span>\r\n </span>\r\n </div>\r\n \r\n <div style="text-align:center; padding-top:5px" id="div_tamlongnhanai"></div>\r\n <div style="text-align:center; padding-top:5px"><script type="text/javascript" src="http://admicro1.vcmedia.vn/ads_codes/ads_box_230.ads"></script></div>\r\n <div class="emailprint">\r\n <ul>\r\n  <li><a href="javascript:;" onclick="share_facebook();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/facebook1.gif" alt="Share on Facebook" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/> </a></li>\r\n  <li><a href="javascript:;" onclick="share_twitter();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/twitter1.gif" alt="Share on Twitter" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a href="javascript:;" onclick="share_google();" style="margin-right: 2px" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/google1.gif" alt="Share on Google" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a href="javascript:;" onclick="share_buzz();" rel="nofollow"><img src="http://dantri3.vcmedia.vn/App_Themes/Default/Images/buzz1.gif" alt="Share on Buzz" border="0" style="height:20px;padding-top:4px;vertical-align:top;width:20px"/></a></li>\r\n  <li><a class="dt-print" href="javascript:;" onclick="return OpenPrint();" rel="nofollow"><span', '', '2012-10-02 10:59:46', '0000-00-00 00:00:00', 'private', 0, 0, 0),
(108, ' Ông Trầm Bê mất trộm sừng tê giác trị giá hơn 4 tỷ đồng ', ' <a href = "http://dantri.com.vn/c76/s76-646997/ong-tram-be-mat-trom-sung-te-giac-tri-gia-hon-4-ty-dong.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/Ic3EyFHpPWFvMJOJFocc/Image/2012/09/2300-5-d7a75.gif" /></a>Công an huyện Trà Cú, tỉnh Trà Vinh đang truy tìm kẻ trộm sừng tê giác tại khu đất gia đình của ông Trầm Bê (thuộc xã Hàm Giang, huyện Trà Cú). ', '', '', '2012-10-02 10:59:46', '0000-00-00 00:00:00', 'private', 0, 0, 0),
(109, ' Vinacomin chưa thể thoái vốn khỏi Bảo hiểm SHB - Vinacomin ', ' (Dân trí) - Phiên đấu giá bán cổ phần của Vinacomin tại SVIC dự kiến diễn ra vào ngày mai đã buộc phải hủy bỏ do chỉ có duy nhất một nhà đầu tư đăng ký tham gia, không đủ điều kiện tổ chức. ', '<div class="fon34 mt3 mr2 fon43"><DIV align=center>\r\n<DIV>\r\n<DIV><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/hOUaiPIzyO22coD06QNl/Image/2012/10/vinacomin-fdaa2.jpg" width=450 _fl="" alt="Vinacomin dự kiến thoái hết v', '', '2012-10-02 10:59:46', '0000-00-00 00:00:00', 'private', 0, 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`) VALUES
(110, ' Công bố danh sách 15 công ty kiểm toán bị kiểm tra ', ' (Dân trí) - Quá trình kiểm tra sẽ tập trung chủ yếu vào kiểm tra chất lượng dịch vụ kiểm toán Báo cáo tài chính, xác định mức độ các sai phạm cụ thể để đề nghị Bộ Tài chính có biện pháp xử lý. ', '<div class="fon34 mt3 mr2 fon43"><DIV align=center>\r\n<DIV><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/hOUaiPIzyO22coD06QNl/Image/2012/10/kiemtoan-711d7.jpg" width=450 _fl="" alt="(Ảnh minh họa)."><BR></DIV><SPAN style="FONT-FAMILY: Arial; FONT-SIZE: 10pt">(Ảnh minh họa).</SPAN><BR></DIV><BR>Hội Kiểm toán viên hành nghề Việt Nam (VACPA) mới đây vừa công bố danh sách kiểm tra chất lượng dịch vụ và hoạt động của 15 công ty kiểm toán năm 2012.<BR><BR>Thành phần đoàn kiểm tra sẽ bao gồm cán bộ của Hội Kiểm toán viên hành nghề Việt Nam, Bộ Tài chính (Vụ Chế độ kế toán và kiểm toán, Uỷ Ban Chứng Khoán Nhà nước) và một số kiểm toán viên - Hội viên của VACPA.<BR><BR>Mỗi đoàn kiểm tra dự kiến gồm 6 - 8 người, trong đó cán bộ của VACPA và của Bộ Tài chính chịu trách nhiệm điều hành và tổng hợp các thông tin về kiểm tra. Các kiểm toán viên tham gia soát xét chuyên môn, nghiệp vụ trên các hồ sơ kiểm toán, từ đó đóng góp ý kiến với VACPA và Bộ Tài chính.<BR><BR>VACPA cho biết, quá trình kiểm tra sẽ tập trung chủ yếu là kiểm tra chất lượng dịch vụ kiểm toán Báo cáo tài chính, xác định mức độ các sai phạm cụ thể để đề nghị Bộ Tài chính có biện pháp xử lý.<BR><BR>Đồng thời, việc kiểm tra yêu cầu phải khách quan, bảo mật và độc lập.<BR><BR>Danh sách các công ty trong diện được kiểm tra bao gồm 8 công ty ở phía Bắc, 7 công ty ở phía Nam và có 4 công ty niêm yết.<BR><BR>\r\n<DIV align=right>\r\n<TABLE style="BORDER-BOTTOM: black 1px solid; BORDER-LEFT: black 1px solid; BACKGROUND-COLOR: rgb(255,218,185); WIDTH: 450px; BORDER-COLLAPSE: collapse; BORDER-TOP: black 1px solid; BORDER-RIGHT: black 1px solid" class=MsoNormalTable border=1 cellSpacing=0 cellPadding=0 align=center>\r\n<TBODY>\r\n<TR style="HEIGHT: 52.5pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes">\r\n<TD style="BORDER-BOTTOM: black 1px solid; BORDER-LEFT: black 1px solid; WIDTH: 54.5pt; HEIGHT: 52.5pt; BORDER-TOP: black 1px solid; BORDER-RIGHT: black 1px solid" width=73>\r\n<P style="TEXT-ALIGN: center; LINE-HEIGHT: normal; MARGIN-BOTTOM: 0pt" class=MsoNormal align=center><SPAN style="COLOR: #000000"><SPAN style="COLOR: #000000; FONT-SIZE: 10pt; FONT-WEIGHT: bold">STT</SPAN><SPAN style="FONT-FAMILY: "Times New Roman","serif"; COLOR: #000000; FONT-SIZE: 12pt; mso-fareast-font-family: "Times New Roman"; mso-fareast-language: VI"></SPAN></SPAN></P></TD>\r\n<TD style="BORDER-BOTTOM: black 1px solid; BORDER-LEFT: black 1px solid; WIDTH: 27.8pt; HEIGHT: 52.5pt; BORDER-TOP: black 1px solid; BORDER-RIGHT: black 1px solid" width=37>\r\n<P style="TEXT-ALIGN: center; LINE-HEIGHT: normal; MARGIN-BOTTOM: 0pt" class=MsoNormal align=center><SPAN style="COLOR: #000000"><SPAN style="COLOR: #000000; FONT-SIZE: 10pt; FONT-WEIGHT: bold">Mã<BR><SPAN style="COLOR: #000000; mso-spacerun: yes">&nbsp;</SPAN>số</SPAN><SPAN style="FONT-FAMILY: "Times New Roman","serif"; COLOR: #000000; FONT-SIZE: 12pt; mso-fareast-font-family: "Times New Roman"; mso-fareast-language: VI"></SPAN></SPAN></P></TD>\r\n<TD style="BORDER-BOTTOM: black 1px solid; BORDER-LEFT: black 1px solid; WIDTH: 125.8pt; HEIGHT: 52.5pt; BORDER-TOP: black 1px solid; BORDER-RIGHT: black 1px solid" width=168 noWrap>\r\n<P style="TEXT-ALIGN: center; LINE-HEIGHT: normal; MARGIN-BOTTOM: 0pt" class=MsoNormal align=center><SPAN style="COLOR: #000000"><SPAN style="COLOR: #000000; FONT-SIZE: 10pt; FONT-WEIGHT: bold">Tên Công ty</SPAN><SPAN style="FONT-FAMILY: "Times New Roman","serif"; COLOR: #000000; FONT-SIZE: 12pt; mso-fareast-font-family: "Times New Roman"; mso-fareast-language: VI"></SPAN></SPAN></P></TD>\r\n<TD style="BORDER-BOTTOM: black 1px solid; BORDER-LEFT: black 1px solid; WIDTH: 45.2pt; HEIGHT: 52.5pt; BORDER-TOP: black 1px solid; BORDER-RIGHT: black 1px solid" wid