INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(1, ' Lại lật xe trên cầu cạn Thanh Trì ', '-Lai-lat-xe-tren-cau-can-Thanh-Tri-', '<p><a href="http://dantri.com.vn/c20/s20-658174/lai-lat-xe-tren-cau-can-thanh-tri.htm"><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/I3KdHJtU0B3ELPKGaTLe/Image/2012/11/lat-xe-7d193.jpg" alt="" /></a>(D&acirc;n tr&iacute;) - V&agrave;o hồi 10h30 ng&agrave;y 1/11, tr&ecirc;n cầu cạn Thanh Tr&igrave;, đoạn đối diện số nh&agrave; 180 Nguyễn Xiển (Thanh Xu&acirc;n, H&agrave; Nội) xảy ra một vụ tai nạn giao th&ocirc;ng li&ecirc;n quan đến xe tải.<br />\r\n<a href="http://dantri.com.vn/c20/s20-655498/3-ngay-xu-hon-150-truong-hop-vi-pham-o-duong-tren-cao.htm"><b>&nbsp;&gt;&gt;&nbsp;3 ng&agrave;y &ldquo;xử&rdquo; hơn 150 trường hợp vi phạm ở đường tr&ecirc;n cao</b></a><br />\r\n<a href="http://dantri.com.vn/c20/s20-654053/Tai-nan-chet-nguoi-tren-cau-can-sau-hon-10-tieng-thong-xe.htm"><b>&nbsp;&gt;&gt;&nbsp;Tai nạn chết người tr&ecirc;n cầu cạn sau hơn 10 tiếng th&ocirc;ng xe</b></a><br />\r\n<a href="http://dantri.com.vn/c20/s20-649075/Ben-xe-tren-cau-can-o-thu-do.htm"><b>&nbsp;&gt;&gt;&nbsp;&ldquo;Bến xe&rdquo; tr&ecirc;n cầu cạn ở thủ đ&ocirc;</b></a></p>', '<div class="fon34 mt3 mr2 fon43">\r\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt" align="center"><img src="http://dantri4.vcmedia.vn/I3KdHJtU0B3ELPKGaTLe/Image/2012/11/lat-xe-7d193.jpg" alt="Chiếc xe tự lật nghi&ecirc;ng" /><br />\r\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">Chiếc xe tự lật nghi&ecirc;ng</span></div>\r\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt">&nbsp;</div>\r\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt">V&agrave;o thời điểm tr&ecirc;n, xe &ocirc; t&ocirc; trọng tải 3,5 tấn BKS 29L-148.53, thuộc doanh nghiệp tư nh&acirc;n Hải Linh, do Phạm Văn T&ugrave;ng, SN 1990 (Ph&uacute;c Sơn, T&acirc;n Y&ecirc;n, Bắc Giang) điều khiển chạy hướng Ho&agrave;ng Mai - Cầu Giấy, v&igrave; chạy qu&aacute; tốc độ, mất l&aacute;i n&ecirc;n bất ngờ lật nh&agrave;o, đ&acirc;m v&agrave;o th&agrave;nh cầu.</div>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><o:p>&nbsp;</o:p></p>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt">Tại hiện trường, chiếc xe nằm ngửa về b&ecirc;n phải, tạo một vệt trượt d&agrave;i tr&ecirc;n mặt đường, th&ugrave;ng h&agrave;ng ph&iacute;a sau co r&uacute;m. Nhiều người ph&iacute;a b&ecirc;n chủ xe đ&atilde; tới đưa số h&agrave;ng h&oacute;a trong th&ugrave;ng ra ngo&agrave;i.</p>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><o:p>&nbsp;</o:p></p>\r\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt">Ngay sau đ&oacute;, Đội CSGT trật tự phản ứng nhanh, C&ocirc;ng an quận Thanh Xu&acirc;n c&oacute; mặt tại hiện trường, ph&acirc;n luồng giao th&ocirc;ng v&agrave; l&agrave;m r&otilde; nguy&ecirc;n nh&acirc;n vụ tai nạn.</div>\r\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt">&nbsp;</div>\r\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt" align="center"><img src="http://dantri4.vcmedia.vn/I3KdHJtU0B3ELPKGaTLe/Image/2012/11/Hi`nh0497-7d193.jpg" alt="Chiếc xe tự lật nghi&ecirc;ng" /></div>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><o:p>&nbsp;</o:p></p>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt">Cầu cạn đầu ti&ecirc;n ở Thủ đ&ocirc; mới được đưa v&agrave;o sử dụng chưa được bao l&acirc;u nhưng đ&atilde; xảy ra nhiều vụ tai nạn giao th&ocirc;ng nghi&ecirc;m trọng, chủ yếu do chủ phương tiện chạy sai đường v&agrave; vượt qu&aacute; tốc độ cho ph&eacute;p.<o:p></o:p></p>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><o:p>&nbsp;</o:p></p>\r\n<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: right" align="right"><b style="mso-bidi-font-weight: normal">H&agrave; Long<o:p></o:p></b></p>\r\n<input type="hidden" value="/c20s20/xa-hoi/trang-1.htm" id="hidNextUsing" /></div>', 'admin', '0000-00-00 00:00:00', '2012-11-06 04:54:11', 'Chưa duyệt', ',11,16,18,', 1, 0, '<p>&nbsp;<img src="/unc/userfiles/image/Lighthouse.jpg" width="1024" height="768" alt="" /></p>', 10, 0),
(2, ' Phát hiện quả bom nặng 230kg trên cánh đồng ', '-Phat-hien-qua-bom-nang-230kg-tren-canh-dong-', '<p>(D&acirc;n tr&iacute;) - Trong qu&aacute; tr&igrave;nh r&agrave; ph&aacute; bom m&igrave;n tại c&aacute;nh đồng Rộc Bốp, x&atilde; Nghi Xu&acirc;n (Nghi Lộc, Nghệ An), đội r&agrave; ph&aacute; bom m&igrave;n đ&atilde; ph&aacute;t hiện quả bom ph&aacute; hoại nặng gần 230kg nằm s&acirc;u dưới l&ograve;ng đất.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<div align="center">\r\n<div><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/7dBrKnsutwiOg2hPbvFQ/Image/NAM-2012/THANG-10/Tuan-5/bom-01-07272.JPG" width="450" align="center" _fl="" alt="C&aacute;c chiến sĩ tiến h&agrave;nh đưa quả bom l&ecirc;n khỏi mặt đất." /></div>\r\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">C&aacute;c chiến sĩ tiến h&agrave;nh đưa quả bom l&ecirc;n khỏi mặt đất.</span></div>\r\n<div>&nbsp;</div>\r\n<div>S&aacute;ng ng&agrave;y 2/11, Thiếu t&aacute; V&otilde; Văn Ba - Gi&aacute;m đốc Trung t&acirc;m r&agrave; ph&aacute; bom m&igrave;n, thuộc Tổng c&ocirc;ng ty 36 - Bộ Quốc ph&ograve;ng - cho PV&nbsp;<i>D&acirc;n tr&iacute;&nbsp;</i>biết, quả bom tr&ecirc;n được c&aacute;c chiến sĩ Trung t&acirc;m r&agrave; ph&aacute;, xử l&yacute; bom m&igrave;n ph&aacute;t hiện tại c&aacute;nh đồng Rộc Bốp (x&atilde; Nghi Xu&acirc;n, huyện Nghi Lộc) v&agrave;o khoảng 9 giờ s&aacute;ng nay 2/11.</div>\r\n<p>&nbsp;</p>\r\n<p>Quả bom c&oacute; trọng lượng 500&nbsp;c&acirc;n Anh (tương đương 230kg), đường k&iacute;nh khoảng 32cm, d&agrave;i 1,4m v&agrave; nằm ở độ s&acirc;u 2m so với mặt đất. Sau khi định vị hướng, tọa độ quả bom n&oacute;i tr&ecirc;n, đội r&agrave; ph&aacute;&nbsp;bom m&igrave;n Tổng C&ocirc;ng ty 36 - Bộ Quốc ph&ograve;ng đ&atilde; v&ocirc; hiệu h&oacute;a ng&ograve;i nổ v&agrave; hơn 1 tiếng đồng hồ đ&atilde; đưa quả bom l&ecirc;n khỏi mặt đất an to&agrave;n.</p>\r\n<p>Thiếu t&aacute; V&otilde; Văn Ba cho biết, đ&acirc;y l&agrave; quả bom ph&aacute; hoại do kh&ocirc;ng qu&acirc;n Mỹ n&eacute;m xuống trong chiến tranh Việt Nam nhưng bị tịt ng&ograve;i kh&ocirc;ng ph&aacute;t nổ.</p>\r\n<p>Cũng theo thiếu t&aacute; V&otilde; Văn Ba, trước đ&oacute;, ng&agrave;y 8/10, tại c&aacute;nh đồng T&ecirc;n Lửa (x&oacute;m 12, Nghi Thạch, Nghi Lộc), đội r&agrave; ph&aacute; bom m&igrave;n cũng đ&atilde; ph&aacute;t hiện 1 quả t&ecirc;n lửa &ldquo;khủng&rdquo; d&agrave;i hơn 3m v&agrave; nặng 700kg,&nbsp;ngay sau&nbsp;đ&oacute;&nbsp;đ&atilde; xử l&yacute; an to&agrave;n.</p>\r\n<div style="TEXT-ALIGN: center">\r\n<div>&nbsp;</div>\r\n<div>\r\n<div><span style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/7dBrKnsutwiOg2hPbvFQ/Image/NAM-2012/THANG-10/Tuan-5/bom-8-07272.JPG" width="450" align="center" _fl="" alt="Nguyễn Duy" /></span></div>\r\n<div><span style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><br />\r\n</span>&nbsp;</div>\r\n<div><object id="PlayerAS4452978a37684118b2bdcc3ec39382e3" height="334" width="425" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000">\r\n<param name="_cx" value="11245" />\r\n<param name="_cy" value="8837" />\r\n<param name="FlashVars" value="" />\r\n<param name="Movie" value="http://cache.hosting.vcmedia.vn/?key=4452978a37684118b2bdcc3ec39382e3&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/7dBrKnsutwiOg2hPbvFQWA/2012/11/pe_html_e39c14ea33.jpg" />\r\n<param name="Src" value="http://cache.hosting.vcmedia.vn/?key=4452978a37684118b2bdcc3ec39382e3&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/7dBrKnsutwiOg2hPbvFQWA/2012/11/pe_html_e39c14ea33.jpg" />\r\n<param name="WMode" value="Transparent" />\r\n<param name="Play" value="-1" />\r\n<param name="Loop" value="-1" />\r\n<param name="Quality" value="High" />\r\n<param name="SAlign" value="LT" />\r\n<param name="Menu" value="-1" />\r\n<param name="Base" value="" />\r\n<param name="AllowScriptAccess" value="always" />\r\n<param name="Scale" value="NoScale" />\r\n<param name="DeviceFont" value="0" />\r\n<param name="EmbedMovie" value="0" />\r\n<param name="BGColor" value="425166" />\r\n<param name="SWRemote" value="" />\r\n<param name="MovieData" value="" />\r\n<param name="SeamlessTabbing" value="1" />\r\n<param name="Profile" value="0" />\r\n<param name="ProfileAddress" value="" />\r\n<param name="ProfilePort" value="0" />\r\n<param name="AllowNetworking" value="all" />\r\n<param name="AllowFullScreen" value="true" /> <embed wmode="transparent" height="334" width="425" quality="high" bgcolor="#425166" flashvars="" allowfullscreen="true" allowscriptaccess="always" name="4452978a37684118b2bdcc3ec39382e3" id="4452978a37684118b2bdcc3ec39382e3" style="" src="http://cache.hosting.vcmedia.vn/?key=4452978a37684118b2bdcc3ec39382e3&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/7dBrKnsutwiOg2hPbvFQWA/2012/11/pe_html_e39c14ea33.jpg" type="application/x-shockwave-flash"></embed></object></div>\r\n<div><br />\r\n&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<b> </b>\r\n<div style="TEXT-ALIGN: right"><b><b>Nguyễn Duy</b></b></div>\r\n</div>\r\n</div>\r\n<input type="hidden" value="/c20s20/xa-hoi/trang-1.htm" id="hidNextUsing" /></div>', 'admin', '0000-00-00 00:00:00', '2012-11-04 09:10:34', 'Công khai', ',16,', 1, 0, '<p>&nbsp;<img src="/unc/userfiles/image/Penguins.jpg" width="1024" height="768" alt="" /></p>', 0, 0),
(3, ' Bộ GTVT và Bộ TT&TT phối hợp cung cấp thông tin cho báo chí ', '-Bo-GTVT-va-Bo-TT&TT-phoi-hop-cung-cap-thong-tin-cho-bao-chi-', '<p>(D&acirc;n tr&iacute;) - Chiều 1/11, tại trụ sở Bộ GTVT, Bộ trưởng GTVT Đinh La Thăng v&agrave; Bộ trưởng TT&amp;TT Nguyễn Bắc Son đ&atilde; k&yacute; kết chương tr&igrave;nh phối hợp cung cấp th&ocirc;ng tin cho b&aacute;o ch&iacute;.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: justify; mso-outline-level: 1" align="left"><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%; FONT-FAMILY: " times="" new="">Theo đ&oacute;, Bộ GTVT sẽ cung cấp th&ocirc;ng tin li&ecirc;n quan tới lĩnh vực giao th&ocirc;ng vận tải bằng văn bản hoặc cử người ph&aacute;t ng&ocirc;n hoặc người đại diện cung cấp th&ocirc;ng tin cho c&aacute;c cơ quan b&aacute;o ch&iacute; định kỳ tại buổi giao ban b&aacute;o ch&iacute; h&agrave;ng tuần tại Bộ TT&amp;TT. Bộ GTVT sẽ chủ động cung cấp th&ocirc;ng tin cho b&aacute;o ch&iacute; qua Bộ TT&amp;TT trong c&aacute;c vấn đề, sự kiện quan trọng, đột xuất m&agrave; dư luận x&atilde; hội quan t&acirc;m để thống nhất c&aacute;ch giải quyết qua việc tổ chức họp b&aacute;o.</span></p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: center">&nbsp;</p>\r\n<div style="TEXT-ALIGN: center"><img style="MARGIN: 5px" alt="Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng Nguyễn Bắc Son khẳng định sự phối hợp&nbsp;" src="http://dantri4.vcmedia.vn/uEzll4k9kas2QtUZIjJS/Image/2012/11/IMG_0360-bc75f.JPG" width="420" align="center" _fl="" /></div>\r\n<div style="TEXT-ALIGN: center"><span style="FONT-SIZE: 10pt; LINE-HEIGHT: 18px; FONT-FAMILY: Tahoma">Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng Nguyễn Bắc Son khẳng định sự phối hợp&nbsp;</span><span style="FONT-SIZE: 10pt; LINE-HEIGHT: 18px; FONT-FAMILY: Tahoma; TEXT-ALIGN: justify">2 Bộ sẽ gi&uacute;p cho cơ quan b&aacute;o ch&iacute; c&oacute; được những th&ocirc;ng tin ch&iacute;nh x&aacute;c, đầy đủ v&agrave; to&agrave;n diện.</span></div>\r\n<p>&nbsp;</p>\r\n<div class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: justify"><span style="BORDER-RIGHT: windowtext 1pt; PADDING-RIGHT: 0in; BORDER-TOP: windowtext 1pt; PADDING-LEFT: 0in; FONT-SIZE: 12pt; PADDING-BOTTOM: 0in; BORDER-LEFT: windowtext 1pt; LINE-HEIGHT: 115%; PADDING-TOP: 0in; BORDER-BOTTOM: windowtext 1pt; FONT-FAMILY: " times="" new="" none="" windowtext="">Khi những vụ việc nghi&ecirc;m trọng xảy ra, Bộ GTVT v&agrave; Bộ TT&amp;TT sẽ kịp thời c&oacute; &yacute; kiến ch&iacute;nh thức bằng văn bản hoặc trả lời trực tiếp th&ocirc;ng qua người ph&aacute;t ng&ocirc;n của Bộ m&igrave;nh, kh&ocirc;ng để lan truyền th&ocirc;ng tin thất thiệt g&acirc;y ảnh hưởng dư luận. Hai b&ecirc;n cũng sẽ thiết lập đường d&acirc;y n&oacute;ng v&agrave; c&ocirc;ng bố đường d&acirc;y n&oacute;ng tr&ecirc;n c&aacute;c phương tiện th&ocirc;ng tin đại ch&uacute;ng để thu nhận v&agrave; xử l&yacute; kịp thời mọi phản &aacute;nh, kiến nghị của người d&acirc;n v&agrave; c&aacute;c cơ quan, doanh nghiệp li&ecirc;n quan đến hoạt động chuy&ecirc;n ng&agrave;nh.</span></div>\r\n<div class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: justify"><span style="BORDER-RIGHT: windowtext 1pt; PADDING-RIGHT: 0in; BORDER-TOP: windowtext 1pt; PADDING-LEFT: 0in; FONT-SIZE: 12pt; PADDING-BOTTOM: 0in; BORDER-LEFT: windowtext 1pt; LINE-HEIGHT: 115%; PADDING-TOP: 0in; BORDER-BOTTOM: windowtext 1pt; FONT-FAMILY: " times="" new="" none="" windowtext="">Tại buổi lễ, Bộ trưởng Bộ TT&amp;TT Nguyễn Bắc Son khẳng định:</span><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%; FONT-FAMILY: " times="" new="">&nbsp;&quot;Chương tr&igrave;nh phối hợp giữa hai Bộ để cung cấp th&ocirc;ng tin cho b&aacute;o ch&iacute; l&agrave; cần thiết v&agrave; thiết thực trong t&igrave;nh h&igrave;nh hiện nay. Phối hợp giữ 2 Bộ sẽ gi&uacute;p cho cơ quan b&aacute;o ch&iacute; c&oacute; được những th&ocirc;ng tin ch&iacute;nh x&aacute;c, đầy đủ v&agrave; to&agrave;n diện về sự chỉ đạo của Ch&iacute;nh phủ, của Bộ GTVT về c&ocirc;ng t&aacute;c quản l&yacute; nh&agrave; nước về giao th&ocirc;ng vận tải&quot;.<!--?xml:namespace prefix = o /--><o:p></o:p></span></div>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: center">&nbsp;</p>\r\n<div style="TEXT-ALIGN: center"><img style="MARGIN: 5px" alt="Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng Nguyễn Bắc Son khẳng định sự phối hợp&nbsp;" src="http://dantri4.vcmedia.vn/uEzll4k9kas2QtUZIjJS/Image/2012/11/IMG_0375-bc75f.JPG" width="420" align="center" _fl="" /></div>\r\n<span style="LINE-HEIGHT: 18px; TEXT-ALIGN: justify">\r\n<div style="TEXT-ALIGN: center"><span style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">Bộ trưởng Đinh La Thăng v&agrave; Bộ trưởng Nguyễn Bắc Son k&yacute; kết chương tr&igrave;nh phối hợp cung cấp th&ocirc;ng tin cho b&aacute;o ch&iacute;.</span></div>\r\n</span>\r\n<p>&nbsp;</p>\r\n<div class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: justify"><span style="BORDER-RIGHT: windowtext 1pt; PADDING-RIGHT: 0in; BORDER-TOP: windowtext 1pt; PADDING-LEFT: 0in; FONT-SIZE: 12pt; PADDING-BOTTOM: 0in; BORDER-LEFT: windowtext 1pt; LINE-HEIGHT: 115%; PADDING-TOP: 0in; BORDER-BOTTOM: windowtext 1pt; FONT-FAMILY: " times="" new="" none="" windowtext="">Bộ trưởng Bộ GTVT Đinh La Thăng cũng khẳng định: C&aacute;c cơ quan truyền th&ocirc;ng đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p rất hiệu quả v&agrave;o th&agrave;nh c&ocirc;ng của ng&agrave;nh GTVT như c&ocirc;ng t&aacute;c bảo đảm trật tự ATGT, tiến độ, chất lượng c&ocirc;ng tr&igrave;nh giao th&ocirc;ng v&agrave; rất nhiều lĩnh vực quản l&yacute; nh&agrave; nước của ng&agrave;nh. Việc phối hợp giữa 2 Bộ sẽ tiếp tục đưa th&ocirc;ng tin về hoạt động quản l&yacute; nh&agrave; nước ng&agrave;nh GTVT đầy đủ v&agrave; kịp thời hơn nữa.</span></div>\r\n<div class="MsoNormal" style="MARGIN-BOTTOM: 12pt; VERTICAL-ALIGN: baseline; TEXT-ALIGN: justify"><span style="BORDER-RIGHT: windowtext 1pt; PADDING-RIGHT: 0in; BORDER-TOP: windowtext 1pt; PADDING-LEFT: 0in; FONT-SIZE: 12pt; PADDING-BOTTOM: 0in; BORDER-LEFT: windowtext 1pt; LINE-HEIGHT: 115%; PADDING-TOP: 0in; BORDER-BOTTOM: windowtext 1pt; FONT-FAMILY: " times="" new="" none="" windowtext=""><o:p>\r\n<div align="center">\r\n<table class="MsoNormalTable" style="BORDER-RIGHT: medium none; BORDER-TOP: medium none; BACKGROUND: whitesmoke; BORDER-LEFT: medium none; WIDTH: 90%; BORDER-BOTTOM: medium none; BORDER-COLLAPSE: collapse; mso-border-alt: outset steelblue .75pt" cellspacing="0" cellpadding="0" width="90%" border="1">\r\n    <tbody>\r\n        <tr style="mso-yfti-irow: 0; mso-yfti-firstrow: yes; mso-yfti-lastrow: yes">\r\n            <td style="BORDER-RIGHT: steelblue 1pt inset; PADDING-RIGHT: 0.75pt; BORDER-TOP: steelblue 1pt inset; PADDING-LEFT: 0.75pt; PADDING-BOTTOM: 0.75pt; BORDER-LEFT: steelblue 1pt inset; PADDING-TOP: 0.75pt; BORDER-BOTTOM: steelblue 1pt inset; BACKGROUND-COLOR: transparent; mso-border-alt: inset steelblue .75pt">\r\n            <p><span style="FONT-SIZE: 10pt"><span style="mso-spacerun: yes"><span style="FONT-FAMILY: Tahoma; TEXT-ALIGN: justify">Ng&agrave;y 1/11, Sở Th&ocirc;ng tin &amp; Truyền th&ocirc;ng Đ&agrave; Nẵng tổ chức tập huấn n&acirc;ng cao kỹ năng tuy&ecirc;n truyền ph&ograve;ng chống ma t&uacute;y. Tham dự kh&oacute;a tập huấn c&oacute; gần 100 học vi&ecirc;n l&agrave; c&aacute;n bộ c&ocirc;ng t&aacute;c tại c&aacute;c ph&ograve;ng văn h&oacute;a th&ocirc;ng tin, Đ&agrave;i truyền thanh cơ sở thuộc TP.</span></span></span></p>\r\n            <div style="TEXT-ALIGN: center">&nbsp;</div>\r\n            <div style="TEXT-ALIGN: center"><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/ysuJrh3MO0KMq51dxOuJ/Image/2012/11/1-11-tin-XH-42a99.JPG" width="450" align="center" _fl="" alt="Bộ GTVT v&agrave; Bộ TT&amp;TT phối hợp cung cấp th&ocirc;ng tin cho b&aacute;o ch&iacute;" /></div>\r\n            <div style="TEXT-ALIGN: center" startcont="this"><span style="FONT-SIZE: 10pt">Gần 100 c&aacute;n bộ c&ocirc;ng t&aacute;c tại c&aacute;c ph&ograve;ng VHTT, Đ&agrave;i Truyền thanh &nbsp;tại Đ&agrave; Nẵng dự tập huấn kỹ năng tuy&ecirc;n truyền về ph&ograve;ng chống ma t&uacute;y</span></div>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            <p class="MsoNormal" align="left"><span style="FONT-FAMILY: Tahoma">Theo đ&oacute;, c&aacute;c học vi&ecirc;n đ&atilde; nghe b&aacute;o c&aacute;o vi&ecirc;n của Đ&agrave;i Ph&aacute;t thanh truyền h&igrave;nh TP, b&aacute;o C&ocirc;ng an TP, Ph&ograve;ng Cảnh s&aacute;t điều tra tội phạm về ma t&uacute;y TP chia sẻ kinh nghiệm th&ocirc;ng tin tuy&ecirc;n truyền ph&ograve;ng chống ma t&uacute;y, c&ocirc;ng t&aacute;c đấu tranh ph&ograve;ng chống ma t&uacute;y, phương ph&aacute;p kết nối th&ocirc;ng tin tuyền truyền ph&ograve;ng chống ma t&uacute;y giữa c&aacute;c cơ quan li&ecirc;n quan.</span></p>\r\n            <p class="MsoNormal" align="left"><span style="FONT-FAMILY: Tahoma">Một số &yacute; kiến nh&igrave;n nhận c&ocirc;ng t&aacute;c tuy&ecirc;n truyền ph&ograve;ng chống ma t&uacute;y c&ograve;n một số hạn chế như th&ocirc;ng tin chưa c&oacute; t&iacute;nh hệ thống, chưa s&acirc;u s&aacute;t với thực tiễn v&agrave; c&ograve;n th&ocirc;ng tin một chiều&hellip; Theo đ&oacute;, đề nghị c&aacute;c cơ quan li&ecirc;n quan ch&uacute; trọng hơn nữa trong việc th&ocirc;ng tin hai chiều, cũng như c&oacute; phối hợp chặt chẽ giữa ch&iacute;nh quyền TP.&nbsp; với b&aacute;o ch&iacute; để n&acirc;ng cao hiệu quả tuy&ecirc;n truyền.</span></p>\r\n            <p class="MsoNormal" align="left"><span style="FONT-FAMILY: Tahoma">C&aacute;c học vi&ecirc;n đồng thời được cập nhật&nbsp; th&ocirc;ng tin về t&igrave;nh h&igrave;nh tệ nạn ma t&uacute;y tr&ecirc;n địa b&agrave;n, c&aacute;c loại ma t&uacute;y tổng hợp xuất hiện ở Việt Nam; kinh nghiệm ph&ograve;ng chống tệ nạn ma t&uacute;y của c&aacute;c nước tr&ecirc;n thế giới như kinh nghiệm của Ch&iacute;nh phủ Ma-rốc về việc th&agrave;nh lập c&aacute;c tổ chức, nh&oacute;m hoạt động tuy&ecirc;n truyền cho học sinh v&agrave; người d&acirc;n về t&aacute;c hại của ma t&uacute;y, n&acirc;ng cao &yacute; thức của người d&acirc;n v&agrave; ngăn chặn việc trồng cần sa; kinh nghiệm của &Uacute;c về việc tập trung tuy&ecirc;n truyền trong giới trẻ (từ 13 -21 tuổi, lứa tuổi dễ bị l&ocirc;i k&eacute;o hay t&ograve; m&ograve; sử dụng ma t&uacute;y), khai th&aacute;c k&ecirc;nh th&ocirc;ng tin tuy&ecirc;n truyền qua c&aacute;c trang mạng x&atilde; hội; hay luật &aacute;p dụng c&aacute;c h&igrave;nh phạt nặng đối với tội phạm ma t&uacute;y, gi&aacute;m s&aacute;t người đ&atilde; ho&agrave;n th&agrave;nh điều trị cai nghiện, ngăn chặn t&aacute;i nghiện bằng việc gắn tr&ecirc;n người họ một bộ cảm biến điện tử c&oacute; thể kết nối với một hệ thống theo d&otilde;i của nh&agrave; nước ở Singapore&hellip; </span></p>\r\n            <p class="MsoNormal" style="TEXT-ALIGN: right"><span style="FONT-FAMILY: Tahoma"><o:p>&nbsp;</o:p><b style="TEXT-ALIGN: right">Kh&aacute;nh Hiền</b></span></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n</o:p></span></div>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; TEXT-ALIGN: right" align="right"><b><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%; FONT-FAMILY: " times="" new="">Anh Thế</span></b><b><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%; FONT-FAMILY: " times="" new=""><o:p></o:p></span></b></p>\r\n<input type="hidden" value="/c20s20/xa-hoi/trang-1.htm" id="hidNextUsing" /></div>', 'admin', '0000-00-00 00:00:00', '2012-11-04 09:11:00', 'Công khai', ',17,', 0, 0, '<p>&nbsp;<img src="/unc/userfiles/image/1%20(5).jpg" width="200" height="200" alt="" /></p>', 0, 0),
(4, ' Phân công người đảm nhận cương vị của 3 cán bộ Tiên Lãng bị khởi tố ', '-Phan-cong-nguoi-dam-nhan-cuong-vi-cua-3-can-bo-Tien-Lang-bi-khoi-to-', '<p>(D&acirc;n tr&iacute;) - UBND huyện Ti&ecirc;n L&atilde;ng vừa c&ocirc;ng bố quyết định tạm đ&igrave;nh chỉ chức vụ đối với 3 c&aacute;n bộ huyện n&agrave;y sau khi C&ocirc;ng an TP Hải Ph&ograve;ng tống đạt quyết định khởi tố bị can li&ecirc;n quan đến vụ &aacute;n &ldquo;Hủy hoạt t&agrave;i sản&rdquo; xẩy ra tại đầm t&ocirc;m &ocirc;ng Đo&agrave;n Văn Vươn.<br />\r\n<a href="http://dantri.com.vn/c20/s20-654165/Vu-cuong-che-dam-tom-Bat-nguyen-Pho-Chu-tich-huyen-Tien-Lang.htm"><b>&nbsp;&gt;&gt;&nbsp;Vụ cưỡng chế đầm t&ocirc;m: Bắt nguy&ecirc;n Ph&oacute; Chủ tịch huyện Ti&ecirc;n L&atilde;ng</b></a></p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p style="MARGIN: 0in 0in 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify" align="left"><span style="LINE-HEIGHT: 115%">&Ocirc;ng Ng&ocirc; Ngọc Kh&aacute;nh - Ch&aacute;nh văn ph&ograve;ng UBND huyện Ti&ecirc;n L&atilde;ng - cho biết, UBND huyện vừa ch&iacute;nh thức c&ocirc;ng bố quyết định tạm đ&igrave;nh chỉ chức vụ đối với &ocirc;ng Phạm Xu&acirc;n Hoa - Trưởng Ph&ograve;ng T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường. Tạm thời đảm nhận cương vị của &ocirc;ng Hoa l&agrave; Ph&oacute; Ph&ograve;ng TN&amp;MT Phạm Văn Chống.</span></p>\r\n<p style="MARGIN: 0in 0in 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify">Quyết định tạm đ&igrave;nh chỉ chức vụ cũng được UBND huyện Ti&ecirc;n L&atilde;ng thực thi đối với B&iacute; thư Đảng ủy, Chủ tịch HĐND x&atilde; Vinh Quang Phạm Đăng Hoan v&agrave; Chủ tịch UBND x&atilde; Vinh Quang L&ecirc; Thanh Li&ecirc;m. Theo đ&oacute;, tạm thay thế cương vị của &ocirc;ng Hoan, &ocirc;ng Li&ecirc;m l&agrave; &ocirc;ng Nguyễn Tiến Ong - Ph&oacute; Chủ tịch HĐND x&atilde; v&agrave; &ocirc;ng Phạm C&ocirc;ng Nh&igrave;u - Ph&oacute; Chủ tịch x&atilde; Vinh Quang.</p>\r\n<p style="MARGIN: 0in 0in 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: center">&nbsp;</p>\r\n<div style="TEXT-ALIGN: center"><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/2PM9NXEQMsZhNqiuV52C/Image/2012/11/clipimage005thumb2_a7683-bc2d1.jpg" width="462" align="center" _fl="" alt="Ph&acirc;n c&ocirc;ng người đảm nhận cương vị của 3 c&aacute;n bộ Ti&ecirc;n L&atilde;ng bị khởi tố" /></div>\r\n<span style="LINE-HEIGHT: 20px; FONT-FAMILY: Tahoma; BACKGROUND-COLOR: rgb(255,255,255)" startcont="this">\r\n<div style="TEXT-ALIGN: center"><span style="FONT-SIZE: 10pt">Hiện trường ng&ocirc;i nh&agrave; v&agrave; t&agrave;i sản của Đo&agrave;n Văn Vươn v&agrave; Đo&agrave;n Văn Qu&yacute; bị ph&aacute; hủy sau cưỡng chế ng&agrave;y 05/1.</span></div>\r\n</span>\r\n<p>&nbsp;</p>\r\n<p style="MARGIN: 0in 0in 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify">Huyện ủy Ti&ecirc;n L&atilde;ng cũng c&oacute; c&aacute;c quyết định đ&igrave;nh chỉ chức vụ B&iacute; thư Đảng ủy x&atilde; Vinh Quang với &ocirc;ng Hoan, đ&igrave;nh chỉ chức vụ Ph&oacute; B&iacute; thư Đảng ủy x&atilde; với &ocirc;ng Li&ecirc;m.</p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify"><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%">Ng&agrave;y 22/10 vừa qua, Cơ quan CSĐT C&ocirc;ng an TP Hải Ph&ograve;ng đ&atilde; tống đạt quyết định khởi tố bị can đối với &ocirc;ng Nguyễn Văn Khanh - nguy&ecirc;n Ph&oacute; Chủ tịch huyện Ti&ecirc;n L&atilde;ng với tội danh &ldquo;Hủy hoại t&agrave;i sản&rdquo;. C&ugrave;ng bị khởi tố như &ocirc;ng Khanh c&ograve;n c&oacute; th&ecirc;m 3 l&atilde;nh đạo c&aacute;n bộ huyện Ti&ecirc;n L&atilde;ng v&agrave; x&atilde; Vinh Quang gồm: Trưởng ph&ograve;ng T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường huyện Ti&ecirc;n L&atilde;ng Phạm Xu&acirc;n Hoa (57 tuổi), B&iacute; thư Đảng ủy x&atilde; Vinh Quang Phạm Đăng Hoan (52 tuổi) v&agrave; Chủ tịch UBND x&atilde; Vinh Quang L&ecirc; Thanh Li&ecirc;m (49 tuổi).<!--?xml:namespace prefix = o /--><o:p></o:p></span></p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify"><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%">Trước đ&oacute;, ng&agrave;y 5/1/2012, UBND huyện Ti&ecirc;n L&atilde;ng thực hiện cưỡng chế t&agrave;i sản tại khu vực đầm nuồi trồng thủy sản của &ocirc;ng Đo&agrave;n Văn Vươn ở x&atilde; Vinh Quang - huyện Ti&ecirc;n L&atilde;ng - Hải Ph&ograve;ng với l&yacute; do gia đ&igrave;nh &ocirc;ng Vươn đ&atilde; hết thời gian thu&ecirc; khu đầm.<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: center">&nbsp;</p>\r\n<div style="TEXT-ALIGN: center"><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/2PM9NXEQMsZhNqiuV52C/Image/2012/11/tlhoan_da0e5-6c5b6-bc2d1.JPG" width="360" align="center" _fl="" alt="&Ocirc;ng Phạm Đăng Hoan - B&iacute; thư Đảng ủy x&atilde; Vinh Quang." /></div>\r\n<span style="LINE-HEIGHT: 20px; FONT-FAMILY: Tahoma; BACKGROUND-COLOR: rgb(255,255,255)">\r\n<div style="TEXT-ALIGN: center"><span style="FONT-SIZE: 10pt">&Ocirc;ng Phạm Đăng Hoan - B&iacute; thư Đảng ủy x&atilde; Vinh Quang.</span></div>\r\n</span>\r\n<p>&nbsp;</p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify"><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%">Sau nhiều lần khiếu nại l&ecirc;n c&aacute;c cơ quan chức năng về quyết định cưỡng chế của UBND huyện Ti&ecirc;n L&atilde;ng với khu đầm nu&ocirc;i trồng thủy sản kh&ocirc;ng c&oacute; kết quả, &ocirc;ng Đo&agrave;n Văn Vươn v&agrave; một số người th&acirc;n trong gia đ&igrave;nh đ&atilde; thực hiện h&agrave;nh vi chống lại lực lượng cưỡng chế huyện Ti&ecirc;n L&atilde;ng khiến 6 chiến sĩ c&ocirc;ng an v&agrave; bộ đội bị thương.<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify"><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%">Sau khi được tại ngoại về, vợ &ocirc;ng Vươn l&agrave; b&agrave; Nguyễn Thị Thương v&agrave; vợ &ocirc;ng Qu&yacute; l&agrave; b&agrave; Phạm Thị Hiền đ&atilde; tố c&aacute;o Hội đồng Cưỡng chế của huyện Ti&ecirc;n L&atilde;ng hủy hoại to&agrave;n bộ nh&agrave; cửa, khu chăn nu&ocirc;i của gia đ&igrave;nh trong khi một số khu vực kh&ocirc;ng thuộc diện t&iacute;ch bị cưỡng chế. <o:p></o:p></span></p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: center">&nbsp;</p>\r\n<div style="TEXT-ALIGN: center"><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/2PM9NXEQMsZhNqiuV52C/Image/2012/11/tlliem_ae614-6c5b6-bc2d1.JPG" width="360" align="center" _fl="" alt="C&ugrave;ng &ocirc;ng L&ecirc; Thanh Li&ecirc;m - Chủ tịch UBND x&atilde; Vinh Quang c&ugrave;ng bị tạm đ&igrave;nh chỉ chức vụ." /></div>\r\n<span style="LINE-HEIGHT: 20px; FONT-FAMILY: Tahoma; BACKGROUND-COLOR: rgb(255,255,255)">\r\n<div style="TEXT-ALIGN: center"><span style="FONT-SIZE: 10pt">C&ugrave;ng &ocirc;ng L&ecirc; Thanh Li&ecirc;m - Chủ tịch UBND x&atilde; Vinh Quang c&ugrave;ng bị tạm đ&igrave;nh chỉ chức vụ.</span></div>\r\n</span>\r\n<p>&nbsp;</p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: justify"><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%">Sau khi v&agrave;o cuộc, đến ng&agrave;y 8/2, Cơ quan CSĐT C&ocirc;ng an TP Hải Ph&ograve;ng đ&atilde; quyết định khởi tố vụ &aacute;n h&igrave;nh sự &ldquo;Hủy hoại t&agrave;i sản&rdquo;. CQĐT đ&atilde; triệu tập c&aacute;c nghi can li&ecirc;n quan đến vụ &aacute;n ph&aacute; nh&agrave; của anh em &ocirc;ng Đo&agrave;n Văn Vươn để l&agrave;m r&otilde;. Đến thời điểm n&agrave;y, C&ocirc;ng an Hải Ph&ograve;ng cho biết đ&atilde; thực hiện quyết định khởi tố bị can với 4 quan chức huyện Ti&ecirc;n L&atilde;ng v&agrave; x&atilde; Vinh Quang. Ngo&agrave;i ra, CQĐT cũng khẳng định kh&ocirc;ng c&oacute; &ldquo;v&ugrave;ng cấm&rdquo; n&agrave;o trong việc mở rộng điều tra, l&agrave;m r&otilde; một số đối tượng kh&aacute;c c&oacute; li&ecirc;n quan đến vụ &aacute;n d&ugrave; cho người đ&oacute; đ&atilde; v&agrave; đang c&ocirc;ng t&aacute;c, l&agrave;m l&atilde;nh đạo tại cơ quan nh&agrave; nước tại địa phương<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="MARGIN-BOTTOM: 12pt; LINE-HEIGHT: 115%; TEXT-ALIGN: right" align="right"><b><span style="FONT-SIZE: 12pt; LINE-HEIGHT: 115%">Quốc Đ&ocirc; - Anh Thế<o:p></o:p></span></b></p>\r\n<input type="hidden" value="/c20s20/xa-hoi/trang-1.htm" id="hidNextUsing" /></div>', 'admin', '0000-00-00 00:00:00', '2012-11-04 09:11:18', 'Chưa duyệt', ',11,16,18,', 1, 0, '<p>&nbsp;<img src="/unc/userfiles/image/1%20(1).jpg" width="200" height="200" alt="" /></p>', 0, 0),
(5, ' Cuộc đua Obama-Romney tăng tốc sau bão ', '-Cuoc-dua-Obama-Romney-tang-toc-sau-bao-', '<p><a href="http://dantri.com.vn/c36/s36-658237/cuoc-dua-obamaromney-tang-toc-sau-bao.htm"><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/SF9n0XnWe1mhJ0SanI/Image/2012/11/1-2d90e.jpg" alt="" /></a>(D&acirc;n tr&iacute;) - Ng&agrave;y 1/11, Tổng thống Obama đ&atilde; trở lại chiến dịch vận động tranh cử ở c&aacute;c bang dao động, sau mấy ng&agrave;y trở về Washington để đối ph&oacute; với b&atilde;o Sandy. Theo c&aacute;c cuộc thăm d&ograve; mới nhất &ocirc;ng Obama đ&atilde; được tăng th&ecirc;m điểm ủng hộ hậu si&ecirc;u b&atilde;o.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal" align="center"><!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><o:p>&nbsp;<img alt="Ng&agrave;y 1/11, &ocirc;ng Obama đ&atilde; đến" src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/11/1-2d90e.jpg" /><br />\r\n<br />\r\n</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Ng&agrave;y 1/11, &ocirc;ng Obama đ&atilde; đến <!--?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /--><st1:state w:st="on">Wisconsin</st1:state>, <st1:state w:st="on">Nevada</st1:state> v&agrave; <st1:state w:st="on"><st1:place w:st="on">Colorado</st1:place></st1:state>, ba tiểu bang được xem l&agrave; bang &ldquo;chiến trường&rdquo; c&oacute; thể quyết định kết quả cuộc bầu cử tổng thống v&agrave;o thứ ba tới. Trong khi đ&oacute;, &ocirc;ng Romney đến vận động ở <st1:state w:st="on"><st1:place w:st="on">Virginia</st1:place></st1:state>, một tiểu bang quyết định kh&aacute;c.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Hai ứng cử vi&ecirc;n tiếp tục quay sang c&ocirc;ng k&iacute;ch nhau về ch&iacute;nh s&aacute;ch cải thiện kinh tế Mỹ. &Ocirc;ng Romney cho rằng thu nhập của giới trung lưu Mỹ bị &ldquo;teo lại&rdquo; trong 4 năm cầm quyền của Tổng thống Obama. C&ograve;n tại <st1:place w:st="on"><st1:state w:st="on">Wisconsin</st1:state></st1:place>, Tổng thống Obama chỉ tr&iacute;ch ch&iacute;nh s&aacute;ch kinh tế của &ocirc;ng Romney ưu đ&atilde;i giới nh&agrave; gi&agrave;u v&agrave; c&aacute;c ch&iacute;nh s&aacute;ch đ&oacute; đ&atilde; từng thất bại trong qu&aacute; khứ.</p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Chiều ng&agrave;y thứ năm 1/11, Tổng thống Obama nhận được tin vui khi thị trưởng th&agrave;nh phố New York tuy&ecirc;n bố ủng hộ &ocirc;ng, v&igrave; tổng thống đ&atilde; c&oacute; những nỗ lực chống &ocirc; nhiễm kh&ocirc;ng kh&iacute; của &ocirc;t&ocirc; v&agrave; nh&agrave; m&aacute;y.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Do th&agrave;nh phố <st1:place w:st="on"><st1:state w:st="on">New York</st1:state></st1:place> l&agrave; th&agrave;nh phố lớn nhất của Mỹ, sự ủng hộ của &ocirc;ng Bloomberg, vốn l&agrave; một đảng vi&ecirc;n Cộng h&ograve;a chuyển sang kh&ocirc;ng đảng ph&aacute;i, được coi l&agrave; quan trọng.</p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Khi c&ograve;n v&agrave;i ng&agrave;y l&agrave; đến bầu cử, c&aacute;c cuộc thăm d&ograve; cho thấy đ&acirc;y l&agrave; một cuộc đua s&iacute;t sao. Theo c&aacute;c cuộc khảo s&aacute;t mới nhất, &ocirc;ng Obama đ&atilde; nắm giữ được 237 phiếu đại cử tri, trong khi Romney nắm giữ 206. Những l&aacute; phiếu c&ograve;n lại nằm ở c&aacute;c bang dao động.</p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><b style="mso-bidi-font-weight: normal">Obama được tăng điểm ủng hộ hậu si&ecirc;u b&atilde;o Sandy<o:p></o:p></b></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><b style="mso-bidi-font-weight: normal"><o:p>&nbsp;</o:p></b></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Ngay từ khi b&atilde;o Sandy đổ bộ v&agrave;o bờ biển miền Đ&ocirc;ng Hoa Kỳ, buộc c&aacute;c ứng vi&ecirc;n Tổng thống Mỹ phải tạm ho&atilde;n cuộc vận động tranh cử, c&acirc;u hỏi được nhiều nh&agrave; quan s&aacute;t đặt ra l&agrave; cơn b&atilde;o bất ngờ n&agrave;y c&oacute; lợi cho ứng cử vi&ecirc;n n&agrave;o. Theo tất cả c&aacute;c nh&agrave; ph&acirc;n t&iacute;ch, trong bối cảnh mọi phương tiện truyền th&ocirc;ng đều ch&uacute; mục v&agrave;o cơn b&atilde;o, sự kiện Tổng thống Obama xuất hiện tr&ecirc;n tuyến đầu trong việc động vi&ecirc;n c&ocirc;ng t&aacute;c ph&ograve;ng chống trước khi b&atilde;o đến, v&agrave; sau đ&oacute; l&agrave; cứu trợ, động vi&ecirc;n c&aacute;c nh&acirc;n, đ&atilde; thu h&uacute;t cảm t&igrave;nh của mọi người, điều rất c&oacute; lợi cho &ocirc;ng với tư c&aacute;ch ứng cử vi&ecirc;n tổng thống.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Nhật b&aacute;o Mỹ Washington Post đ&atilde; nhận định trong d&ograve;ng t&iacute;t: &ldquo;Cơn b&atilde;o đ&atilde; mang lại cho &ocirc;ng Obama tầm v&oacute;c của một vị tổng tư lệnh&rdquo;. Nhưng ngược lại, Thống đốc Mitt Romney, đối thủ của &ocirc;ng Obama trong cuộc chạy đua v&agrave;o Nh&agrave; Trắng lại bị l&acirc;m v&agrave;o một ho&agrave;n cảnh tế nhị, phải cố gắng thu h&uacute;t mối quan t&acirc;m của mọi người v&agrave;o l&uacute;c dư luận tập trung đến &ldquo;một c&acirc;u chuyện kh&ocirc;ng li&ecirc;n quan g&igrave; đến &ocirc;ng&rdquo;.</p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Trong những ng&agrave;y qua, dư luận Mỹ cũng chứng kiến c&aacute;ch cụ thể c&aacute;c hoạt động của cơ quan li&ecirc;n bang Hoa Kỳ phụ tr&aacute;ch đối ph&oacute; với c&aacute;c t&igrave;nh huống khẩn cấp l&agrave; FEMA. Đ&aacute;nh gi&aacute; chung cho đến nay l&agrave; cơ quan n&agrave;y l&agrave;m việc hiệu quả.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Theo giới quan s&aacute;t, đ&acirc;y cũng l&agrave; một yếu tố bất lợi cho ứng cử vi&ecirc;n Romney, nh&acirc;n vật vốn trong một năm nay, lu&ocirc;n lu&ocirc;n đả k&iacute;ch c&aacute;c cơ quan li&ecirc;n bang v&agrave; ch&iacute;nh quyền trung ương, kể cả cơ quan FEMA. Một số tờ b&aacute;o mới đ&acirc;y đ&atilde; kh&ocirc;ng ngần ngại nhắc lại c&acirc;u n&oacute;i của &ocirc;ng Romney c&aacute;ch nay một năm l&agrave; cần phải dẹp cơ quan FEMA.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Kh&ocirc;ng chỉ thế, người đứng chung li&ecirc;n danh với &ocirc;ng Romney l&agrave; Paul Ryan c&ograve;n nằm trong số c&aacute;c d&acirc;n biểu Mỹ thuộc đảng Cộng H&ograve;a đ&atilde; bỏ phiếu quyết định giảm 43% ng&acirc;n s&aacute;ch d&agrave;nh cho FEMA trong v&ograve;ng hai năm gần đ&acirc;y.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal">Khi chỉ c&ograve;n v&agrave;i ng&agrave;y tranh cử nữa, chắc chắn c&aacute;c quan điểm chống FEMA của Romney v&agrave; Ryan sẽ bị ph&iacute;a đảng D&acirc;n chủ khai th&aacute;c.<o:p></o:p></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class="MsoNormal" align="right"><b style="mso-bidi-font-weight: normal">Vũ Qu&yacute;<o:p></o:p></b></p>\r\n<p style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class="MsoNormal" align="right">Theo <i style="mso-bidi-font-style: normal">AP, AFP<o:p></o:p></i></p>\r\n<p style="MARGIN: 0in 0in 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<input type="hidden" value="/c36s36/Thegioi/trang-1.htm" id="hidNextUsing" /></div>', '', '0000-00-00 00:00:00', '2012-11-02 02:50:57', 'Công khai', '17,', 1, 0, '<p>&nbsp;<img src="/unc/userfiles/image/1%20(2).jpg" width="500" height="750" alt="" /></p>', 0, 0),
(6, ' Bé gái 4 tuổi khóc nức nở vì chán cuộc bầu cử tổng thống Mỹ ', '-Be-gai-4-tuoi-khoc-nuc-no-vi-chan-cuoc-bau-cu-tong-thong-Mỹ-', ' (Dân trí) - Một bé gái 4 tuổi ở Mỹ bỗng chốc nổi tiếng sau khi đoạn video trên YouTube cho thấy bé khóc nức nở vì phải nghe quá nhiều về chiến dịch tranh cử tổng thống giữa hai đối thủ Obama-Romney. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p>&nbsp;<IMG alt="Abigael Evans vừa nói vừa khóc trong đoạn video được đăng trên YouTube." src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/10/1-9fd7b.jpg"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Abigael Evans vừa nói vừa khóc trong đoạn video được đăng trên YouTube.</SPAN><BR></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Mẹ cô bé Elizabeth Evans, 27 tuổi, đã đăng tải đoạn video dài 22 giây lên mạng YouTube vào ngày 30/10, trong đó cô bé Abigael Evans, 4 tuổi, nước mắt ngắn dài nói đã chán “Bronco Bamma và Mitt Romney” rồi. (Cô bé nói sai tên Tổng thống Mỹ thành Bronco Bamma)</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Elizabeth Evans cho biết trên ABC News rằng bình thường cô bé rất thích nghe đài đài phát thanh công cộng quốc gia NPR. “Tôi vừa mới đón con từ nhà trẻ. Nó cáu kỉnh sau một ngày dài phải đi học. Chúng tôi vừa lái xe tới cửa hàng tạp hóa vừa nghe đài NPR”, Evans kể lại. “Thực sự lúc đó tôi không để ý nghe đài. Khi tôi ra khỏi xe, con bé nói rằng nó mệt vì nghe Bronco Bamma và Mitt Romney.”<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Đó là lý do vì sao con khóc sao?”, mẹ cô bé hỏi trong đoạn video.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Sau khi cô bé trả lời bằng cái gật đầu, mẹ Abby nói: “Cuộc bầu cử sẽ sớm qua thôi, được không con?”</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Được”, cô bé trả lời đầy miễn cưỡng.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Mẹ Abby cho biết cô bé “lớn trước tuổi”. “Chúng tôi không xem TV ở nhà, vì vậy chúng tôi chỉ nghe qua đài và nghe mọi người nói về chính trị”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Mặc dù bé gái 4 tuổi không tiếp xúc với chính trị tại nhà, nhưng ngôi sao nhí YouTube này lại tỏ ra biết rất nhiều.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Cô bé nói về Mitt Romney và Barack Obama suốt”, Evans cho biết trên ABC. “Cô bé luôn nói Obama là tổng thống và Mitt Romney là người xấu, chỉ muốn tiền và muốn trở thành tổng thống…Con bé lớn trước tuổi”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Sau khi đoạn video được đăng tải trên YouTube, đài NPR đã xin lỗi cô bé 4 tuổi trong một bản thông cáo báo chí. “Thay mặt NPR và tất cả các hãng tin khác, chúng tôi xin lỗi Abigael và nhiều người khác có thể như bé. Chúng tôi phải thừa nhận rằng, chiến dịch tranh cử cũng đã quá dài đối với cả chúng tôi nữa. Chúng ta hãy nói với nhau: “Chỉ còn vài ngày nữa thôi, chỉ còn vài ngày nữa thôi”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Đoạn video đã tràn ngập bình luận của những người đồng tình với Abigael. “Đó chính là những gì tôi cảm thấy. Không khí thù hận và tiêu cực quá nhiều”, một người bình luận cho biết. “Tôi chỉ muốn nó qua đi nhanh…giống như ai đó hỏi: “Muốn được đâm hay bị bắn?”.</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center>\r\n<OBJECT id=PlayerAScf59144467b74a6eb3249634156b2c58 classid=clsid:D27CDB6E-AE6D-11cf-96B8-444553540000 width=425 height=334><PARAM NAME="_cx" VALUE="11244"><PARAM NAME="_cy" VALUE="8837"><PARAM NAME="FlashVars" VALUE=""><PARAM NAME="Movie" VALUE="http://cache.hosting.vcmedia.vn/?key=cf59144467b74a6eb3249634156b2c58&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/SF9n0XnWe1mhJ0SanIZw/2012/11/1155968404_1937127410001_Tired-of-Bronco-Bamma-and-Mitt-Romney-da491.jpg"><PARAM NAME="Src" VALUE="http://cache.hosting.vcmedia.vn/?key=cf59144467b74a6eb3249634156b2c58&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/SF9n0XnWe1mhJ0SanIZw/2012/11/1155968404_1937127410001_Tired-of-Bronco-Bamma-and-Mitt-Romney-da491.jpg"><PARAM NAME="WMode" VALUE="Transparent"><PARAM NAME="Play" VALUE="-1"><PARAM NAME="Loop" VALUE="-1"><PARAM NAME="Quality" VALUE="High"><PARAM NAME="SAlign" VALUE="LT"><PARAM NAME="Menu" VALUE="-1"><PARAM NAME="Base" VALUE=""><PARAM NAME="AllowScriptAccess" VALUE="always"><PARAM NAME="Scale" VALUE="NoScale"><PARAM NAME="DeviceFont" VALUE="0"><PARAM NAME="EmbedMovie" VALUE="0"><PARAM NAME="BGColor" VALUE="425166"><PARAM NAME="SWRemote" VALUE=""><PARAM NAME="MovieData" VALUE=""><PARAM NAME="SeamlessTabbing" VALUE="1"><PARAM NAME="Profile" VALUE="0"><PARAM NAME="ProfileAddress" VALUE=""><PARAM NAME="ProfilePort" VALUE="0"><PARAM NAME="AllowNetworking" VALUE="all"><PARAM NAME="AllowFullScreen" VALUE="true"><PARAM NAME="AllowFullScreenInteractive" VALUE=""><PARAM NAME="IsDependent" VALUE="0">\r\n<embed wmode="transparent" height="334" width="425" quality="high" bgcolor="#425166" flashvars="" wmode="transparent" allowfullscreen="true" allowscriptaccess="always" name="cf59144467b74a6eb3249634156b2c58" id="cf59144467b74a6eb3249634156b2c58" style="" src="http://cache.hosting.vcmedia.vn/?key=cf59144467b74a6eb3249634156b2c58&pname=mediaplayer.swf&img=http://video-thumbs.vcmedia.vn/dantri/SF9n0XnWe1mhJ0SanIZw/2012/11/1155968404_1937127410001_Tired-of-Bronco-Bamma-and-Mitt-Romney-da491.jpg" type="application/x-shockwave-flash"/></embed></OBJECT></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Vũ Quý<o:p></o:p></B></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right>Theo <I style="mso-bidi-font-style: normal">ABC</I></P><input type="hidden" value="/c36s36/Thegioi/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(7, ' Trung Quốc đã bổ nhiệm tư lệnh về Biển Hoa Đông ', '-Trung-Quoc-da-bo-nhiem-tu-lenh-ve-Bien-Hoa-Đong-', ' Mạng tin Sankei của Nhật Bản dẫn nguồn tin trang web Mạng tài chính mới của Trung Quốc ngày 1/11 cho biết nguyên Phó Tổng tham mưu trưởng Sái Anh Đĩnh, 58 tuổi, đã chính thức nhậm chức Tư lệnh Quân khu Nam Kinh của Quân Giải phóng Nhân dân Trung Quốc. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p>&nbsp;<IMG src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/10/1-d36ac.jpg" alt="Ông Sái Anh Đĩnh. (Nguồn: china.org)"><BR></P>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ông Sái Anh Đĩnh. (Nguồn: china.org)</SPAN></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center></o:p><o:p></o:p>&nbsp;</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Quân khu Nam Kinh là đơn vị quân đội có nhiệm vụ chủ yếu là đảm bảo an ninh trên Biển Hoa Đông, bao gồm cả quần đảo tranh chấp Senkaku (Bắc Kinh gọi là Điếu Ngư) và bộ phận tác chiến chống Đài Loan.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Ông Sái Anh Đĩnh được cho là người khá thông hiểu tình hình Eo biển Đài Loan và vùng biển xung quanh trên cương vị Tư lệnh quân khu này.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trong chuyến thăm Mỹ trên cương vị Phó Tổng tham mưu trưởng hồi tháng 8/2012, ông Sái Anh Đĩnh đã phản đối mạnh mẽ lập trường của Mỹ coi Senkaku là đối tượng điều chỉnh của Hiệp ước An ninh Nhật-Mỹ./.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right>Theo <I style="mso-bidi-font-style: normal">Vietnam+<o:p></o:p></I></P><input type="hidden" value="/c36s36/Thegioi/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(8, ' Thi hài ông Yasser Arafat sẽ được khai quật vào cuối tháng ', '-Thi-hai-ong-Yasser-Arafat-se-duoc-khai-quat-vao-cuoi-thang-', ' (Dân trí) – Một nhóm các nhà điều tra hình sự của Pháp sẽ tới Palestine để tiến hành khai quật và khám nghiệm thi hài nhà lãnh đạo Yasser Arafat để xác định xem ông có thực sự bị đầu độc hay không. Công việc dự kiến diễn ra trong khoảng ngày 24 – 26/11. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="TEXT-ALIGN: center">\r\n<DIV><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/yT0YJzvK8t63z214dHr/Image/2012/11/arafat-23415.jpg" width=450 _fl="" alt="Ông Arafat trong những ngày cuối đời"></DIV></DIV>\r\n<DIV style="TEXT-ALIGN: center"> <SPAN style="FONT-SIZE: 10pt">Ông Arafat trong những ngày cuối đời</SPAN></DIV>\r\n<DIV><BR></DIV>\r\n<DIV>Thông tin trên vừa được giới chức Pháp và Palestine cùng xác nhận. Đồng thời, theo hãng tin AP, cơ quan chức năng Palestine cho biết một nhóm điều tra độc lập từ Thụy Sỹ cũng sẽ tới bờ Tây thành phố Ramallah vào cùng thời điểm trên.</DIV>\r\n<DIV><BR></DIV>\r\n<DIV>Việc khai quật thi hài ông Arafat được thực hiện theo nguyện vọng của gia đình sau khi một phòng thí nghiệm tại Thụy Sỹ cho rằng ông qua đời năm 2004 vì đã bị đầu độc bằng chất phóng xạ chết người polonium-210. Phòng thí nghiệm này đã tìm thấy dấu vết của chất độc này trên quần áo mà gia đình nói là ông từng mặc.</DIV>\r\n<DIV><BR></DIV>\r\n<DIV>Các nhà điều tra Pháp và Thụy Sỹ sẽ tiến hành điều tra song song về vụ việc này với sự ủy quyền của bà Suha Arafat và nhà chức trách Palestine. Hồi mùa Hè vừa qua, chính bà Arafat đã chính thức yêu cầu cơ quan điều tra Pháp hỗ trợ. Theo cơ quan chức năng, dù điều tra riêng lẻ nhưng các nhà điều tra Pháp và Thụy Sỹ sẽ đến mộ của ông Arafat cùng lúc và chỉ có 1 cơ hội duy nhất để lấy mẫu xét nghiệm.</DIV>\r\n<DIV><BR></DIV>\r\n<DIV style="TEXT-ALIGN: right"><B>Thanh Tùng</B></DIV>\r\n<DIV style="TEXT-ALIGN: right">Theo <I>AP</I></DIV><input type="hidden" value="/c36s36/Thegioi/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(9, ' “Tướng” Hùng tin tưởng tuyển Việt Nam giành chiến thắng ', '-“Tuong”-Hung-tin-tuong-tuyen-Viet-Nam-gianh-chien-thang-', ' <a href = "http://dantri.com.vn/c26/s26-658233/tuong-hung-tin-tuong-tuyen-viet-nam-gianh-chien-thang.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/xAMQAUs9LhwrT16Af2k/Image/2012/11/V.21112-77e9d.jpg" /></a>(Dân trí) - Với bước đà tâm lý hưng phấn sau trận thắng 2-0 ngay trên đất khách, trong trận tái đấu Malaysia trên sân Mỹ Đình diễn ra tối mai (3/11), HLV Phan Thanh Hùng tin tưởng các tuyển thủ sẽ lặp lại thành tích dù đội hình thiếu vắng nhiều trụ cột.<br><a href="http://dantri.com.vn/c26/s400-657990/Tuyen-Malaysia-neu-cao-quyet-tam-gianh-chien-thang.htm"><b>&nbsp;>>&nbsp;Tuyển Malaysia nêu cao quyết tâm giành chiến thắng</b></a><br><a href="http://dantri.com.vn/c26/s400-657785/DT-Viet-am-gap-kho-truoc-tran-tai-dau-Malaysia.htm"><b>&nbsp;>>&nbsp;ĐT Việt Nam gặp khó trước trận tái đấu Malaysia</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P class=MsoNormal style="MARGIN: 0in 0in 0pt"><B style="mso-bidi-font-weight: normal">HLV Phan Thanh Hùng: Gặp <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:country-region w:st="on"><st1:place w:st="on">Malaysia</st1:place></st1:country-region> là bài kiểm tra rất quan trọng<?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></B></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Sau khi kế hoạch thi đấu giao hữu với Singapore và Ấn Độ phá sản, phương án cọ xát với CLB Gremio đến từ Brazil nhân dịp CLB này đến tham dự BTV Cup cũng đổ vỡ vì nhiều nguyên nhân nên 90 phút thi đấu với Malaysia trên sân Mỹ Đình được xem như cơ hội cuối để HLV Phan Thanh Hùng kiểm định lực lượng, chiến thuật cho nhiệm vụ nặng nề tìm kiếm ngôi “vương” Đông Nam Á.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">.</P>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt">Trước giờ thi đấu, ông Hùng cho biết: “Trận gặp <st1:country-region w:st="on">Malaysia</st1:country-region> là trận đấu nằm trong kế hoạch chuẩn bị AFF Cup, trận này ĐT Việt <st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region> phải cố gắng thể hiện được hình ảnh trước giai đoạn cuối. <st1:country-region w:st="on"><st1:place w:st="on">Malaysia</st1:place></st1:country-region> là đội ĐKVĐ, tôi rất ngưỡng mộ ông Rajagobal khi xây dựng một đội tuyển ngày càng tiến bộ. </DIV>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt">&nbsp;</DIV>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><IMG style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/xAMQAUs9LhwrT16Af2k/Image/2012/11/V.21112-77e9d.jpg" width=450 align=center _fl="" alt="HLV PThanh Hùng chờ đợi một chiến thắng&nbsp;dành cho Việt Nam (Ảnh: Gia Hưng)"></DIV>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">HLV PThanh Hùng chờ đợi một chiến thắng&nbsp;dành cho Việt Nam (Ảnh: Gia Hưng)</SPAN></DIV>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Trận lượt đi họ thua ĐT Việt <st1:place w:st="on"><st1:country-region w:st="on">Nam</st1:country-region></st1:place> vì chưa đạt được phong độ cao nhất, nhưng lần này tôi tin họ sẽ thể hiện đúng sức mạnh. <st1:country-region w:st="on">Malaysia</st1:country-region> đang rất hưng phấn sau trận thắng 3-0 trước <st1:place w:st="on">Hong Kong</st1:place> (Trung Quốc) trên sân khách. Tuy nhiên, với sự chuẩn bị đang có, tôi tin tuyển Việt <st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region> có cơ hội giành thắng lợi chung cuộc…”.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Về tình hình lực lượng của ĐT Việt <st1:country-region w:st="on">Nam</st1:country-region>, HLV Phan Thanh Hùng cho biết có 3 trụ cột không thể ra sân khi tái đấu <st1:country-region w:st="on"><st1:place w:st="on">Malaysia</st1:place></st1:country-region> là: Minh Đức, Tấn Tài và Công Vinh.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Kế hoạch thi đấu giao hữu với CLB Gremio tiếp tục bị phá sản vào phút chót, nhưng “thuyền trưởng” Phan Thanh Hùng khẳng định việc này không ảnh hưỏng nhiều đến kế hoạch chuẩn bị cho AFF Cup 2012 của ĐT Việt Nam.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><B style="mso-bidi-font-weight: normal">HLV Rajagobal: <st1:country-region w:st="on"><st1:place w:st="on">Malaysia</st1:place></st1:country-region> không ngại đối thủ nào<o:p></o:p></B></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Sau thất bại ở trận lượt đi, thầy trò HLV Rajagobal đang nêu cao quyết tâm đòi lại “món nợ” đã vay tuyển Việt Nam. Dù phải thi đấu trên đất khách, HLV Rajagobal vẫn tỏ ra rất tự tin: “Chúng tôi không sợ bất cứ đối thủ nào dù là ai đi chăng nữa, tất nhiên chúng tôi vẫn sẽ nhập cuộc với sự tôn trọng cao nhất. Tôi cho rằng mỗi đội bóng khi chuẩn bị cho một giải đấu lớn có chiến thắng hay thất bại, nhưng các đội luôn cần có đội hình mạnh nhất.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt">Năm 2010 chúng tôi không có nhiều hy vọng và cơ hội lớn. Chúng tôi thua <st1:country-region w:st="on">Indonesia</st1:country-region> ở vòng bảng, gặp ĐKVĐ Việt <st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region> ở bán kết, nhưng điều quan trọng nhất là sau đó chúng tôi đã giành chức vô địch”.</DIV>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt">&nbsp;</DIV>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><IMG style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/xAMQAUs9LhwrT16Af2k/Image/2012/11/M.21112-77e9d.jpg" width=450 align=center _fl="" alt="Ông Rajagobal tỏ ra rất tự tin về sự chuẩn bị của đội nhà (Ảnh: Gia Hưng)"></DIV>\r\n<DIV class=MsoNormal style="MARGIN: 0in 0in 0pt" align=center><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">Ông Rajagobal tỏ ra rất tự tin về sự chuẩn bị của đội nhà (Ảnh: Gia Hưng)</SPAN></DIV>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Đánh giá về trận giao hữu lượt đi để thua 0-2 ngay trên sân nhà trước ĐT Việt <st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region>, HLV Rajagobal cho biết: “Trận đó chúng tôi thua xứng đáng, nhưng giờ thì khác. Tôi mong muốn đội mình sẽ chơi tốt hơn. Ngoài mục tiêu ngoài giành chiến thắng, tôi muốn kiểm nghiệm tinh thần thi đấu, bản lĩnh thi đấu trên sân khách của các học trò”.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Nhận định về ĐT Việt <st1:country-region w:st="on">Nam</st1:country-region>, HLV Rajagobal phát biểu: “Tôi nghĩ ĐT Việt <st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region> hiện tại rất tốt dưới sự dẫn dắt của HLV Phan Thanh Hùng. Năm 2010 ĐT Việt Nam cũng rất mạnh dưới thời HLV Calisto, nhưng 2 năm gần đây các đội Đông Nam Á đã thay đổi nhiều về nhân sự, HLV, cầu thủ”.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Đánh giá về AFF Cup sắp tới, HLV Rajagobal khẳng định AFF Cup năm nay sẽ hấp dẫn hơn so với kỳ trước. Về các ứng cử viên vô địch, HLV người <st1:country-region w:st="on">Malaysia</st1:country-region> khẳng định ở bảng của ĐT Việt <st1:country-region w:st="on">Nam</st1:country-region> có Việt <st1:country-region w:st="on">Nam</st1:country-region>, <st1:country-region w:st="on"><st1:place w:st="on">Philippines</st1:place></st1:country-region>, Thái Lan. Bảng còn lại có 3 đội <st1:country-region w:st="on">Singapore</st1:country-region>, <st1:country-region w:st="on">Indonesia</st1:country-region>, <st1:place w:st="on"><st1:country-region w:st="on">Malaysia</st1:country-region></st1:place>. Tuy nhiên, ông Rajagobal cũng khuyến cáo các ứng cử viên đề phòng bất ngờ có thể xảy ra từ Lào và <st1:place w:st="on"><st1:country-region w:st="on">Myanmar</st1:country-region></st1:place>. </P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt">Để phục vụ nhu cầu của người hâm mộ, từ sáng 1/11, BTC đã mở cửa bán vé trận giao hữu Việt <st1:country-region w:st="on">Nam</st1:country-region> - <st1:country-region w:st="on"><st1:place w:st="on">Malaysia</st1:place></st1:country-region> tại sân Mỹ Đình, sân Hàng Đẫy, phố Hoàng Cầu. Vé được phát hành có 4 mệnh giá: 50.000đ, 80.000đ, 120.000đ và 150.000đ.</P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><o:p>&nbsp;</o:p></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt; TEXT-ALIGN: right" align=right><B style="mso-bidi-font-weight: normal">Thành Vinh</B></P><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(10, ' HLV Wenger kêu gọi CĐV Arsenal tôn trọng Van Persie ', '-HLV-Wenger-keu-goi-CĐV-Arsenal-ton-trong-Van-Persie-', ' (Dân trí) - Van Persie sẽ là tâm điểm trong trận đại diện MU – Arsenal cuối tuần này. Trước thềm trận đấu sắp tới, HLV Wenger đã phải đứng lên kêu gọi những CĐV nhà dành sự tôn trọng cho chân sút người Hà Lan này. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P>Trong cuộc điều tra mới đây, Van Persie đang nằm trong số những cầu thủ bị ghét nhất Premier League, đó là điều không mấy ngạc nhiên bởi hiện tại, chân sút này đang trở thành “cái gai” trong mắt CĐV Arsenal khi dám cả gan phản bội lại đội bóng để đầu quân cho MU trong mùa Hè qua.</P>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Van Persie sẽ là tâm điểm của cuộc đọ sức&nbsp;giữa MU - Arsenal" align=center src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/11/van-persie9-a4288.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Van Persie sẽ là tâm điểm của cuộc đọ sức&nbsp;giữa MU - Arsenal</SPAN></DIV>\r\n<DIV align=center>&nbsp;</DIV>\r\n<P>Lo ngại điều không hay sẽ tới với cậu học trò cũ trong trận đấu sắp tới, HLV Wenger đã phải đứng lên kêu gọi những CĐV nhà dành sự tôn trọng cho chân sút người Hà Lan này. Phát biểu trước báo giới, ông cho biết: “Tôi không biết những người hâm mộ sẽ đối xử với Van Persie như thế nào trong trận đấu sắp tới nhưng tôi hy vọng họ sẽ dành cho cầu thủ này sự tôn trọng.</P>\r\n<P>Van Persie đã cống hiến hết mình cho Arsenal trong 8 năm qua và anh ấy luôn thể hiện được phong độ tuyệt vời. Chính vì lẽ đó, Van Persie xứng đáng nhận được sự tôn trọng trong trận đấu này.</P>\r\n<P>Đôi bóng đang mở chiến dịch chống phân biệt đối xử. Trong quá khứ, chúng ta đã làm rất tốt điều này. Tại sao trong trận đấu sắp tới, những CĐV Arsenal lại không làm điều tương tự với trường hợp của Van Persie?”.</P>\r\n<P>Hiện tại, HLV Wenger cũng đang đối diện với sự đào tẩu của Walcott, theo chiến lược gia này, Arsenal đang tìm mọi cách giữ chân “ngôi sao” này trước Giáng sinh. Ông tiếp tục thổ lộ: “Trường hợp của Walcott rất khẩn cấp, cậu ấy chỉ còn 12 tháng trong hợp đồng.</P>\r\n<P>Vì vậy, bằng bất cứ giá nào, chúng tôi phải hoàn thành việc gia hạn với cầu thủ này trước Giáng sinh. Tôi luôn nói với Walcott rằng điều quan trọng với cậu ấy là chơi ở bên cánh phải, trong tương lai, nhiều khả năng cậu ấy sẽ được đá cắm. Các cầu thủ chạy cánh cần thay đổi và lui về phòng thủ nhiều hơn, trong khi Walcott cần để dành riêng cho mặt trận tấn công”.</P>\r\n<P align=right><STRONG>H.Long</STRONG></P><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(11, ' Ronaldinho thoát chết sau tai nạn kinh hoàng ', '-Ronaldinho-thoat-chet-sau-tai-nan-kinh-hoang-', ' (Dân trí) - Ngôi sao Ronaldinho đã đâm phải một chiếc xe tải hạng nặng trên đường tới sân tập của CLB Atletico Mineiro, rất may là anh đã không gặp phải một chấn thương nào. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trên đường đi, Ronaldinho dính vào một vụ tai nạn giao thông cùng một chiếc xe tải hạng nặng và một chiếc xe con khác. Vụ tai nạn này khiến chiếc xe tải bị hư hại khá nặng, một tài xế bị thương và phải đưa vào bệnh viện cấp cứu sớm.</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG style="MARGIN: 5px" alt="Ronaldinho may mắn thoát chết sau vụ tai nạn xe hơi" align=center src="http://dantri4.vcmedia.vn/UjQgQgjU2baoHmB2Oa5Y/Image/2012/11/Dinho-fa498.jpg" width=450 _fl=""><BR>Ronaldinho may mắn thoát chết sau vụ tai nạn xe hơi<BR></SPAN></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Ronaldinho may mắn không bị thương, nhưng chiếc xe của anh đã bị hỏng. Cựu cầu thủ <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:City w:st="on">Barcelona</st1:City> đã gọi điện cho người đồng đội Jo (từng đá cho <st1:place w:st="on"><st1:PlaceName w:st="on">Man</st1:PlaceName> <st1:PlaceType w:st="on">City</st1:PlaceType></st1:place>) chở anh đến sân tập. Tâm lý Ronaldinho đã thực sự hoảng loạn và Ban huấn luyện CLB Mineiro phải bố trí anh tập riêng.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trong sự nghiệp của mình, Ronaldinho chưa từng dính vào scandal liên quan đến tốc độ hay đua xe. Vì lý do đó, cầu thủ này thực sự bị sốc về tâm lý, anh chưa khỏi bàng hoàng khi nghĩ đến mình vừa thoát khỏi lưỡi hái tử thần một cách may mắn đến vậy.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Ronaldinho là là một cầu thủ vô cùng quan trọng của Atletico Mineiro và sự tỏa sáng của anh giúp CLB này đang đứng thứ hai trên BXH giải Brazil trước 5 vòng đấu cuối, thành tích vượt xa những gì Mineiro có được ở mùa giải năm ngoái (thứ 15).</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Nguyện vọng lớn nhất của Ronaldinho là phong độ xuất sắc này sẽ giúp anh có cơ hội tham dự World Cup 2014 trên sân nhà. Chính HLV tuyển Brazil Menezes khẳng định nếu Dinho nỗ lực, cơ hội của anh tại Selecao vẫn còn khá sáng sủa.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Kiều My<o:p></o:p></B></P><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(12, ' Messi sắp phá kỷ lục tồn tại hơn 100 năm qua ', '-Messi-sap-pha-kỷ-luc-ton-tai-hon-100-nam-qua-', ' (Dân trí) - Messi sinh ra để xô đổ những kỷ lục vĩ đại tưởng chừng tồn tại mãi mãi với thời gian, bằng chứng là anh không chỉ “đe dọa” kỷ lục ghi bàn của “nhà vua” Pele mà còn sắp xô đổ một kỷ lục ghi bàn đã tồn tại hơn…100 năm qua. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV>Có lẽ cái tên Vivian Woodward rất xa lạ đối với những người hâm mộ bóng đá bây giờ bởi vì thời mà ông chơi bóng đã cách đây một thế kỷ, khi mà bóng đá mới chỉ mới ở vào giai đoạn thành hình. Thế cho nên ít ai biết rằng Vivian Woodward là một trong những chân sút cự phách mà bóng đá “xứ sở sương mù” từng sản sinh và nắm giữ một kỷ lục ghi bàn đã thách thức tất cả các hậu bối của mình suốt 100 năm qua, đó là kỷ lục ghi bàn ở các trận đấu quốc tế trong một năm.</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG style="MARGIN: 5px" alt="Messi đang thách thức kỷ lục tồn tại 100 năm của Woodward" align=center src="http://dantri4.vcmedia.vn/RIZNWFsXL4Thvv6ub1QL/Image/2012/11/Messi021112-65fc6.jpg" width=450 _fl=""></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Messi đang thách thức kỷ lục tồn tại 100 năm của Woodward</SPAN></DIV>\r\n<P>Thực ra thì thời điểm Woodward lập “kỷ lục” này thì chưa có một tổ chức bóng đá chính thức nào công nhận. Tuy nhiên nếu dựa trên những thống kê từ thời ông thi đấu cùng quy định để chọn ra chân sút xuất sắc nhất năm của Liên đoàn thống kê &amp; lịch sử bóng đá thế giới IFFHS (tính những bàn thắng ở các trận đấu cấp độ quốc tế của CLB, các trận đấu của đội tuyển và cả Olympic) thì Woodward chính là chân sút đang nắm giữ kỷ lục ghi được nhiều bàn thắng nhất trong một năm.</P>\r\n<P>Chính xác thì vào 1909, Vivian Woodward đã ghi được 25 bàn thắng cho Chelsea và đội tuyển Anh trong các trận đấu quốc tế, số lần lập công mà chưa có một chân sút cự phách nào sau này có thể tái lập được. Những người tiến đến gần nhất với “kỷ lục xuyên thế kỷ” của Woodward là chân sút huyền thoại của bóng đá Iran Ali Daei với 22 bàn vào năm 1996, xếp sau với một bàn kém hơn là Ronaldo (1997) và Al-Hami Omani (2001). Ali Daei đã có 22 pha lập công cho đội tuyển Iran và Al-Sadd, đội bóng mà Raul Gonzalez đang khoác áo.</P>\r\n<P>Trở lại với thời điểm hiện tại, Lionel Messi với những màn trình diễn chói sáng cả trong màu áo Barcelona lẫn đội tuyển Argentina đã có được 22 pha lập công (10 cho Barcelona, 12 cho Argentina) ở các trận đấu quốc tế (cân bằng kỷ lục của Ali Daei) và chỉ còn kém kỷ lục ghi bàn của Woodward đúng 3 pha lập công nữa. Trước mắt <I>El Pulga</I> vẫn còn 4 trận đấu quốc tế trước mắt trong năm 2011 trong khi với hiệu suất ghi bàn&nbsp;đều&nbsp;đặn&nbsp;đang thể hiện thì có lẽ kỷ lục đã tồn tại hơn 100 năm (chính xác là 103 năm) nhiều khả năng sẽ bị xô đổ.</P>\r\n<P>Cụ thể Ngoài 3 trận đấu tại vòng bảng Champions League, Messi sẽ còn có cơ hội tham dự trận giao hữu giữa đội tuyển Argentina và đội tuyển Saudi Arabia vào ngày 14/11 tới đây để nâng cao thành tích. Trong danh sách những chân sút xuất sắc nhất năm 2012 của IFFHS thì xếp sau Messi chính là “thần đồng” Neymar với 20 bàn. Tiếp theo, chia sẻ vị trí thứ 3 với 17 bàn không phải ai khác ngoài hai sát thủ đang cùng <I>El Pulga </I>tạo nên cuộc đua tam mã đến danh hiệu Pichichi là Falcao và Cristiano Ronaldo.</P>\r\n<P align=right><B>Ngọc Trung</B></P><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(13, ' Tuyển GV tiếng Anh bản ngữ: Liệu có "tiền nào của nấy?" ', '-Tuyen-GV-tieng-Anh-ban-ngu:-Lieu-co-"tien-nao-cua-nay?"-', ' <a href = "http://dantri.com.vn/c25/s25-658063/tuyen-gv-tieng-anh-ban-ngu-lieu-co-tien-nao-cua-nay.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/7MPEnE5WMWukgZZut8l/Image/2012/11/giaovien1-64934.JPG" /></a>(Dân trí) - Lý do mức lương giáo viên đến từ các Anh, Mỹ, Úc hay nhiều nước châu Âu quá cao nên TPHCM chọn giáo viên người Philippines vào dạy tiếng Anh ở trường phổ thông. Điều này làm nhiều người lo ngại liệu có “tiền nào của nấy?”<br><a href="http://dantri.com.vn/c25/s25-657268/Phu-huynh-ganh-100-kinh-phi-thue-GV-tieng-Anh-ban-ngu.htm"><b>&nbsp;>>&nbsp;  Phụ huynh “gánh” 100% kinh phí thuê GV tiếng Anh bản ngữ</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P><B>Tại sao là GV Philippines?</B></P>\r\n<P>Trong tháng 11/2012, 100 giáo viên (GV) Philippines sẽ được phân bổ về các trường tiểu học, THCS ở TPHCM phục vụ cho việc dạy học tiếng Anh.<B> </B>Đội ngũ GV này do thành phố tuyển, có sở pháp lý rõ ràng, đã trải qua khảo sát về năng lực chuyên môn và khả năng sư phạm. Toàn bộ tiền thuê GV bản ngữ sẽ do phụ huynh có con tham gia chương trình đóng kinh phí.</P>\r\n<P>Các GV bản ngữ sẽ trực tiếp đứng lớp giảng dạy học sinh (HS)&nbsp;theo phân phối chương trình của Sở GD-ĐT, tham gia các hoạt động chuyên môn ở trường và trên địa bà, dự giờ góp ý và chia sẻ kinh nghiệm với các đồng nghiệp và tổ chức các hoạt động ngoại khóa nhằm xây dựng môi trường ngôn ngữ cho HS. Tổng 35 tiết mỗi tháng với mức lương 2.000 USD. </P>\r\n<P style="TEXT-ALIGN: center"></P>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Học sinh tiểu học tại TPHCM trong buổi giao lưu với giáo viên, sinh viên nước ngoài.&nbsp;" align=middle src="http://dantri4.vcmedia.vn/7MPEnE5WMWukgZZut8l/Image/2012/11/giaovien1-64934.JPG" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Học sinh tiểu học tại TPHCM trong buổi giao lưu với giáo viên, sinh viên nước ngoài.&nbsp;</SPAN></DIV>\r\n<P></P>\r\n<P>Ông Lê Hồng Sơn - Giám đốc Sở GD-ĐT TPHCM cho hay trước khi quyết định chọn GV người Philippines, Sở đã khảo sát lương trả cho GV bản ngữ đến từ Úc khoảng 5.000 USD/tháng, người Anh, Mỹ và các nước Châu Âu mức lương cao hơn. Trong khi đó, mức lương của GV người Philippines chỉ 2.000 USD/tháng. Theo tính toán với mức lương này, mỗi HS tham gia chương trình sẽ đóng khoảng 120.000 đồng/tháng. </P>\r\n<P>Theo đánh giá của Sở GD-ĐT TPHCM, Philippines là quốc gia ở khu vực Đông Nam Á có tiếng Anh là một trong 2 ngôn ngữ chính thức. Tiếng Anh tại Philippines được sử dụng trong giao tiếp hằng ngày và trong các loại văn bản hành chính. Hầu hết các trường phổ thông và đại học ở Philippines giảng dạy hoàn toàn bằng tiếng Anh và học theo giáo trình của Mỹ.</P>\r\n<P>Trong những năm gần đây, Philippines trở thành địa chỉ du học tiếng Anh lớn ở khu vực châu Á. Sinh viên châu Á chọn Philippines để du học bởi lợi thế Philippines có dân số nói tiếng Anh đông, giọng phát âm chuẩn, học phí và chi phí sinh hoạt rẻ hơn nhiều so với các nước châu Âu, Bắc Mỹ.</P>\r\n<P><B>Ít tiền có đảm bảo chất lượng? </B></P>\r\n<P>Trước thông tin Sở GD-ĐT TPHCM chọn GV Philippines vào giảng dạy tiếng Anh ở trường phổ thông, các trường và phụ huynh trên địa bàn đã có những phản ứng trái ngược nhau. </P>\r\n<P>Nhiều trường đã có hợp đồng với GV bản ngữ từ các trung tâm cho rằng HS của mình đang được tiếp cận với GV người Anh, Mỹ, Úc có chất giọng và phát âm tiếng Anh chuẩn. Nếu như Sở tuyển GV từ Philippines&nbsp;- dù sao cũng là một nước châu Á&nbsp;- thì chẳng khác yêu cầu trường tự hạ chất lượng dạy học?</P>\r\n<P>“Hiện nay trường đã ký hợp đồng một năm với GV người Mỹ từ trung tâm ngoại ngữ nên phải hết năm nay chúng tôi mới nhận GV phân bổ từ Sở. Có điều trường rất hài lòng về GV bản ngữ hiện tại, nếu không phải thay thì tốt quá", hiệu trưởng một Trường tiểu học ở Q.4 băn khoăn và bày tỏ lo ngại, phụ huynh thích cho học với GV người Mỹ, nếu trường đổi GV biết đâu họ không cho con tham gia mà chọn đi học ở trung tâm. </P>\r\n<DIV>Tuy nhiên, không ít người đồng tình với việc tuyển GV Philippines. Về vấn đề tiền bạc, nếu chờ thuê GV người Anh, Úc, Mỹ với mức lương "ngất ngưởng" thì rất khó để phụ huynh HS kham nổi chi phí. Như vậy, chỉ một số em gia đình có điều kiện mới có cơ hội theo học. Bên cạnh đó, khả năng tiếng Anh của người Philippines cũng được nhiều người ghi nhận. </DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV>\r\n<TABLE style="WIDTH: 156.95pt; BACKGROUND: #fff1dd; mso-cellspacing: 0in; mso-table-lspace: 2.25pt; mso-table-rspace: 2.25pt; mso-table-anchor-vertical: paragraph; mso-table-anchor-horizontal: column; mso-table-left: right; mso-table-top: middle; mso-padding-alt: 0in 0in 0in 0in" class=MsoNormalTable border=1 cellSpacing=0 cellPadding=0 width=209 align=right>\r\n<TBODY>\r\n<TR style="HEIGHT: 109.8pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes; mso-yfti-lastrow: yes">\r\n<TD style="BORDER-BOTTOM: #ff9a63 1pt inset; BORDER-LEFT: #ff9a63 1pt inset; PADDING-BOTTOM: 3pt; PADDING-LEFT: 3pt; WIDTH: 156.95pt; PADDING-RIGHT: 3pt; BACKGROUND: none transparent scroll repeat 0% 0%; HEIGHT: 109.8pt; BORDER-TOP: #ff9a63 1pt inset; BORDER-RIGHT: #ff9a63 1pt inset; PADDING-TOP: 3pt" width=209>\r\n<P style="mso-element: frame; mso-element-frame-hspace: 2.25pt; mso-element-wrap: around; mso-element-anchor-vertical: paragraph; mso-element-anchor-horizontal: column; mso-element-left: right; mso-element-top: middle; mso-height-rule: exactly"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">“Tôi đang học tiếng Anh với thầy giáo người Philippines ở trung tâm ngoại ngữ và thấy họ dạy rất tốt, không thua kém với GV các nước đến từ Anh, Úc, Mỹ đâu, phương pháp còn dễ tiếp thu hơn. Nên khi TPHCM tuyển GV Philippines vào trường học dạy tiếng Anh, tôi thấy không vấn đề gì, tôi sẽ cho con tham gia. Chỉ có điều, nói tiếng Anh tốt chưa hẳn là dạy tốt nên phải là những người được khảo sát chặt về chuyên môn và phương pháp sư phạm”. - chị Phạm Thị Nhung, ngụ ở đường Lý Thường Kiệt, Q.11, TPHCM - một phụ huynh học sinh, đang làm việc cho công ty nước ngoài.<?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P></TD></TR></TBODY></TABLE>Cô Hồ Dương Châu - Tổ trưởng bộ môn Ngoại ngữ trường Tiểu học Lê Ngọc Hân (Q.1) cho biết, thông qua hợp đồng với trung tâm Apollo, trước đây trường từng có GV bản ngữ là người Philippines đến giảng dạy tiếng Anh. </DIV>\r\n<P>“Về những GV Sở tuyển thì mình chưa thể đánh giá vì chưa có cơ hội tiếp xúc nhưng những GV người Philippines từng dạy ở trường, tôi đánh giá cao khả năng tiếng Anh và chuyên môn sư phạm của họ.</P>\r\n<P>Nhiều phương pháp dạy tiếng Anh của họ rất hay vì tiếng Anh là ngôn ngữ thứ hai của họ. Không chỉ có kinh nghiệm về chuyển tải, GV Philippines còn có lợi thế mà GV đến từ các nước châu Âu đôi khi không thể bằng là văn hóa của họ rất gần với mình”, cô Châu cho hay. </P>\r\n<P>Đại diện nhiều trường cho rằng, chưa thể đánh giá về GV khi chưa làm việc nên… chờ rồi mới biết. Nhưng các trường tỏ ra ra khá yên tâm vì đội ngũ này đã được khảo sát về chuyên môn và khả năng sư phạm. Hơn nữa, Sở GD-ĐT TPHCM đã chia sẻ, trong quá trình làm việc, nếu trường thấy GV không đủ chuyên môn và không đáp ứng các yêu cầu thì có thể ngưng hợp đồng. </P>\r\n<P>Khi việc thuê GV bản ngữ ở các trường lâu nay vẫn theo kiểu “tự lo”, nhiều trường loay hoay lúng túng thì chủ trương tuyển GV nước ngoài tham gia sâu vào việc dạy học tiếng Anh ở trường là cần thiết. </P>\r\n<P>Nhưng phải chăng cần linh động trong việc phân bổ GV. Nếu các trường đã có thể thuê GV đến từ Mỹ, Anh hay Úc, liệu&nbsp;có cần thiết phải đổi GV người Philippines theo chủ trương? Còn nữa, việc kiểm soát chất lượng giảng dạy của đội ngũ GV bản ngữ cũng cần thật chặt chẽ để không rơi vào cảnh sai rồi mới tìm cách sửa như tình trạng học ngoại ngữ lâu nay chúng ta đang gặp phải. <BR></P>\r\n<P align=right><B>Hoài Nam</B></P><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(14, ' Thấy gì từ lớp học tương tác?  ', '-Thay-gi-tu-lop-hoc-tuong-tac?--', ' (Dân trí) - Ứng dụng CNTT vào giảng dạy đang ngày trở nên phổ biến đối với các trường học. Tùy theo điều kiện ở từng nơi mà cách thực hiện đa dạng khác nhau. Với thành phố lớn, vài năm trở lại đây mô hình lớp học tương tác đã xuất hiện ở bậc tiểu học. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P align=left>Theo ông Phạm Xuân Tiến&nbsp;- Trưởng phòng giáo dục tiểu học (Sở GD-ĐT Hà Nội) thì lớp học tương tác là mô hình học tập hiện đại, giúp tiết học hiệu quả, góp phần đổi mới phương pháp dạy học. Để xây dựng một lớp học tương tác thì cần có kinh phí để mua bảng tương tác, xây dựng các giáo án điện tử, phần mềm giúp bảng tương tác hoạt động đúng công năng. </P>\r\n<P align=left><B>Đa dạng cách học tạo hứng thú cho HS </B></P>\r\n<DIV align=left>Vừa qua,&nbsp;phóng viên đã trực tiếp tham dự một số lớp học tương tác trong đó có lớp học của học sinh (HS) lớp 1 Trường tiểu học Thành Công B - đây là đơn vị thực hiện mô hình lớp học tương tác ở Hà Nội. </DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=left>Khác với phương pháp truyền thống là<B> </B>giáo viên (GV) đứng lớp đóng vai trò trung tâm, người học bị động, chủ yếu chỉ nghe và ghi lại những điều cần thiết vào vở. Điều này đồng nghĩa với việc GV hầu như không kiểm soát được việc luyện tập cũng như thực hành bài học của HS. </DIV>\r\n<P align=left>Bên cạnh đó với bậc tiểu học đòi hỏi GV phải làm hình ảnh, đồ dùng minh họa… khiến cho việc chuẩn bị mất nhiều thời gian. Quan trọng hơn là tiết học bị gián đoạn giữa chừng khi GV thay đổi hình ảnh hoặc viết lên bảng làm cho HS ít hào hứng. </P>\r\n<DIV align=left>Với mô hình tương tác, người học đóng vai trò trung tâm theo đúng nghĩa, GV chỉ là người trợ giúp và hướng dẫn. HS chủ động tham gia vào các bài học, các em có thể tự điều khiển bảng điện tử làm bài tập thực hành, tự kiểm tra kết quả và khả năng ngôn ngữ của mình hoặc chơi các trò chơi để được hiệu quả cao nhất cho mỗi bài học.</DIV>\r\n<DIV align=justify>&nbsp;</DIV>\r\n<DIV align=center><STRONG><IMG style="MARGIN: 5px" alt="Lớp học tương tác đang được nhiều bậc phụ huynh ở thành phố lớn" align=center src="http://dantri4.vcmedia.vn/HbxTheXQ02TdWUD081Fn/Image/2012/11/hs_02112012-166a8.jpg" width=450 _fl=""><BR></STRONG><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><SPAN style="FONT-FAMILY: Arial; FONT-SIZE: 10pt">Lớp học tương tác đang được nhiều bậc phụ huynh ở thành phố lớn</SPAN><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">&nbsp;đặc biệt quan tâm.</SPAN></SPAN><BR></DIV>\r\n<P align=left>Để có một lớp học tương tác, các trang - thiết bị gồm: Máy vi tính, máy chiếu, bảng tương tác, thiết bị kiểm tra trắc nghiệm, phần mềm soạn giáo án, tích hợp sẵn tích hợp sẵn thư viện tài nguyên ảnh cùng với các thông tin liên quan... Bảng tương tác có chức năng giống như màn hình cảm ứng, tạo ra môi trường học tập ảo trực quan, sinh động, giúp GV, HS có thể tương tác trực tiếp vào giáo án điện tử. </P>\r\n<P align=left>Thông qua bút từ tương tác vừa có chức năng như bút viết bảng, vừa hoạt động như chuột máy tính, hệ thống thiết bị kiểm tra trắc nghiệm giúp GV kiểm tra, đánh giá các đáp án của HS trong thời gian nhanh nhất. Phần mềm soạn giáo án Active Primary hỗ trợ GV trong soạn và giảng bài, giúp họ chủ động từ việc soạn giáo án, giảng dạy đến việc đánh giá kết quả học tập của HS. Mỗi bài giảng thực hiện trên phần mềm Active Primary sẽ đem đến cho HS những kiến thức hết sức sinh động, trực quan, dễ hiểu, nhớ lâu, tạo ấn tượng mạnh, làm cho HS hào hứng hơn khi tham gia học tập...</P>\r\n<DIV align=left>Hiện nay ở thành phố lớn tình trạng sĩ số lớp đông phần lớn xảy ra ở các trường. Do đó hoạt động giảng dạy phần nào đó cũng bị ảnh hưởng cho dù nhiều GV đưa ra các hình thức sáng tạo khác nhau. Không ít phụ huynh từng đặt ra câu hỏi: Với sĩ số lớp như vậy thì cô giáo sẽ quan tâm đến HS như thế nào? Việc kiểm tra khả năng làm bài tập ra sao?...</DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Sự sinh động trong bài giảng đã tạo cho HS thực sự cuốn hút với tiết học." align=center src="http://dantri4.vcmedia.vn/HbxTheXQ02TdWUD081Fn/Image/2012/11/hs1_02112012-0a495.jpg" width=450 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Sự sinh động trong bài giảng đã tạo cho HS thực sự cuốn hút với tiết học.</SPAN></DIV>\r\n<P align=left>Nếu không có hỗ trợ của công nghệ thì bài toán này vô cùng khó. Tuy nhiên, ưu thế của lớp học tương tác là mỗi HS được cấp một thiết bị cũng mã số để phản hồi câu hỏi của GV dưới hình thức chọn đáp án. Chỉ mất vài giây là GV đã biết chính xác HS nào trả lời đúng và sai để qua đó có thể điều chỉnh uốn ắn các em. </P>\r\n<DIV align=left>Theo cô Trần Thu Hà - GV trường tiểu học Thành Công B, mô hình lớp học tương tác tạo hứng thú cho HS tham gia vào bài giảng. Cùng với một đáp án GV có thể thay đổi dưới dạng cấu trúc câu hỏi trong khoảng thời gian ngắn. Thông qua cách này sẽ tạo cho HS tăng thêm tính tư duy và sáng tạo. </DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Sự sinh động trong bài giảng đã tạo cho HS thực sự cuốn hút với tiết học." align=center src="http://dantri4.vcmedia.vn/HbxTheXQ02TdWUD081Fn/Image/2012/11/hs2_02112012-33282.jpg" width=450 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma"><SPAN style="FONT-FAMILY: Arial; FONT-SIZE: 10pt">Với thiết bị kiểm tra trắc nghiệm giáo viên GV sẽ biết chính xác HS nào trả lời đúng, trả lời sai và </SPAN><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">qua đó đánh giá được khả năng tiếp thu bài của từng HS.</SPAN></SPAN></DIV>\r\n<P align=left>Bên cạnh đó, khi chuyển nội dung bài giảng thì tiết học không bị gián đoạn bởi các em có thể vừa thư giản bằng cách nghe một đoạn nhạc thiếu nhi nhưng vẫn tham gia các hoạt động khác như cất đồ dùng học tập, lấy sách vở mới… Một hình thức vừa học, vừa chơi tạo cảm giác thoải mái cho HS. </P>\r\n<P align=left><B>GV vất vả hơn </B></P>\r\n<P align=left>Để chuẩn bị một tiết học ở lớp tương tác, các GV phải vất vả hơn rất nhiều so với cách dạy truyền thống. Ngoài việc giỏi về kỹ năng tin học để vận hành các thiết tương tác, máy chiếu đa vật thể…, GV còn phải luôn tìm tòi và cập nhật thêm các thông tin mới. </P>\r\n<P align=left>Cô Phạm Thị Yến&nbsp;- Hiệu trưởng Trường tiểu học Thành Công B chia sẻ: “Đối với lớp học tương tác thì không có điểm dừng nghĩa là phải luôn sáng tạo và bắt kịp với kiến thức hàng ngày. GV ngoài việc làm thực hiện điều đó thì hàng tuần còn phải ngồi cùng lại với nhà trường để đánh giá, rút kinh nghiệm. Thông qua đó sẽ có sự điều chỉnh để tiết học thú vị hơn”.</P>\r\n<DIV align=left>Dưới góc độ là GV trực tiếp tham gia giảng dạy, cô Trần Thu Hà cho biết thêm, năm đầu tiên triển khai GV gặp rất nhiều khó khăn và vất vả. Cách tiếp cận bài học mới, phương pháp giảng dạy mới cộng thêm sử dụng các thiết bị công nghệ đòi hỏi GV phải rất nỗ lực. Những năm sau đó thì có phần nhẹ nhàng hơn một chút bởi GV đã quen với mô hình và quan trọng hơn là nền giáo án đã có. </DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Lớp học tương tác đòi hỏi GV&nbsp;phải&nbsp;luôn sáng tạo." align=center src="http://dantri4.vcmedia.vn/HbxTheXQ02TdWUD081Fn/Image/2012/11/hs3_02112012-91c0d.jpg" width=450 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Lớp học tương tác đòi hỏi GV&nbsp;phải&nbsp;luôn sáng tạo.</SPAN></DIV>\r\n<DIV align=left><BR>Theo đánh giá của các GV từng tham gia dạy mô hình này thì các sản phẩm trong hệ thống lớp học tương tác giúp dễ dàng soạn ra các giáo án trực quan, sinh động. Tuy nhiên để làm được điều đó đòi hỏi GV phải sáng tạo và có ý tưởng để phát triển bài học làm sao tạo được hứng thú cho HS. </DIV>\r\n<P align=left>Ông Lê Tiến Thành&nbsp;- Vụ trưởng Vụ giáo dục tiểu học (Bộ GD-ĐT) cho rằng, một tiết học thu hút HS phụ thuộc lớn vào người thầy. Tuy nhiên, nếu có sự hỗ trợ của công nghệ thì nó sẽ làm cho tiết học sinh động và cuốn hút HS hơn. </P>\r\n<P align=left>Với những lợi thế nhất định, lớp học tương tác đang được nhiều bậc phụ huynh Hà Nội có con đang học ở bậc tiểu học quan tâm. Tuy nhiên, với mức chi phí đầu tư ban đầu khá lớn nên các trường không thể triển khai được nếu thiếu sự đóng góp từ phía các bậc phụ huynh. Ngoài ra, làm thế nào để phụ huynh đồng tình và tự nguyện cho con tham gia vào lớp học tương tác là điều không đơn giản. Vậy làm thế nào để giải quyết bài toán này? Chúng tôi sẽ tiếp tục trao đổi với bạn đọc trong bài kế tiếp. </P>\r\n<P align=right><B>S.H </B></P><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(15, ' KTX thừa chỗ, SV ở trọ giá cao ', '-KTX-thua-cho,-SV-o-tro-gia-cao-', ' Vì ngại mất an ninh, phải làm đêm về khuya, hàng ngàn sinh viên phải thuê nhà trọ giá cao, bị chủ trọ bắt chẹt. Cần có biện pháp bảo vệ trật tự cho khu vực làng ĐH.<br><a href="http://dantri.com.vn/c25/s25-654477/Sinh-vien-o-tro-gheo-ma-phai-xai-sang.htm"><b>&nbsp;>>&nbsp;Sinh viên ở trọ: Nghèo mà phải... “xài sang”</b></a><br><a href="http://dantri.com.vn/c25/s25-649187/Khi-SV-bi-chu-nha-tro-vet-tui.htm"><b>&nbsp;>>&nbsp;Khi SV bị chủ nhà trọ “vét túi” </b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Những năm trước, ký túc xá (KTX) ĐH Quốc gia TPHCM thiếu chỗ, năm học mới nào sinh viên (SV) cũng đối mặt với chuyện phải chạy tìm phòng trọ, giá phòng trọ tăng. Tuy nhiên, năm nay KTX có đủ chỗ ở nhưng giá phòng trọ vẫn tăng đến mức từ 1 triệu đến 1 triệu rưỡi đồng/tháng. </P>\r\n<P><B>Phải đóng tiền trước sáu tháng</B></P>\r\n<P>Điều đáng nói, chủ trọ đưa ra yêu cầu trả trước nhiều tháng tiền phòng. Có nơi bắt trả trước ba tháng, có nơi là sáu tháng và thậm chí là một năm. Trả lời thắc mắc SV, một chủ trọ nói: “Ở đây cô bắt đặt cọc như vậy là ít đó, có đi tới chiều thì&nbsp; ở đâu cũng vậy à, có nơi còn bắt trả trước một năm nữa kìa”. Vì sao phải chấp nhận việc chủ trọ bóp chẹt như vậy? Trung, SV năm 2 ĐH Bách khoa, nói: “Ở đây nhiều SV ở trọ nên kiếm được phòng là phải đặt cọc ngay kẻo hết chỗ”. Nếu không thu tiền trước một lần các chủ nhà trọ ra chiêu đóng nhiều đợt, cách nhau 15 đến 20 ngày nhưng rốt cuộc SV vẫn phải đóng trước ít nhất sáu tháng tiền nhà&nbsp; mới được ở tiếp. Cách thu tiền trước này vừa chiếm dụng vốn, vừa đẩy SV vào tình thế khó khăn nhiều rủi ro. Sơn, SV Trường ĐH Thể dục thể thao, cho biết: “Phòng trọ ở đây là 1.200.000 đồng/tháng, chưa kể tiền điện nước, tiền phí rác. Tôi bắt đầu ở ngày 5-9, phải đóng trước 2.400.000 đồng. Tới ngày 15-9 đóng thêm 4.800.000 đồng và tới tháng 11 phải đóng 6 triệu đồng. Chủ báo trước sau tết này tiền phòng sẽ tăng 1 triệu rưỡi chứ không ở mức 1.200.000 nữa”. Do không đủ tiền đóng tiếp trong tháng 11 nên một vài ngày nữa Sơn sẽ xin vào KTX Trường ĐH Thể dục thể thao. Số tiền Sơn đã đặt cọc bị mất trắng.</P>\r\n<P>Cùng tâm trạng này, Lệ Thao, SV năm nhất khoa Ngữ văn Anh Trường ĐH KHXH&amp;NV, cho biết: “Em cùng với đứa bạn thuê phòng phòng trọ 1.200.000 đồng/tháng, trả&nbsp; trước nửa năm. Tháng sau đứa bạn chuyển đi rồi, nếu không tìm được người ở cùng thì em cũng phải chuyển mà không biết có được trả lại tiền phòng không nữa”. </P>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-style: italic">\r\n<DIV><IMG style="MARGIN: 5px" alt="Phòng trọ ọp ẹp của một SV được thuê giá 700.000 đồng/tháng và phải đóng trước sáu tháng. (Ảnh: PV)" align=center src="http://dantri4.vcmedia.vn/vtfPRccccccccccccodZ/Image/2012/11/phong-tro-bef05.jpg" width=400 _fl=""></DIV></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-style: italic">Phòng trọ ọp ẹp của một SV được thuê giá 700.000 đồng/tháng và phải đóng trước sáu tháng. (Ảnh: PV)</SPAN></DIV>\r\n<P><B>Trả giá cao chất lượng thấp</B></P>\r\n<P>Với giá từ 1 triệu đến 1 triệu rưỡi đồng/tháng SV được thuê một phòng rộng 12-18 m<SUP>2</SUP>; mái lợp bằng tôn và trần sơ sài bằng xốp mỏng; trời nắng thì nóng hừng hực, trời mưa thì dột vì phòng xuống cấp. Tình hình an ninh không được đảm bảo. Việc bị kẻ&nbsp; xấu viếng thăm và&nbsp; “mượn” tạm đồ được xem là “chuyện thường ngày ở huyện”. </P>\r\n<P>Nước sinh hoạt ở nhà trọ chủ yếu là nước giếng khoan, giá cả lại có sự chênh lệch đáng kể: có nơi 3.000 đồng/khối, nơi thì 20.000 đồng/người, nơi 30.000 đồng/người và thậm chí có nơi lại 40.000 đồng/người. Nước thì thường xuyên bị váng màu, có lúc đen và mùi hôi, đi mua nước bên ngoài để ăn thì tốn kém nên phải nhắm mắt dùng. Nhiều chủ trọ còn đưa ra những quy định khắt khe như bạn tới chơi nộp 10.000 đồng/người, không cho người lạ ngủ lại (dù là bạn bè hay người thân)…</P>\r\n<P><B>Đâu là hướng gỡ?</B></P>\r\n<P>Chị&nbsp; Hương Anh, nhân viên Trung tâm Hỗ trợ SV Trường ĐH Khoa học Tự nhiên - Cơ sở Linh Trung, cho biết: “Hai năm trước trung tâm có tổ chức tư vấn phòng trọ cho SV. Thời đó, trước khi nhập học một tuần, trung tâm khảo sát phòng trọ vòng quanh làng ĐH và mở rộng ra khu vực Nông Lâm. Khi nhập học, trung tâm tư vấn và dẫn SV đến tận nơi cho thuê, tìm các nhà trọ chất lượng và không bị ép giá. Năm 2012 KTX được xây dụng thêm để&nbsp; khuyến khích SV vào ở trong KTX, trung tâm không triển khai chương trình này nữa. Có một vài trường hợp quá khó khăn, trung tâm đã giúp tìm chỗ&nbsp; ở tại nhà dân và được hỗ trợ tiền sinh hoạt phí”. </P>\r\n<P>Về&nbsp; nguyên nhân không vào KTX các SV cho rằng có nhiều nguyên nhân khác nhau, có&nbsp; người không quen sống tập thể, có người phải đi làm đêm về khuya quá giờ quy định… nhưng nguyên nhân phổ biến đáng lưu ý nhất là đường vào KTX khu B quá&nbsp; xa, vắng vẻ và mất an ninh. Khu vực này từng xảy ra nhiều vụ cướp xe và đâm chém. Mới đây, có hai SV bị đâm trọng thương ở gần khu B. Thiết nghĩ&nbsp; Ban Quả n lý&nbsp; KTX cần tăng cường quan hệ với cơ quan chức năng địa phương bảo đảm trật tự khu vực này. Nên chăng xem xét có chế độ giờ giấc thoáng hơn cho SV làm thêm để giải quyết tình trạng KTX thừa phòng, còn SV bị chủ trọ bắt chẹt. </P>\r\n<DIV align=center>\r\n<TABLE style="BORDER-BOTTOM: medium none; BORDER-LEFT: medium none; WIDTH: 80.46%; BORDER-COLLAPSE: collapse; BACKGROUND: whitesmoke; BORDER-TOP: medium none; BORDER-RIGHT: medium none; mso-border-alt: outset steelblue .75pt" class=MsoNormalTable border=1 cellSpacing=0 cellPadding=0 width="80%">\r\n<TBODY>\r\n<TR style="HEIGHT: 131.75pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes; mso-yfti-lastrow: yes">\r\n<TD style="BORDER-BOTTOM: steelblue 1pt inset; BORDER-LEFT: steelblue 1pt inset; PADDING-BOTTOM: 0.75pt; BACKGROUND-COLOR: transparent; PADDING-LEFT: 0.75pt; PADDING-RIGHT: 0.75pt; HEIGHT: 131.75pt; BORDER-TOP: steelblue 1pt inset; BORDER-RIGHT: steelblue 1pt inset; PADDING-TOP: 0.75pt; mso-border-alt: inset steelblue .75pt">\r\n<P><B><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt">KTX ĐHQG TPHCM còn dư 3.000 chỗ ở</SPAN></B><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt">Hiện tại KTX còn hơn 3.000 chỗ ở đang mời gọi các bạn SV. Vào thời điểm này, SV đã ở ổn định, chúng tôi đang chờ SV đăng ký trong học kỳ 2. Chúng tôi có 20 dãy nhà cũ và năm lốc nhà 12 tầng, chia làm hai khu, khu A có 11.000 chỗ ở, khu B có 5.000 chỗ.<o:p></o:p></SPAN></P>\r\n<P><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt">SV sống trong KTX mỗi tháng chỉ đóng 120.000 đồng, có các điểm đón xe buýt tới các trường học. KTX được xây mới nên tiện nghi giống khách sạn hai sao, mỗi phòng bình quân 6-8 SV. KTX có đầy đủ hệ thống an ninh, y tế, các câu lạc bộ như tin học, phát thanh, nữ sinh, CLB SV yêu điện ảnh… để SV học tập và giải trí.<o:p></o:p></SPAN></P>\r\n<P><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt">Đối với SV năm nhất, thủ tục đăng ký khá đơn giản, chỉ cần phôtô CMND, giấy báo nhập học và đơn xin vào ở KTX. Đối với SV năm 2, 3, 4 cần thêm thẻ SV. SV và người nhà có thể liên hệ và nộp hồ sơ tại Phòng công tác SV nhà A1 (khu A) KTX ĐHQG TP.HCM. Số điện thoại: 08.37242265". - <I style="mso-bidi-font-style: normal"><SPAN style="mso-bidi-font-weight: bold">Ông Trần Thanh An,</SPAN> <SPAN style="mso-bidi-font-style: italic">Giám đốc KTX ĐHQG TP.HCM</SPAN></I><o:p></o:p></SPAN></P>\r\n<P style="TEXT-ALIGN: right" align=right><B><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt">Hàn Giang</SPAN></B><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt"> <I>ghi</I></SPAN><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><o:p></o:p></SPAN></P></TD></TR></TBODY></TABLE></DIV>\r\n<DIV style="TEXT-ALIGN: right" align=right>&nbsp;</DIV>\r\n<DIV style="TEXT-ALIGN: right" align=right>Theo <B style="mso-bidi-font-weight: normal">Huệ Nguyễn - Phạm Nhung</B></DIV>\r\n<DIV style="TEXT-ALIGN: right" align=right><I style="mso-bidi-font-style: normal">Pháp luật TPHCM<o:p></o:p></I></DIV><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(16, ' Rối tìm cách quản dạy thêm ', '-Roi-tim-cach-quan-day-them-', ' Tại hội thảo bàn về quản lý dạy thêm học thêm do Sở GD&ĐT Hà Nội tổ chức hôm qua, nhiều nội dung cần được hướng dẫn vẫn chưa tường minh. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=ptitle align=center><SPAN style="FONT-FAMILY: Tahoma; COLOR: windowtext; FONT-SIZE: 10pt; FONT-WEIGHT: normal; mso-bidi-font-weight: bold">\r\n<DIV><IMG style="MARGIN: 5px" alt="Rối tìm cách quản dạy thêm" align=center src="http://dantri4.vcmedia.vn/vtfPRccccccccccccodZ/Image/2012/11/hs-th-95f99.jpg" width=400 _fl=""></DIV></SPAN></DIV>\r\n<DIV style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=ptitle align=center><SPAN style="FONT-FAMILY: Tahoma; COLOR: windowtext; FONT-SIZE: 10pt; FONT-WEIGHT: normal; mso-bidi-font-weight: bold">Theo Thông tư 17 của Bộ GD&amp;ĐT, các cá nhân, tập thể không được tổ chức dạy thêm với học sinh tiểu học.&nbsp; <?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></DIV>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><STRONG><SPAN style="COLOR: windowtext; FONT-SIZE: 12pt"><o:p><SPAN style="FONT-FAMILY: Verdana">&nbsp;</SPAN></o:p></SPAN></STRONG></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><STRONG><SPAN style="FONT-FAMILY: Times New Roman; COLOR: windowtext; FONT-SIZE: 12pt">Dạy thêm cấp tiểu học: được hay không?<o:p></o:p></SPAN></STRONG></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Theo dự thảo quy định về quản lý dạy thêm học thêm trên địa bàn thành phố Hà Nội, một số trường hợp không được dạy thêm, trong đó có trường hợp “không dạy thêm với học sinh tiểu học, trừ các trường hợp bồi dưỡng nghệ thuật, thể dục thể thao, rèn luyện kỹ năng sống”. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Đây là một nội dung được lấy từ Thông tư 17 của Bộ GD&amp;ĐT, quy định về dạy thêm học thêm. Tuy nhiên, các nội dung hướng dẫn tiếp theo của dự thảo lại mâu thuẫn với quy định này.<o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Chẳng hạn, trong điều 5 của dự thảo có mục nêu một trong những hình thức dạy thêm học thêm là: “Nhận quản lý học sinh tiểu học ngoài giờ học theo yêu cầu của gia đình”.<o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Thậm chí, cũng trong điều 5 này ở mục quy định về thời gian thực hiện dạy thêm học thêm viết: “Học sinh tiểu học không quá hai tiết/ buổi học, không quá hai buổi/ tuần”. Được biết, những nội dung này từng có trong dự thảo Thông tư 17 nhưng đến khi ban hành chính thức thì bị gạch bỏ. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Trong hội thảo mà Sở GD&amp;ĐT Hà Nội tổ chức nhằm lấy ý kiến các cán bộ quản lý giáo dục, những nội dung này được bàn luận khá rôm rả.<o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Bà Dương Thị Thanh Huyền, Trưởng Phòng GD&amp;ĐT quận Hoàn Kiếm đề nghị bộ phận chắp bút dự thảo lưu ý quy định không dạy thêm học thêm với học sinh tiểu học của Thông tư 17.<o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Theo bà Huyền, về phương diện quản lý, nếu có đủ phòng học thì các phòng GD&amp;ĐT sẽ khuyến khích các trường tiểu học tổ chức dạy hai buổi/ngày chứ không phải là tổ chức trông giữ trẻ ngoài giờ theo yêu cầu của gia đình. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Tuy nhiên, ông Nguyễn Hữu Quý, Trưởng Phòng GD&amp;ĐT huyện Gia Lâm lại ủng hộ cho phép hoạt động trông giữ trẻ ngoài giờ học.<o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">“Ngày thứ bảy, phụ huynh tha thiết mong các trường tổ chức trông giữ trẻ, nhân đó giáo dục nghệ thuật, kỹ năng sống… cho các em với mức thu vừa phải để họ có thể yên tâm đi làm”, ông Quý nói. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Còn một đại diện Phòng GD Tiểu học Sở GD&amp;ĐT Hà Nội cho rằng, không nên gọi là học thêm cũng như “trông giữ trẻ” với việc tổ chức hoạt động nghệ thuật, thể dục thể thao, rèn kỹ năng sống sau buổi học mà nên gọi đó là câu lạc bộ. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Từ đề xuất này, câu chuyện “trông giữ trẻ” hay “câu lạc bộ” với học sinh tiểu học được trở đi trở lại khá nhiều lần trong hội thảo.<o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><STRONG><SPAN style="FONT-FAMILY: Times New Roman; COLOR: windowtext; FONT-SIZE: 12pt">Có nên quy định mức thu?<o:p></o:p></SPAN></STRONG></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Vấn đề thu chi trong dạy thêm, học thêm cũng là nội dung được bàn luận sôi nổi trong hội thảo. Theo dự thảo, mức thu tiền học thêm trong hay ngoài nhà trường là do thỏa thuận giữa đơn vị, cá nhân, tập thể tổ chức dạy thêm với cha mẹ học sinh. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Nhiều đại biểu cho rằng, quy định như thế là chung chung và tạo điều kiện để mỗi nơi đưa ra một mức giá theo đòi hỏi chủ quan mà không phải vì chất lượng. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">“Tôi rất muốn có khung về mức thu. Hiện nay, vì không có quy định khung nên có sự bất công về thu nhập giữa các giáo viên. Cũng là giáo viên nhưng có những người dạy thêm thu hàng chục triệu đồng/ tháng, có giáo viên không được mấy đồng”, đại biểu phòng GD&amp;ĐT của một huyện ngoại thành nói. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Bà Nguyễn Thị Hường, chuyên viên Phòng GD&amp;ĐT huyện Từ Liêm cũng chia sẻ: “Thu nhập từ dạy thêm của các giáo sư, tiến sĩ cũng bị khống chế bởi các quy định, trong khi các giáo viên tiểu học thu tiền dạy thêm thì không ai quy định. Thực tế, có giáo viên tiểu học dạy thêm trong dịp hè thu cả trăm triệu đồng. Thu mỗi học sinh 100.000 đồng, mỗi lớp chỉ cần 30 học sinh là cô đã có 3 triệu đồng/ buổi học”. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Theo ông Lê Ngọc Quang, Phó Giám đốc Sở GD&amp;ĐT Hà Nội, lãnh đạo ngành GD&amp;ĐT rất quan tâm tới mức thu tiền học thêm nhưng đưa vào quy định là rất khó. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Tuy nhiên, việc quản lý vấn đề này có thể mong chờ vào hai yếu tố: sự thỏa thuận với phụ huynh học sinh và việc bắt buộc phải có dự toán thu - chi của các cá nhân, tập thể tổ chức dạy thêm học thêm khi làm thủ tục xin cấp phép. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<DIV style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt">Ông Quang cho biết, trong lúc chờ quy định chính thức của UBND TP Hà Nội, việc quản lý dạy thêm học thêm trên địa bàn thành phố thực hiện theo Thông tư 17 của Bộ GD&amp;ĐT. </SPAN></DIV>\r\n<DIV style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></DIV>\r\n<DIV align=center>\r\n<TABLE style="BORDER-BOTTOM: medium none; BORDER-LEFT: medium none; WIDTH: 80.46%; BORDER-COLLAPSE: collapse; BACKGROUND: whitesmoke; BORDER-TOP: medium none; BORDER-RIGHT: medium none; mso-border-alt: outset steelblue .75pt" class=MsoNormalTable border=1 cellSpacing=0 cellPadding=0 width="80%">\r\n<TBODY>\r\n<TR style="HEIGHT: 131.75pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes; mso-yfti-lastrow: yes">\r\n<TD style="BORDER-BOTTOM: steelblue 1pt inset; BORDER-LEFT: steelblue 1pt inset; PADDING-BOTTOM: 0.75pt; BACKGROUND-COLOR: transparent; PADDING-LEFT: 0.75pt; PADDING-RIGHT: 0.75pt; HEIGHT: 131.75pt; BORDER-TOP: steelblue 1pt inset; BORDER-RIGHT: steelblue 1pt inset; PADDING-TOP: 0.75pt; mso-border-alt: inset steelblue .75pt">\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt" class=pbody><SPAN style="FONT-FAMILY: Tahoma; COLOR: windowtext; FONT-SIZE: 10pt">Việc chi những khoản gì từ tiền thu được do dạy thêm cũng là vấn đề được nhiều đại biểu bàn cãi. Đại diện các phòng GD&amp;ĐT mong muốn được trích lại một số phần trăm nhất định để chi phí cho quản lý hoạt động dạy thêm. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt" class=pbody><SPAN style="FONT-FAMILY: Tahoma; COLOR: windowtext; FONT-SIZE: 10pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt" class=pbody><SPAN style="FONT-FAMILY: Tahoma; COLOR: windowtext; FONT-SIZE: 10pt">“Trách nhiệm của phòng GD&amp;ĐT là quản lý và cấp phép hoạt động dạy thêm học thêm. Muốn quản lý được thì phải tổ chức kiểm tra và phải là kiểm tra liên ngành, ngành giáo dục chỉ một vài đại diện. <o:p></o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt" class=pbody><SPAN style="FONT-FAMILY: Tahoma; COLOR: windowtext; FONT-SIZE: 10pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt" class=pbody><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Nếu vậy phải có kinh phí mà thành phố thì không chấp nhận khoản này chiếm quá nhiều”, cán bộ một phòng GD&amp;ĐT nói. Đại diện Phòng Kế hoạch Tài chính, Sở GD&amp;ĐT cho rằng, cơ chế tự chủ tài chính không cho phép cơ quan quản lý nhà nước quy định tỷ lệ phần trăm cơ cấu chi cho khoản thu từ dạy thêm học thêm.</SPAN><SPAN style="FONT-SIZE: 10pt"><SPAN style="FONT-FAMILY: Verdana; COLOR: windowtext; FONT-SIZE: 12pt"><o:p></o:p></SPAN></SPAN></P></TD></TR></TBODY></TABLE></DIV>\r\n<P style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pbody><SPAN style="FONT-FAMILY: "Times New Roman"; COLOR: windowtext; FONT-SIZE: 12pt"><o:p>&nbsp;</o:p></SPAN></P><SPAN style="FONT-FAMILY: "Times New Roman"; FONT-SIZE: 12pt; FONT-WEIGHT: normal; mso-bidi-font-weight: bold">\r\n<DIV style="TEXT-ALIGN: left; LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pauthor align=right>&nbsp;</DIV>\r\n<DIV style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pauthor align=right><EM>Theo </EM><STRONG>Quý Hiên</STRONG></DIV>\r\n<DIV style="LINE-HEIGHT: normal; MARGIN: 0in 0in 0pt; BACKGROUND: white" class=pauthor align=right><EM>Tiền Phong</EM></SPAN></DIV><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(17, ' Cha mất, mẹ bỏ đi, bé 15 tuổi gánh gồng nuôi 2 em ăn học ', '-Cha-mat,-me-bo-di,-be-15-tuoi-ganh-gong-nuoi-2-em-an-hoc-', ' <a href = "http://dantri.com.vn/c167/s167-657994/cha-mat-me-bo-di-be-15-tuoi-ganh-gong-nuoi-2-em-an-hoc.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/x9pGccccccccccccDacc/Image/2012/10/h2-437c0.jpg" /></a>(Dân trí) - Tâm hồn trẻ thơ vốn nhạy cảm và khao khát được yêu thương, nhưng từ nhỏ ba anh em Vương, Huy, Lên đã thiếu tiếng ầu ơ của mẹ, không nhận được cái nhìn nghiêm nghị của cha… Thế nhưng ba anh em vẫn bảo ban nhau sống và học hành,... ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="TEXT-ALIGN: justify" class=MsoNormal><B style="mso-bidi-font-weight: normal">Gánh trách nhiệm “trụ cột gia đình” ở tuổi 15</B></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Người ta thường nói: “Mồ côi cha ăn cơm với cá; Mồ côi má lót lá mà nằm”, câu nói này chưa hẳn đúng với hoàn cảnh của 3 anh em mồ côi: Ngô Văn Vương (1997), Ngô Văn Lên (1999) và em Ngô Văn Huy (2002) nhà ở ấp Thuận, xã Dưỡng Điềm, Châu Thành, Tiền Giang. Cha mẹ chia tay nhau, mẹ bỏ ba anh em đi biệt tích mà chẳng một lần về thăm. Cha của em là anh Ngô Văn Quỡn làm thuê, làm mướn để nuôi 3 đứa con thơ. Năm 2007, sau một cơn bạo bệnh, anh Quỡn qua đời, để lại 3 đứa con thơ dại, côi cút bơ vơ… </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Lúc cha chết, cậu bé Vương (anh cả) chỉ mới 10 tuổi. Cái tuổi ăn chưa no, lo chưa tới thế mà Vương phải thay cha, mẹ gánh lấy trách nhiệm “trụ cột trong gia đình” nên học hết lớp 5, Vương nghỉ học, đi bán vé số nuôi hai em mình ăn học. Cứ thế, mỗi ngày Vương lãnh 100 tờ vé số đi bán, nhưng buổi sáng Vương tranh thủ bán đến 10 giờ rồi chạy về nấu cơm để sẵn cho em Lên, Huy về ăn. Vương lại tiếp tục đi bán vé số cho đến chiều tối.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/10/h1-437c0.jpg" width=336 _fl="" alt="Nhiều lần Vương dành dụm tiền để mua chiếc xe đạp mới cho hai em đi học nhưng lực bất tòng tâm"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Nhiều lần Vương dành dụm tiền để mua chiếc xe đạp mới cho hai em đi học "nhưng lực bất tòng tâm"</SPAN><BR></DIV><BR>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Anh Ngô Văn Ngon - bác ruột của anh em Vương cho biết: “Khi cha các cháu mất, vợ chồng tôi cũng định đưa các cháu về nhà chăm sóc, nhưng các cháu không đồng ý vì hai nhà gần bên nên các cháu nói ở đây cũng như ở nhà Bác. Bởi thế, vợ chồng tôi chỉ đến bảo ban và chỉ dạy cho Vương cách chăm sóc các em. Có lon gạo, con cá thì mình mang qua cho tụi nó rồi phải đi làm thuê kiếm sống, chứ nghỉ làm một ngày là thiếu gạo ăn một ngày!</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Sống trong cảnh mồ côi, ba anh em của Vương chẳng khác nào như những “dây đờn đứt dây chông chênh giữa cuộc đời”…Thế nhưng, vượt qua cảnh nghèo khó, ba anh em vẫn cố gắng phấn đấu “vì một ngày mai tươi sáng”. Thương anh hai vất vả sớm hôm bé Lên và bé Huy rất chăm học. Hiện tại, bé Lên đang học lớp 6A9 trường THCS Dưỡng Điềm, bé Huy học lớp 4/3 trường Tiểu học Điềm Hy (Châu Thành), các em thường có được giấy khen là học sinh tiên tiến. </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Vương cho biết: “Các em ngày một lớn, chi phí cho sinh hoạt ngày càng nhiều nên em phải ráng đi bán vé số nhiều hơn nữa mới đủ tiền lo cho các em. Nhưng hiện nay có nhiều người ra bán quá nên việc bán vé số của em cũng ế ẩm lắm. Chắc phải tìm một việc gì đó có thu nhập ổn định để lo cho các em”.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Được biết, 2 em của Vương đi học được ngoài sự cố gắng của Vương cũng nhờ cái sổ hộ nghèo của cha để lại nên được miễn giảm tiền học phí. Riêng sách vở, quần áo thì các thầy cô, bà con trong xóm giúp đỡ hết, vì tiền lời bán vé số của Vương chỉ đủ đong gạo ăn hàng ngày, hôm nào bán khá thì mới giám mua ít thịt cho hai em ăn cho biết chứ không thể ăn no như người ta.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal><B style="mso-bidi-font-weight: normal">Chuyển nghề và đi học bổ túc ban đêm</B></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Thấy việc bán vé số ế quá nên Vương dẫn hai em qua nhà hàng xóm xin học nghề đan giỏ gia công. Nhờ khéo tay, chăm chỉ nên sau 1 tháng miệt mài luyện đến nay cả ba anh em đều có thể tự đan một cái giỏ hoàn chỉnh. Hàng ngày, sau khi lo chuyện cơm nước xong, Vương đến chỗ đan giỏ để làm. Khi hết giờ học em Lên và Huy cũng đến cùng làm với anh hai. Cứ 3 ngày, 3 anh em đan được được một bộ gồm 3 giỏ, tiền công được trả là 15.000 đồng/ cái. Toàn bộ số tiền này, Vương trích ra 1 phần mua gạo, số còn lại để dành lo chuyện học hành cho Huy và Lên sau này. </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Vương cho biết em đã đăng kí học lại lớp 7 hệ bổ túc vào ban đêm. Mặc dù ban ngày làm việc vất vả nhưng đêm đến, em không vắng một bữa học nào. Vương quyết tâm tốt nghiệp phổ thông, sau đó thi vào trường nghề, chọn học nghành điện để có việc làm ổn định lo cho các em ăn học, chứ nghề đan giỏ này chẳng biết có được bền lâu. <BR></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/10/h2-437c0.jpg" width=448 _fl="" alt="Nhiều lần Vương dành dụm tiền để mua chiếc xe đạp mới cho hai em đi học nhưng lực bất tòng tâm"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ban ngày Vương cùng hai em đi đan giỏ, ban đêm Vương đi học bổ túc, quyết tâm thi đỗ tốt nghiệp THPT đi học nghề để lo cho hai em lâu dài</SPAN><BR></DIV><BR>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Ngây thơ, bé Huy nói: “Ai cũng bảo mẹ của con còn sống, sao con chẳng thấy mẹ về thăm, mẹ không thương tụi con đâu…!” Nghe các cháu kể mà chúng tôi cũng không cầm được nước mắt. Ước mơ của bé Lên là có một chiếc xe đạp để đi học vì chiếc xe của em đã quá cũ và bị hư hoài, có bữa xe tuột sên,&nbsp;em phải dẫn bộ về nhà đổ cả mồ hôi. </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>“Đã mấy lần, em dành dụm định mua chiếc xe mới cho mấy em, nhưng cứ đụng chuyện xài hoài, nhất là thời gian trước bé Huy phải nằm viện mổ, em đã vay mượn tiền của chủ đại lý vé số. Bác ấy không tính tiền lời, chỉ cho mượn không và con góp trả hàng ngày nên đâu có dư tiền mà mua xe đạp cho em”. <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>Em Vương cho biết.</P>\r\n<DIV style="TEXT-ALIGN: justify" class=MsoNormal>Bé Lên ước mơ được đi học và trở thành thầy giáo, còn bé Huy thì trả lời chắc nịch rằng mình sau này sẽ làm bác sĩ để chữa bệnh cho mọi người. Chẳng biết ước mở của 3 anh em mồ côi giàu nghị lực có thực hiện được mơ ước của mình được hay không khi chuyện “cơm áo gạo tiền” là gánh nặng quá sức đối với tuổi của các em!</DIV>\r\n<DIV style="TEXT-ALIGN: justify" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="TEXT-ALIGN: justify" class=MsoNormal>\r\n<DIV align=center>\r\n<TABLE border=1 cellSpacing=0 cellPadding=0 width="90%">\r\n<TBODY>\r\n<TR>\r\n<TD>\r\n<P align=center><B>Mọi đóng góp hảo tâm xin gửi về:</B></P>\r\n<P><B>1.</B> <B>Mã số 788: </B>Em<B> Ngô Văn Vương, </B>ấp Thuận, xã Dưỡng Điềm, Châu Thành, Tiền Giang. </P>\r\n<P>ĐT: 0982.945.494 (gặp cô Phương)</P>\r\n<P><B>2. Quỹ Nhân ái - Báo Khuyến học &amp; Dân trí - Báo điện tử Dân trí. </B></P>\r\n<P>Ngõ 2 nhà số 48 Giảng Võ, Đống Đa, Hà Nội (Cạnh cây xăng Kim Mã)</P>\r\n<P>Tel: 04. 3. 7366.491/ Fax: 04. 3. 7366.490 </P>\r\n<P>Email: <A href="mailto:quynhanai@dantri.com.vn">quynhanai@dantri.com.vn</A> </P>\r\n<P><B><I><U>Bạn đọc ủng hộ qua các tài khoản sau:</U></I></B></P>\r\n<P><B><I>* Tài khoản VNĐ tại VietComBank: <BR></I></B>Tên TK: <B>Báo Khuyến học &amp; Dân trí <BR></B>Số TK: 045 100 194 4487 <BR>Tại: Ngân Hàng TMCP Ngoại thương Việt Nam - Chi nhánh Thành Công - Hà Nội.</P>\r\n<P><B><I>* Tài khoản USD tại VietComBank: <BR></I></B>Account Name: <B>Bao Khuyen hoc &amp; Dan tri</B></P>\r\n<P>Account Number: 045 137 195 6482</P>\r\n<P>Swift Code: BFTVVNVX</P>\r\n<P>Bank Name: THE BANK FOR FOREIGN TRADE OF VIETNAM (VietComBank)</P>\r\n<P><B><I>* Tài khoản VNĐ tại VietinBank: <BR></I></B>Tên TK: <B>Báo Khuyến học &amp; Dân trí <BR></B>Số TK: 10 201 0000 220 639 <BR>Tại: Ngân hàng Thương mại Cổ phần Công Thương Việt Nam - Chi nhánh Hoàn Kiếm</P>\r\n<P><B>* Tài khoản VNĐ tại Ngân hàng Quân đội (MB)</B></P>\r\n<P>Tên TK: <B>Báo Khuyến học &amp; Dân trí <BR></B>Số TK: 0721100356359</P>\r\n<P>Tại Ngân hàng TMCP Quân đội – Chi nhánh Thái Thịnh - Hà Nội</P>\r\n<P><B>* Tài khoản&nbsp;USD tại Ngân hàng Quân đội (MB)</B></P>\r\n<P>Tên TK: <B>Báo Khuyến học &amp; Dân trí <BR></B>Số TK: 0721100357002</P>\r\n<P>Swift Code: MSCBVNVX</P>\r\n<P>Bank Name: MILITARY COMMERCIAL JOINT STOCK BANK -&nbsp; MCSB ( No.3, Lieu Giai str., Ba Dinh Dist., Hanoi, Vietnam)</P>\r\n<P><B>3. Văn phòng đại diện của báo: </B></P>\r\n<P>&nbsp;VP Hà Tĩnh: 46 Nguyễn Công Trứ, Phường Tân Giang, TP Hà Tĩnh. Tel: 039.3.857.122 </P>\r\n<P>VP Đà Nẵng: 25 Nguyễn Tri Phương, Quận Thanh Khê, TP Đà Nẵng. Tel: 0511.3653.725 </P>\r\n<P>VP HCM: số 39L đường 11 (Miếu Nổi), phường 3, quận Bình Thạnh, TP.HCM. Tel: 0866786885</P>\r\n<P>VP Cần Thơ: 53/13 Lý Tự Trọng, Q Ninh Kiều, TP Cần Thơ. Tel: 0710.3.733.269&nbsp;</P></TD></TR></TBODY></TABLE></DIV></DIV>\r\n<P style="TEXT-ALIGN: right" align=right><B style="mso-bidi-font-weight: normal">Ngô Nguyễn – Diệu Hiếu</B></P>\r\n<STYLE>\r\nst1\\:*{behavior:url(#ieooui) }\r\n</STYLE><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(18, ' Hơn 116 triệu đồng đến với “bé 22 tháng tuổi bị cha mẹ bỏ rơi” ', '-Hon-116-trieu-dong-den-voi-“be-22-thang-tuoi-bi-cha-me-bo-roi”-', ' (Dân trí) - Ngày 31/10, PV Dân trí đến nhà thăm bé Huỳnh Duy Khang – nhân vật trong bài viết: “Cha mẹ bỏ đi biệt tích, bé 22 tháng tuổi thoi thóp chờ phép mầu”. Dịp này chúng tôi trao tới gia đình bé Khang 116.250.000 đồng do bạn đọc gửi giúp trong tuần 1,2,3 tháng 9.<br><a href="http://dantri.com.vn/c167/s718-635805/Cha-me-bo-di-biet-tich-be-22-thang-tuoi-thoi-thop-cho-phep-mau.htm"><b>&nbsp;>>&nbsp;Cha mẹ bỏ đi biệt tích, bé 22 tháng tuổi thoi thóp chờ phép mầu</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="TEXT-ALIGN: justify" class=MsoNormal>Sau khi hay tin bé Khang cùng ông bà ngoại trở về từ Bệnh viện Nhi Đồng 2 (tái khám), PV Dân trí đến tận nhà thăm hỏi sức khỏe bé. Bà Trần Thị Nga – bà ngoại bé Khang cho biết: “Đúng lúc gia đình tui không còn đủ sức để lo cho cháu thì bạn đọc Dân trí giúp đỡ kịp thời. Gần 2 tháng qua, toàn bộ chi phí nằm viện từ TP. Cần Thơ đến bệnh viện Nhi Đồng 2 đều do bạn đọc Dân trí chia sẻ, nếu không, vợ chồng tui chẳng biết bám víu vào đâu”</P>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/11/h1-0947e.JPG" width=336 _fl="" alt="Ngôi nhà mới của ông bà ngoại cháu Khang do người dân ở xã Khánh Hòa dựng nên"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ngôi nhà mới của ông bà ngoại cháu Khang do người dân ở xã Khánh Hòa dựng nên</SPAN><BR></DIV>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>&nbsp;Hỏi thăm về bệnh tình bé Khang, bà Nga cho biết sau khi làm các xét nghiệm tổng quát cho bé Khang, các bác sĩ bệnh viện Nhi Đồng 2 cho biết hiện tượng cháu Khang còn khò khè, tái sốt, co giật là do di chứng của bệnh viêm não, phải tiếp tục điều trị. Sau 2 tháng uống thuốc gia đình đưa cháu Khang lên tái khám, nếu tiến triển tốt thì tiếp tục điều trị và có thể tập vật lí trị liệu cho 2 chân bé. Và khi nào những cơn co giật của bé Khang giảm thì mới tiến hành phẫu thuật bệnh thoát vị rốn cho bé được.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Thời gian qua, một số bạn đọc Dân trí trực tiếp đến gia đình, chứng kiến nơi ở tạm của ông bà ngoại cháu Khang (huyện Tịnh Biên) xa xôi, không có điện, nước sạch sinh hoạt nên các bạn đọc đã vận động về quê cũ (xã Khánh Hòa, huyện Châu Phú) ở và được bà con dựng một ngôi nhà sàn nhỏ sinh sống. Ngoài ra, các nhà hảo tâm còn góp giúp tiền vô điện, nước và mua tặng ông bà ngoại cháu Khang 1 con bò để nuôi.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>\r\n<DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/x9pGccccccccccccDacc/Image/2012/11/h2-0947e.JPG" width=448 _fl="" alt="Ngôi nhà mới của ông bà ngoại cháu Khang do người dân ở xã Khánh Hòa dựng nên"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Đại diện bạn đọc Dân trí, ông Phạm Quốc Tuấn - Chủ tịch UBND xã Khánh Hòa trao số tiền 116.250.000 đồng cho ông ngoại cháu Khang</SPAN><BR></DIV><BR></DIV>Đại diện bạn đọc Dân trí, Ông Phạm Quốc Tuấn – Chủ tịch UBND xã Khánh Hòa trao số tiền <STRONG>116.250.000 đồng&nbsp;</STRONG>đến ông bà ngoại cháu Khang và phối hợp với báo Dân trí tiến hành mở số tiết kiệm cho ông bà 100 triệu đồng, số còn lại (16.250.000 đồng) trao cho ông bà ngoại bé Khang mang về nhà để trả nợ.\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Bà Nga chia sẻ: “Toàn bộ số tiền trong sổ tiết kiệm, vợ chồng tui sẽ gìn giữ cho bé Khang để lo việc chữa bệnh cho cháu, vì theo các bác sĩ bệnh tình của cháu chẳng sẽ phải điều trị trong thời gian dài. Với tấm lòng của các bạn đọc Dân trí, các nhà hảo tâm và sự quan tâm của địa phương trong thời gian qua vợ chồng tui xin chân thành cám ơn và xin hứa sẽ bảo quản, chi xài số tiền bạn đọc giúp đỡ cho cháu Khang đúng mục đích.”</P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal align=right>&nbsp;<B style="mso-bidi-font-weight: normal">Ngô Nguyễn</B></P><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(19, ' Hơn 32 triệu đồng đến với 3 chị em sống nhờ bằng…máu người khác ', '-Hon-32-trieu-dong-den-voi-3-chi-em-song-nho-bang…mau-nguoi-khac-', ' (Dân trí)- Sáng ngày 1/11, Nhà báo Phan Huy- Trưởng VP báo Dân trí tại TP Cần Thơ đã trao tiền bạn đọc hỗ trợ đến gia đình của 3 chị em bị ung thư máu ở Bến Tre.<br><a href="http://dantri.com.vn/c167/s718-650327/3-chi-em-song-nho-bang-mau-nguoi-khac.htm"><b>&nbsp;>>&nbsp;3 chị em sống nhờ bằng... máu người khác</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P class=MsoNormal><B style="mso-bidi-font-weight: normal"></B>Khi hay tin bạn đọc hỗ trợ một số tiền trong việc điều trị bệnh cho con, từ Bến Tre, bà Bùi Ngọc Nga đã nhờ người thân chở xe máy đến VP báo <I style="mso-bidi-font-style: normal">Dân trí</I> tại TP Cần Thơ để bày tỏ sự tri ân với những tấm lòng cao cả của bạn đọc khắp cả nước.</P>\r\n<P class=MsoNormal>Bà Nga cho biết, sau khi mục Tấm lòng nhân ái của báo <I style="mso-bidi-font-style: normal">Dân trí</I> có đăng bài “<I style="mso-bidi-font-style: normal">3 chị em sống nhờ bằng…máu người khác</I>” vào ngày 11/10 nói về 3 đứa con của bà là Nguyễn Thị Cẩm Tú, Nguyễn Thị Cẩm Hường và Nguyễn Hoàng Thiện bị bệnh ung thư máu và hoàn cảnh gia đình khó khăn không có điều kiện chữa trị, có rất nhiều bạn đọc ở khắp nơi gọi điện thăm hỏi, chia sẻ cũng như mong muốn được hỗ trợ cho gia đình.</P>\r\n<P class=MsoNormal>“Khi nhận được điện thoại gọi đến từ nhiều số lạ, hỏi ra mới biết đều là bạn đọc báo <I style="mso-bidi-font-style: normal">Dân trí</I> muốn giúp đỡ gia đình tiền chữa bệnh cho các con, vợ chồng tôi xúc động lắm. Thật sự tôi không ngờ hoàn cảnh của gia đình lại nhận được nhiều sự quan tâm đến như vậy”,bà Nga chia sẻ.</P>\r\n<P class=MsoNormal>Bà Nga cũng cho hay, từ khi bài báo đăng, gia đình đã nhận được tiền từ bưu điện gửi về, từ chuyển khoản và trực tiếp của bạn đọc cho gia đình. Từ số tiền này, gia đình đã đưa 3 chị em Cẩm Tú, Cẩm Hường, Hoàng Thiện lên TPHCM điều trị tiếp trong mấy tuần qua.</P>\r\n<P style="TEXT-ALIGN: center" class=MsoNormal></P>\r\n<DIV style="TEXT-ALIGN: center">\r\n<DIV><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/qePPb5vVAImhcccccccc/Image/2012/11/Hinh-498c7.jpg" width=450 _fl="" alt="Nhà báo Phan Huy- Trưởng VP Dân trí tại Cần Thơ trao tiền bạn đọc đến bà Nga."></DIV></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Nhà báo Phan Huy- Trưởng VP Dân trí tại Cần Thơ trao tiền bạn đọc đến bà Nga.</SPAN><BR></DIV>\r\n<P></P>\r\n<P class=MsoNormal>Trò chuyện với bà Nga, Nhà báo Phan Huy- Trưởng VP báo <I style="mso-bidi-font-style: normal">Dân trí</I> tại Cần Thơ cho biết, văn phòng cũng nhận được nhiều lời sẻ chia của bạn đọc đến với 3 chị em Cẩm Tú. Nhà báo Phan Huy cũng gửi lời thăm hỏi sức khỏe đến 3 người con bị bệnh của bà Nga. </P>\r\n<P class=MsoNormal>Sau những lời thăm hỏi ân cần, Nhà báo Phan Huy đã trao trực tiếp số tiền <STRONG>32.400.000 đồng </STRONG>của bạn đọc gửi hỗ trợ trong tuần 1, 2, 3 của tháng 10/2012 đến tận tay bà Nga để giúp điều trị bệnh cho các con. </P>\r\n<P class=MsoNormal>Nhận được số tiền trên của bạn đọc từ Trưởng VP báo <I style="mso-bidi-font-style: normal">Dân trí</I> Cần Thơ, bà Nga gửi lời bày tỏ cảm ơn chân thành đến bạn đọc gần xa. “Thêm một ngàn đồng, con tôi đã có thể sống thêm một giây, một phút. Ơn nghĩa của bạn đọc, gia đình tôi sẽ mãi không bao giờ quên”, bà Nga bộc bạch. Bà Nga cho biết sẽ sử dụng số tiền đúng như tấm lòng của bạn đọc gửi đến là thay máu, duy trì cuộc sống cho các con của bà.<B style="mso-bidi-font-weight: normal"> <BR></B></P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal><B style="mso-bidi-font-weight: normal"><SPAN style="mso-tab-count: 8">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Huỳnh Hải - Ngô Nguyễn </B></P><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(20, ' Hơn 30 triệu đồng đến với cậu bé vảy nến thể mủ  ', '-Hon-30-trieu-dong-den-voi-cau-be-vay-nen-the-mu--', ' (Dân trí) - Mười hai tuổi, bé Nguyễn Mạnh Hoàng mắc căn bệnh vảy nến thể mủ quái ác. Để có tiền chữa trị cho con, bố mẹ đã vật lộn với đủ thứ nghề từ cắt tóc, xe ôm hay đi làm may thuê nhưng cũng không thấm vào đâu<br><a href="http://dantri.com.vn/c167/s718-646697/Xot-thuong-cau-be-bi-vay-nen-the-mu.htm"><b>&nbsp;>>&nbsp;Xót thương cậu bé bị vảy nến thể mủ </b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">Gặp lại Hoàng sau gần 1 tháng tại bệnh viện, hiện tại em đang được về nghỉ ít ngày tại phố Nguyễn Sơn, Gia Lâm, Hà Nội. Căn nhà nhỏ nằm trên gác 2 rộng chừng hơn 10 mét vuông ẩm thấp chật chội là chỗ chui ra chui vào của 3 con người tội nghiệp. </SPAN></DIV>\r\n<DIV style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt"></SPAN>&nbsp;</DIV>\r\n<DIV style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">Lấy nhau đã mười mấy năm nay nhưng vợ chồng anh Chiến, chị Mai chỉ có duy nhất một mình bé Hoàng nhưng em lại mang bệnh nan y không chữa được. Phần vì do sinh khó, phần vì lo sợ đẻ con ra lại mang bệnh giống anh và cũng vì hoàn cảnh gia đình khó khăn nên anh chị quyết định không sinh nữa. Ngậm ngùi nói về đường con cái, chị Mai tâm sự : “Nhìn nhà người ta con cái có anh có em tôi cũng tủi lắm, nhưng Hoàng bị bệnh thế này nói thật vợ chồng tôi không đủ sức nếu có thêm em bé nữa”.</SPAN></DIV>\r\n<P style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">\r\n<DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/OiXSkesLXcY7767ccccc/Image/2012/11/10-05cc1.jpg" width=450 _fl="" alt="Bị bệnh vảy nến thể mủ nên cậu bé Hoàng thường xuyên phải đến viện chữa trị"><BR></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bị bệnh vảy nến thể mủ nên cậu bé Hoàng thường xuyên phải đến viện chữa trị <BR></SPAN></DIV></DIV></SPAN>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt"></SPAN><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">Hàng ngày với công việc cắt tóc ở một tiệm nhỏ nằm khuất trong ngõ hẻm, anh Chiến cũng chẳng kiếm được là bao nên chị Mai cũng thêm vất vả. Đi làm may thuê rồi lại kiêm luôn công việc dọn dẹp nhà cửa mọi người thuê theo tiếng, mỗi tháng chị có hơn 2 triệu lo mua thuốc cho con. Những ngày Hoàng ở viện, một trong hai người phải đi chăm nên không thể làm được thành ra hoàn cảnh đã khó lại càng thêm túng bấn. </SPAN></P>\r\n<P style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">Anh Chiến cho biết từ khi được bạn đọc Dân trí giúp đỡ, gia đình cũng nhận được nhiều cuộc điện thoại hỏi thăm và chỉ cho các cách chữa bệnh. Số tiền nhận qua đường bưu điện không có nhiều nhưng quan trọng là tinh thần được động viên và những lời chia sẻ khiến cả hai vợ chồng đều cảm động và càng quyết tâm chữa bệnh cho con. Tuy chỉ có điều khiến anh chị phân vân đó là hàng ngày nhìn các bạn được đi học, Hoàng lại khóc vì tủi thân. Thấy con như thế cả hai vợ chồng lại không cầm được nước mắt nên cũng khóc theo con. Mấy năm liền đi chữa bệnh, gia đình kiệt quệ lắm rồi nhưng thương con nên vợ chồng vẫn động viên nhau cố gắng. <BR></SPAN></P>\r\n<P style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">\r\n<DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/OiXSkesLXcY7767ccccc/Image/2012/11/9-05cc1.jpg" width=450 _fl="" alt="Bé Hoàng nhận quà bạn đọc Dân trí giúp đỡ tuần 1 + 2/ 10"><BR></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma"><SPAN style="FONT-SIZE: 10pt">Bé Hoàng nhận quà bạn đọc Dân trí giúp đỡ tuần 1 + 2/ 10</SPAN><BR></SPAN></DIV></DIV></SPAN>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt"></SPAN><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">Nhân dịp này PV Dân trí đã gửi đến gia đình số tiền <STRONG>33.850.000 đồng </STRONG>của bạn đọc giúp đỡ tuần 1+2/10. Nhận món quà, anh Chiến xúc động nói lời cám ơn : “Gia đình tôi xin cám ơn tất cả những tấm lòng hảo tâm đã quan tâm giúp đỡ vợ chồng tôi có điều kiện để tiếp tục chữa trị cho cháu. Tôi biết bệnh của cháu rất khó để chữa khỏi nhưng vợ chồng tôi sẽ cố gắng đến cùng vì con bị bệnh nó đã khổ lắm rồi mà chúng tôi thì không thể gánh được cho con”.</SPAN></P>\r\n<P style="TEXT-ALIGN: right; LINE-HEIGHT: 130%; MARGIN-BOTTOM: 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal"><SPAN style="LINE-HEIGHT: 130%; FONT-FAMILY: "Times New Roman","serif"; FONT-SIZE: 12pt">Phạm Oanh</SPAN></B></P><input type="hidden" value="/c167s167/tamlongnhanai/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(21, ' Gần 170 mã giảm sàn, chứng khoán lao dốc ', '-Gan-170-ma-giam-san,-chung-khoan-lao-doc-', ' <a href = "http://dantri.com.vn/c76/s76-658213/gan-170-ma-giam-san-chung-khoan-lao-doc.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/hOUaiPIzyO22coD06QNl/Image/2012/11/giam-san-179d4.jpg" /></a>(Dân trí) - Với hơn 360 mã giảm điểm, trong đó 169 mã giảm sàn, thị trường lao dốc nhanh chóng, VN-Index mất tới 10,18 điểm. Cổ phiếu SBT, SCR giảm sàn và bị bán ồ ạt sau thông tin bà Huỳnh Bích Ngọc rút khỏi HĐQT của SBT.<br><a href="http://dantri.com.vn/c76/s235-658115/Chu-tich-Sacomreal-chi-con-so-huu-duoi-10-von.htm"><b>&nbsp;>>&nbsp;Chủ tịch Sacomreal chỉ còn sở hữu dưới 10% vốn</b></a><br><a href="http://dantri.com.vn/c76/s235-657918/Bon-diem-nguoc-doi-cua-TTCK-Viet-am.htm"><b>&nbsp;>>&nbsp;Bốn điểm “ngược đời” của TTCK Việt Nam </b></a><br><a href="http://dantri.com.vn/c76/s235-657848/EIB-tiep-tuc-bung-no-thoa-thuan-hon-19-trieu-co-phieu-phien-dau-thang.htm"><b>&nbsp;>>&nbsp;EIB tiếp tục bùng nổ thỏa thuận hơn 19 triệu cổ phiếu phiên đầu tháng</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV align=center>\r\n<DIV><IMG style="MARGIN: 5px" alt="Nhà đầu tư dường như đang không giữ được bình tĩnh." align=middle src="http://dantri4.vcmedia.vn/hOUaiPIzyO22coD06QNl/Image/2012/11/giam-san-179d4.jpg" width=450 _fl=""><BR></DIV><SPAN style="FONT-FAMILY: Arial; FONT-SIZE: 10pt">Nhà đầu tư dường như đang không giữ được bình tĩnh.</SPAN><BR></DIV><BR>Thị trường phiên sáng nay (2/11/2012) tiếp tục chứng kiến một đợt lao dốc bất ngờ của thị trường. <BR><BR>Ngay từ đầu phiên, trên cả hai sàn, cổ phiếu đều giảm sàn đồng loạt, rất khó để nhìn thấy các mã xanh điểm trên bảng giá, thay vào đó là màu đỏ và xanh xám bao trùm.<BR><BR>Chỉ số trên cả hai sàn đều mất điểm mạnh. VN-Index lùi về 377,77 điểm, mất 10,18 điểm tương ứng 2,62%. HNX-Index mất 1,77 điểm tương ứng 3,36% xuống còn 50,89 điểm. VN30-Index thậm chí mất tới 12,34 điểm còn 445,72 điểm.<BR><BR>Chốt phiên, toàn sàn ghi nhận 169 mã giảm sàn, trong số 361 mã mất điểm, trong khi tổng cộng chỉ có 52 mã tăng và 20 mã tăng trần. Rổ VN30 thậm chí chỉ có một mã tăng, HNX30 không có mã nào tăng điểm. Số mã giảm trong mỗi rổ là 29 mã.<BR><BR>Trong phiên tăng điểm hiếm hoi vào hôm qua, sáng nay, STB lại nối mạch giảm, mất tới 700 đồng mỗi cổ phiếu, đánh mất mốc 19.000 và chỉ còn 18.600 đồng/cp. Khớp lệnh đối với STB rất mạnh, đạt 1,76 triệu đơn vị - hơn gấp 3 lần so với tổng khối lượng được khớp trong cả ngày trước đó.<BR><BR>SBT phiên này giảm sàn còn 16.900 đồng/cp, chịu áp lực bán rất mạnh và không hề có lệnh đặt mua. Cuối phiên, SBT còn tới 1,17 triệu cổ phiếu dư bán giá sàn.<BR><BR>Từ hôm qua, Hội đồng quản trị của Bourbon Tây Ninh đã chấp thuận việc từ nhiệm thành viên HĐQT của bà Huỳnh Bích Ngọc. Tại SBT, con gái bà Ngọc là bà Đặng Huỳnh Ức My là Chủ tịch HĐQT công ty.<BR><BR>Rút khỏi HĐQT của SBT, bà Ngọc vẫn đang còn giữ chức vụ Chủ tịch HĐQT của CTCP Sản xuất - Thương mại Thành Thành Công; là Phó Chủ tịch HĐQT của CTCP Địa ốc Sài Gòn Thương Tín (SCR) - nơi con trai bà là ông Đặng Hồng Anh là Chủ tịch HĐQT.<BR><BR>Cũng trong phiên này, SCR bị bán tháo rất mạnh. Cổ phiếu này giảm sàn còn 5.100 đồng/cp, cuối phiên, dư bán sàn còn tới gần 7 triệu đơn vị, không hề có lệnh đặt mua.<BR><BR>Rổ VN30, ngoài KDC tăng 300 đồng thì tất cả các mã còn lại đều mất điểm. <BR><BR>Giao dịch mạnh tiếp tục diễn ra ở ITA. Mã này sáng này mất 100 đồng mỗi cổ phiếu, giao dịch khớp lệnh gần 3 triệu đơn vị. Trong khi KBC giảm sàn, còn 5.400 đồng, cuối phiên trên bảng giá trắng bên mua.<BR><BR>Phiên này, HNX bị kích động do khối lượng giao dịch tại KLS, SHB, VND, PVX... đều đạt cao. KLS khớp lệnh 3,4 triệu đơn vị, SHB khớp gần 4,9 triệu đơn vị và VND cũng khớp trên 3,4 triệu đơn vị. <BR><BR>Giảm sàn còn 4.000 đồng, PVX hôm nay được giao dịch tới 5,4 triệu đơn vị, cuối phiên dư mua giá sàn còn 1,17 triệu cổ phiếu cho thấy cầu bắt đáy ở mã này khá lớn. Tương tự ở SHB còn dư mua hơn 5 triệu đơn vị.<BR><BR>Thị trường biến động đã đẩy khối lượng giao dịch trên cả hai sàn lên cao. Tại sàn TPHCM có 39,3 triệu cổ phiếu giao dịch, ứng với 417,3 tỷ đồng. Tại sàn Hà Nội có 31,8 triệu cổ phiếu giao dịch tương ứng 191,5 tỷ đồng.<BR><BR>\r\n<DIV align=right><B>Mai Chi</B><BR></DIV><input type="hidden" value="/c76s76/kinhdoanh/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(22, ' Nợ công của Việt Nam tiếp tục gia tăng vì tăng trưởng thấp ', '-No-cong-cua-Viet-Nam-tiep-tuc-gia-tang-vi-tang-truong-thap-', ' (Dân trí) - Báo cáo của Chính phủ về tình hình nợ công thể hiện, tổng số dư nợ công ở Việt Nam đang ở mức 55,4% GDP. Tăng trưởng không đạt kế hoạch, nợ công, nợ Chính phủ trong năm 2012 và các năm tiếp theo được dự báo tiếp tục gia tăng.<br><a href="http://dantri.com.vn/c76/s76-637296/o-cong-Viet-am-tu-goc-nhin-cua-bao-Economist.htm"><b>&nbsp;>>&nbsp;Nợ công Việt Nam từ góc nhìn của báo Economist</b></a><br><a href="http://dantri.com.vn/c76/s76-634508/Can-lap-ban-quan-ly-no-cong-cua-quoc-hoi.htm"><b>&nbsp;>>&nbsp;Cần lập ban quản lý nợ công của quốc hội</b></a><br><a href="http://dantri.com.vn/c76/s76-633676/Quy-mo-thi-truong-no-cong-Viet-am-bang-14-GDP.htm"><b>&nbsp;>>&nbsp;Quy mô thị trường nợ công Việt Nam bằng 14% GDP</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P><B>Chỉ tiêu trả nợ 10 năm qua vẫn an toàn </B></P>\r\n<P>Theo thống kê, danh sách các chủ nợ nước ngoài của Việt Nam, Nhật Bản vẫn đứng đầu (17%), tiếp sau là Ngân hàng thế giới (13%), Ngân hàng Phát triển châu Á (8%), còn lại là các chủ nợ khác.</P>\r\n<DIV>Cũng theo số liệu tổng hợp đến cuối năm ngoái, tổng số vốn vay ODA và vay ưu đãi nước ngoài khoảng 71,7 tỷ USD.</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Chính phủ khẳng định tỷ lệ trả nợ 10 năm qua vẫn được khống chế ở mức dưới 20% GDP." align=center src="http://dantri4.vcmedia.vn/VLEfzoZuPAIveDWb3xCC/Image/2012/11/no-cong-1-9b0e1.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Chính phủ khẳng định tỷ lệ trả nợ 10 năm qua vẫn được khống chế ở mức dưới 20% GDP.</SPAN></DIV>\r\n<P>Về kết quả huy động, sử dụng vốn vay và trả nợ của Chính phủ, báo cáo cho biết, tổng số vốn vay ODA và vay ưu đãi nước ngoài tính đến cuối năm 2011 đạt 71,7 tỷ USD, trong đó số vốn đã được ký kết (thông qua các hiệp định vay/thoả thuận viện trợ không hoàn lại) là 54,1 tỷ USD. Vốn ODA đã được giải ngân cho các chương trình, dự án là 33,41 tỷ USD.</P>\r\n<P>Phát hành trái phiếu Chính phủ trong nước năm 2010 được 68.292 tỷ đồng, năm 2011 đạt 80.447 tỷ đồng và và dự kiến năm 2012 là 120.000 tỷ đồng.</P>\r\n<P>Thời điểm kết thúc năm 2011, tổng số vốn cam kết vay thương mại nước ngoài là 4,08 tỷ USD, tổng số vốn đã rút là 3,06 tỷ USD, dư nợ là 2,9 tỷ USD.</P>\r\n<P>68% dư nợ vốn vay nước ngoài của Chính phủ được đưa vào cân đối ngân sách, 32% cho vay lại các dự án đầu tư có khả năng thu hồi vốn, báo cáo phân tích.</P>\r\n<P>Đối với phần vốn vay nước ngoài của Chính phủ, một phần được đưa vào cân đối ngân sách (chiếm 68% dư nợ vay nước ngoài của Chính phủ), một phần cho vay lại các dự án đầu tư có khả năng thu hồi vốn (chiếm 32%).</P>\r\n<P>Tổng trị giá vay nước ngoài của Chính phủ đã giải ngân để cho vay lại tương đương 12,6 tỉ USD. Dư nợ các dự án cho vay lại của Chính phủ tương đương 10,3 tỉ USD, bằng 32% dư nợ nước ngoài của Chính phủ, và bằng 8,5% GDP. </P>\r\n<P>Các khoản vay được tập trung vào một số ngành là điện, dầu khí, công nghiệp tàu thủy, cấp nước, nông nghiệp, đường cao tốc, hàng không, cảng biển, công nghiệp, xi măng, phát triển hạ tầng đô thị…</P>\r\n<P>Báo cáo nêu nhận định: “Nhìn chung, các dự án vay lại vốn vay nước ngoài của Chính phủ hoạt động có hiệu quả thể hiện qua việc trả nợ đầy đủ, đúng hạn”. </P>\r\n<P>Trong 580 dự án cho vay lại, số dự án có nợ quá hạn đến cuối năm 2011 là 55 dự án với số dư nợ gốc quá hạn chiếm 0,7% tổng dư nợ cho vay lại. </P>\r\n<P>Nguyên nhân chủ yếu do các biến động của thị trường tiêu thụ sản phẩm, đặc biệt là đối với các dự án nông nghiệp, nông sản, tỷ giá ngoại tệ/VNĐ biến động mạnh theo hướng làm tăng dư nợ của các dự án vay lại bằng ngoại tệ... </P>\r\n<P>Tuy nhiên, Chính phủ khẳng định, chỉ tiêu về trả nợ của Chính phủ so với thu ngân sách nhà nước trong 10 năm qua luôn nằm trong giới hạn an toàn (dưới 20% tổng thu ngân sách nhà nước).</P>\r\n<P>Cơ quan lập báo cáo phân tích, vì nhu cầu vốn đầu tư cho các chương trình, dự án xây dựng kết cấu hạ tầng kinh tế - xã hội ngày càng tăng, bên cạnh đó tăng trưởng kinh tế năm 2012 dự kiến chỉ đạt khoảng 5,2 - 5,7% (kế hoạch là 6,0 - 6,5%) nên xu hướng gia tăng về nợ công, nợ Chính phủ trong năm 2012 và các năm tiếp theo vẫn tiếp tục. </P>\r\n<P>Chi phí vay nợ có xu hướng gia tăng, nhất là việc chuyển đổi các điều kiện vay áp dụng cho Việt Nam khi trở thành nước thu nhập trung bình. Trong cơ cấu danh mục nợ công hiện tại vẫn còn có một số rủi ro.</P>\r\n<P><B>Nợ công đến năm 2015 không quá 65% GDP</B></P>\r\n<P>Chính phủ cho biết, tính đến 31/12/2011, tổng số dư nợ công bằng 55,4% GDP, giảm 1,9% so với năm 2010. Trong đó, các khoản nợ trực tiếp của Chính phủ chiếm 78% và bằng 43,1% GDP. Nợ được Chính phủ bảo lãnh chiếm khoảng 21% và bằng 11,7% GDP. Nợ chính quyền địa phương chiếm khoảng 1,0% và bằng 0,5% GDP.</P>\r\n<P>Cùng với xu hướng gia tăng về nợ công, nợ Chính phủ, chi phí vay nợ cũng có xu hướng gia tăng. </P>\r\n<P>Theo định hướng của Chính phủ, nợ công (bao gồm nợ Chính phủ, nợ được Chính phủ bảo lãnh và nợ chính quyền địa phương) đến năm 2015 không quá 65% GDP, trong đó dư nợ chính phủ không quá 50% GDP và nợ nước ngoài của quốc gia không quá 50% GDP. </P>\r\n<P>Nghĩa vụ trả nợ trực tiếp của Chính phủ (không kể cho vay lại) so với tổng thu ngân sách nhà nước hàng năm không quá 25% và nghĩa vụ trả nợ nước ngoài của quốc gia hàng năm dưới 25% giá trị xuất khẩu hàng hoá và dịch vụ.</P>\r\n<P>Bên cạnh đó, đảm bảo chỉ tiêu tỷ lệ dự trữ ngoại hối nhà nước so với tổng dư nợ nước ngoài ngắn hạn hàng năm trên 200%.</P>\r\n<P>Chính phủ cũng cho biết sẽ chỉ xem xét cấp bảo lãnh vay trong nước đối với các dự án cấp bách, công trình trọng điểm quốc gia đã được Thủ tướng Chính phủ phê duyệt chủ trương cấp bảo lãnh vay trong nước.</P>\r\n<P>Trước mắt chưa xem xét bảo lãnh phát hành trái phiếu quốc tế. Các doanh nghiệp hoặc ngân hàng thương mại nếu có nhu cầu thì chủ động phát hành trái phiếu quốc tế không có bảo lãnh chính phủ.</P>\r\n<P>Sẽ thực hiện đầy đủ nghĩa vụ trả nợ, không để xảy ra tình trạng nợ quá hạn, làm ảnh hưởng đến các cam kết quốc tế, Chính phủ khẳng định.</P>\r\n<P align=right><B>P.Thảo </B></P><input type="hidden" value="/c76s76/kinhdoanh/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(23, ' Xem cách Thổ Nhĩ Kỳ huy động vàng trong dân ', '-Xem-cach-Tho-Nhi-Kỳ-huy-dong-vang-trong-dan-', ' (Dân trí) - Người dân Thổ Nhĩ Kỳ đang nắm giữ số vàng lên tới 5.000 tấn, trị giá khoảng 302 tỷ USD. Các ngân hàng ở nước này đang tìm cách hút số vàng trên để chuyển đổi thành tiền mặt.<br><a href="http://dantri.com.vn/c76/s76-592382/Duoc-huy-dong-vang-them-7-thang.htm"><b>&nbsp;>>&nbsp;Được huy động vàng thêm 7 tháng</b></a><br><a href="http://dantri.com.vn/c76/s76-585292/Tren-the-gioi-co-le-chi-co-cac-ngan-hang-Viet-am-huy-dong-vang.htm"><b>&nbsp;>>&nbsp;“Trên thế giới có lẽ chỉ có các ngân hàng Việt Nam huy động vàng”</b></a><br><a href="http://dantri.com.vn/c76/s76-432087/Huy-dong-vang-trong-dan-Khong-vi-ngua-rui-ro-ma-lang-phi.htm"><b>&nbsp;>>&nbsp;Huy động vàng trong dân: Không vì ngừa rủi ro mà lãng phí</b></a><br><a href="http://dantri.com.vn/c76/s76-390027/Dung-huy-dong-vang-17-ti-USD-chet-di.htm"><b>&nbsp;>>&nbsp;Dừng huy động vàng, 17 tỉ USD chết dí</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV align=center><IMG style="MARGIN: 5px" alt="Huy động vàng trong dân: Kinh nghiệm từ Thổ Nhĩ Kỳ" align=middle src="http://dantri4.vcmedia.vn/DVDhXuovZ8m77cpT6Cx/Image/2012/11/00-90877.jpg" width=450 _fl=""></DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV>Cô Deniz Kalkan, một phụ nữ nội trợ ở thủ đô Istanbul (Thổ Nhĩ Kỳ) vừa quyết định đem nửa tá tiền xu bằng vàng tích cóp lâu nay đi gửi ở ngân hàng. “Gửi ở ngân hàng an toàn hơn là để ở nhà”, cô Kalkan cho biết.<BR><BR>Không chỉ riêng Kalkan, người dân Thổ Nhĩ Kỳ đang xếp hàng đi gửi vàng ở ngân hàng, hưởng ứng nỗ lực thu hút vốn vàng trong dân mà các nhà băng nước này đang dốc sức thực hiện. Thu hút vàng trong dân được xem là một cách để Thổ Nhĩ Kỳ giải quyết mức thâm hụt cán cân vãng lai lớn thứ nhì thế giới, chỉ sau Mỹ này. Trong năm 2011, thâm hụt cán cân vãng lai của nước này&nbsp;đã vượt mức 77 tỷ USD.<BR><BR>Theo số liệu của Ngân hàng Trung ương Thổ Nhĩ Kỳ, từ&nbsp;đầu năm&nbsp;đến tháng 7 vừa qua,&nbsp;số tài khoản gửi vàng tại các ngân hàng ở Thổ Nhĩ Kỳ đã tăng 15%, cao gấp 5 lần tốc độ tăng của số tài khoản gửi tiết kiệm. </DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV>Cũng từ đầu năm, lượng vàng người dân gửi vào ngân hàng Yapi Kredi Bankasi của Thổ Nhĩ kỳ đã tăng 62%. Còn tại ngân hàng lớn nhất nước này về tài sản là Turkiye Is Bankasi AS, lượng vàng người dân gửi vào tăng 10 lần trong thời gian 2 năm tính đến hết tháng 6 năm nay.<BR><BR>Khi mở tài khoản vàng, khách hàng sẽ có được một lượng tiền mặt tương đương với giá trị của số vàng mà họ giao cho ngân hàng. Khách có thể rút tiền mặt hoặc vay vốn từ ngân hàng. Về phần mình, ngân hàng có thể nắm giữ hoặc bán số vàng tùy theo chiến lược kinh doanh riêng.<BR><BR>Các ngân hàng Thổ Nhĩ Kỳ đang tung ra hàng loạt dịch vụ khác nhau để thu hút người dân gửi vàng. Các ngân hàng Isbank và Turkiye Garanti Bankasi AS - ngân hàng lớn nhất nước này về giá trị vốn hóa - cùng cung cấp các khoản vay bảo đảm bằng vàng. Với dịch vụ này, khách hàng có thể mang nữ trang hoặc tiền xu vàng tới ngân hàng để thế chấp vay vốn. Garanti cũng phát hành thẻ tín dụng dựa trên tài khoản gửi vàng. Ngân hàng này sẽ sớm cho phép khách hàng rút tiết kiệm bằng vàng, thay vì tiền mặt hoặc ngoại tệ.<BR><BR>Ngân hàng Hồi giáo Asya Katilim Bankasi AS thì tổ chức những “ngày vàng” để khách hàng đem tiền xu và nữ trang vàng đến cho các chuyên gia kiểm định. Ngân hàng này cho biết đã thu hút được 3 tấn vàng thông qua những “ngày vàng” như vậy từ tháng 3/2012 đến nay. Số tài khoản tiết kiệm vàng trong ngân hàng này tính đến hết tháng 7 đã tăng 22%.<BR><BR>Người dân Thổ Nhĩ Kỳ có truyền thống tặng tiền xu vàng hoặc nữ trang vàng trong những dịp như đám cưới, sinh nở hay lễ tết. Truyền thống này càng được đẩy mạnh từ một thập kỷ trước khi tỷ lệ lạm phát ở nước này vượt ngưỡng 70%. Giá vàng tăng trong những năm gần đây càng thúc đẩy người dân Thổ Nhĩ Kỳ giữ vàng. Từ đầu năm đến nay, giá vàng giao ngay trên thị trường quốc tế đã tăng gần 9%, tiến tới năm tăng thứ 12 liên tục.<BR><BR>Theo số liệu của Hội đồng Vàng Thế giới (WGC), người dân Thổ Nhĩ Kỳ hiện đang nắm giữ khoảng 5.000 tấn vàng, giá trị 302 tỷ USD, lớn hơn của GDP của Ireland. Huy động được số vàng này trong dân, Thổ Nhĩ Kỳ có thể giảm được nhập khẩu vàng cũng như vay nợ từ bên ngoài.<BR><BR>Trong 8 tháng đầu năm nay, mức thâm hụt vãng lai của Thổ Nhĩ Kỳ là 59 tỷ USD, giảm 23% so với cùng kỳ năm trước nhờ xuất khẩu vàng tăng mạnh sang Các tiểu vương quốc Arab thống nhất (UAE) và Iran. Trong 8 tháng, lượng vàng mà Thổ Nhĩ Kỳ xuất sang hai nước này đạt kim ngạch 9,2 tỷ USD, so với mức chỉ 645 triệu USD cùng kỳ năm ngoái.<BR><BR>Các mỏ vàng của Thổ Nhĩ Kỳ không sản xuất đủ vàng cho xuất khẩu. Trong 8 tháng đầu năm nay, các mỏ vàng của nước này chỉ khai thác được 25 tấn vàng. Trong khi đó, tính đến cuối tháng 9, Thổ Nhĩ Kỳ đã nhập khẩu tới 109 tấn vàng.<BR><BR>Thu hút vốn vàng cũng sẽ làm gia tăng tỷ lệ tiết kiệm của các hộ gia đình ở Thổ Nhĩ Kỳ vốn đang ở mức thấp nhất trong số các thị trường đang nổi. Theo Ngân hàng Thế giới, tỷ lệ tiết kiệm của các hộ gia đình ở Thổ Nhĩ Kỳ hiện chỉ là 12,7% so với mức 53% của Trung Quốc. Tỷ lệ tiết kiệm tăng sẽ giúp làm giảm áp lực tìm nguồn vốn của các ngân hàng, đồng thời làm gia tăng khả năng cấp vốn vay của các ngân hàng.<BR><BR>“Chúng tôi ủng hộ chủ trương tăng tỷ lệ tiết kiệm trong dân mà Chính phủ đưa ra. Tài khoản vàng sẽ giữ một vị trí quan trọng trong việc này, khi mà tài sản chìm của người dân được đưa vào hệ thống tài chính”, ông Galip Tozge, Phó chủ tịch điều hành mảng ngân hàng tiêu dùng của Akbank ở Istanbul, phát biểu.<BR><BR>Chính phủ Thổ Nhĩ Kỳ đang có những biện pháp để hỗ trợ các ngân hàng thu hút vốn vàng. Hôm 16/8 vừa rồi, Ngân hàng Trung ương nước này ra quyết định cho phép các ngân hàng tăng tỷ lệ vàng trong dự trữ lên mức 30% từ mức 25% trước đó. <BR><BR>Ngoài ra, các nhà chức trách Thổ Nhĩ Kỳ còn đang xem xét đưa ra các quy định cho phép người dân mua hoặc bán vàng tại các chi nhánh ngân hàng hoặc chuyển vàng vào các tài khoản khác. Tuy nhiên, kế hoạch này bị các tiệm vàng ở Thổ Nhĩ Kỳ phản đối vì cho rằng, việc ngân hàng mua bán vàng sẽ khiến doanh thu của các tiệm kim hoàn sa sút.<BR><B></B></DIV>\r\n<DIV align=right><B>Phương Anh</B><BR>Theo <I>Bloomberg</I></DIV><input type="hidden" value="/c76s76/kinhdoanh/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(24, ' Tinh vi mật ong "xịn" từ.... vỏ cây, đường và phèn chua ', '-Tinh-vi-mat-ong-"xin"-tu....-vo-cay,-duong-va-phen-chua-', ' Chỉ cần vỏ cây núc nác, một ít phèn nấu với đường trong chừng 20 phút là có ngay chai mật ong y như thật. Loại mật ong giả này đang được rao bán công khai, không có bất cứ sự kiểm soát nào. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P><STRONG>“Công nghệ” làm mật ong giả</STRONG></P>\r\n<P>“Mật ong giá nào cũng có, từ 100 - 300 nghìn đồng/chai, nhưng cô biết rồi đó, tiền nào của ấy. Rẻ tiền thì chỉ có đồ giả thôi”. Từ chỉ dẫn của bà Nguyễn Thị Ngạt - chủ một cửa hiệu kinh doanh mật ong lâu năm tại TP.Thanh Hoá, chúng tôi về xã Xuân Tín (huyện Thọ Xuân, Thanh Hoá) được xem là “thủ phủ” của hàng loạt hàng giả như: Cao hổ, nhung hươu, mì chính… và mật ong giả.</P>\r\n<TABLE border=0 cellSpacing=1 cellPadding=1 width=200 align=center>\r\n<TBODY>\r\n<TR>\r\n<TD>\r\n<DIV><IMG style="MARGIN: 5px; WIDTH: 409px; HEIGHT: 290px" align=center src="http://dantri4.vcmedia.vn/Ic3EyFHpPWFvMJOJFocc/Image/2012/11/263_15_mat-ong-b1e65.jpg" width=450 height=337 _fl="" alt="Ông H cho một ít sáp ong vào chai cho giống mật ong thật."></DIV></TD></TR>\r\n<TR>\r\n<TD>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ông H cho một ít sáp ong vào chai cho giống mật ong thật.</SPAN></DIV></TD></TR></TBODY></TABLE>\r\n<P>Trong vai người đi buôn mật ong, tôi tìm đến nhà ông Nguyễn Văn V (thôn 22, xã Xuân Tín). Ông V có thâm niên buôn bán, làm mật ong giả hơn 30 năm. Phải mất nhiều giờ hỏi thăm, tâm sự, ông mới hé lộ bí quyết làm mật ong giả cho chúng tôi. Theo ông V, 4 nguyên liệu chính để làm mật ong giả là đường, vỏ cây núc nác, nước lọc và phèn (hoặc chanh tươi).</P>\r\n<P>Sau một hồi thuyết phục, ông V gọi em họ là Nguyễn Văn H tới “biểu diễn” cho chúng tôi xem. Rất nhanh chóng, ông H đổ nước vào chiếc nồi, rồi hất luôn thìa phèn cùng vài hạt muối vào nồi nước đang đun nóng. Chừng 10 phút sau, ông đổ 2kg đường vào nồi đun sôi và khuấy đều. “Muốn mật đẹp, khi cho đường vào nồi phải lấy đũa quấy đều tay, không để đường bén dưới xoong”- ông H tiết lộ bí quyết gia truyền.</P>\r\n<P>Cũng theo ông H, khi nấu, thay vì dùng phèn, người nấu cũng có thể cho chanh tươi vào để không bị đóng đường lại. Tuy nhiên, dùng chanh tuy ít độc hơn, nhưng “mật” lại hay bị loãng và không để được lâu như phèn. Tuỳ vào chất lượng, màu sắc của nước đường mà có thể đun vỏ của cây núc nác để tạo màu vàng đậm cho chai mật ong. Chưa đầy 20 phút, công đoạn biến đường thành mật ong của ông H đã hoàn thành.</P>\r\n<P><STRONG>Giá nào cũng có</STRONG></P>\r\n<P>Để ngụy trang cho hỗn hợp nước đường trở thành mật ong thứ thiệt, sau khi để nguội, rót vào chai, ông H còn cà nhỏ một ít sáp ong cho lên cổ chai và đổ thêm vài giọt mật thật lên thành chai để có mùi thơm đặc trưng của mật ong, rồi nút lại bằng lá chuối.</P>\r\n<DIV>Phèn chua là một hoá chất cực độc, không được sử dụng trong việc chế biến đồ ăn. Chính vì vậy, việc sử dụng sản phẩm mật ong giả có thể gây nguy hiểm tới sức khoẻ của người tiêu dùng.</DIV>\r\n<P>Sau khi hoàn tất các thủ tục biến hoá, chúng tôi đặt 2 chai mật ong giả cạnh những trai mật ong thật thì thấy chúng giống nhau đến 90%. Nếu chỉ nhìn mắt thường, thậm chí ngửi thì khó mà phân biệt được đâu là mật ong thật, đâu là mật ong giả.</P>\r\n<P>Theo một số người dân xã Xuân Tín, xã này vẫn còn chừng 20-30 hộ làm nghề buôn bán mật ong giả. Thông thường, mỗi ngày họ nấu chừng 5-10 chai, bán hết mới nấu mẻ khác.</P>\r\n<P>Khảo sát thị trường tại Hà Nội như chợ Long Biên, phố Hải Thượng Lãn Ông… chúng tôi thấy giá bán mật ong có độ dãn cách khá xa. Tại chợ đầu mối Long Biên, một chai mật ong dung lượng khoảng 650ml có giá 150-250 nghìn đồng.</P>\r\n<P>Theo chị Nguyễn Thị Giang - chủ cửa hàng mật ong ở đây: “Mật xịn có giá từ 200-250 nghìn đồng/chai, nhưng khách muốn mua loại chỉ hơn 100 nghìn/chai cũng có, nhưng loại rẻ tiền thì không thể thật 100%”.</P>\r\n<DIV align=right>Theo <STRONG>Minh Nguyệt</STRONG></DIV>\r\n<DIV align=right><EM>Dân Việt</EM></DIV><input type="hidden" value="/c76s76/kinhdoanh/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(25, ' Nỗi đau lặng thầm của những bà mẹ người nổi tiếng ', '-Noi-dau-lạng-thàm-cua-nhũng-ba-me-nguoi-noi-tieng-', ' <a href = "http://dantri.com.vn/c730/s730-657982/noi-dau-lang-tham-cua-nhung-ba-me-nguoi-noi-tieng.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/WF7a6LT8JiJdVF1l6FZ/Image/2012/11/k-d2d96.jpg" /></a>(Dân trí)– Nhìn người phụ nữ ở tuổi xế chiều nhỏ bé đứng trên sân khấu đêm nhạc Bằng Kiều, mắt rưng rưng nói lời tạ ơn khán giả đã mở rộng vòng tay đón đứa con trai từng gặp nhiều sóng gió trở về..., đã khiến không ít trái tim xúc động và đồng cảm. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P>Trở lại nơi chôn rau cắt rốn biểu diễn sau chặng đường đời 10 năm đầy biến động, lời biết ơn từ đáy lòng, ca sĩ Bằng Kiều hướng về người mẹ ngồi ở hàng ghế khán giả. </P>\r\n<DIV>Bằng Kiều chia sẻ, trở về nước biểu diễn lần này anh rất hồi hộp và lo lắng, dành hết thời gian cho việc tập luyện với ban nhạc. Tuy nhiên, người lo lắng, stress hơn cả lại là mẹ của anh, nghệ sĩ chèo Lưu Nga. “Mẹ lo chương trình lớn như thế này, lại sốt vé thế này nếu Bằng Kiều hát không ra gì thì…muối mặt”, Bằng Kiều hóm hỉnh nói trong đêm nhạc ngày 28/10.</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG alt="Mẹ Bằng Kiều tạ ơn khán giả trên sân khấu Hà Nội" src="http://dantri4.vcmedia.vn/WF7a6LT8JiJdVF1l6FZ/Image/2012/11/k1-d2d96.jpg"></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Mẹ Bằng Kiều tạ ơn khán giả trên sân khấu Hà Nội<BR><BR></SPAN><IMG alt="Mẹ Bằng Kiều tạ ơn khán giả trên sân khấu Hà Nội" src="http://dantri4.vcmedia.vn/WF7a6LT8JiJdVF1l6FZ/Image/2012/11/k2-d2d96.jpg"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Niềm hạnh phúc của mẹ Bằng Kiều khi hội ngộ cùng con cháu trong show diễn được khán giả đón nhận sau 10 năm...<BR></SPAN></DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV>Cũng trên sân khấu Trung tâm Hội nghị Quốc gia Mỹ Đình, người phụ nữ ở tuổi xế chiều nghẹn ngào nói lời tạ ơn khán giả đã mở rộng vòng tay đón đứa con trai bé bỏng của bà trở về, đã dành cơ hội và nhiều tình cảm cho giọng hát Bằng Kiều. Không ít lần, Lưu Nga nói nhầm, câu nói bị ngắt quãng vì quá xúc động…</DIV>\r\n<P>Sinh con ra, bất cứ ai cũng hi vọng con khôn lớn, trưởng thành và thành công trong sự nghiệp. Nhìn thấy con ngày càng được yêu mến và nổi tiếng, bậc làm cha mẹ hạnh phúc hơn ai hết và ngược lại, trước sự vấp ngã của con- họ cũng là người phải chịu nhiều khổ đau, tổn thất về tinh thần. Là mẹ của người nổi tiếng, niềm hạnh phúc hay đau khổ của họ lại càng thấm thía hơn gấp nhiều lần…</P>\r\n<P>Hẳn nghệ sĩ chèo Lưu Nga đã khóc hết nước mắt trước dồn dập những luồng dư luận khắc nghiệt về Bằng Kiều khi bước đầu sinh sống và phát triển sự nghiệp tại hải ngoại. Và khi Bằng Kiều được cấp phép biểu diễn tại Việt Nam, bà mẹ ấy một lần nữa lại… lo lắng. Bà lo lắng cho đêm nhạc ngày trở về của con có thành công và được đón nhận như mong đợi? </P>\r\n<P>Từ phía trong hậu trường hay ngồi ở hàng ghế khán giả, gương mặt bà lúc nào cũng căng thẳng, hồi hộp và trực trào nước mắt trước lời động viên, chia sẻ của người thân, bạn bè của các con…</P>\r\n<P>Từ hình ảnh mẹ của Bằng Kiều, không ít người lại liên tưởng đến những đấng sinh thành của các nghệ sĩ như Thu Phương, Huy MC…cũng trải qua nhiều biến động thăng trầm trước sóng gió hôn nhân và đường đời lầm lạc của các con.</P>\r\n<P>Chia sẻ với báo chí vào năm 2005, thời điểm Huy MC và Thu Phương chia tay, mỗi người mỗi ngả kiếm sống ở Mỹ, mẹ chồng của ca sĩ Thu Phương đã không giấu nổi sự mệt mỏi và buồn bã khi nói về sự tan vỡ của các con. Dù giận Thu Phương đã hành động nông nổi để lại nhiều điều tiếng, áp lực cho người thân nhưng trong sâu thẳm mẹ chồng của nữ ca sĩ <I>Dòng sông lơ đãng</I> vẫn thương và mong con dâu trở về mái nhà xưa… </P>\r\n<DIV>Không chỉ chịu nhiều dằn vặt, tổn thương trước vấp ngã của con trẻ như trường hợp mẹ của ca sĩ Bằng Kiều, Thu Phương, Huy MC; không ít bà mẹ của những nghệ sĩ nổi tiếng còn là chỗ dựa tinh thần vững chắc, che chở, cùng con vượt qua những vấp ngã trong cuộc sống.</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG alt="Đan Lê và mẹ trong đám cưới lần đầu" src="http://dantri4.vcmedia.vn/WF7a6LT8JiJdVF1l6FZ/Image/2012/11/k3-d2d96.jpg"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Đan Lê và mẹ trong đám cưới lần đầu<BR></SPAN></DIV>\r\n<P>Những phóng viên đã từng tác nghiệp trước sự việc nữ MC kiêm diễn viên, Đan Lê bị nghi lộ clip nhạy cảm năm 2008 hẳn đã chứng kiến sự mệt mỏi của gia đình của người nổi tiếng này. Dù cứng rắn lên tiếng phủ nhận và tìm cách chứng minh sự trong sạch nhưng nữ MC, diễn viên xinh đẹp này cũng không giấu được sự mệt mỏi, bức xúc trước thanh danh bị bôi nhọ, ảnh hưởng. Mẹ và người thân của Đan Lê đã luôn ở bên cạnh tin tưởng và cùng giúp cô vượt qua giai đoạn khó khăn nhất.</P>\r\n<P>Khi sự thật được sáng tỏ, Đan Lê không phải nhân vật trong clip “nóng”, công chúng một lần nữa lại đổ dồn sự chú ý vào đám cưới đầu tiên trong đời của cô. Trước sự “chăm sóc” quá kỹ, thậm chí soi mói của dư luận, mẹ của Đan Lê đã xót xa xin giới truyền thông cho con gái bà chút bình yên. “Các cháu không hiểu hết những gì Đan Lê và gia đình bác đã trải qua thời gian vừa rồi đâu, mong các cháu hãy để ngày vui của con gái bác được trọn vẹn”, lời chia sẻ của mẹ Đan Lê khiến không ít người phải suy nghĩ.</P>\r\n<P>Gây rúng động dư luận gấp nhiều lần phải kể đến trước đó khoảng một năm, việc nữ diễn viên <I>Nhật ký Vàng Anh</I> - Hoàng Thùy Linh lộ clip sex thật sự như “quả bom ném vào làng giải trí Việt”. Lần đầu tiên ở Việt Nam, nữ nghệ sĩ tuổi đời mới 19 đã để lộ clip ân ái với bạn trai cũ khiến dư luận xôn xao.</P>\r\n<DIV>Tại thời điểm đó, dư luận đổ dồn những ánh mắt thiếu thiện cảm vào Hoàng Thùy Linh cũng như bố mẹ của cô. Công chúng thì tò mò, giới truyền thông thì bám sát nhất cử nhất động từng diễn biến của sự kiện lộ clip sex, từ việc liên lạc với Hoàng Thùy Linh, bạn bè của cô cũng như những hàng xóm của gia đình cô... Ít ai kịp nhận ra rằng, đằng sau cơn bão scandal của Hoàng Thùy Linh có hai trái tim đập chậm chạp đang tan nát… vì con!</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG alt="Mẹ Hoàng Thùy Linh luôn theo sát con gái trong các chuyến lưu diễn" src="http://dantri4.vcmedia.vn/WF7a6LT8JiJdVF1l6FZ/Image/2012/11/k4-d2d96.jpg"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Mẹ Hoàng Thùy Linh luôn theo sát con gái trong các chuyến lưu diễn<BR></SPAN></DIV>\r\n<P>Có nữ phóng viên đến nhà Hoàng Thùy Linh kể lại rằng, chị không thể quên hình ảnh bố của nữ diễn viên này đang ngồi trước hiên nhà, gương mặt hốc hác, thất thần trước sự “mất mát” quá lớn. Ông tâm sự, vừa trở về nhà sau chuyến công tác và biết tin về con như “sét đánh ngang tai”.</P>\r\n<P>Hoàng Thùy Linh cũng khẳng định có gặp nạn mới hiểu được công ơn bố mẹ. Bố cô chỉ nói: “Con hãy dũng cảm, nhìn vào sự thật và tiếp tục bước đi”. Mẹ cô khóc không biết bao nhiêu lần vì thương, vì giận&nbsp;con. Sợ con không chịu được áp lực dư luận, bà luôn theo dõi nhất cử nhất động của con và luôn đi cùng con mỗi khi ra khỏi nhà. </P>\r\n<P>Và, cho đến thời điểm này, khi Hoàng Thùy Linh đã trở lại làng giải trí, người luôn sát cánh bên cô ở các hoạt động, buổi biểu diễn; người lo lắng phục trang kiêm người quản lý cho cô chính là mẹ. Hoàng Thùy Linh nói, cô đã đứng dậy, vượt qua những chuyện cũ và sống, cố gắng thành công là vì niềm tin,&nbsp;niềm tự hào của bố mẹ.</P>\r\n<DIV align=right><B></B>&nbsp;</DIV>\r\n<DIV align=right><B></B>&nbsp;</DIV>\r\n<DIV align=right><B>Nguyễn Hằng</B></DIV><input type="hidden" value="/c730s730/van-hoa/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
