INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(53, ' Hình ảnh đầu tiên của xe Mercedes E-Class 2014 ', '-Hinh-anh-dau-tien-cua-xe-Mercedes-E-Class-2014-', ' <a href = "http://dantri.com.vn/c111/s111-658154/hinh-anh-dau-tien-cua-xe-mercedes-eclass-2014.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/2014-Mercedes-Benz-E-Class-Sedan-2e7e3.jpg" /></a>(Dân trí) - Xe Mercedes-Benz E-Class sedan 2014 mới đây đã xuất hiện trước một khách sạn ở Madrid, Tây Ban Nha. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center><IMG src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/2014-Mercedes-Benz-E-Class-Sedan-2b[4]-2e7e3.jpg" alt="Hình ảnh đầu tiên của xe Mercedes E-Class 2014"></DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>Chiếc xe được cho là bản E63 AMG, vì có lưới tản nhiệt trước đặc trưng của bản này, bộ la-zăng đa chấu với piston phanh màu đỏ, nhiều chi tiết khí động học, như cánh gió trên nắp cốp sau và dòng chữ “V8 Biturbo” ở tai xe.</DIV>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>Dựa trên bức ảnh này và một số ảnh chụp trộm trên đường xe chạy thử, có vẻ như phiên bản 2014 của dòng E-Class chỉ có một số thay đổi, như đèn pha liền khối sử dụng đèn bi-xenon và LED, thay cho kiểu cụm 4 đèn như trước; lưới tản nhiệt và nắp ca-pô mới; hông sau không còn mang kiểu thuyền phao nên nhiều khả năng cửa xe cũng được thiết kế lại.</DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center><IMG src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/2014-Mercedes-Benz-E-Class-Sedan-1b[4]-2e7e3.jpg" alt="Theo trang"></DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center><IMG src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/2014-Mercedes-Benz-E-Class-Sedan-3b[4]-2e7e3.jpg" alt="Theo trang"></DIV>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>Theo trang <I style="mso-bidi-font-style: normal">WCF</I>, một nguồn tin cho biết, bản E63 AMG sẽ được trang bị tiêu chuẩn hệ dẫn động 4 bánh 4MATIC kết hợp với động cơ công suất 543 mã lực. Xe có khả năng tăng tốc từ 0 lên 100km/h trong 3,4 giây.</P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Nhật Minh<o:p></o:p></B></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class=MsoNormal align=right><I style="mso-bidi-font-style: normal">Ảnh: Carscoop<o:p></o:p></I></P>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P><input type="hidden" value="/c111s111/otoxemay/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(54, ' Thiệt mình, hại người vì kiểu lái xe “cướp đường” ', '-Thiet-minh,-hai-nguoi-vi-kieu-lai-xe-“cuop-duong”-', ' (Dân trí) - Tài xế của chiếc Lexus IS màu trắng vì quá nóng vội muốn vượt các xe khác nên đã luồn lách vào mọi chỗ trống trên đường và hậu quả là bị mất lái, lạng sang trái tông lật một chiếc xe van. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal align=center>\r\n<OBJECT id=PlayerAS9ce300f25e2740769c902b930558a922 classid=clsid:D27CDB6E-AE6D-11cf-96B8-444553540000 width=425 height=334><PARAM NAME="_cx" VALUE="11244"><PARAM NAME="_cy" VALUE="8837"><PARAM NAME="FlashVars" VALUE="11244"><PARAM NAME="Movie" VALUE="http://cache.hosting.vcmedia.vn/?key=9ce300f25e2740769c902b930558a922&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/oEj8Djn5XzGK0De5Uol4rA/2012/11/VIP_Style-669ad.jpg"><PARAM NAME="Src" VALUE="http://cache.hosting.vcmedia.vn/?key=9ce300f25e2740769c902b930558a922&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/oEj8Djn5XzGK0De5Uol4rA/2012/11/VIP_Style-669ad.jpg"><PARAM NAME="WMode" VALUE="Transparent"><PARAM NAME="Play" VALUE="-1"><PARAM NAME="Loop" VALUE="-1"><PARAM NAME="Quality" VALUE="High"><PARAM NAME="SAlign" VALUE=""><PARAM NAME="Menu" VALUE="-1"><PARAM NAME="Base" VALUE=""><PARAM NAME="AllowScriptAccess" VALUE="always"><PARAM NAME="Scale" VALUE="ShowAll"><PARAM NAME="DeviceFont" VALUE="0"><PARAM NAME="EmbedMovie" VALUE="0"><PARAM NAME="BGColor" VALUE="425166"><PARAM NAME="SWRemote" VALUE="">\r\n<embed wmode="transparent" height="334" width="425" quality="high" bgcolor="#425166" flashvars="" wmode="transparent" allowfullscreen="true" allowscriptaccess="always" name="9ce300f25e2740769c902b930558a922" id="9ce300f25e2740769c902b930558a922" style="" src="http://cache.hosting.vcmedia.vn/?key=9ce300f25e2740769c902b930558a922&pname=mediaplayer.swf&img=http://video-thumbs.vcmedia.vn/dantri/oEj8Djn5XzGK0De5Uol4rA/2012/11/VIP_Style-669ad.jpg" type="application/x-shockwave-flash"/></embed></OBJECT></DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0cm 0cm 0pt" class=MsoNormal>Tài xế xe gắn video ghi lại tai nạn này cho biết, ngày tháng hiện trên video không chính xác (ngày đúng là 30/10/2012) và không có ai bị thương nặng. </DIV>\r\n<P style="MARGIN: 0cm 0cm 0pt" class=MsoNormal><!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><?xml:namespace prefix = o /><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Nhật Minh<o:p></o:p></B></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class=MsoNormal align=right><I style="mso-bidi-font-style: normal">Nguồn video: Youtube<o:p></o:p></I></P><input type="hidden" value="/c111s111/otoxemay/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(55, ' Nhập khẩu ôtô không chính hãng: Xe mới thành cũ  ', '-Nhap-khau-oto-khong-chinh-hang:-Xe-moi-thanh-cu--', ' Sau khi xe ô tô không chính hãng được nhập khẩu trở lại, nhiều doanh nghiệp đã ký thêm phụ lục hợp đồng thay đổi model, đời xe khiến cho thủ tục gỡ khó xe nhập khẩu vẫn vướng mắc. ', '<div class="fon34 mt3 mr2 fon43">\r\n <p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes"></span></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Sau hơn&nbsp;một năm siết chặt xe ngoại 9 chỗ ngồi nguyên chiếc chưa qua sử dụng, kể từ ngày 7/9/2012, Bộ Công thương đã “miễn” các điều kiện nhập khẩu xe theo Thông tư 20, như yêu cầu về giấy tờ ủy quyền phân phối chính hãng... để giải quyết hàng loạt hợp đồng tồn đọng của doanh nghiệp.<!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Cụ thể, những doanh nghiệp đã ký hợp đồng nhập xe không chính hãng trước ngày 12/5/2011- thời điểm Thông tư 20 được ban hành, nếu xuất trình được chứng từ đã thanh toán của ngân hàng cũng trước ngày 12/5/2011 thì sẽ được giải quyết thông quan. Lô xe phải đảm bảo đúng với hợp đồng đã ký cả về chủng loại, số lượng.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Tuy nhiên, trong quá trình triển khai, Cục Hải quan Tp Hải Phòng cho biết, nhiều trường hợp doanh nghiệp thuộc đối tượng trên lại ký thêm phụ lục hợp đồng sau ngày 12/5/2011 thay đổi model xe, đời xe. Số lượng xe và trị giá xe theo thỏa thuận phụ lục hợp đồng không thay đổi so với hợp đồng gốc đã ký trước ngày 12/5/2011. Song, Bộ Công thương lại chưa hướng dẫn điểm phát sinh này.<o:p></o:p></p>\r\n<p style="TEXT-ALIGN: center; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><span style="FONT-FAMILY: Verdana; FONT-SIZE: 9pt"><o:p>&nbsp;</o:p></span></p>\r\n<div style="TEXT-ALIGN: center; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><span style="FONT-FAMILY: Verdana; FONT-SIZE: 9pt">\r\n<div><img style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/nk-db367.jpg" _fl="" alt="Được thông quan, xe mới về Việt" align="middle" width="450"></div></span></div>\r\n<div style="TEXT-ALIGN: center; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><span style="FONT-FAMILY: Verdana; FONT-SIZE: 9pt">Được thông quan, xe mới về Việt <!--?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /--><st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region> biến thành xe cũ<o:p></o:p></span></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Đánh giá về động thái "nới lỏng" xe nhập này, công ty cổ phần Xe Hàn, có trụ sở tại Tứ Hiệp, Thanh Trì, TP Hà Nội phân tích, từ khi ký hợp đồng trước 12/5/2011 đến nay, thời gian đã trôi qua rất dài, hơn 20 tháng, thị trường ôtô thế giới đã có rất nhiều thay đổi. Do đó, đối với một số model xe trong hợp đồng đã ký, hiện đối tác nước ngoài không thể cung cấp được vì những model này hiện không còn được sản xuất nữa.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Bên cạnh đó, khi ký kết hợp đồng mua xe thì yếu tố đầu tiên phải xem xét đến là nhu cầu của thị trường. Trong 20 tháng vừa qua, thị trường xe ôtô đã xảy ra rất nhiều biến động, nếu tiếp tục nhập về các model xe trong hợp đồng đã ký thì sẽ không còn phù hợp với thị hiếu của khách hàng tại thời điểm này dẫn đến khả năng lô xe sẽ không tiêu thụ được hoặc chỉ tiêu thụ được rất ít.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Như vậy, khi Bộ Công thương yêu cầu các doanh nghiệp phải đảm bảo lô xe đúng với chủng loại, số lượng theo hợp đồng đã ký thì các doanh nghiệp chỉ được gỡ khó về thủ tục hành chính đơn thuần nhưng về hiệu quả kinh doanh thì không đạt.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Đây là đơn vị tồn đọng nhiều hợp đồng nhập khẩu ký với với đối tác ngày 14/02/2011. Theo công ty này, các DN đều mong muốn Bộ Công thương xem xét cho điều chỉnh số lượng, chủng loại để phù hợp với thị trường ô tô hiện nay.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Cũng liên quan đến việc nới lỏng ô tô nhập khẩu “hậu” Thông tư 20, công ty CP thương mại Kylin GX668, trụ sở tại Tp Hải Phòng cũng bày tỏ, Bộ Công Thương ra điều kiện các lô xe phải về cảng Việt Nam trong 3 tháng, tức trước ngày 7/12 năm nay là không khả thi. Trên thực tế, theo hợp đồng ký với đối tác, việc giao nhận xe về Việt <st1:country-region w:st="on"><st1:place w:st="on">Nam</st1:place></st1:country-region> sẽ theo kế hoạch, lộ trình, phù hợp với năng lực kinh doanh trong nước.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Trước vấn đề này, Tổng cục Hải quan cho rằng, các doanh nghiệp chủ động hỏi Bộ Công Thương để được hướng dẫn.<o:p></o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><span style="mso-spacerun: yes">&nbsp;</span><o:p></o:p></p>\r\n<p style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="right">Theo <b style="mso-bidi-font-weight: normal">Phạm Huyền<o:p></o:p></b></p>\r\n<p style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="right"><i style="mso-bidi-font-style: normal">VEF<o:p></o:p></i></p><input type="hidden" value="/c111s111/otoxemay/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(56, ' Hyundai Veloster độ phong cách trẻ và cá tính ', '-Hyundai-Veloster-do-phong-cach-tre-va-ca-tinh-', ' (Dân trí) - Dàn xe Veloster độ tại triển lãm SEMA khá bắt mắt, với định hướng rõ ràng vào giới trẻ. ', '<div class="fon34 mt3 mr2 fon43">\r\n <p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Trong số đó, Veloster Re:Mix là mẫu xe duy nhất đã được lên kế hoạch sản xuất thực tế, còn lại đều mới ở dạng concept.</p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><!--?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /--><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><b style="mso-bidi-font-weight: normal">Veloster Re:Mix:<o:p></o:p></b></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Triển lãm xe độ SEMA 2012:" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/05-hyundai-veloster-remix-edition-sema-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Thay đổi ở xe Veloster phiên bản Re:Mix gồm bộ bodykit và một số nâng cấp như nút bấm khởi động, bộ vành đa chấu 18-inch, đèn pha dạng chiếu có bổ sung đèn LED, đèn hậu LED, bộ đèn nội thất LED, thảm sàn đặc biệt, logo Re:Mix, dàn âm thanh Dimension 450W với 8 loa và một loa siêu trầm 8-inch.</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Xe Veloster Re:Mix chỉ có một màu sơn là xám Spirit Gray." src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/10-hyundai-veloster-remix-edition-sema-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Xe Veloster Re:Mix chỉ có một màu sơn là xám Spirit Gray." src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/11-hyundai-veloster-remix-edition-sema-e42a1.jpg"></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Xe Veloster Re:Mix chỉ có một màu sơn là xám Spirit Gray. </div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Xe Veloster Re:Mix chỉ có một màu sơn là xám Spirit Gray." src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/02-hyundai-veloster-remix-edition-sema-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Xe Veloster Re:Mix chỉ có một màu sơn là xám Spirit Gray." src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/04-hyundai-veloster-remix-edition-sema-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Xe Veloster Re:Mix chỉ có một màu sơn là xám Spirit Gray." src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/03-hyundai-veloster-remix-edition-sema-e42a1.jpg" _fl="null" align="middle" width="450"></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Hyundai đã báo giá xe Veloster Re:Mix tại thị trường Mỹ là 19.900 USD cho bản số sàn 6 cấp và 21.150 USD cho bản DCT.</p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><b style="mso-bidi-font-weight: normal">Veloster Turbo Velocity Concept:</b></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p></o:p>&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Phiên bản Veloster của nhà độ Cosworth sở hữu động cơ công suất 400 mã lực và mô-men xoắn 350 lb-ft. </div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Velosters" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/01-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Velosters" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/05-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Velosters" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/03-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Velosters" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/02-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><!--?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /--><st1:place w:st="on"><st1:city w:st="on"><b style="mso-bidi-font-weight: normal">Velosters</b></st1:city><b style="mso-bidi-font-weight: normal"> <st1:state w:st="on">ARK</st1:state></b></st1:place><b style="mso-bidi-font-weight: normal"> Performance:<o:p></o:p></b></p>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="left">Từ một chiếc Hyundai Veloster Turbo tiêu chuẩn 201 mã lực, các kỹ sư của Ark đã độ công suất động cơ lên 232 mã lực và mô-men xoắn 240 lb-ft. Ngoài xe, hãng còn lắp hệ thống ống xả ARK, hệ thống hút gió K&amp;N, bộ kit Nitrous Express Proton, hệ thống làm mát bằng dung dịch Ark và<span style="mso-spacerun: yes">&nbsp; </span>bộ tản nhiệt Koyo.</div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">Nội thất sử dụng vải cao cấp Alcantara, WeatherTek và da. </div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Veloster JP Edition Concept:" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/09-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Veloster JP Edition Concept:" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/06-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><br><img alt="Veloster JP Edition Concept:" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/08-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center"><img alt="Veloster JP Edition Concept:" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/07-hyundai-veloster-sema-2012-e42a1.jpg"></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><b style="mso-bidi-font-weight: normal">Veloster JP Edition Concept:</b></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">“JP” là viết tắt của John Pangilinan, một chuyên gia độ xe, và chiếc Veloster của ông có màu xanh bạc hà khá lạ mắt.</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal">&nbsp;</div>\r\n<div style="MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="center">\r\n<div><img alt="Nhật Minh" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/10-hyundai-veloster-sema-2012-e42a1.jpg"><br><br><img alt="Nhật Minh" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/11-hyundai-veloster-sema-2012-e42a1.jpg"><br><br><img alt="Nhật Minh" src="http://dantri4.vcmedia.vn/oEj8Djn5XzGK0De5Uol4/Image/2012/11/Tuan1/12-hyundai-veloster-sema-2012-e42a1.jpg"></div></div>\r\n<p style="MARGIN: 0cm 0cm 0pt" class="MsoNormal"><o:p>&nbsp;</o:p></p>\r\n<p style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="right"><b style="mso-bidi-font-weight: normal">Nhật Minh<o:p></o:p></b></p>\r\n<p style="TEXT-ALIGN: right; MARGIN: 0cm 0cm 0pt" class="MsoNormal" align="right">Theo <i style="mso-bidi-font-style: normal">Autoblog<o:p></o:p></i></p><input type="hidden" value="/c111s111/otoxemay/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(57, ' Đề nghị làm rõ vụ khiếu nại cấp sổ đỏ sai quy định tại quận Bình Thạnh ', '-Đe-nghi-lam-ro-vu-khieu-nai-cap-so-do-sai-quy-dinh-tai-quan-Binh-Thanh-', ' <a href = "http://dantri.com.vn/c202/s202-657615/de-nghi-lam-ro-vu-khieu-nai-cap-so-do-sai-quy-dinh-tai-quan-binh-thanh.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/y4ICFcccccccccccccLa/Image/2012/10/NHaTP.301012-ca691.jpg" /></a>(Dân trí) - Có tên trên hợp đồng thuê và bỏ tiền ra mua nhà năm 2007 theo Nghị định 61/CP, nhưng chị Nguyễn Thị Hồng Phúc đang đứng trước nguy cơ không được sử dụng phần tài sản hợp pháp do quyết định cấp đất mập mờ, sai quy định của UBND quận Bình Thạnh, TP.HCM. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P></P>\r\n<P></P>\r\n<DIV>Trong đơn đề nghị giải quyết của bà Nguyễn Thị Hồng Phúc sinh năm 1962, trú tại số 5/99/4 Nơ Trang Long, phường 7, quận Bình Thạnh, TP.HCM gửi đến báo <EM>Dân trí </EM>phản ánh: Bà Phúc và các cháu Đỗ Thái Anh, Đỗ Thy Khánh, Đỗ Mỹ Khánh cùng có tên trên hợp đồng thuê nhà số 00618/01 ký ngày 25/10/2004 với Công ty Dịch vụ công ích quận Bình Thạnh. Tuy nhiên, khi cấp giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM>, UBND quận Bình Thạnh chỉ đề tên mình bà Đào Thị Ninh (mẹ bà Phúc). Việc cấp giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM> sai quy định là nguyên nhân khiến quyền lợi hợp pháp của bà Nguyễn Thị Hồng Phúc và 3 cháu nhỏ bị xâm hại nghiêm trọng.</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Đơn đề nghị của bà Hồng Phúc gửi đến báo" align=center src="http://dantri4.vcmedia.vn/y4ICFcccccccccccccLa/Image/2012/10/DDT.301012-ca691.jpg" width=400 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Đơn đề nghị của bà Hồng Phúc gửi đến báo <EM>Dân trí</EM></SPAN></DIV>\r\n<P></P>\r\n<P>Theo đơn của bà Nguyễn Thị Hồng Phúc, ngày 25/10/2004, gia đình bà có ký hợp đồng thuê nhà số 00618/01với Công ty Dịch vụ công ích quận Bình Thạnh. Người đứng tên ký hợp đồng là bà Đào Thị Ninh (mẹ bà Phúc), các thành viên khác cùng đứng tên trong danh sách thành viên hợp đồng thuê nhà cùng bà Đào Thị Ninh gồm có: Nguyễn Thị Ngọc Điều, Nguyễn Thị Hồng Phúc, Nguyễn Ngọc Tuấn Cường, Đỗ Thy Khánh, Đỗ Mỹ Khánh, Đỗ Thái Anh, Nguyễn Ngọc Long Phi.</P>\r\n<P></P>\r\n<P>Năm 2007 nhà bà Phúc làm thủ tục mua hóa giá nhà theo chủ trương của nhà nước. Vì điều kiện làm ăn xa, bà Phúc gửi tiền nhờ chị gái là Nguyễn Thị Ngọc Điều liên hệ mua nhà. Sau đó bà Phúc và các thành viên trong gia đình nhiều lần hỏi bà Điều về việc mua nhà, nhưng bà Điều đều trả lời chưa xong thủ tục mua nhà.</P>\r\n<P></P>\r\n<P>Tháng 11/2009, bà Đào Thị Ninh qua đời sau thời gian dài lâm bệnh. Sau tang lễ, gia đình tổ chức họp bàn việc chuyển tên chủ quyền và đồng thừa kế thì được bà Nguyễn Thị Ngọc Điều cho biết khi còn sống bà Đào Thị Ninh đã cho bà Điều toàn bộ căn nhà số 5/99/4 Nơ Trang Long, phường 7, quận Bình Thạnh, dù căn nhà là tài sản thuộc sở hữu chung của tất cả các thành viên được nêu tên trong hợp đồng thuê nhà trước đây. Trước đó, ngày 5/5/2009, UBND quận Bình Thạnh đã cấp cấp giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM> đứng tên bà Đào Thị Ninh.</P>\r\n<P></P>\r\n<P>Cầm trong tay những giấy tờ liên quan ngôi nhà, ngày 18/11/2011, bà Nguyễn Thị Ngọc Điều tự ý chuyển nhượng một phần căn nhà (35m2) cho người khác mà không không qua gia đình. Không những vậy, bà Điều còn mang cả sổ Hộ khẩu gia đình đi nơi khác với mục đích không cho các thành viên khác trong gia đình được sử dụng.</P>\r\n<P></P>\r\n<DIV>Nhằm đảm bảo quyền lợi cho các thành viên, trong đó có 2 cháu nhỏ chịu cảnh mồ côi là Đỗ Thy Khánh và Đỗ Mỹ Khánh. Ngày 29/02/2012, bà Nguyễn Thị Hồng Phúc làm đơn gửi Chủ tịch UBND quận Bình Thạnh và Trưởng Phòng TNMT quận Bình Thạnh với nôi dung: Đề nghị các cơ quan chức năng xem xét lại việc chỉ ghi tên một mình bà Đào Thị Ninh trên giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM> số <B>1799/2009/GCN</B> được quận Bình Thạnh cấp ngày 5/5/2009 vì thực hiện sai theo Nghị định 61/CP; Ngăn chặn mọi giao dịch liên quan đến phần nhà còn lại của căn nhà số 5/99/4 Nơ Trang Long, phường 7, quận Bình Thạnh (Do nhà đã bị bà Điều bán một phần vào tháng 11/2011).</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Bà Hồng Phúc đề nghị các cơ quan chức năng làm rõ vụ cấp giấy chứng nhận" align=center src="http://dantri4.vcmedia.vn/y4ICFcccccccccccccLa/Image/2012/10/NHaTP.301012-ca691.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bà Hồng Phúc đề nghị các cơ quan chức năng làm rõ vụ cấp giấy chứng nhận </SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Quyền sở hữu nhà ở và QSDĐ ở sai quy định của quận Bình Thạnh</SPAN></DIV>\r\n<P>6 tháng sau khi gửi đơn, ngày 13/08/2012, bà Nguyễn Thị Hồng Phúc mới nhận được công văn số 520/TNMT ký ngày 8/8/2012 của Phòng TNMT quận Bình Thạnh nhưng nội dung công văn chỉ tường thuật lại quá trình mua bán nhà ở, cho tặng và chuyển nhượng. Không đề cập đến việc vì sao lại cấp giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM> cho một mình bà Đào Thị Ninh? Mặc dù trong công văn 520/TNMT có thừa nhận bà Đào Thị Ninh là Chủ hợp đồng cùng các thành viên kèm theo phụ lục 1 hợp đồng thuê nhà số 00618/01 ngày 25/10/2004. </P>\r\n<P></P>\r\n<P>Nhận được công văn trả lời của Phòng TNMT, ngày 15/8/2012, bà Hồng Phúc tiếp tục khiếu nại nội dung công văn&nbsp; số 520/TNMT nhưng đến nay vẫn chưa nhận được câu trả lời từ Phòng TNMT quận Bình Thạnh. Theo lời bà Phúc, việc Phòng TNMT cấp giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM> cho một mình bà Đào Thị Ninh là sai luật, bởi Điều 5 Nghị định 61/CP nêu rõ: “<B>Nhà nước thực hiện bán nhà ở thuộc sở hữu Nhà nước cho người đang thuê phần diện tích đang được thuê để người mua nhà có điều kiện cải thiện chỗ ở</B>”.</P>\r\n<P>Không chỉ đối mặt nguy cơ không được sử dụng phần tài sản hợp pháp, các thành viên còn lại trong gia đình bà Phúc còn gặp phải vô vàn khó khăn khi không có sổ Hộ khẩu gốc để đăng ký các dịch vụ phúc lợi phục vụ sinh hoạt hàng ngày do bà Điều tự ý mang sổ Hộ khẩu gốc đi nơi khác từ cuối năm 2011. Từ tháng 3/2012 đến nay, bà Phúc đã làm đơn đề nghị giải quyết vấn đề hộ khẩu gửi lên Công an phường 7, Công an quận Bình Thạnh nhưng chưa được giải quyết. </P>\r\n<P></P>\r\n<P>Sau đó bà Nguyễn Thị Hồng Phúc tiếp tục gửi đơn đến UBND TP.HCM đề nghị xem xét giải quyết nhưng không mang lại kết quả. Ngày 22/10/2012, Văn phòng tiếp công dân UBND TP.HCM có giấy báo tin số 1877/GBT-TCD có nội dung:</P>\r\n<P></P>\r\n<P>“Ngày 01/10/2012, Công an quận Bình Thạnh có công văn số 662/CAQ(QLHC) phúc đáp đến Văn phòng Tiếp công dân Thành phố với nội dung: Đội Cảnh sát QLHC về TNXH Công an quận Bình Thạnh đã hoà giải và bà Điều đồng ý cho bà Phúc mượn sổ hộ khẩu để giải quyết các vấn đề cá nhân, bà Điều cho biết khi nào bà Phúc cần hộ khẩu thì liên lạc bà Điều sẽ mang hộ khẩu đến chứ không giao sổ hộ khẩu trực tiếp cho bà Phúc. Hai bên đã đồng ý và ký vào biên bản giải quyết.”</P>\r\n<P></P>\r\n<P>Nhận được văn bản trả lời của Văn phòng Tiếp công dân, bà Nguyễn Thị Hồng Phúc tỏ ra rất bức xúc, bà Phúc cho biết: “Theo Điều 24 Luật cư trú có ghi: “<B>Sổ Hộ khẩu được cấp cho hộ gia đình, có giá trị xác định nơi thường trú của các thành viên trong gia đình</B>”. Như vây, tất cả các thành viên trong gia đình đều có quyền ngang nhau trong việc sử dụng hộ khẩu, không thành viên nào được phép chiếm giữ cản trở các thành viên khác sử dụng sổ hộ khẩu. Bản thân tôi không hề đồng ý ký vào biên bản như công văn Công an quận Bình Thạnh trả lời cho Văn phòng Tiếp công dân”.</P>\r\n<P></P>\r\n<P>Để các thành viên trong gia đình được sử dụng những tài sản sở hữu hợp pháp, bà Nguyễn Thị Hồng Phúc khẩn thiết cầu cứu các cơ quan chức năng của TP.HCM làm rõ việc cấp Giấy chứng nhận <EM>Quyền sở hữu nhà ở và QSDĐ ở</EM> sai quy định cho một mình bà Đào Thị Ninh của UBND quận Bình Thạnh; Sớm giải quyết vấn đề hộ khẩu để các thành viên gia đình (có 2 cháu mồ côi) được hưởng quyền cư trú theo Luật cư trú Nhà nước đã ban hành, được sử dụng Hộ khẩu thường trú theo đúng qui định.</P>\r\n<P></P>\r\n<P>Báo <EM>Dân trí </EM>sẽ tiếp tục thông tin về vụ việc trên.</P>\r\n<P></P>\r\n<P align=right><B>Vũ Văn Tiến - Ngọc Cương</B></P><input type="hidden" value="/c202s202/diendan-bandoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(58, ' Truy nã gắt gao Chủ tịch HĐQT Muaban24  ', '-Truy-na-gat-gao-Chu-tich-HĐQT-Muaban24--', ' (Dân trí) - Nguồn tin riêng của Dân trí từ Cơ quan CSĐT Công an TP. Hà Nội cho biết: Liên ngành tư pháp Trung ương đã có thông tư hướng dẫn các cơ quan tố tụng trong việc điều tra vụ án liên quan đến tội phạm công nghệ cao làm rúng động dư luận toàn quốc.<br><a href="http://dantri.com.vn/c202/s202-629341/Di-tim-toi-danh-cho-cac-doi-tuong-cam-dau-Muaban24.htm"><b>&nbsp;>>&nbsp;  Đi tìm tội danh cho các đối tượng cầm đầu Muaban24</b></a><br><a href="http://dantri.com.vn/c202/s202-628335/Khoi-to-vu-an-Muaban24-tai-8-tinh-thanh.htm"><b>&nbsp;>>&nbsp;  Khởi tố vụ án Muaban24 tại 8 tỉnh, thành</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV align=center><IMG style="MARGIN: 5px" alt="Quyết định truy nã đặc biệt Nguyễn Tuấn Minh, Chủ tịch HĐQT Muaban24" align=center src="http://dantri4.vcmedia.vn/digNE15ccccccccccccF/Image/2012/11/Truy-na-Tuan-Minh1111-e022d-539c4.jpg" width=430 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Quyết định truy nã đặc biệt Nguyễn Tuấn Minh, Chủ tịch HĐQT Muaban24</SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">(Ảnh: Vũ Văn Tiến)</SPAN></DIV>\r\n<P align=center></P>\r\n<P>Ngày 1/10/2012, nguồn tin riêng của <EM>Dân trí</EM> cho hay, liên ngành tư pháp Trung ương đang tập trung chỉ đạo Cơ quan Cảnh sát điều tra các tỉnh, thành khẩn trương mở rộng điều tra, thu thập thông tin liên quan đến các chi nhánh của mạng lưới Muaban24 ở hàng chục địa phương trong cả nước.</P>\r\n<DIV>Ngoài ra, Giám đốc Công an TP. Hà Nội cũng đang chỉ đạo sát sao Cơ quan CSĐT Công an Hà Nội tích cực phối hợp với Cục Cảnh sát Phòng chống tội phạm sử dụng công nghệ cao (C50) Bộ Công an cùng các đơn vị liên quan khôi phục các dữ liệu trong các máy tính mà Cơ quan CSĐT tạm giữ khi khám xét tại trụ sở của Muaban24; đồng thời Giám đốc Công an TP. Hà Nội chỉ đạo truy bắt gắt gao đối với bị can Nguyễn Tuấn Minh, sinh năm 1973, đăng ký hộ khẩu thường trú tại tổ 36, phường Khương Trung, quận Thanh Xuân, Hà Nội (chỗ ở: số 2, ngõ 3 đường Nguyễn Văn Linh, phường Liên Bảo, TP. Vĩnh Yên, tỉnh Vĩnh Phúc). Bắt được đối tượng truy nã trên, yêu cầu báo ngay cho điều tra viên Phạm Đức Hà, Đội 14, PC45, Công an TP. Hà Nội, ĐT: 04.39396134.</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Ít ai biết rằng sau vẻ hoàng nhoáng này là sự lừa đảo" align=center src="http://dantri4.vcmedia.vn/digNE15ccccccccccccF/Image/2012/11/123-8c7e2.jpg" width=462 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ít ai biết rằng sau vẻ hào nhoáng này là sự lừa đảo </SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"></SPAN>&nbsp;</DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">\r\n<DIV><IMG style="MARGIN: 5px" alt="Huy (trái), Cường (giữa) và Hà&nbsp;đã bị tạm giam 4 tháng để phục vụ điều tra" align=center src="http://dantri4.vcmedia.vn/digNE15ccccccccccccF/Image/2012/11/Untitled-1-680fc-d6ad9.jpg" width=429 _fl=""></DIV></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Huy (trái), Cường (giữa) và Hà&nbsp;đã bị tạm giam 4 tháng để phục vụ điều tra</SPAN></DIV>\r\n<P>Như <I>Dân trí</I> đã phản ánh, ngày 7/8/2012, Cơ quan CSĐT Công an TP. Hà Nội đã có Quyết định khởi tố bị can số 768 đối với Nguyễn Tuấn Minh, Chủ tịch HĐQT Muaban24, có hành vi “sử dụng mạng máy tính, mạng viễn thông, mạng internet để thực hiện hành vi chiếm đoạt tài sản, phạm vào Điều 226B, Bộ luật hình sự. </P>\r\n<DIV>Hiện nay, Cơ quan CSĐT Công an TP. Hà Nội đã ra lệnh bắt tạm giam Ngô Văn Huy (39 tuổi, trú ở đường Hàm Nghi, Mỹ Đình, Từ Liêm, Tổng Giám&nbsp;đốc&nbsp;Muaban 24) Lê Văn Cường (37 tuổi, trú ở Mỹ Đình 1, Từ Liêm, Phó Chủ tịch, Phó&nbsp;Tổng giám đốc Muaban 24) và Nguyễn Mạnh Hà (32 tuổi, trú ở Quang Tiến, Sóc Sơn, Hà Nội, là Trưởng phòng Kỹ thuật của Muaban 24). </DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Cơ quan CSĐT Công an TP. Hà Nội đọc lệnh bắt khẩn cấp các đối tượng" align=center src="http://dantri4.vcmedia.vn/digNE15ccccccccccccF/Image/2012/11/Bat-khan-cap--anh-chuan-1883c-3581e.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Cơ quan CSĐT Công an TP. Hà Nội đọc lệnh bắt khẩn cấp các đối tượng </SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">cầm đầu đường dây Muban24 tại Hà Nội </SPAN></DIV>\r\n<P>Theo tài liệu của cơ quan Công an thì Muaban 24 được thành lập theo giấy chứng nhận đăng ký kinh doanh lần đầu vào ngày 25/5/2011, do 3 thành viên Nguyễn Tuấn Minh, Ngô Văn Huy và Lê Văn Cường sáng lập, hoạt động theo phương thực mô hình của sàn thương mại điện tử qua trang web muaban24.vn. </P>\r\n<P>Tính đến khi bị bắt, mô hình hoạt động của Công ty Muaban 24 có 3 cấp giám đốc (gồm Minh, Huy và Cường), 18 cấp phó giám đốc, 83 cấp VIP lãnh đạo, 680 VIP và hơn 136 nghìn hội viên, trong đó có hơn 17 nghìn hội viên từ Công ty Tâm Mặt Trời (nơi Huy, Minh, Cường từng làm việc) chuyển sang. </P>\r\n<P>Với tư cách là Chủ tịch HĐQT, Nguyễn Tuấn Minh đã chỉ đạo Nguyễn Mạnh Hà đưa tiền ảo vào hệ thống mạng Muaban24 để rút tiền thật ra. Hằng tháng, sau khi hạch toán, số tiền trên được chia thành 4 phần, mỗi đối tượng hưởng 1 phần.</P>\r\n<P>Ngày 2/8, Trung tướng Triệu Văn Đạt, Phó Tổng cục trưởng Tổng cục Cảnh sát phòng, chống tội phạm (Tổng cục VI) cho biết, Tổng cục VI đã chỉ đạo Công an 32 tỉnh, thành phố có Văn phòng Công ty Muaban24 đồng loạt khám xét khẩn cấp, thu giữ tài liệu để phục vụ công tác điều tra. </P>\r\n<P>Cơ quan chức năng xác định các đối tượng trong đường dây trên đã lợi dụng thương mại điện tử để&nbsp;chiếm&nbsp;đoạt tài sản của&nbsp;người dân nên sẽ xử lí các đối tượng về hành vi sử dụng mạng máy tính, mạng viễn thông, mạng Internet hoặc thiết bị số thực hiện hành vi chiếm đoạt tài sản theo điều 226b, Bộ Luật hình sự.</P>\r\n<P>Ngày 31/7, các mũi công tác của C50 phối hợp với Công an Phú Thọ, Công an Hà Nội thi hành lệnh khám xét khẩn cấp 5 tòa nhà đặt trụ sở Muaban 24 tại Mỹ Đình.</P>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Cơ quan CSĐT Công an TP. Hà Nội đọc lệnh bắt khẩn cấp các đối tượng" align=center src="http://dantri4.vcmedia.vn/digNE15ccccccccccccF/Image/2012/11/ngo-van-huy-to-ra-suy-sup-khi-tra-tay-vao-cong-anh-vvt-hk-6ae0e-a77e7-9f42e.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ngô Văn Huy, Tổng GĐ&nbsp;Công ty CP Đào tạo Mua bán trực tuyến - Muaban24 tỏ ra suy sụp khi tra tay vào còng </SPAN></DIV>\r\n<P>Theo các trinh sát, điều tra viên, các đối tượng đã xây dựng hệ thống Muaban24 theo mô hình mở các tài khoản, gian hàng trên mạng và phát hành một loại tiền ảo (tiền D) có giá trị trong hệ thống Muaban24, tương đương 1D = 10 nghìn đồng tiền thật.</P>\r\n<P>Khi người chơi tham gia phải nộp 5,2 triệu đồng để mua gian hàng, theo đó, sẽ có 520D để chia cho hoa hồng và các cấp phía trên (như: VIP, VIP lãnh đạo, Phó Giám đốc, Giám đốc…), còn tiền thật sẽ được chuyển về "đầu não" của Muaban24. Do đó, người chơi chỉ được sở hữu một loại tiền ảo, còn tiền thật đã bị chiếm đoạt mà không biết. Với quy định, tiền ảo sẽ được đổi thành tiền thật, nếu người chơi muốn rút, các đối tượng đã khiến người chơi tưởng rằng mình có tiền nhưng thực chất, tiền đã bị các đối tượng cầm đầu rút ra chia nhau hết.</P>\r\n<P>Chính vì vậy, nhiều người chơi, sau khi nhận thấy bị chiếm đoạt tiền đã muốn đổi điểm để rút tiền thật. Đến lúc này, các đối tượng mới yêu cầu người chơi phải "nhượng" tiền ảo đó cho người khác.&nbsp;</P>\r\n<TABLE style="WIDTH: 100%; BACKGROUND: #e0ffff" border=0 cellSpacing=0 cellPadding=0>\r\n<TBODY>\r\n<TR>\r\n<TD>\r\n<P>Sau hơn 30 bài báo trên <I>Dân trí</I> lật tẩy nhiều dấu hiệu sai phạm nghiêm trọng của Muaban24 trong cả nước, đến nay đường dây này đã từng bước được phanh phui&nbsp;với sự vào cuộc rốt ráo của Bộ Công an và Công an các tỉnh, thành. </P>\r\n<P>Theo cơ quan điều tra, mạng lưới Muaban24 hiện có hơn 50 chi nhánh ở nhiều tỉnh, thành cả nước. Trong vòng 1 năm, Muaban24 đã phát triển tới 120.000 gian hàng, với số tiền nộp vào hệ thống này lên tới hơn 600 tỷ đồng.</P>\r\n<P>&nbsp;Đây là vụ việc có nhiều tính chất mới, phức tạp liên quan đến công nghệ cao, gây thiệt hại kinh tế lớn, được dư luận đặc biệt quan tâm. </P>\r\n<P>&nbsp;Để góp phần làm rõ hành vi của mạng lưới này trước pháp luật, các bị hại có thể đến trụ sở Phòng CSĐT tội phạm về Trật tự Xã hội - Công an Hà Nội, số 7, phố Thiền Quang để trình báo.</P></TD></TR></TBODY></TABLE>\r\n<P align=right><STRONG>Vũ Văn Tiến</STRONG></P>\r\n<P>&nbsp;</P><input type="hidden" value="/c202s202/diendan-bandoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(59, ' “Người phụ nữ ngày ngày đi kiện” trông đợi sự công tâm của tòa án ', '-“Nguoi-phu-nu-ngay-ngay-di-kien”-trong-doi-su-cong-tam-cua-toa-an-', ' (Dân trí) - 4 tháng sau phiên xử sơ thẩm ở TAND quận Long Biên với nội dung bác đơn yêu cầu hủy quyết định 1737/QĐ - UBND quận Long Biên, gia đình bà Nguyễn Thị Hoàn đang chờ đợi TAND TP. Hà Nội đưa ra bản án công tâm hơn tại phiên phúc thẩm ngày 9/11/2012.<br><a href="http://dantri.com.vn/c202/s202-647034/TAD-Toi-cao-chi-dao-giai-quyet-vu-guoi-phu-nu-ngay-ngay-di-kien-doi-cong-ly.htm"><b>&nbsp;>>&nbsp;  TAND Tối cao chỉ đạo giải quyết vụ “Người phụ nữ ngày ngày đi kiện đòi công lý”</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P align=justify>Như thông tin đã đưa, bà Nguyễn Thị Hoàn, trú tại số nhà 184 Ngô Gia Tự, phường Đức Giang, quận Long Biên nhiều lần gửi đơn kêu cứu và tố cáo đến báo <I>Dân trí</I> phản ánh: UBND quận Long Biên thực hiện thu hồi cưỡng chế đất gây ảnh hưởng quyền lợi của bà Hoàn, người có quyền lợi liên quan tại 2 ngôi nhà 845 - 847 Ngô Gia Tự, đồng thời được ông Nguyễn Gia Thúy (anh trai), Nguyễn Văn Hồng (em trai) ủy quyền đại diện. </P>\r\n<DIV align=justify>Bà Hoàn khẳng định gia đình bà là chủ sở hữu hợp pháp lô đất diện tích 128m2. Khi giải phóng mặt bằng dự án nâng cấp quốc lộ 1A, UBND quận Long Biên thực hiện sai quy định, gây ảnh hưởng nghiêm trọng quyền lợi của các thành viên trong gia đình. Mảnh đất 128m2 (gồm 2 số nhà 845 - 847) được các cụ thân sinh là Nguyễn Gia Tham và Đỗ Thị Vân mua lại vào năm 1964. Từ lúc mua đến nay, gia đình bà sử dụng ổn định, thực hiện đầy đủ nghĩa vụ nộp thuế.</DIV>\r\n<DIV align=justify>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Bà Hoàn mong mỏi TAND TP. Hà Nội đưa ra bản án công tâm" align=center src="http://dantri4.vcmedia.vn/y4ICFcccccccccccccLa/Image/2012/09/NTHoan.311012-6cad0.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bà Hoàn mong mỏi TAND TP. Hà Nội đưa ra bản án công tâm</SPAN></DIV>\r\n<P align=justify>Ngày 23/6/2011, gia đình đình bà Hoàn nhận được Quyết định 1737/QĐ - UBND của UBND quận Long Biên về việc phê duyệt dự án chi tiền bồi thường, hỗ trợ và tái định cư khi thu hồi diện tích nhà đất số nhà 845 - 847. Gia đình bà Hoàn không nhất chí phương án đền bù 55,2m2, vì diện tích này thấp hơn 27,3m2 so với diện tích thực gia đình bị thu hồi. Phần diện tích thiếu hụt này đã được xác nhận khi tổ công tác GPMB phường Đức Giang tiến hành phúc tra, đo đạc thực tế vào ngày 12/5/2011.</P>\r\n<P align=justify>Không đồng ý với phương án đền bù, ông Nguyễn Gia Thúy làm đơn khởi kiện ra TAND quận Long Biên đề nghị hủy Quyết định số 1737/QĐ-UBND của UBND quận Long Biên; hủy Quyết định cưỡng chế số 115/QĐ - CTUBND của UBND quận Long Biên về việc cưỡng chế quyết định thu hồi đất của gia đình ông Thúy.</P>\r\n<DIV align=justify>Ngày 15/6/2012, TAND quận Long Biên đưa vụ án khởi kiện hủy Quyết định số 1737/QĐ-UBND của UBND quận Long Biên về việc thu hồi đất ra xét xử. Tuy nhiên, Hội đồng xét xử lại bỏ qua nhiều tình tiết mâu thuẫn, không căn cứ vào biên bản thẩm định tại chỗ, biên bản đối thoại giữa gia đình bà Hoàn với UBND quận Long Biên và tuyên bác toàn bộ đơn khởi kiện của nguyên đơn là ông Nguyễn Gia Thúy. </DIV>\r\n<DIV align=justify>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Hiện trạng nhà 845 - 847 sau khi bị thu hồi" align=center src="http://dantri4.vcmedia.vn/y4ICFcccccccccccccLa/Image/2012/09/NgGTu.311012-6cad0.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Hiện trạng nhà 845 - 847 sau khi bị thu hồi </SPAN></DIV>\r\n<P align=justify>Hội đồng xét xử kết luận, năm 1964, ông Nguyễn Gia Tham (là bố đẻ của ông Thúy) nhận chuyển nhượng đất của bà Đinh Thị Xã, diện tích đất tự ghi là 128m2. Năm 1984, ông Tham mất để lại nhà đất cho vợ là bà Đỗ Thị Vân và các con sử dụng. Năm 1985, bà Đỗ Thị Vân có đơn đăng ký quyền sử dụng ruộng đất gửi UBND TP. Hà nội và đơn đăng ký nhà ở và đất ở gửi UBND thị trấn Đức Giang với diện tích toàn bộ là 93m2. Năm 1998 (sau khi bà Vân mất), ông Nguyễn Gia Thúy làm thủ tục kê khai đăng ký nhà ở và đất ở với toàn bộ nhà ở và đất ở đứng tên ông và vợ là Lê Bích Hương, diện tích là 77m2 gồm 2 thửa 54 + 55, tờ bản đồ 308 - 15 gồm 2 số nhà 845 - 847 đường Ngô Gia Tự, phường Đức Giang, Long Biên, Hà Nội. Từ nhận định trên, TAND quận Long Biên cho rằng việc áp dụng mức bồi thường diện tích đất thu hồi 55,2m2 với ông Nguyễn Gia Thúy là đúng quy định pháp luật.</P>\r\n<P align=justify>Về chế độ tái định cư, tại bản án sơ thẩm TAND quận Long Biên thừa nhận nhà số 845 - 847 thuộc 2 thửa đất. Thửa số 54 diện tích 49m2, thửa số 55 diện tích 28m2. Tuy nhiên, TAND quận Long Biên vẫn khẳng định việc áp dụng 1 suất tái định cư diện tích 40m2 là đúng quy định, với lý do ông Nguyễn Gia Thúy đã kê khai tài sản năm 2005 với tư cách chủ sử dụng đối với toàn bộ 2 thửa đất này với diện tích là 92m2. Nhưng trên thực tế, từ năm 1989, 2 thửa đất này đã được xây dựng tách biệt thành 2 hộ riêng biệt và có Hộ khẩu riêng biệt thể hiện 2 số nhà tại UBND phường Đức Giang</P>\r\n<DIV align=justify>Không đồng tình với bản án sơ thẩm số 01/2012/HC-ST ngày 18/6/2012 của TAND quận Long Biên, gia đình bà Hoàn tiếp tục làm đơn kháng cáo lên TAND TP. Hà Nội.</DIV>\r\n<DIV align=justify>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" alt="Gia đình bà Hoàn cho rằng việc cấp 1 suất tái định cư là không đúng" align=center src="http://dantri4.vcmedia.vn/y4ICFcccccccccccccLa/Image/2012/09/SHK.311012-6cad0.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Gia đình bà Hoàn cho rằng việc cấp 1 suất tái định cư là không đúng </SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">quy định khi trên mảnh đất có 2 nhà tách biệt, với sổ hộ khẩu riêng biệt từ năm 1989</SPAN></DIV>\r\n<P align=justify>Ngày 10/9/2012, TAND quận Long Biên đã chuyển hồ sơ vụ án lên TAND TP. Hà Nội. Nhưng đến ngày 26/10/2012, TAND TP. Hà Nội mới ra quyết định số 312/2012/QĐPT - HC về việc đưa vụ án ra xét xử phúc thẩm vào ngày 9/11/2012. </P>\r\n<P align=justify>Trao đổi với PV <I>Dân trí</I> ngày 30/10/2012, bà Nguyễn Thị Hoàn tái khẳng định gia đình là chủ sở hữu hợp pháp lô đất 128m2 tại số nhà 845 - 847 Ngô Gia Tự, đã sử dụng ổn định nhiều năm và luôn hoàn thành nghĩa vụ nộp thuế theo quy định. Trên phần đất này đã tồn tại 2 thửa đất 54 và 55, được thể hiện bằng 2 số nhà riêng biệt, với 2 hộ khẩu riêng biệt mang tên ông Nguyễn Gia Thúy và Nguyễn Văn Hồng.</P>\r\n<P align=justify>Ngày 13/10/2009, UBND quận Long Biên đã ban hành Quyết định số 3645/QĐ- UBND về việc thu hồi 55,2m2. Điều này gây thiệt hại nặng nề về kinh tế cho gia đình bà vì thấp hơn diện tích sử dụng thực đến 27,3m2. Việc thiếu sót này đã được xác nhận khi tổ công tác GPMB phường Đức Giang tiến hành phúc tra, đo đạc thực tế vào ngày 12/5/2011. </P>\r\n<P align=justify>Trước phiên xử phúc thẩm, bà Nguyễn Thị Hoàn đề nghị Hội đồng xét xử xem xét chứng cứ liên quan để đưa bản án công tâm, đúng pháp luật, đảm bảo những quyền lợi hợp pháp của công dân.</P>\r\n<P align=justify>Trước đó, ngày 24/9/2012, Phó Chánh án TAND Tối cao Nguyễn Sơn đã ký công văn số 201/TANDTC-KHXX đề nghị TAND quận Long Biên, TP. Hà Nội lập hồ sơ kháng cáo và chuyển hồ sơ kháng cáo của bà Nguyễn Thị Hoàn lên TAND TP. Hà Nội giải quyết theo đúng quy định pháp luật.</P>\r\n<P align=justify>Báo <I>Dân trí</I> sẽ tiếp tục thông tin về vụ việc trên.</P>\r\n<P align=right><B>Vũ Văn Tiến - Ngọc Cương</B></P><input type="hidden" value="/c202s202/diendan-bandoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(60, ' “Tá hỏa” khi bỗng dưng đất mình bị người khác đến xây nhà ', '-“Ta-hoa”-khi-bong-dung-dat-minh-bi-nguoi-khac-den-xay-nha-', ' (Dân trí) – Vụ việc lạ kỳ đã tốn nhiều công sức ông Trương Trọng Phương với miếng đất của mình (ở lô số 201, Khu đô thị Nam Đông Hà, phường Đông Lương, TP Đông Hà, tỉnh Quảng Trị) bỗng bị người lạ đến… xây nhà cho đến nay vẫn chưa được giải quyết dứt điểm. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P class=MsoNormal><B style="mso-bidi-font-weight: normal">Đất mua xong bỗng bị người khác đến “chiếm” để xây nhà</B></P>\r\n<P class=MsoNormal>Theo đơn phản ánh của ông Trương Trọng Phương (tạm trú 74 Quang Trung, phường 1, Thị xã Quảng Trị), ông có lô đất số 201 ở Khu đô thị Nam Đông Hà, phường Đông Lương, TP Đông Hà, tỉnh Quảng Trị. Diện tích 161m2, mục đích sử dụng: đất ở tại đô thị; thời gian sử dụng là lâu dài.</P>\r\n<P class=MsoNormal>Lô đất này được ông Phương mua vào năm 2005, đã có giấy chứng nhận quyền sử dụng đất số AC 705209 của UBND Thị xã Đông Hà cấp ngày 20/6/2005 cho ông Phương (chủ hộ) và vợ sử dụng. Từ đó đến nay, hàng năm ông Phương đều nộp thuế đất đầy đủ.</P>\r\n<P class=MsoNormal>Tuy nhiên, vào giữa tháng 5/2011, ông Phương bỗng “phát hoảng” khi tự dưng thấy ông Lê Thiên Thành đến xây dựng phần móng nền trên lô đất 201 của mình. Ngày 6/8, ông Phương đã có đơn trình báo “Về việc xây nhà trái phép trên đất của tôi” gửi UBND TP Đông Hà, Phòng Quản lý Đô thị địachính TP Đông Hà và UBND phường Đông Lương.</P>\r\n<P style="TEXT-ALIGN: center" class=MsoNormal></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/31101-2e7fd.JPG" width=399 _fl="" alt="Ngôi nhà lầu 3 tầng của ông Lê Thiên Thành nằm chễm chệ trên đất của ông Trương Trọng Phương"></DIV>\r\n<DIV style="TEXT-ALIGN: center; FONT-FAMILY: Tahoma"><SPAN style="FONT-SIZE: 10pt">Ngôi nhà lầu 3 tầng của ông Lê Thiên Thành nằm "chễm chệ" trên đất của ông Trương Trọng Phương</SPAN><BR></DIV>\r\n<P></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Đến ngày 24/8/2011, ông Đặng Trọng Vân, Trưởng phòng TN&amp;MT TP Đông Hà đã chủ trì cuộc họp giải quyết kiến nghị về đất ông Phương. Nhưng kết luận cuối buổi họp, chỉ xác định việc… cấp giấy CNQSDĐ lô đất của cả 2 ông Phương và Thành trên cơ sở bản vẽ Hoàn công cắm mốc phân lô và Quyết định phê duyệt giá của UBND tỉnh Quảng Trị và Hồ sơ đấu giá đất do Ban QLDACSHT tỉnh (nay là TTPTQĐ tỉnh). Qua đó, xác định lô ông Phương thuộc số 201,còn lô ông Thành là số 203 kế bên.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Nhưng riêng việc kiểm tra hiện trạng, lập biên bản xây dựng trái phép của ông Thành trên đất ông Phương vẫn không thấy nhắc đến trong cuộc họp này (?!) </P>\r\n<P class=MsoNormal>Bức xúc trước việc làm này, ông Phương tiếp tục gửi đơn đề nghị giải quyết vụ việc lần 2 cho UBND TP Đông Hà. Ông đề nghị cần làm rõ việc xem xét xây dựng nhà trái phép của ông Thành; kiểm tra lại quy trình thủ tục hồ sơ cấp giấy phép xây dựng cho ông Thành và đình chỉ ngay việc thi công nhà của ông Thành và chuyển trả lại mặt bằng cho gia đình mình để xây dựng.</P>\r\n<P class=MsoNormal>Ngày 6/10, Phòng Quản lý đô thị TP Đông Hà đã nhóm họp và xác định ông Thành đã xây nhà trên đất ông Phương ở lô số 201. Giấy phép xây dựng được cấp cho ông Thành là đúng quy định pháp luật. Tuy nhiên, sơ đồ mặt bằng xây dựng ngày 31/5/2011 của UBND TP Đông Hà cấp cho ông Thành là chỉ được phép xây trên đất thuộc quyền sử<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>dụng của mình ở lô số 203. Cuộc họp cũng chỉ kết thúc tại đây và không có ý kiến giải quyết gì thêm.</P>\r\n<P style="TEXT-ALIGN: center" class=MsoNormal></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/31103-2e7fd.JPG" width=448 _fl="" alt="Sổ đỏ của ông Phương về lô đất 201"></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Sổ đỏ của ông Phương về lô đất 201</SPAN><BR></DIV>\r\n<P></P>\r\n<P class=MsoNormal><B style="mso-bidi-font-weight: normal">Tỉnh kết luận ông Thành đã sai khi xây dựng nhà trái phép trên đất người khác</B></P>\r\n<P class=MsoNormal>Không đồng tình với những cách giải quyết “xoa dịu” mà không đi thẳng vào vấn đề, ông Phương đã gửi đơn lên tỉnh. Ngày 26/12/2011, ông Nguyễn Hữu Dũng, Phó chủ tịch UBND tỉnh Quảng Trị đã có đơn số 3580/UBND-ND về việc giải quyết đơn kiến nghị của ông Trương Trọng Phương gửi UBND TP Đông Hà. </P>\r\n<P class=MsoNormal>Trong đơn đã chỉ ra rõ việc làm sai trái của ông Thành như sau: “Việc phản ánh của ông Trương Trọng Phương là có căn cứ; Ông Lê Thiên Thành đã xây dựng nhà ở trái phép trên đất ở hợp pháp của ông Trương Trọng Phương”.</P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/31104-2e7fd.JPG" width=317 _fl="" alt="Sổ đỏ của ông Phương về lô đất 201"></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Văn bản của phó chủ tịch tỉnh Quảng Trị đã kết luận vụ việc phần sai toàn bộ do ông Lê Thiên Thành gây ra</SPAN><BR></DIV>\r\n<P class=MsoNormal>Tỉnh đã yêu cầu UBND TP Đông Hà khẩn trương tiến hành đình chỉ hành vi xây dựng trái phép và tiến hành xử lý vi phạm hành chính hành vi chiếm đất, vi phạm trên lĩnh vực hoạt động xây dựng của ông Lê Thiên Thành.</P>\r\n<P class=MsoNormal>“Đồng thời, để tránh lãng phí về tài sản do hành vi xây dựng trái phép gây ra, UBND TP Đông Hà chỉ đạo UBND phường Đông Lương, các phòng ban liên quan vận động ông Lê Thiên Thành và ông Trương Trọng Phương thỏa thuận với nhau phương thức giải quyết, đảm bảo thấu tình đạt lý. Trường hợp 2 bên không thỏa thuận được thì buộc phải yêu cầu ông Lê Thiên Thành khôi phục lại tình trạng của đất như trước khi vi phạm theo đúng quy định của pháp luật” – ông Nguyễn Hữu Dũng kết luận cách giải quyết ở cuối đơn này.</P>\r\n<P class=MsoNormal>Tuy nhiên, thật khó hiểu những động thái của UBND TP Đông Hà. Phải chờ đến 3 tháng sau khi có chỉ đạo của tỉnh, trùng với lúc nhà ông Thành xây hoàn thiện trên đất ông Phương, vào tháng 3/2012 UBND TP Đông Hà mới có quyết định đình chỉ và xử phạt hành chính ông Thành. Cho đến nay, chính quyền TP Đông Hà cũng chưa giải quyết giữa 2 bên, khi ông Thành vẫn ở trong nhà mới xây trên đất người khác. Và ông Phương vẫn tiếp tục đi kêu cứu đến các cơ quan báo chí.</P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/31102-2e7fd.JPG" _fl="" alt="Sổ đỏ của ông Phương về lô đất 201"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Nhưng sự chậm chạp hay có gì "mập mờ" sau vụ việc của UBND TP Đông Hà khiến gần 1 năm sau khi có tỉnh chỉ đạo mà thành phố vẫn chưa giải quyết được việc ông Phương (ngoài cùng bên phải) đòi lại được miếng đất của mình</SPAN><BR></DIV>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Có một điều nhập nhằng và khó hiểu, là trong bản vẽ hoàn công cắm mốc phân lô, lô xuất phát có số 195 (theo dãy lô số lẻ) đến lô thứ tư là lô số 201, trùng với diện tích trong sổ đỏ đã cấp cho vợ chồng ông Trương Trọng Phương. Trái ngược lại với bản vẽ hoàn công cắm mốc, tại bản quy hoạch phân lô đấu giá, lô xuất phát là lô số 197, mất 1 lô 195, nên đến lô thứ ba là lô số 201. Phần diện tích lô 201 mới này không trùng với diện tích sổ đỏ được giao cho ông Trương Trọng Phương. Điều này chứng minh bản vẽ hoàn công cắm mốc phân lô và bản quy hoạch phân lô đấu giá đã có sự khác nhau!</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Ngày 25/10, PV báo <I style="mso-bidi-font-style: normal">Dân trí</I> đã có cuộc trao đổi với ông Nguyễn Đức Chính - Phó chủ tịch thường trực UBND tỉnh Quảng Trị về vấn đế này.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>“Đây là thiếu sót của UBND TP Đông Hà trong việc chấp hành chỉ đạo của UBND tỉnh. </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Qua kiểm tra, đã xác định ông Thành xây nhà trên lô đất số 201 là không đúng với quy hoạch phân lô, đấu giá. UBND TP Đông Hà đã có quyết định xử phạt vi phạm hành chính, buộc tháo gỡ. Nhưng đến nay quyết định này đang bị ông Thành khiếu nại. <BR></P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Khi Tòa án xem xét xử lý nếu thừa nhận thì quyết định của UBND TP Đông Hà có hiệu lực. Lúc đó, sẽ buộc ông Thành phải thực hiện chế tài nộp phạt hành chính và tháo gỡ công trình. Việc ông Thành có giấy phép xây dựng nhưng không thực hiện đúng theo quy định của pháp luật buộc phải chịu trách nhiệm về hậu quả. </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Quan điểm của UBND tỉnh, sẽ cho kiểm tra lại toàn bộ vụ việc xem chậm ở khâu nào, ai làm chậm, lý do làm sao. Sau khi Tòa án có quyết định chính thức tỉnh sẽ có bước xử lý tiếp theo một cách nghiêm khắc đối với từng cán bộ, phòng ban liên quan đã để xảy ra sự việc. </P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal>Riêng bản quy hoạch phân lô đấu giá và bản vẽ hoàn công cắm mốc phân lô có sự khác nhau, UBND tỉnh sẽ yêu cầu BQLDA CSHT tỉnh, Sở Xây dựng - cơ quan duyệt quy hoạch có văn bản báo cáo cụ thể rồi trả lời cho báo chí sau” – ông Chính cho biết ý kiến.</P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Đại Dương</B></P><input type="hidden" value="/c202s202/diendan-bandoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(61, ' Xóa những “khoảng tối” trong lĩnh vực xuất khẩu lao động ', '-Xoa-nhung-“khoang-toi”-trong-linh-vuc-xuat-khau-lao-dong-', ' <a href = "http://dantri.com.vn/c673/s673-658193/xoa-nhung-khoang-toi-trong-linh-vuc-xuat-khau-lao-dong.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/cvNlsQoYcVFxP1FHlpn/Image/2012/11/nguoiLD2-11a-c1347.jpg" /></a>(Dân trí) - Là người từng dạy tiếng Hàn cho các LĐVN từ những ngày đầu, tôi đã được nghe họ tâm sự bao điều cay đắng về các “khoảng tối”, nhất là về những khoản đóng góp “ngất ngưởng” phải chi ra “lót đường… bay”. Quá buồn là VN ta cứ để tình trạng kéo dài mãi!!!<br><a href="http://dantri.com.vn/c673/s673-657948/XKLD-Chua-benh-lao-dong-V-bo-tron-phai-tu-ca-hai-phia.htm"><b>&nbsp;>>&nbsp;  XKLĐ: Chữa “bệnh” lao động VN bỏ trốn phải từ cả hai phía </b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p><IMG alt="Xóa những “khoảng tối” trong lĩnh vực xuất khẩu lao động" src="http://dantri4.vcmedia.vn/cvNlsQoYcVFxP1FHlpn/Image/2012/11/nguoiLD2-11a-c1347.jpg"></o:p></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><o:p><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Người lao động ở nhiều vùng quê vẫn mong muốn được đi làm việc tại các thị trường thu nhập tốt như Hàn Quốc, Nhật Bản, Đài Loan (ảnh: Thu Uyên, báo CAND)</SPAN></o:p></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Cảm ơn bạn Đô đã dũng cảm nói lên một sự thật đáng buồn đã diễn ra từ rất lâu, vâng rất lâu rồi. Ngay từ những ngày đầu chúng ta có chương trình XKLĐ sang Hàn Quốc (cỡ khoảng 20 năm gì đó), tôi đã từng tham gia giảng dạy tiếng Hàn cho nhiều khóa LĐXK sang Hàn Quốc nên được các học viên chân tình chia sẻ về&nbsp;bao gian nan, nhất là&nbsp;về những khoản đóng góp "ngất ngưởng" mà họ phải cố lo cho bằng&nbsp;được&nbsp;để&nbsp;chi ra nếu muốn đi trót lọt. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Thậm chí có học viên còn kể rằng đã phải thế chấp cả nhà, thế chấp cả thuyền đánh cá hoặc các tài sản có giá trị khác của gia đình. Hoặc thậm chí còn phải vay với lãi suất cao để đóng góp cho các khoản chi phí bị đội lên gấp nhiều lần quy định, cũng chỉ với hy vọng sang đó sẽ kiếm được nhiều tiền hơn&nbsp;ở nhà&nbsp;để&nbsp;trả nợ, và cũng là&nbsp;để còn có cơ hội&nbsp;thay đổi được cuộc đời khốn khó... </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Họ khốn khổ như vậy, thế&nbsp;mà mọi lỗi lầm dường như đều bị ngành chức năng quy hết cho người LĐ. Nói vậy cũng là Đúng nhưng không Đủ. Và cái phần Đủ đó thì đúng như bạn Đô đã chỉ ra các địa chỉ cần tìm. </DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Thật buồn và đáng thương cho những người LĐ giờ đây đã và đang bị mất niềm hy vọng được sang HQ để lao động,&nbsp;để có cơ hội&nbsp;thay đổi được cuộc sống khó khăn nơi quê nhà. </DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Hy vọng các cấp chức năng và những người có trách nhiệm trước hết hãy tự xem xét lại một cách công tâm nhất những gì đang diễn ra trong lĩnh vực XKLĐ. Để từ đó cương quyết và mạnh tay thay đổi. </DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Cần làm vậy trước hết vì vừa giữ được thể diện quốc gia, vừa thực sự là vì dân, vì nước. Đồng thời cũng vì chính chức trách, nhiệm vụ mà họ được Đảng, Nhà nước và nhân dân tin cậy giao phó. </DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Minh Thang<o:p></o:p></B></P><input type="hidden" value="/c673s673/dien-dan/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(62, ' XKLĐ: Chữa “bệnh” lao động VN bỏ trốn phải từ cả hai phía  ', '-XKLĐ:-Chua-“benh”-lao-dong-VN-bo-tron-phai-tu-ca-hai-phia--', ' (Dân trí) - "Ý thức" lại được chỉ rõ đóng vai trò lớn nhất trong những nguyên nhân khiến thị trường lao động Hàn Quốc “đóng băng”, dẫn tới nguy cơ dừng chương trình cấp phép mới (EPS) cho lao động VN, đe dọa gây thiệt hại kinh tế từ thị trường này gần 1 tỷ USD/năm.<br><a href="http://dantri.com.vn/c673/s673-657895/Doi-dong-kinh-gui-Bo-truong-LDTBXH-ve-LDV-bo-tron-tai-Han-Quoc.htm"><b>&nbsp;>>&nbsp;  Đôi dòng kính gửi Bộ trưởng LĐTBXH về LĐVN bỏ trốn tại Hàn Quốc</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><B style="mso-bidi-font-weight: normal"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p><IMG alt="Hàng trăm người dân Nghệ An chen lấn đăng ký thi tuyển tiếng Hàn (ảnh tư liệu: Nguyễn Duy)" src="http://dantri4.vcmedia.vn/cvNlsQoYcVFxP1FHlpn/Image/2012/11/nguoiLD1-11-b0cce.jpg"></o:p></B></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><o:p><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Hàng trăm người dân Nghệ An chen lấn đăng ký thi tuyển tiếng Hàn (ảnh tư liệu: Nguyễn Duy)</SPAN></o:p></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal><STRONG>Đường vòng và... chen ngang</STRONG> </DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Cũng theo các giới chức Cục trưởng Cục Quản lý lao động ngoài nước (Bộ LĐTBXH), phải gánh chịu thiệt hại “trong tầm tay” trước hết chính là 12.000 lao động (LĐ) đã hoàn thiện hồ sơ gửi lên mạng nhưng chưa được chủ sử dụng lao động Hàn Quốc (HQ) lựa chọn, vì Chương trình EPS với VN không được ký tiếp. </DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Rào cản lớn nhất được nêu rõ là do tỉ lệ LĐVN bỏ trốn ra ngoài sống và làm việc bất hợp pháp quá cao (có tháng tới 57%), thì <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>dù người LĐ có viện bất kỳ lý do gì cũng không thể biện minh cho sự thực: ý thức của LĐVN nhìn chung còn kém. Và chắc chắn không ai phản bác điều được Cục trưởng Cục Quản lý lao động ngoài nước Nguyễn Ngọc Quỳnh nhấn mạnh: Không thể vì lợi ích cá nhân của một số người LĐ mà làm ảnh hưởng đến quyền lợi chính đáng của hàng vạn LĐ khác và tới lợi ích quốc gia!</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Vậy nhưng quy định thường là rõ ràng, đầy đủ và hợp lý, hợp tình. Nhưng khi vận dụng vào thực tế thì có lẽ cả từ phía người làm công tác XKLĐ tới các LĐVN muốn đi XKLĐ đều… không thích đường thẳng? Bởi vì nếu cứ đường thẳng (hay đường quang nhỉ?) mà đi thì bên A mất đứt đi các khoản “tế nhị”. Còn bên B&nbsp;nếu cứ phải xếp hàng lần lượt chẳng thể được ưu tiên chen ngang, chen dọc. Chẳng thế mà đã có nhiều bạn đọc&nbsp;phải than trời:&nbsp;Sao dân ta giờ không còn văn hóa xếp hàng nữa rồi!!!&nbsp;</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal">Hung</B> <A href="mailto:manhhungatk@yahoo.com">manhhungatk@yahoo.com</A> cũng một lần nữa nhấn mạnh:</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Các bạn cứ nói là do cơ quan quản lý nhà nước về hoạt động XKLĐ làm việc không tốt, đó cũng là một lý do. Nhưng lý do quan trọng là ý thức của người Việt ta còn kém. Nếu không có Cầu thì làm gì có Cung? Người LĐ cứ đi đúng trình tự XKLĐ, không ai chen ngang lên trước thì cơ quan quản lý LĐ sẽ bắt buộc phải làm đúng. Tại phần lớn người LĐ cứ muốn đi trước, đi nhanh...và đưa tiền cho họ để được như ý mình muốn, thì mất nhiều chi phí là đúng. Vậy hãy nghĩ xem nên trách ai?”</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Dân ta còn nghèo, biết là đi XKLĐ cũng vất vả, khổ sở thật đấy nhưng ít ra lương cũng cao hơn ở nhà (nhiều khi còn thất nghiệp). Vậy nên chỉ tiêu XKLĐ nhiêu chắc cũng chẳng đủ&nbsp;nhu cầu&nbsp;vì số người muốn đi luôn vượt trội. Nhất là với những thị trường được người LĐ ưa chuộng như HQ, Nhật Bản, Đài Loan…Song đúng là rằng hay thì thật là hay, xem ra ngậm đắng nuốt cay cũng nhiều.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Thế nên có lẽ phải những ai từng trải qua cuộc sống thực nơi xứ người mới thấm những tâm sự đắng cay như của <B style="mso-bidi-font-weight: normal">Hoàng Hải Bến</B> <A href="mailto:Hoanghaiben@yahoo.com">Hoanghaiben@yahoo.com</A>:</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Tôi tin chẳng ai muốn ra ngoài kiếm tiền kiểu đó đâu, vì nguy hiểm và rủi ro rất nhiều. Tôi cũng sống cảnh như vậy vài năm ở Đài Loan nên tôi hiểu, nhưng vẫn phải chấp nhận mạo hiểm, vì sao ư? Đơn giản thôi:</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>1 - Bộ LĐTBXH công bố mức phí đi XKLĐ là 1, nhưng người LĐ muốn đi được phải đóng gấp đôi, thậm chí hàng trăm triệu. Số tiền đó rất lớn đối với gia đình nông dân, nhưng Bộ có quản lý nổi không? Sang 3 năm kiếm không lại nổi đã hết hạn phải về. Để đi sang lại ư? Lại phải đóng tiền tiếp nhé, thế thì ai chịu được nên đành phải trốn ra ngoài thôi. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>2 - Ai có đi mới biết, toàn cảnh môi giới đem con bỏ chợ...thôi rồi. Nhưng các khoản cần trừ thì họ trừ đều đặn lắm, LĐ không chạy đi đâu được. Ở nhà bảo là lương được tương đương 15 triệu đồng/tháng, nhưng xin lỗi nhé, tôi khẳng định: Không đúng đâu! Vì đấy là chưa trừ gì, chứ sau khi trừ còn lại tầm 3 – 4 triệu đồng/tháng thôi. Mọi người nhân lên đi xem 3 năm làm bị... trừ đầu trừ đuôi hết, còn đâu mà bỏ ra dành dụm nữa. Tôi thấy hình như mọi người ở nhà toàn bàn chuyện...trên trời, chỉ thấy được cái trước mắt. Cơ quan có chức năng thì chẳng quản lý được, cuối cùng lại chỉ tội dân nghèo thôi!” </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Nhiều LĐ<STRONG> </STRONG>đã và đang ở HQ tiếp tục chia sẻ các kinh nghiệm xương máu, nhưng trong khuôn khổ bài này chúng tôi chỉ có thể chọn trích đưa 1 lên ý kiến đại diện của&nbsp;<STRONG>&nbsp;Nguyen Thanh Do </STRONG><A href="mailto:victory_korea84@yahoo.com.vn">victory_korea84@yahoo.com.vn</A>:</DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Mình hiện đang LĐ tại HQ, xưởng<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>mình có đến 50 người VN mà hầu như ai cũng… nản với phát biểu của bà Bộ trưởng LĐTBXH. Mình sang HQ được hơn 4 năm, dù số tiền mình chi phí để đi sang có hơn 30 triệu, chả thấm thía gì so với nhiều bạn tiền trăm triệu mới đi được. Nhưng mà nhiều lúc nghĩ lại mình mất tiền cho ông trưởng phòng CTHS SV trường học cũng ấm ức, thấy mình khờ. Năm 2008 trường mình được Bộ LĐTBXH cho 70 chỉ tiêu, trường giao xuống khoa, rồi lớp chọn sinh viên (SV), rồi thi chọn mấy lượt, rồi chọn 70 SV thi tiếng Hàn, tất cả đều đậu. Vậy mà ông trưởng phòng đó cũng lấy ít nhất của mỗi người từ 20 đến 50 triệu. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Riêng mình học định hướng xong rồi, chờ hơn 1 tháng chưa đi được, ông lấy lý do: Có lẽ ngoài Bộ LĐTBXH làm <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>thất lạc thủ tục. Mi đưa thầy ít tiền thầy ra coi răng. Thế là mất 5 triệu cho ông ấy. Ai ngờ đợt đó ông đi học cao học ở HN… Rồi đến khi mình có quyết định bay ông lại bảo: “Đem thầy ít tiền để thầy cảm ơn các thầy cô đã dạy dỗ và làm hồ sơ”. Mình thật thà hỏi tầm bao nhiêu nữa… thế là 15 triệu nữa lại… ra đi. Các bạn thấy đấy là một trường ĐH… hẳn hoi, mình cũng là SV loại giỏi, làm công tác XH nữa nên<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>thường xuyên tiếp xúc với ông mà còn như thế. Huống chi là những người phải qua cửa “cò vạc”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal">Thanh Huyền Korea </B><A href="mailto:cutexnhok@gmail.com">cutexnhok@gmail.com</A>&nbsp; nêu rõ&nbsp;nỗi ám ảnh với những người “một đi không muốn trở lại”:</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Không chạy tiền để đi thì chỉ có nước chờ hết hạn rồi đi lấy hộ chiếu về. Không phải ai cũng muốn ra lưu vong đâu. Giám đốc người HQ cũng lắm người nhiều chiêu trò lắm… Có câu "không ở trong chăn làm sao biết chăn có rận"… Nhiều người hết hạn cũng muốn về VN lắm chứ, nhưng câu hỏi đặt ra là:" Về rồi sẽ làm gì? lương bao nhiêu?... Quả thật rất khó tìm câu trả lời cho họ. Chỉ một số ít về vì lí do gia đình hoặc vì họ cũng xác định được việc làm ở VN thôi. Số còn lại không dám đánh đổi mức lương khoảng 20 triệu để lấy mức lương 2 triệu/tháng đâu. Nên thà… bị bắt chứ chẳng ai (đã bỏ trốn) chịu tự nguyện về cả. Cái đó cũng thật khó trách ai. Nhất là khi ở HQ tiền lương thưởng cao hơn hẳn, làm việc gì cũng quy củ… Trong khi đó ở VN thì hầu như đi đâu cũng phải đưa đồng tiền ra trước con người...”</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><IMG alt="Nhu cầu đi LĐXK sang Nhật Bản, Hàn Quốc rất lớn (ảnh minh họa: hoptacquocte.com)" src="http://dantri4.vcmedia.vn/cvNlsQoYcVFxP1FHlpn/Image/2012/11/nguoiLDa1-11-b0cce.jpg"></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Nhu cầu đi LĐXK sang Nhật Bản, Hàn Quốc rất lớn (ảnh minh họa: hoptacquocte.com)</SPAN></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal><STRONG>Biết lợi&nbsp;bất cập hại, nhưng...</STRONG></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Cũng đồng ý với nhận định của các cơ quan chức năng rằng thị trường LĐ HQ "đóng băng" gây tổn thất lớn cho cả đất nước và cá nhân những người LĐVN, song nguyên nhân dẫn tới tỉ lệ LĐVN bỏ trốn cao vẫn được chính những người LĐ nhấn mạnh và chắc cũng được khá nhiều người dân đồng tình rằng: khởi nguồn từ phía những người “nắm đằng chuôi” – họ chẳng cần đi đâu mà vẫn được hưởng lợi nhiều hơn hẳn số phải đổ mồ hôi, sôi nước mắt kiếm sống nơi xứ người. </DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Mà khi&nbsp;cán bộ không gương mẫu thì tuyên truyền vận động bao nhiêu cũng chẳng ăn thua, vì đa phần dân ta ý thức còn thấp, chẳng thể mong nhanh chóng đưa mọi việc vào quy củ ngay được. Tuy nhiên, từ chính thực tế cuộc sống và làm việc, không ít LĐXK cũng tự nhận thức được vấn đề và hơn thế nữa còn đề xuất thêm những giải pháp thiết thực:</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Tôi là&nbsp;LĐVN tại HQ đã về nước, tôi xin nêu ra 1 số nguyên nhân khiến LĐVN tại HQ bỏ trốn hàng loạt như sau: </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>+ 1 là do chính sách thủ tục tuyển người quá nhiều phiền hà, nhất là đối với những người hết hợp đồng về nước. Trong khi đó HQ luôn rất cần người đã có kinh nghiệm làm việc tại đất nước của họ. Chính việc này là nguyên nhân cho các cuộc trốn chạy và sống lưu vong bất hợp pháp tại HQ gia tăng.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>+ 2 là những tiêu cực trong công tác tuyển lựa và các loại dịch vụ “trên trời dưới đất” của các đối tượng cò mồi, khiến người LĐ phải chịu nhiều tốn kém, để đi ra nước ngoài được phải mất rất nhiều tiền. Nên khi đi được họ cảm thấy tiếc cho những đồng tiền đã chi ra, mà nếu về nước đúng hạn thì lại khó có cơ hội đi tiếp. Nên họ đành liều cứ sống bất hợp pháp nơi đất khách với bao rủi ro và phiền toái. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Tôi nghĩ, nếu cơ quan quản lý&nbsp;LĐ ngoài nước làm tốt hơn trong việc tuyển chọn, nghiêm khắc hơn trong việc quản lý, linh hoạt hơn trong việc tái tuyển chọn đối với những LĐ về nước đúng hạn <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>(cụ thể là có thưởng có phạt), thì tôi nghĩ đây không phải là 1 bài toán khó... Nhân đây xin chúc tất cả anh em đang LĐ tại HQ luôn mạnh khỏe và nên về nước đúng hạn, để chúng ta luôn mang tiếng thơm tại các thị trường LĐ ở nước ngoài” - <B style="mso-bidi-font-weight: normal">Trần Trung Chính</B>:<SPAN style="mso-spacerun: yes">&nbsp; </SPAN><A href="mailto:trungchinh2007@yahoo.com">trungchinh2007@yahoo.com</A></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Theo tôi, muốn không còn có hiện tượng LĐVN trốn ra ngoài làm việc bất hợp pháp bên HQ này cũng đơn giản thui? Chỉ cần thưởng cho những ai báo có người bất hợp pháp từ 200 đến 300USD,<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>thì tui đảm bảo trong vòng 1 năm thui bất hợp pháp sẽ không còn 1 người nào nữa. Bản thân tui sắp hết hợp đồng bên này nhưng tui sẽ về, nếu các bên chức năng liên quan đồng ý áp dụng thưởng như vậy, <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>trong 1 tháng tui chắc chắn kiếm được 4.000 USD vì quanh công ty tui số LĐ bất hợp pháp có thể nói là nhiều lắm, nước nào có lẽ cũng có cả chứ&nbsp;không cứ gì riêng LĐVN” - <B style="mso-bidi-font-weight: normal">Nguyen Van Tan</B>:<SPAN style="mso-spacerun: yes">&nbsp; </SPAN><A href="mailto:nguyentan_83@yahoo.com.vn">nguyentan_83@yahoo.com.vn</A></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-spacerun: yes">&nbsp; </SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Hai bàn tay cùng vỗ vào nhau thì mới nên tiếng, sai trái đúng là chẳng chỉ riêng bên nào. Nhưng để chữa những căn bệnh đã&nbsp;thành trầm kha rồi thì&nbsp;vô cùng phức tạp, nhất là khi lợi ích đã được hưởng bao lâu nay giờ bảo tự cắt bỏ chắc chẳng ai tự nguyện cắt…</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Rồi thì nhiều người vẫn ung dung với các triết lý: “Người khôn, của khó”, “Khôn sống, mống chết”, “Chân mình còn lấm bề bề, lại còn cầm đuốc đi rê chân người” làm chi…??? Nhưng muốn chữa dứt “căn bệnh” LĐXKVN bỏ trốn, rõ ràng không thể chỉ từ một phía liên quan!</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Khánh Tùng<o:p></o:p></B></P><input type="hidden" value="/c673s673/dien-dan/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(63, ' Đôi dòng kính gửi Bộ trưởng LĐTBXH về LĐVN bỏ trốn tại Hàn Quốc ', '-Đoi-dong-kinh-gui-Bo-truong-LĐTBXH-ve-LĐVN-bo-tron-tai-Han-Quoc-', ' (Dân trí) - Hôm nay mới xem được hết chương trình "Dân hỏi, Bộ trưởng trả lời" mới nhất. Tự dưng tôi lại thấy hụt hẫng và hơi bức xúc vì thời lượng quá ngắn, các câu hỏi còn chung chung và câu trả lời cũng… quá sức chung chung. Tôi thấy chưa đúng tầm và chưa “đã”.<br><a href="http://dantri.com.vn/c20/s20-656412/Lao-dong-bo-tron-oi-nhuc-nhoi-cua-nganh-Xuat-khau-lao-dong.htm"><b>&nbsp;>>&nbsp;  Lao động bỏ trốn: Nỗi nhức nhối của ngành Xuất khẩu lao động</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="MARGIN: 0in 0in 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p>&nbsp;</o:p></B>Chưa “đã” vì chưa đem lại thông tin giải đáp được nhiều thắc mắc của người lao động (LĐ).</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Ở đây tôi không dám bàn đến cách làm của Bộ trưởng&nbsp;(BT), chỉ nói đến những sự thật chắc chắn ngành LĐTBXH<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>không phải là không biết, mà có lẽ là không nói và không làm trong lĩnh vực xuất khẩu lao động (XKLĐ) riêng sang thị trường Hàn Quốc (HQ) mà&nbsp;thôi.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Tôi cũng muốn nhấn mạnh trước hết rằng: Đừng đổ lỗi hết cho LĐ bất hợp pháp hay cho ý thức chấp hành của người&nbsp;LĐ, mà <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>cũng phải đổ lỗi cho chính sự yếu kém trong công tác quản lý, để xảy ra tham nhũng tràn lan thông qua các quy định người LĐ thấy rõ chỉ “hành dân là chính”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Xin đừng chỉ nói những cụm từ: "nghe nói" "có thể" "vì"... Theo tôi nghĩ, tất cả những điều báo trước cho sự tạm dừng nhận LĐXK VN đã được báo trước. HQ là 1 thị trường lao động tiềm năng cần khai thác, cần phát triển. Nhưng chính “miếng bánh ngon” này đã bị “mổ xẻ” từ rất sớm.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Xin đừng nói "bất ngờ vì tạm dừng", khi thật ra điều đó đã được báo trước lâu rồi. Trước HQ, chẳng phải đã có 1 vài thị trường LĐ khác “quay lưng” với LĐVN đó sao,&nbsp;không phải là những bài học nhãn tiền hay sao mà giờ đây ta mới thấy cần rút kinh nghiệm theo kiểu “nước đến chân mới nhảy”?</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Đừng nên chỉ đổ lỗi cho người LĐ khi bắt đầu đi không biết rõ thủ tục! Cũng đừng nên chỉ đổ lỗi cho gia đình người LĐ không<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>tỉnh táo nắm bắt thông tin hay động viên tư tưởng giúp người LĐ vững vàng…. Mà xin hãy nói THẲNG những chuyện mà chúng tôi chỉ có thể dùng cụm từ là “lập lờ để bòn rút&nbsp;từng đồng tiền của người LĐ” ngay khi họ bắt đầu manh nha ý muốn đến với thị trường HQ.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Việc “ăn” tiền, chúng tôi nhận thấy có ở hầu<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>khắp mọi nơi: từ các giáo viên dạy học (cho LĐVN trước khi đi), các trung tâm, cơ sở nhà nước, cơ sở học định hướng, bệnh viện khám sức khỏe , cấp Sở ... Cho đến khi người LĐ “bay được” cũng lại “ăn” tiếp cả từ tiền valy, quần áo đồng phục. Những sự thực này không rõ bà BT có biết không? BT có tự mình tìm hiểu hoặc chỉ đạo cấp dưới điều tra thật công tâm hay không?</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><IMG alt="Người LĐVN&nbsp;làm thủ tục đăng ký hồ sơ dự kiểm tra tiếng Hàn (ảnh tư liệu, nguồn:&nbsp;CTV)" src="http://dantri4.vcmedia.vn/cvNlsQoYcVFxP1FHlpn/Image/2012/11/LDXK1-11-d5956.jpg"></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Người LĐVN&nbsp;làm thủ tục đăng ký hồ sơ dự kiểm tra tiếng Hàn (ảnh tư liệu, nguồn:&nbsp;CTV)</SPAN></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Theo chúng tôi, nếu BT thật sự muốn làm trong sạch các bộ máy bên dưới của mình, có lẽ BT cũng không cần phải "vi hành" đâu mà chỉ cần đặt "cái bàn" (bí mật) tại sân bay. Bất cứ người LĐ nào khi chuẩn bị lên máy bay, BT cứ <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>cho thực hiện việc thu âm phỏng vấn họ có sự chứng kiến của cơ quan báo chí, Bộ Công an và ngay cả truyền hình trực tiếp cũng được.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Mong BT cứ cho hỏi thẳng họ về chi phí, hỏi thẳng họ ai đã “ăn” tiền và đưa cho ai thêm ngoài chi phí được quy định khi đi XKLĐ sang HQ. Và cũng mong được BT trực tiếp hứa với họ rằng: Sẽ lấy lại ngay số tiền đó trong 1 tuần; rằng cơ quan Công an sẽ vào cuộc ngay; Kể cả người nhận tiền đó có là vị nào ở cấp nào đi chăng nữa cũng bị xử lý; Nếu trong vòng 10 ngày không lấy lại được số tiền đó thì sẽ lấy tiền từ chính quỹ của Bộ ra trả lại.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Được vậy, tôi tin là người LĐ sẽ nói thật hết, không dám dối câu nào vì "nói có sách, mách có chứng". Họ chẳng dám vu oan cho ai, càng không dám dựng chuyện ra đâu.<SPAN style="mso-spacerun: yes">&nbsp;&nbsp;</SPAN>Tôi dám chắc là trong 100&nbsp;LĐ được phỏng vấn phải có đến 88 người khẳng định đã mất gấp 10 lần chi phí theo quy định thì mới đấy mình vào được những "cửa" cần thiết. Dù&nbsp;họ, gia đình họ biết chắc chắn đó là số tiền “ngu dại”, “tiền tươi" - số tiền mà gia đình họ còng lưng vay mượn bà con, ngân hàng… <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>để cho con em đi XKLĐ.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Chính những điều này, những hệ quả này đã kéo dài lâu rồi, ngấm sâu vào máu nhiều người&nbsp;lâu rồi. Cho nên tôi nghĩ, dù BT có ban hành 1.000 đề án vận động đi chăng nữa thì <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>chắc gia đình họ (các LĐVN bỏ trốn), bản thân họ cũng chẳng về. <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>Đây là sự thật tuy "trần trụi" nhưng tôi cho rằng không phải là không có lý do. Và tôi nghĩ, lỗi do người LĐ ở lại bất hợp pháp chỉ 20 %, do quản lý 10%, 70% còn lại do chi phí thực mà người LĐ phải bỏ ra quá cao.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Bà BT có biết là từ rất lâu rồi, khi LĐ qua được đến các trung tâm định hướng bên HQ, họ đã có những đợt điều tra. Họ phát cho mỗi người 1 tờ giấy trong đó có câu hỏi: "Chi phí mất bao nhiêu?" Mỗi tờ giấy đó đến tay người LĐVN đều theo dạng ngẫu nhiên để người LĐ tự ghi ra xem chi phí đi hết bao nhiêu, sau đó trung tâm thu lại. Và liệu BT có ngạc nhiên khi biết theo kết quả điều tra, đa số LĐVN ghi: ít nhất trên 5.000USD.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>BT có biết hay không???</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Về chuyện HQ tạm dừng kí tiếp thỏa thuận, theo tôi, tất cả những người làm việc trong lĩnh vực XKLĐ của VN đều biết nhưng cũng… không thể làm gì khác. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Và yếu tố LĐVN trốn ra ngoài bất hợp pháp được nhấn mạnh. Vâng đúng, đúng hoàn toàn! Nhưng bất hợp pháp do đâu? Câu hỏi này không khó, giải đáp cũng không khó. Vậy “cứu” thị trường LĐ tại HQ này ra sao? Chúng tôi tin là “cứu” được đấy, nhưng liệu BT có tin là ngành LĐTBXH đủ sức làm không?</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Chúng tôi chỉ có một mong muốn: Hãy cứu những gì… còn sót lại, không riêng gì từ thị trường LĐ HQ!</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Nguyễn Khá Đô </B>(<I style="mso-bidi-font-style: normal">từ Hàn Quốc</I>)</P><input type="hidden" value="/c673s673/dien-dan/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(64, ' Xem xét lại việc thẩm định, chuyển đổi vàng ', '-Xem-xet-lai-viec-tham-dinh,-chuyen-doi-vang-', ' (Dân trí) - Vừa rồi tôi đến ngân hàng VA, đưa ra mấy lượng vàng và được nơi đây thẩm định bằng mắt thường rồi họ nói tôi có 2 miếng phải chịu phí gia công lại. Tôi không thể phục được và nghĩ rằng công tác thẩm định, chuyển đổi vàng có vấn đề, cần xem xét.<br><a href="http://dantri.com.vn/c76/s76-657765/Loi-xin-loi-va-nhan-hieu-SC.htm"><b>&nbsp;>>&nbsp;  Lời xin lỗi và nhãn hiệu SJC</b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><B style="mso-bidi-font-weight: normal"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></B></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><o:p><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG src="http://dantri4.vcmedia.vn/cvNlsQoYcVFxP1FHlpn/Image/2012/11/xemvang1-11-7d81b.jpg" alt="(ảnh minh họa)"><BR></SPAN></o:p><o:p><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">(ảnh minh họa)</SPAN></o:p></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Vì ví dụ như với trường hợp vàng của tôi, họ nói cây thì phải chịu phí thay bao bì (đành chấp nhận thôi vì là bao bì kiểu cũ, dù cũng của SJC). Còn có 1 dây 2 x 1 lượng thì có 1 lượng không sao, một lượng thì bị kêu phí 57 ngàn gia công vì “móp méo”. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trong khi tôi chẳng thấy móp méo gì cả vì vẫn nguyên đai nguyên kiện, bao bì kiểu mới của SJC và là tôi cắt ra từ 1 dây 5 x 1 lượng mà tôi rút ra từ 1 ngân hàng khác. Như vậy, trong số những miếng vàng tôi mang đến, người ta bảo tôi có 2 miếng phải chịu phí gia công lại mà tôi không thể phục được. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Vậy thế nào thì gọi là vàng miếng bị cong vênh, bị móp méo, phải gia công lại? Xin vui lòng hướng dẫn người dân và cả các nhân viên thẩm định cho kỹ và công tâm. Trong trường hợp của tôi thì không thể thấy miếng vàng của tôi bị cong vênh, móp méo gì cả. Dù nhân viên ngân hàng cứ cố chỉ trỏ cho tôi, song tôi đã phải mang thêm mắt kính vào mà vẫn không thể nào nhận ra có gì khác thường thì sao gọi là cong vênh, móp méo được.&nbsp;Vậy sao phải gia công lại? </DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Có chăng tình trạng đang cố tình lợi dụng để ép giá, để gây khó dễ cho người dân có nhu cầu chuyển đổi vàng? Ai biết xin trả lời giúp tôi!</DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Trần Hương Duyên<SPAN style="mso-spacerun: yes">&nbsp; </SPAN><o:p></o:p></B></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal align=right><A href="mailto:h.duyen@hcm.vnn.vn">h.duyen@hcm.vnn.vn</A></P><input type="hidden" value="/c673s673/dien-dan/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(65, ' Kỳ lạ chú chó con sinh ra có 2 bộ phận sinh dục, 3 chân ', '-Kỳ-la-chu-cho-con-sinh-ra-co-2-bo-phan-sinh-duc,-3-chan-', ' <a href = "http://dantri.com.vn/c132/s132-658196/ky-la-chu-cho-con-sinh-ra-co-2-bo-phan-sinh-duc-3-chan.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/HvrZZiM2C5QLR4u67tfR/Image/2012/20/2112-fbe55.jpg" /></a>(Dân trí) - Chú chó con giống cái mới sinh ra được 5 tháng tuổi của nhà chị Ngô Thị Thu (thôn Ngọc Anh, xã Phú Thượng, huyện Phú Vang, TT-Huế) rất kỳ lạ với 2 bộ phận sinh dục và 3 chân. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P class=MsoNormal>Chó mẹ sinh ra chú chó con kỳ lạ này trong những đợt sinh nở trước cũng sinh ra 1 chú chó con 3 chân, sau đó đã có người tới xin đem về nuôi. Chú chó con này vẫn sinh hoạt bình thường như những chú con trong bầy của mẹ. “Có điều, nó khôn, mạnh mẽ và tình cảm hơn anh em nó” – chị Thu cho biết.</P>\r\n<P class=MsoNormal>Cái chân bị khuyết là chân phải phía trước. Hai bộ phận sinh dục nằm giữa 2 chân sau (cũng giống với vị trí chó cái thường nhưng có… thêm 1 bộ phận sinh dục nữa). Trong lúc đi lại, chú chó phải nhảy lẩng cẩng để di chuyển bước đi trông rất tội nghiệp.</P>\r\n<P style="TEXT-ALIGN: center" class=MsoNormal></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="Chị Thu bên chú chó cưng với 3 chân, 2 bộ phận sinh dục" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/2111-fbe55.jpg" width=480 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Chị Thu bên chú chó cưng với 3 chân, 2 bộ phận sinh dục</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="Chú chó ngồi với 1 chân trước bên phải không có" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/2112-fbe55.jpg" width=336 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Chú chó ngồi với 1 chân trước bên phải không có</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="và nằm thật dễ thương" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/2113-fbe55.jpg" width=394 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">và nằm thật dễ thương</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="2 bộ phận sinh dục nằm ở 2 chân sau, dưới đuôi" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/2115-fbe55.jpg" width=448 _fl=""><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">2 bộ phận sinh dục nằm ở 2 chân sau, dưới đuôi</SPAN><BR></DIV>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="2 bộ phận sinh dục nằm ở 2 chân sau, dưới đuôi" align=middle src="http://dantri4.vcmedia.vn/HvrZZiM2C5QLR4u67tfR/Image/2012/20/2114-fbe55.jpg" width=313 _fl=""></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Dù có đặc điểm khác thường nhưng chú chó vẫn tỏ ra lanh lẹ và rất khôn</SPAN></DIV>\r\n<P></P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Đại Dương</B></P><input type="hidden" value="/c132s132/chuyenla/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(66, ' Kinh ngạc chú voi biết "nói" tiếng Hàn ', '-Kinh-ngac-chu-voi-biet-"noi"-tieng-Han-', ' (Dân trí) - Một chú voi châu Á có tên gọi Koshik đã làm kinh ngạc giới khoa học về khả năng phát âm tiếng Hàn Quốc của mình. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">&nbsp;<IMG alt="Koshik luôn cho vòi vào miệng khi phát ra âm thanh giống tiếng Hàn Quốc." src="http://dantri4.vcmedia.vn/SF9n0XnWe1mhJ0SanI/Image/2012/11/1-eac50.jpg"><BR>Koshik luôn cho vòi vào miệng khi phát ra âm thanh giống tiếng Hàn Quốc.</SPAN></o:p></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><o:p>&nbsp;</DIV></o:p>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Các nhà nghiên cứu cho biết chú voi đã học bắt chước tiếng người và có thể nói 5 từ tiếng bằng tiếng Hàn, gồm Xin chào, Không, Ngồi xuống, Nằm xuống và Tốt.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Giới chức vườn thú đã đặt đầu vòi voi vào miệng để chuyển âm thanh thấp tự nhiên của chú voi thành tiếng người đầy thuyết phục.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Nghiên cứu đã được đăng tải trên tạp chí Current Biology (Sinh vật học ngày nay).<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Khả năng phát âm của Koshik đã khiến voi giờ đây gia nhập danh sách dài loài vật có thể bắt chước tiếng người, từ vẹt, đến những loài động vật như sư tử biển hay gần đây nhất là trường hợp biết bắt chước tiếng người của một con cá voi.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><B style="mso-bidi-font-weight: normal">Giao tiếp có nghĩa?<o:p></o:p></B></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trưởng nhóm nghiên cứu, Tiến sỹ Angela Stoeger, đại học Vienna, Áo, cho biết lần đầu tiên bà tình cờ biết Koshik sau khi các đoạn video về chú voi ở vườn thú Everland, Hàn Quốc này được đăng tải lên trang chia sẻ hình ảnh YouTube.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Sau khi liên hệ với vườn thú, bà đã tới Hàn Quốc để ghi lại tiếng của chú voi nhằm nghiên cứu về tài năng phát âm khác thường của nó.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Tiến sỹ Stoeger cho biết: “Chúng tôi đã yêu cầu những người bản địa Hàn Quốc, người chưa từng tiếp xúc với con voi trước đây, viết lại những gì họ hiểu khi chúng tôi bật lại băng ghi âm của Koshik”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>“Chúng tôi thấy họ cho ra kết quả khá giống nhau về nghĩa nói chung”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Tiến sỹ Stoeger và đồng nghiệp nhận thấy tiếng của Koshik tương đương với 5 từ tiếng Hàn là xin chào, ngồi xuống, không, nằm xuống và tốt.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Thông thường voi phát ra âm thanh sâu hơn, và đôi khi là tần số thấp, ngoài ngưỡng nghe của con người và các âm thanh này âm vang xa tới nhiều km.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Vì vậy để có thể phát âm ra “tiếng Hàn”, Koshik cần phải cho vòi vào miệng để giúp tạo tiếng giống người. Giới nghiên cứu cho biết điều này chưa từng được thấy trước đó. “Chú voi luôn cho vòi vào miệng mình và sau đó bắt chước tiếng của người quản tượng”, tiến sỹ Stoeger cho hay</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Nhưng mặc dù âm thanh Koshik phát ra có vẻ thuyết phục song các nhà nghiên cứu tin rằng chú voi không hiểu gì những gì chú “nói” ra.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Giới nghiên cứu đánh giá chú voi học nói nhằm xác lập quan hệ với con người. Bởi từ năm 5-12 tuổi, Koshik là chú voi duy nhật tại vườn thú Everland và theo các nhà nghiên cứu, đây là giai đoạn phát triển quan trọng của một con voi.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Tiến sỹ Stoeger giải thích: “Con người là mối liên hệ xã hội duy nhất của chú voi và chúng tôi tin Koshik dùng những âm thanh này như là chức năng để củng cố mối quan hệ xã hội với những người bạn đồng hành của chú, mà ở trường hợp này là con người”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>Giáo sư Klaus Zuberbuehler tại trường Tâm lý và Khoa học thần kinh, đại học <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:place w:st="on">St Andrews</st1:place>, cho rằng đây là phát hiện “khai sáng”. Hiểu được cách động vật bắt chước tiếng người giúp giới khoa học hiểu hơn về ngôn ngữ của con người, hiểu được ngôn ngữ đã phát triển như thế nào.</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><o:p>\r\n<OBJECT id=PlayerAS464f99488d954edb9c0d36b9c31fc321 classid=clsid:D27CDB6E-AE6D-11cf-96B8-444553540000 width=425 height=334><PARAM NAME="_cx" VALUE="11244"><PARAM NAME="_cy" VALUE="8837"><PARAM NAME="FlashVars" VALUE=""><PARAM NAME="Movie" VALUE="http://cache.hosting.vcmedia.vn/?key=464f99488d954edb9c0d36b9c31fc321&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/SF9n0XnWe1mhJ0SanIZw/2012/11/pe_html_b541e525c2.jpg"><PARAM NAME="Src" VALUE="http://cache.hosting.vcmedia.vn/?key=464f99488d954edb9c0d36b9c31fc321&amp;pname=mediaplayer.swf&amp;img=http://video-thumbs.vcmedia.vn/dantri/SF9n0XnWe1mhJ0SanIZw/2012/11/pe_html_b541e525c2.jpg"><PARAM NAME="WMode" VALUE="Transparent"><PARAM NAME="Play" VALUE="-1"><PARAM NAME="Loop" VALUE="-1"><PARAM NAME="Quality" VALUE="High"><PARAM NAME="SAlign" VALUE="LT"><PARAM NAME="Menu" VALUE="-1"><PARAM NAME="Base" VALUE=""><PARAM NAME="AllowScriptAccess" VALUE="always"><PARAM NAME="Scale" VALUE="NoScale"><PARAM NAME="DeviceFont" VALUE="0"><PARAM NAME="EmbedMovie" VALUE="0"><PARAM NAME="BGColor" VALUE="425166"><PARAM NAME="SWRemote" VALUE=""><PARAM NAME="MovieData" VALUE=""><PARAM NAME="SeamlessTabbing" VALUE="1"><PARAM NAME="Profile" VALUE="0"><PARAM NAME="ProfileAddress" VALUE=""><PARAM NAME="ProfilePort" VALUE="0"><PARAM NAME="AllowNetworking" VALUE="all"><PARAM NAME="AllowFullScreen" VALUE="true">\r\n<embed wmode="transparent" height="334" width="425" quality="high" bgcolor="#425166" flashvars="" wmode="transparent" allowfullscreen="true" allowscriptaccess="always" name="464f99488d954edb9c0d36b9c31fc321" id="464f99488d954edb9c0d36b9c31fc321" style="" src="http://cache.hosting.vcmedia.vn/?key=464f99488d954edb9c0d36b9c31fc321&pname=mediaplayer.swf&img=http://video-thumbs.vcmedia.vn/dantri/SF9n0XnWe1mhJ0SanIZw/2012/11/pe_html_b541e525c2.jpg" type="application/x-shockwave-flash"/></embed></OBJECT></o:p></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Phan Anh</B></DIV>\r\n<DIV style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right>Theo <I style="mso-bidi-font-style: normal">BBC</I></P><o:p></o:p></DIV><input type="hidden" value="/c132s132/chuyenla/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(67, ' Gặp người phụ nữ thích lang thang ở nghĩa địa ', '-Gap-nguoi-phu-nu-thich-lang-thang-o-nghia-dia-', ' (Dân trí) - Thường xuất hiện trong trang phục lễ tang thời Victoria (thế kỷ 19), dành hầu hết thời gian lang thang ở các nghĩa địa và thích “bầu bạn” với ma… đó là những nét chính về cô Jeane Trend-Hill. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Sáu năm trước, cô Jean Trend-Hill đến từ thị trấn Langdon Hills, thành phố Essex (Anh) đã từ bỏ công việc là nhân viên điều tra gian lận để dấn thân vào một nghề lạ lùng – nghề săn bia mộ. Và giờ đây, cô dành hầu hết thời gian của mình để “làm việc” tại các nghĩa địa.<?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><IMG alt="Cô Jeane Trend-Hill" src="http://dantri4.vcmedia.vn/kLmtCyYGgph1lHhk1u5h/Image/2012/11/anh5-6ecf1.jpg"></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Cô Jeane Trend-Hill</SPAN></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><BR><BR>Nỗi ám ảnh với nghĩa địa và cái chết bắt đầu ngay từ khi Jeane còn là một cô bé và thường cùng cha mẹ đi viếng mộ người thân. “Khi người lớn trò chuyện và chăm sóc hoa trên mộ, tôi thường đi lang thang khắp nghĩa địa và bị cuốn hút bởi những hình ảnh thiên thần, chim bồ cầu và dấu thập khắc trên các ngôi mộ. Tôi bị mê hoặc bởi vẻ đẹp của chúng. Sau này, khi trưởng thành, tôi bắt đầu chụp các hình ảnh đó chỉ đơn giản là để thỏa mãn niềm yêu thích của riêng tôi”.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Với niềm đam mê tột cùng với những vấn đề huyền bí và cái chết, cô đã xăm lên cánh tay mình một con chim đen – hình ảnh được khắc trên bia mộ của cố danh ca nổi tiếng Amy Winehouse. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><IMG alt="Jeane thích mặc những trang phục từ thờ Victoria" src="http://dantri4.vcmedia.vn/kLmtCyYGgph1lHhk1u5h/Image/2012/11/anh1-6ecf1.jpg"></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Jeane thích mặc những trang phục từ thời Victoria</SPAN></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center>&nbsp;</DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><IMG alt="Jeane thích mặc những trang phục từ thờ Victoria" src="http://dantri4.vcmedia.vn/kLmtCyYGgph1lHhk1u5h/Image/2012/11/anh4-6ecf1.jpg"><BR><BR></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><BR>“Con chim đen này là kỷ niệm gợi nhớ đến ca sỹ Amy Winehouse – người mà tôi thường gặp khi tôi sống ở <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:City w:st="on">Camden</st1:City> (một thị trấn nhỏ ở tây nam <st1:City w:st="on"><st1:place w:st="on">London</st1:place></st1:City>) và cũng là kỷ niệm gợi nhớ tới cha tôi. Kể từ sau khi ông qua đời, tôi luôn nhìn thấy những chú chim màu đen thế này ở những nơi kỳ lạ. Thật dễ chịu khi nghĩ rằng những chú chim đen đó có thể chính là cha tôi đang dõi theo tôi”, Jeane tâm sự.</P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Sau sáu năm vào nghề, công việc hàng ngày của Jeane bây giờ là dẫn các đoàn tìm hiểu và chụp hình về các khu vực bị ma ám và thậm chí là điều tra và nghiên cứu những vấn đề huyền bí, siêu nhiên cho các chương trình truyền hình. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Chia sẻ về những bộ trang phục và trang sức lễ tang thời <st1:State w:st="on">Victoria</st1:State> mà cô thường mặc, Jean cho biết cô “yêu thích tất cả những thứ thuộc về thời <st1:place w:st="on"><st1:State w:st="on">Victoria</st1:State></st1:place> và vẫn sưu tầm album ảnh, trang sức, trang phục, thiệp tang lễ và cả hình xăm tang lễ của thời này”. </P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><IMG alt="Hình xăm con chim đen gợi nhớ tới ca sỹ quá cố Amy Winehouse" src="http://dantri4.vcmedia.vn/kLmtCyYGgph1lHhk1u5h/Image/2012/11/anh3-6ecf1.jpg"></DIV>\r\n<DIV style="MARGIN: 0in 0in 0pt" class=MsoNormal align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Hình xăm con chim đen gợi nhớ tới ca sỹ quá cố Amy Winehouse</SPAN></DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><BR><BR>Gần đây, mọi người quan tâm hơn đến công việc của Jeane khiến cô cảm thấy rất vui mừng và hạnh phúc: “Thật tuyệt khi gần đây mọi người thường muốn sử dụng các bức hình tưởng niệm của tôi để thiết kế các hình xăm”.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trong khi gia đình của Jean hoàn toàn ủng hộ công việc bất thường này của cô thì có một số người lại cho rằng đó là những việc làm “không lành mạnh”. Tuy nhiên, bản thân Jean thì tự tin với công việc của mình vì “bằng việc chụp lại các di tích và viết sách về các nghĩa địa, tôi mang đến cho mọi người những khái niệm cơ bản về phía bên kia thế giới, nhưng theo một cách tích cực chứ không phải là đáng sợ”.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Anh Khôi<o:p></o:p></B></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right>Theo <I style="mso-bidi-font-style: normal">DM</I></P><input type="hidden" value="/c132s132/chuyenla/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0),
(68, ' Kỳ lạ cuộc thi “Vua tinh trùng” ở Trung Quốc ', '-Kỳ-la-cuoc-thi-“Vua-tinh-trung”-o-Trung-Quoc-', ' (Dân trí) - Nhằm khuyến khích việc hiến tặng tinh trùng và cải thiện sức khỏe sinh sản ở nam giới, Bệnh viện Từ thiện Quảng Châu (Trung Quốc) mới đây đã tổ chức cuộc thi “Vua tinh trùng” dành cho các đấng mày râu. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Với giải nhất trị giá 20.000 Nhân dân tệ (tương đương khoảng 70 triệu đồng), giải nhì là một iPhone4 và giải ba là một iPad3, hàng trăm nam giới Trung Quốc đã hào hứng tham dự cuộc thi này. <?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Trong nhiều năm trở lại đây, Trung Quốc đang phải đối mặt với tình trạng thiếu người hiến tặng tinh trùng do một số lý do về văn hóa và đạo đức. Theo thông tin do một cán bộ của Bệnh viện Từ thiện Quảng Châu cho biết, gần đây việc ngân hàng tinh trùng ở Quảng Châu thông báo khả năng sinh sản của nam giới đang ngày càng suy giảm đã gây hoang mang và lo lắng trong dư luận. <o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Chính vì thế, bệnh viện này đã quyết định tổ chức cuộc thi “Vua tinh trùng” Quảng Châu để giúp nam giới hiểu về sức khỏe thể chất của họ, giúp họ tránh ngại ngùng khi đề cập đến vấn đề này và cũng giúp nâng cao hiểu biết của chính các đấng mày râu và cả cộng đồng về sức khỏe sinh sản nam giới.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal>Được biết Bệnh viện Từ thiện Quảng Châu đã thành lập phòng thí nghiệm vô sinh từ tháng 5 năm 2012 trong đó bao gồm cả hoạt động “Cấp chứng nhận bản lĩnh đàn ông” dựa trên sức khỏe tinh trùng của đấng mày râu.<o:p></o:p></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><o:p>&nbsp;</o:p></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right><B style="mso-bidi-font-weight: normal">Anh Khôi<o:p></o:p></B></P>\r\n<P style="TEXT-ALIGN: right; MARGIN: 0in 0in 0pt" class=MsoNormal align=right>Theo <I style="mso-bidi-font-style: normal">Live</I></P><input type="hidden" value="/c132s132/chuyenla/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n</div>\r', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Công khai', '0', 0, 0, '', 0, 0);
