INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(1, 'R Phó Giám đốc Vườn quốc gia Yok Đôn quan hệ với... lâm tặc ', 'R-Pho-Giam-doc-Vuon-quoc-gia-Yok-Đon-quan-he-voi...-lam-tac-', '<p><a href="http://dantri.com.vn/c20/s20-660683/pho-giam-doc-vuon-quoc-gia-yok-don-quan-he-voi-lam-tac.htm"><img border="0" alt="" src="http://dantri21.vcmedia.vn/zoom/130_100/x8SuE6cccccccccccccJ/Image/2012/11/Anh-2-bc5de-73275.JPG" /></a>(D&acirc;n tr&iacute;) - Trong B&aacute;o c&aacute;o số 912/BC-VYD của Vườn quốc gia Yok Đ&ocirc;n &ldquo;về t&igrave;nh h&igrave;nh thực hiện nhiệm vụ v&agrave; c&ocirc;ng t&aacute;c quản l&yacute; c&aacute;n bộ&rdquo;, ng&agrave;y 3/11/2012, gửi Tổng cục L&acirc;m nhiệp đề cập đến việc &ldquo;cần phải chấn chỉnh mạnh, nếu kh&ocirc;ng chuyển biến n&ecirc;n thay đổi 1 hoặc 2 ph&oacute; gi&aacute;m đốc Vườn&rdquo;.<br />\r\n<a href="http://dantri.com.vn/c20/s20-634695/Quan-the-voi-rung-VQG-Yok-Don-se-khong-con-ben-vung.htm"><b>&nbsp;&gt;&gt;&nbsp;Quần thể voi rừng VQG Yok Đ&ocirc;n sẽ kh&ocirc;ng c&ograve;n bền vững </b></a><br />\r\n<a href="http://dantri.com.vn/c20/s20-633943/Khung-khiep-nan-pha-rung-tai-Vuon-quoc-gia-Yok-Don.htm"><b>&nbsp;&gt;&gt;&nbsp;&ldquo;Khủng khiếp&rdquo; nạn ph&aacute; rừng tại Vườn quốc gia Yok Đ&ocirc;n</b></a></p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p align="center" style="TEXT-ALIGN: left">B&aacute;o c&aacute;o n&agrave;y ghi r&otilde;: &ldquo;Đặc biệt c&aacute;c ph&oacute; gi&aacute;m đốc do năng lực l&atilde;nh đạo, điều h&agrave;nh c&ograve;n nhiều giới hạn, t&iacute;nh c&aacute;ch nặng về quyền lợi c&aacute; nh&acirc;n, chưa t&iacute;ch cực tham gia c&ocirc;ng t&aacute;c quản l&yacute; điều h&agrave;nh, tham mưu đề xuất c&aacute;c giải ph&aacute;p để ổn định v&agrave; ph&aacute;t triển đơn vị, kết quả l&agrave;m vi<span lang="VI">ệ</span>c k&eacute;m hiệu quả, quan hệ với l&acirc;m tặc kh&aacute; nhiều&hellip; n&ecirc;n trong qu&aacute; tr&igrave;nh l&atilde;nh đạo, chỉ đạo điều h&agrave;nh kh&ocirc;ng ki&ecirc;n quyết, thậm ch&iacute; bao che người sai tr&aacute;i, tr&ugrave; dập người t&iacute;ch cực&rdquo;.</p>\r\n<p>Từ ph&acirc;n t&iacute;ch tr&ecirc;n, b&aacute;o c&aacute;o n&agrave;y cũng ghi r&otilde;: &ldquo;2 ph&oacute; gi&aacute;m đốc hi&ecirc;n nay kh&ocirc;ng ph&ugrave; hợp<span lang="VI">, </span>cần phải chấn chỉnh mạnh, nếu kh&ocirc;ng chuyển biến n&ecirc;n thay đổi 1 hoặc 2 ph&oacute; gi&aacute;m đốc Vườn&rdquo;.</p>\r\n<p>&nbsp;</p>\r\n<div style="TEXT-ALIGN: center"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><img width="420" align="middle" alt="Gỗ tại vườn quốc gia Yok Đ&ocirc;n bị l&acirc;m tặc cưa hạ v&agrave;o" _fl="" src="http://dantri4.vcmedia.vn/x8SuE6cccccccccccccJ/Image/2012/11/Anh-2-bc5de-73275.JPG" style="MARGIN: 5px" /></span></div>\r\n<div style="TEXT-ALIGN: center"><span style="LINE-HEIGHT: 150%"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Gỗ tại vườn quốc gia Yok Đ&ocirc;n bị l&acirc;m tặc cưa hạ v&agrave;o <span style="BACKGROUND-COLOR: white; FONT-SIZE: 10pt">cuối th&aacute;ng 7, đầu th&aacute;ng 8/2012 VQG Yok Đ&ocirc;n, tỉnh Đắk Lắk.</span></span><span style="FONT-SIZE: 12pt"><!--?xml:namespace prefix = o /--><o:p></o:p></span></span></div>\r\n<p>&nbsp;</p>\r\n<p>Trước đ&oacute;, đầu th&aacute;ng 8/2012, sau khi nhận được tin b&aacute;o của quần ch&uacute;ng nh&acirc;n d&acirc;n về t&igrave;nh trạng ph&aacute; rừng tại Vườn quốc gia Yok Đ&ocirc;n, một nh&oacute;m ph&oacute;ng vi&ecirc;n của c&aacute;c b&aacute;o trung ương v&agrave; địa phương đ&atilde; x&acirc;m nhập thực tế v&ugrave;ng l&otilde;i VQG Yok Đ&ocirc;n. Ngay sau đ&oacute; một loạt c&aacute;c b&agrave;i ph&oacute;ng sự n&oacute;ng hổi về t&igrave;nh h&igrave;nh ph&aacute; rừng tại Vườn quốc gia n&agrave;y được phơi b&agrave;y tr&ecirc;n c&aacute;c mặt b&aacute;o. Ph&oacute; thủ tướng Ho&agrave;ng Trung Hải đ&atilde; chỉ đạo Tổng cục l&acirc;m nghiệp l&agrave;m r&otilde; vẫn đề b&aacute;o ch&iacute; n&ecirc;u. Tuy nhi&ecirc;n, hiện theo phản &aacute;nh của người d&acirc;n, t&igrave;nh trạng ph&aacute; rừng tại Vườn quốc gia Yok Đ&ocirc;n vẫn đang &acirc;m ỉ tiếp diễn.</p>\r\n<p>Được biết, VQG Yok Đ&ocirc;n c&oacute; diện t&iacute;ch rừng gần 115.000 h&eacute;c ta, trải d&agrave;i tr&ecirc;n địa b&agrave;n 2 tỉnh Đắk Lắk v&agrave; Đắk N&ocirc;ng, l&agrave; một trong những khu rừng đặc dụng c&oacute; diện t&iacute;ch lớn nhất Việt Nam.</p>\r\n<p align="right"><b>V.Hảo</b></p>\r\n<input type="hidden" id="hidNextUsing" value="/c20s20/xa-hoi/trang-1.htm" /></div>\r\n<p>&lt;</p>', 'pv1', '2012-11-09 00:00:00', '2012-11-10 08:48:17', 'Đã duyệt', ',11,16,', 1, 0, '<p><img width="130" height="100" src="/unc/userfiles/image/Anh-2-bc5de-73275.JPG" alt="" /></p>', 0, 0),
(2, ' Ô tô tông liên tiếp 3 xe máy rồi lao thẳng xuống hồ ', '-Ô-to-tong-lien-tiep-3-xe-may-roi-lao-thang-xuong-ho-', '<p>(D&acirc;n tr&iacute;) - Những người ngồi tr&ecirc;n 3 xe m&aacute;y đ&atilde; được đưa đến Bệnh viện Đa khoa tỉnh L&acirc;m Đồng cấp cứu sau khi bị một &ocirc; t&ocirc; lưu th&ocirc;ng chiều ngược lại đ&acirc;m li&ecirc;n ho&agrave;n.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p></p>\r\n<div style="TEXT-ALIGN: center"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><img width="420" align="middle" style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/x8SuE6cccccccccccccJ/Image/2012/11/DSC_0620-c99e1.JPG" _fl="" alt="Cơ quan chức năng trục vớt chiếc xe &ocirc; t&ocirc; &ldquo;l&agrave;m loạn&rdquo; trước khi lao xuống hồ Xu&acirc;n Hương." /><br />\r\n</span><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Cơ quan chức năng trục vớt chiếc xe &ocirc; t&ocirc; &ldquo;l&agrave;m loạn&rdquo; trước khi lao xuống hồ Xu&acirc;n Hương.</span></div>\r\n<p></p>\r\n<p>Th&ocirc;ng tin ban đầu, vụ tai nạn xảy ra tr&ecirc;n đường Trần Quốc Toản, đoạn gần cầu &ocirc;ng Đạo b&ecirc;n bờ hồ Xu&acirc;n Hương, khoảng 19h30, ng&agrave;y 8/11.</p>\r\n<p>V&agrave;o thời điểm tr&ecirc;n, chiếc xe con 4 chỗ mang BKS 50Z - 8619 lưu th&ocirc;ng với tốc độ nhanh, đ&acirc;m một loạt 3 chiếc xe m&aacute;y mang BKS 49N1-7859; 19R1-3525; 78H1-021.00 lưu th&ocirc;ng chiều ngược lại.</p>\r\n<p>Hậu quả, những người ngồi tr&ecirc;n 3 xe gắn m&aacute;y đều bị thương kh&aacute; nặng, được người đi đường nhanh ch&oacute;ng đến cấp cứu tại Bệnh viện Đa khoa tỉnh L&acirc;m Đồng.</p>\r\n<p>Sau khi g&acirc;y tai nạn li&ecirc;n ho&agrave;n, chiếc xe &ocirc; t&ocirc; lao thẳng xuống hồ Xu&acirc;n Hương, TP Đ&agrave; Lạt, L&acirc;m Đồng. 3 thanh ni&ecirc;n ngồi tr&ecirc;n xe &ocirc; t&ocirc; may mắn bơi được v&agrave;o bờ, tho&aacute;t nạn.</p>\r\n<p>Tại hiện trường vụ tai nạn, chiếc xe &ocirc; t&ocirc; ch&igrave;m nghỉm dưới nước; tr&ecirc;n bờ l&agrave; 3 chiếc xe gắn m&aacute;y vỡ n&aacute;t, trong đ&oacute; 2 chiếc bị hất văng b&ecirc;n lề đường. Vụ tai nạn khiến nhiều người đi dạo đ&ecirc;m quanh hồ Xu&acirc;n Hương được một phen th&oacute;t tim, giao th&ocirc;ng ở khu vực n&agrave;y cũng &aacute;ch tắc cục bộ.</p>\r\n<p>Lực lượng c&ocirc;ng an th&agrave;nh phố Đ&agrave; Lạt đ&atilde; nhanh ch&oacute;ng c&oacute; mặt tại hiện trường sau khi tai nạn xảy ra, giải tỏa hiện trường, trục vớt chiếc xe &ocirc; t&ocirc; nằm dưới hồ.</p>\r\n<p>Hiện nguy&ecirc;n nh&acirc;n vụ tai nạn đang được cơ quan chức năng điều tra, l&agrave;m r&otilde;.</p>\r\n<p align="right"><b>Viết Hảo</b></p>\r\n</div>', 'admin', '2012-11-09 00:00:00', '2012-11-11 12:44:45', 'Công khai', ',16,18,', 1, 0, '<p><img width="420" height="295" src="/unc/userfiles/DSC_0620-c99e1.JPG" alt="" /></p>', 0, 0),
(3, ' Miền Bắc tăng nhiệt mạnh, Nam Bộ mưa rào ', '-Mien-Bac-tang-nhiet-manh,-Nam-Bo-mua-rao-', '<p>(D&acirc;n tr&iacute;) - V&ugrave;ng &aacute;p thấp lạnh th&ecirc;m suy yếu khiến nền nhiệt miền Bắc tăng mạnh, khu vực H&agrave; Nội ban ng&agrave;y nắng n&oacute;ng nhẹ, đ&ecirc;m v&agrave; s&aacute;ng sớm chuyển m&aacute;t.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p class="MsoNormal">Chuy&ecirc;n gia Trung t&acirc;m Dự b&aacute;o kh&iacute; tượng thủy văn TƯ cho biết, trong 2-3 ng&agrave;y tới nền nhiệt c&aacute;c địa phương miền Bắc c&ograve;n tiếp tục tăng th&ecirc;m do khối kh&ocirc;ng kh&iacute; lạnh đ&atilde; suy yếu. Khu vực v&ugrave;ng n&uacute;i cao cũng chấm dứt r&eacute;t về đ&ecirc;m, chuyển se lạnh; v&ugrave;ng Đồng bằng Bắc bộ trong đ&oacute; c&oacute; H&agrave; Nội ng&agrave;y nắng n&oacute;ng nhẹ, đ&ecirc;m v&agrave; s&aacute;ng sớm chuyển m&aacute;t.</p>\r\n<p class="MsoNormal"><i>Chi tiết thời tiết c&aacute;c địa phương tr&ecirc;n cả nước ng&agrave;y v&agrave; đ&ecirc;m 9/11 như sau:</i></p>\r\n<p class="MsoNormal">Ph&iacute;a t&acirc;y Bắc bộ<span class="thoitietvung">, c&oacute; mưa r&agrave;o v&agrave; gi&ocirc;ng v&agrave;i nơi; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">20 - 23 độ, </span></strong><span class="thoitietvung">cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">31 - 34 độ, </span></strong>ph&iacute;a đ&ocirc;ng Bắc bộ; n<span class="thoitietvung">hiều m&acirc;y, c&oacute; mưa r&agrave;o v&agrave;i nơi, trưa chiều giảm m&acirc;y trời nắng; ri&ecirc;ng v&ugrave;ng n&uacute;i ph&iacute;a bắc s&aacute;ng c&oacute; mưa, mưa r&agrave;o rải r&aacute;c v&agrave; c&oacute; nơi c&oacute; gi&ocirc;ng; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">23 - 26 độ, v&ugrave;ng n&uacute;i c&oacute; nơi 21 - 23 độ, </span></strong><span class="thoitietvung">cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">29 - 32 độ, v&ugrave;ng n&uacute;i 27 &ndash; 29 độ.</span></strong></p>\r\n<p class="MsoNormal">Khu vực H&agrave; Nội<span class="thoitietvung">, c&oacute; mưa v&agrave;i nơi, trưa chiều giảm m&acirc;y trời nắng; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">24 -27 độ, </span></strong><span class="thoitietvung">cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">30 - 33 độ</span></strong></p>\r\n<p class="MsoNormal">Từ Thanh H&oacute;a đến Thừa Thi&ecirc;n Huế<span class="thoitietvung">, ng&agrave;y nắng, đ&ecirc;m kh&ocirc;ng mưa; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">23 - 26 độ, </span></strong><span class="thoitietvung">cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">30 - 33 độ.</span></strong></p>\r\n<p class="MsoNormal">Đ&agrave; Nẵng đến B&igrave;nh Thuận, <span class="thoitietvung">c&oacute; mưa r&agrave;o v&agrave; gi&ocirc;ng v&agrave;i nơi; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">23 - 26</span></strong><span class="thoitietvung"> độ, cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">29 - 32 độ.</span></strong></p>\r\n<p class="MsoNormal">T&acirc;y Nguy&ecirc;n<span class="thoitietvung">, c&oacute; mưa r&agrave;o v&agrave; gi&ocirc;ng v&agrave;i nơi; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">19 - 22 độ, </span></strong><span class="thoitietvung">cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">29 - 32 độ.</span></strong></p>\r\n<p class="MsoNormal">Nam Bộ<span class="thoitietvung">, c&oacute; mưa r&agrave;o v&agrave; gi&ocirc;ng v&agrave;i nơi; nhiệt độ thấp nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">24 - 27 độ, </span></strong><span class="thoitietvung">cao nhất </span><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">31 - 34 độ.</span></strong></p>\r\n<p align="right" class="MsoNormal" style="TEXT-ALIGN: right"><strong><span style="FONT-WEIGHT: normal; mso-bidi-font-weight: bold">&nbsp;</span>Phạm Thanh</strong></p>\r\n<input type="hidden" id="hidNextUsing" value="/c20s20/xa-hoi/trang-1.htm" /></div>', 'admin', '2012-11-09 00:00:00', '2012-11-09 02:07:10', 'Công khai', ',18,17,', 0, 0, '<p><img width="1920" height="1200" alt="" src="/unc/userfiles/image/wallpaper-4353.jpg" /></p>', 0, 0),
(4, ' TPHCM: Kiến nghị thu phí đỗ xe ô tô theo giờ ', '-TPHCM:-Kien-nghi-thu-phi-do-xe-o-to-theo-gio-', '<p>(D&acirc;n tr&iacute;) - Nhằm tr&aacute;nh t&igrave;nh trạng nhiều người biến l&ograve;ng đường th&agrave;nh nơi đỗ xe, đậu suốt cả ng&agrave;y, UBND quận 1 đ&atilde; đề xuất kiến nghị thu ph&iacute; đỗ xe &ocirc; t&ocirc; theo giờ thay v&igrave; theo lượt như hiện nay. Sở Giao th&ocirc;ng Vận tải cũng đồng &yacute; với kiến nghị n&agrave;y.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p align="left" style="mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Theo UBND quận 1, hiện nhu cầu đậu &ocirc; t&ocirc; tại khu vực trung t&acirc;m quận 1 rất cao n&ecirc;n th&agrave;nh phố cho ph&eacute;p sử dụng l&ograve;ng đường một số tuyến đường l&agrave;m nơi đỗ xe, giải quyết nhu cầu của kh&aacute;ch v&atilde;ng lai c&oacute; c&ocirc;ng việc phải giải quyết tại khu vực n&agrave;y. Tuy nhi&ecirc;n, hiện c&oacute; một số lượng lớn &ocirc; t&ocirc; dừng đậu rất l&acirc;u (từ 7&nbsp;- 22 tiếng đồng hồ mỗi ng&agrave;y) tr&ecirc;n c&aacute;c tuyến đường c&oacute; thu ph&iacute;, dẫn đến qu&aacute; tải chỗ đậu xe cho kh&aacute;ch v&atilde;ng lai c&oacute; nhu cầu.</p>\r\n<p align="left" style="mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Sở dĩ c&oacute; t&igrave;nh trạng tr&ecirc;n l&agrave; do mức thu ph&iacute; đậu &ocirc; t&ocirc; hiện nay chỉ 5.000 đồng/lượt. V&igrave; vậy, chủ phương tiện kh&ocirc;ng gửi xe trong c&aacute;c t&ograve;a nh&agrave; hoặc b&atilde;i giữ xe c&oacute; thu ph&iacute; m&agrave; đổ ra đường, biến chỗ đỗ xe tạm thời th&agrave;nh nơi gửi xe.</p>\r\n<p align="center" style="TEXT-ALIGN: center; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">&nbsp;</p>\r\n<div align="center"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><img width="448" align="middle" style="MARGIN: 5px" src="http://dantri4.vcmedia.vn/ZIBfP10h1bNGDgMRbGR/Image/2012/11/dauxeduoilongduong_9.11.2012-dc518.jpg" _fl="" alt="TPHCM: Kiến nghị thu ph&iacute; đỗ xe &ocirc; t&ocirc; theo giờ" /></span></div>\r\n<div align="center" startcont="this"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Hiện nhiều chủ xe biến nơi đỗ xe c&oacute; thu ph&iacute; tại c&aacute;c tuyến đường th&agrave;nh nơi để xe cả ng&agrave;y lẫn đ&ecirc;m (ảnh minh họa)</span></div>\r\n<p>&nbsp;</p>\r\n<p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Trước t&igrave;nh trạng n&agrave;y, UBND quận 1 đề xuất thu ph&iacute; đỗ xe tr&ecirc;n đường theo giờ với mức ph&iacute; cụ thể như sau:</p>\r\n<div align="center">\r\n<table cellspacing="0" cellpadding="0" border="1" style="BORDER-BOTTOM: medium none; BORDER-LEFT: medium none; BORDER-COLLAPSE: collapse; BORDER-TOP: medium none; BORDER-RIGHT: medium none; mso-border-alt: solid windowtext .5pt; mso-yfti-tbllook: 480; mso-padding-alt: 0in 5.4pt 0in 5.4pt; mso-border-insideh: .5pt solid windowtext; mso-border-insidev: .5pt solid windowtext" class="MsoTableGrid">\r\n    <tbody>\r\n        <tr style="mso-yfti-irow: 0; mso-yfti-firstrow: yes">\r\n            <td width="151" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: windowtext 1pt solid; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 113.4pt; PADDING-RIGHT: 5.4pt; BORDER-TOP: windowtext 1pt solid; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><b style="mso-bidi-font-weight: normal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Loại xe</span></b></p>\r\n            </td>\r\n            <td width="96" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 1in; PADDING-RIGHT: 5.4pt; BORDER-TOP: windowtext 1pt solid; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><b style="mso-bidi-font-weight: normal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">2 giờ đầu</span></b></p>\r\n            </td>\r\n            <td width="132" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 99pt; PADDING-RIGHT: 5.4pt; BORDER-TOP: windowtext 1pt solid; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><b style="mso-bidi-font-weight: normal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Mỗi giờ tiếp theo</span></b></p>\r\n            </td>\r\n        </tr>\r\n        <tr style="mso-yfti-irow: 1">\r\n            <td width="151" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: windowtext 1pt solid; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 113.4pt; PADDING-RIGHT: 5.4pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">&Ocirc; t&ocirc; dưới 16 chỗ</span></p>\r\n            </td>\r\n            <td width="96" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 1in; PADDING-RIGHT: 5.4pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">10.000 đồng</span></p>\r\n            </td>\r\n            <td width="132" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 99pt; PADDING-RIGHT: 5.4pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">10.000 đồng</span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style="mso-yfti-irow: 2">\r\n            <td width="151" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: windowtext 1pt solid; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 113.4pt; PADDING-RIGHT: 5.4pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">từ 16 đến dưới 30 chỗ</span></p>\r\n            </td>\r\n            <td width="96" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 1in; PADDING-RIGHT: 5.4pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">15.000 đồng</span></p>\r\n            </td>\r\n            <td width="132" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 99pt; PADDING-RIGHT: 5.4pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">15.000 đồng</span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style="HEIGHT: 6.7pt; mso-yfti-irow: 3; mso-yfti-lastrow: yes">\r\n            <td width="151" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: windowtext 1pt solid; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 113.4pt; PADDING-RIGHT: 5.4pt; HEIGHT: 6.7pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">từ 30 chỗ ngồi trở l&ecirc;n</span></p>\r\n            </td>\r\n            <td width="96" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 1in; PADDING-RIGHT: 5.4pt; HEIGHT: 6.7pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">20.000 đồng</span></p>\r\n            </td>\r\n            <td width="132" valign="top" style="BORDER-BOTTOM: windowtext 1pt solid; BORDER-LEFT: medium none; PADDING-BOTTOM: 0in; PADDING-LEFT: 5.4pt; WIDTH: 99pt; PADDING-RIGHT: 5.4pt; HEIGHT: 6.7pt; BORDER-TOP: medium none; BORDER-RIGHT: windowtext 1pt solid; PADDING-TOP: 0in; mso-border-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-top-alt: solid windowtext .5pt">\r\n            <p style="TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">20.000 đồng</span></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p align="left" style="mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Về kiến nghị tr&ecirc;n, Sở GTVT cho biết Sở thống nhất l&agrave; cần thiết phải thu ph&iacute; đỗ xe &ocirc; t&ocirc; theo giờ nhằm hạn chế t&igrave;nh trạng một số phương tiện lợi dụng việc thu ph&iacute; theo lượt để dừng đỗ l&acirc;u dưới l&ograve;ng đường m&agrave; kh&ocirc;ng thực hiện việc gửi xe tại c&aacute;c điểm tr&ocirc;ng giữ xe c&ocirc;ng cộng, hoặc trong c&aacute;c t&ograve;a nh&agrave; c&oacute; b&atilde;i giữ xe. Qua đ&oacute;, g&oacute;p phần giảm &ugrave;n tắc giao th&ocirc;ng, tăng nguồn thu cho ng&acirc;n s&aacute;ch Nh&agrave; nước.</p>\r\n<p align="left" style="mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Tuy nhi&ecirc;n, để việc triển khai thực hiện th&iacute; điểm được thuận lợi, Sở GTVT đề nghị UBND quận 1 phải x&acirc;y dựng đề &aacute;n thu ph&iacute; đỗ xe theo giờ tr&ecirc;n c&aacute;c tuyến đường do m&igrave;nh quản l&yacute;, tr&igrave;nh UBND TP xem x&eacute;t, quyết định.</p>\r\n<p align="left" style="mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Ngo&agrave;i ra, để tạo cơ sở ph&aacute;p l&yacute; thu ph&iacute; đỗ xe &ocirc; t&ocirc; theo giờ, Sở T&agrave;i ch&iacute;nh phải phối hợp với Sở Tư ph&aacute;p tham mưu UBND TP văn bản kiến nghị Bộ T&agrave;i ch&iacute;nh điều chỉnh Th&ocirc;ng tư số 97/2006/TT-BTC theo hướng thu ph&iacute; dừng, đỗ xe &ocirc; t&ocirc; theo thời gian thay v&igrave; theo số lần đỗ xe v&agrave; thu theo phương ph&aacute;p lũy tiến để g&oacute;p phần giảm &ugrave;n tắc giao th&ocirc;ng, lấy nguồn thu đầu tư cho cơ sở hạ tầng giao th&ocirc;ng.</p>\r\n<p align="left" style="mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal">Theo Sở GTVT th&igrave; việc sửa đổi n&agrave;y kh&ocirc;ng vi phạm ph&aacute;p luật v&igrave; v&agrave;o ng&agrave;y 13/8/2010, Bộ T&agrave;i ch&iacute;nh đ&atilde; ban h&agrave;nh hướng dẫn: &ldquo;C&aacute;c mức ph&iacute; cụ thể được x&acirc;y dựng theo phương ph&aacute;p cố định hay phương ph&aacute;p lũy tiến l&agrave; vấn đề kỹ thuật, kh&ocirc;ng vi phạm c&aacute;c quy định ph&aacute;p luật trong lĩnh vực ph&iacute;, lệ ph&iacute;&rdquo;.</p>\r\n<p align="right" style="TEXT-ALIGN: right; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto" class="MsoNormal"><b style="mso-bidi-font-weight: normal">T&ugrave;ng Nguy&ecirc;n</b></p>\r\n<input type="hidden" value="/c20s20/xa-hoi/trang-1.htm" id="hidNextUsing" /></div>', 'admin', '0000-00-00 00:00:00', '2012-11-09 02:05:45', 'Công khai', ',16,18,', 0, 0, '<p><img width="448" height="256" alt="" src="/unc/userfiles/image/dauxeduoilongduong_9_11_2012-dc518.jpg" /></p>', 0, 0),
(5, ' "Nga thay Tổng Tham mưu trưởng các lực lượng vũ trang" ', '-Nga-thay-Tong-Tham-muu-truong-cac-luc-luong-vu-trang-', '<p><a href="http://dantri.com.vn/c36/s36-660714/nga-thay-tong-tham-muu-truong-cac-luc-luong-vu-trang.htm"><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/87f1qPhjcalNI3wAqb6p/Image/2012/11/makarov-cc0b8.jpg" alt="" /></a>(D&acirc;n tr&iacute;) &ndash; Tổng Tham mưu trưởng ki&ecirc;m Thứ trưởng thứ nhất Bộ Quốc ph&ograve;ng Nga, Đại tướng Nikolai Makaro, sẽ trở th&agrave;nh người thứ hai bị c&aacute;ch chức trong đợt cải tổ qu&acirc;n đội mạnh mẽ nhất tại nước n&agrave;y, chỉ hai ng&agrave;y sau khi Bộ trưởng Quốc ph&ograve;ng Anatoly Serdiukov bị c&aacute;ch chức.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p align="center" style="TEXT-ALIGN: center" class="MsoNormal">&nbsp;</p>\r\n<div align="center">&nbsp;</div>\r\n<b style="mso-bidi-font-weight: normal">\r\n<div align="center"><img width="450" align="middle" style="MARGIN: 5px" alt="Nga thay Tổng Tham mưu trưởng c&aacute;c lực lượng vũ trang" src="http://dantri4.vcmedia.vn/87f1qPhjcalNI3wAqb6p/Image/2012/11/makarov-cc0b8.jpg" _fl="" /></div>\r\n</b>\r\n<div align="center"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><b style="mso-bidi-font-weight: normal">&nbsp; </b>Đại tướng Nikolai Makaro nối g&oacute;t Bộ trưởng Serdiukov ra đi trong cuộc cải tổ qu&acirc;n đội mạnh mẽ của Tổng thống ki&ecirc;m Tổng Tư lệnh c&aacute;c lực lượng vũ trang Nga Vladimir Putin.</span></div>\r\n<p>&nbsp;</p>\r\n<p class="MsoNormal">&nbsp;</p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">T&acirc;n Hoa x&atilde; dẫn nguồn tin chưa được kiểm chứng&nbsp;kiểm chứng cho biết quyết định được đưa ra sau cuộc gọp mới nhất của t&acirc;n Bộ trưởng Quốc ph&ograve;ng Nga Sergey Shoigu, người vừa được chỉ định thay Bộ trưởng Serdiukov hồi tuần trước. Theo đ&oacute;&nbsp;Thứ trưởng Quốc ph&ograve;ng Makaro sẽ bị c&aacute;ch chức v&agrave; thay thế &ocirc;ng l&agrave; Thượng tướng Valery Gherasimov, cựu Ph&oacute; Tổng Tham mưu trưởng ki&ecirc;m Tư lệnh qu&acirc;n khu trung t&acirc;m.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Quyết định được đưa ra trong cuộc họp giữa t&acirc;n Bộ trưởng Shoigu v&agrave; c&aacute;c cấp ph&oacute; của &ocirc;ng với ban l&atilde;nh đạo Bộ Tổng tham mưu c&aacute;c lực lượng vũ trang Li&ecirc;n bang Nga. Th&agrave;nh phần của ban l&atilde;nh đạo gồm c&aacute;c ph&oacute; Tổng tham mưu trưởng, tư lệnh c&aacute;c qu&acirc;n khu, hạm đội, c&aacute;c Tổng cục trưởng trực thuộc Bộ Quốc ph&ograve;ng Nga. </span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Tuy nhi&ecirc;n tin kh&ocirc;ng cho biết l&yacute; do Đại tướng Makaro bị c&aacute;ch chức.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Thượng tướng Gherasimov sinh ng&agrave;y 8/9/1955 tại Kazan, từng tốt nghiệp Học viện qu&acirc;n sự Suvorov, Học viện tăng-thiết gi&aacute;p Kazan v&agrave; Malinov, Học viện cấp cao trực thuộc Bộ Tổng tham mưu c&aacute;c lực lượng vũ trang Li&ecirc;n bang Nga.</span></p>\r\n<div class="MsoNormal"><span style="FONT-SIZE: 12pt">Trước đ&oacute;, Tổng thống ki&ecirc;m Tổng Tư lệnh c&aacute;c lực lượng vũ trang Nga, &ocirc;ng Vladimir Putin, cũng đ&atilde; quyết định c&aacute;ch chức Bộ trưởng Quốc ph&ograve;ng Serdiukov v&agrave; bổ nhiệm Tỉnh trưởng M&aacute;txcơva Sergey Shoigu thay thế. </span></div>\r\n<div class="MsoNormal">&nbsp;</div>\r\n<div class="MsoNormal"><span style="FONT-SIZE: 12pt">Dư luận Nga cho rằng nguy&ecirc;n nh&acirc;n khiến cả hai nh&agrave; l&atilde;nh đạo cao nhất Bộ Quốc ph&ograve;ng bị c&aacute;ch chức c&oacute; thể c&oacute; li&ecirc;n quan đến t&igrave;nh trạng tham nhũng lan tr&agrave;n trong qu&acirc;n đội Nga, l&agrave;m suy giảm đ&aacute;ng kể khả năng sẵn s&agrave;ng chiến đấu cũng như c&ocirc;ng cuộc hiện đại h&oacute;a c&aacute;c lực lượng vũ trang của nước n&agrave;y. </span></div>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Giả thiết n&agrave;y của dư luận tr&ugrave;ng với tuy&ecirc;n bố của Tổng thống Putin đưa ra ngay sau đ&oacute; rằng t&acirc;n bộ trưởng quốc ph&ograve;ng Shoigu phải c&oacute; trọng tr&aacute;ch bảo đảm cho c&aacute;c lực lượng vũ trang Li&ecirc;n bang Nga ph&aacute;t triển năng động, nhanh ch&oacute;ng ho&agrave;n th&agrave;nh c&aacute;c đơn đặt h&agrave;ng quốc ph&ograve;ng đ&atilde; bị qu&aacute; hạn, đồng thời đẩy nhanh kế hoạch tham vọng về t&aacute;i trang bị cho qu&acirc;n đội Nga.</span></p>\r\n<div align="right" style="TEXT-ALIGN: right" class="MsoNormal"><span style="FONT-SIZE: 12pt"><b style="mso-bidi-font-weight: normal">Việt Giang </b></span></div>\r\n<div align="right" style="TEXT-ALIGN: right" class="MsoNormal"><span style="FONT-SIZE: 12pt">Theo <i style="mso-bidi-font-style: normal">Xinhua </i></span></div>\r\n<object id="ieooui" classid="clsid:38481807-CA0E-42D2-BF39-B33AF135CC4D"></object> <style type="text/css">\r\nst1:*{behavior:url(#ieooui) }\r\n</style><input type="hidden" value="/c36s36/Thegioi/trang-1.htm" id="hidNextUsing" /></div>\r\n<p>&lt;</p>', 'admin', '2012-11-09 00:00:00', '2012-11-09 11:23:08', 'Công khai', ',17,', 1, 0, '<p><img border="0" alt="" src="http://dantri21.vcmedia.vn/zoom/130_100/87f1qPhjcalNI3wAqb6p/Image/2012/11/makarov-cc0b8.jpg" /></p>', 0, 0),
(6, ' Little Sài Gòn lần đầu có thị trưởng gốc Việt ', '-Little-Sai-Gon-lan-dau-co-thi-truong-goc-Viet-', '<p>(D&acirc;n tr&iacute;) - Với chiến thắng trong cuộc bầu cử tại th&agrave;nh phố Westminster hồi giữa tuần, &ocirc;ng Tri Ta đ&atilde; trở th&agrave;nh thị trưởng gốc Việt đầu ti&ecirc;n tại th&agrave;nh phố nổi tiếng với khu Little Saigon.<br />\r\n<a href="http://dantri.com.vn/c36/s36-658413/Thanh-pho-My-sap-co-thi-truong-goc-Viet-dau-tien.htm"><b>&gt;&gt;Th&agrave;nh phố Mỹ sắp c&oacute; thị trưởng gốc Việt đầu ti&ecirc;n?</b></a></p>', '<div class="fon34 mt3 mr2 fon43">\r\n<div style="text-align: center">\r\n<div><img alt="&Ocirc;ng Tri Ta &ndash; thị trưởng gốc việt đầu ti&ecirc;n tại th&agrave;nh phố Mỹ" align="middle" width="450" _fl="" style="margin: 5px" src="http://dantri4.vcmedia.vn/yT0YJzvK8t63z214dHr/Image/2012/11/Tri-Ta-5ff70.jpg" /></div>\r\n</div>\r\n<div style="text-align: center"><span style="font-family: Tahoma; font-size: 10pt"> INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(8, ' Hàn Quốc tố Triều Tiên sắp thử hạt nhân và tên lửa tầm xa  ', '-Han-Quoc-to-Trieu-Tien-sap-thu-hat-nhan-va-ten-lua-tam-xa--', '<p>(D&acirc;n tr&iacute;) - Bộ trưởng Quốc ph&ograve;ng H&agrave;n Quốc Kim Kwan-Jin h&ocirc;m qua cho biết Triều Ti&ecirc;n đ&atilde; ho&agrave;n tất c&ocirc;ng t&aacute;c chuẩn bị cho một vụ thử hạt nh&acirc;n kh&aacute;c v&agrave; ph&oacute;ng thử c&aacute;c t&ecirc;n lửa tầm xa trong tương lai.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p style="TEXT-ALIGN: center" class="MsoNormal" align="center"></p>\r\n<div align="center"><img style="MARGIN: 5px" align="middle" src="http://dantri4.vcmedia.vn/87f1qPhjcalNI3wAqb6p/Image/2012/11/Ten-lua-Trieu-Tien-19153.jpg" width="450" _fl="" alt="Một vụ ph&oacute;ng t&ecirc;n lửa của Triều Ti&ecirc;n." /></div>\r\n<br />\r\n<p></p>\r\n<p style="TEXT-ALIGN: center" class="MsoNormal" align="center"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Một vụ ph&oacute;ng t&ecirc;n lửa của Triều Ti&ecirc;n.</span></p>\r\n<p style="TEXT-ALIGN: center" class="MsoNormal" align="center"><span style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><br />\r\n</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Ph&aacute;t biểu với c&aacute;c ph&oacute;ng vi&ecirc;n, &ocirc;ng Kim Kwan-Jin n&oacute;i: Nhiều c&ocirc;ng t&aacute;c chuẩn bị đ&atilde; được thực hiện cho vụ thử hạt nh&acirc;n thứ ba, song thời gian tiến h&agrave;nh c&ograve;n phụ thuộc v&agrave;o một quyết định ch&iacute;nh trị.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">&Ocirc;ng Kim Kwan-Jin cũng dự đo&aacute;n B&igrave;nh Nhưỡng sẽ nỗ lực tiến h&agrave;nh ph&oacute;ng thử t&ecirc;n lửa tầm xa v&agrave;o một thời điểm n&agrave;o đ&oacute; sau khi vụ ph&oacute;ng t&ecirc;n lửa hồi th&aacute;ng 4 thất bại.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Hồi th&aacute;ng 9, trang web <i style="mso-bidi-font-style: normal">38 North</i> của Mỹ đăng c&aacute;c bức ảnh vệ tinh cho thấy Triều Ti&ecirc;n đ&atilde; ngừng c&ocirc;ng việc tại một khu vực c&oacute; khả năng ph&oacute;ng c&aacute;c t&ecirc;n lửa li&ecirc;n lục địa, l&agrave;m dấy l&ecirc;n khả năng dự &aacute;n n&agrave;y c&oacute; thể bị ho&atilde;n tới 2 năm.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Trang web của Viện Mỹ-H&agrave;n kh&ocirc;ng cho biết nguy&ecirc;n nh&acirc;n dừng c&ocirc;ng tr&igrave;nh tr&ecirc;n, mặc d&ugrave; c&oacute; một số &yacute; kiến cho rằng mưa to c&oacute; thể l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y tr&igrave; ho&atilde;n.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Theo trang web n&agrave;y, bệ ph&oacute;ng mới được x&acirc;y dựng ở ph&iacute;a Đ&ocirc;ng Bắc Triều Ti&ecirc;n dự kiến sẽ ho&agrave;n tất v&agrave;o khoảng năm 2015. Tuy nhi&ecirc;n, ngay cả trong trường hợp việc ho&agrave;n c&ocirc;ng chậm trễ, B&igrave;nh Nhưỡng vẫn c&oacute; thể ph&oacute;ng thử t&ecirc;n lửa tầm xa tại một căn cứ kh&aacute;c ở ph&iacute;a T&acirc;y Bắc nước n&agrave;y.</span></p>\r\n<p class="MsoNormal"><span style="FONT-SIZE: 12pt">Hồi th&aacute;ng Tư, Triều Ti&ecirc;n từng tiến h&agrave;nh một vụ ph&oacute;ng t&ecirc;n lửa tầm xa mang theo vệ tinh địa tĩnh nhưng bất th&agrave;nh. Vụ việc đ&atilde; g&acirc;y phản ứngcủa c&aacute;c nước khu vực Đ&ocirc;ng Bắc &Aacute; v&agrave; l&agrave;m dấy l&ecirc;n quan ngại về một cuộc chiến tranh c&oacute; thể xảy ra ở B&aacute;n đảo Triều Ti&ecirc;n, khu vực duy nhất tr&ecirc;n thế giới về mặt kỹ thuật vẫn c&ograve;n trong t&igrave;nh trạng chiến tranh.<br />\r\n</span></p>\r\n<div style="TEXT-ALIGN: right" class="MsoNormal" align="right"><span style="FONT-SIZE: 12pt"><b style="mso-bidi-font-weight: normal">Linh Giang</b></span></div>\r\n<div style="TEXT-ALIGN: right" class="MsoNormal" align="right"><span style="FONT-SIZE: 12pt">Theo <i style="mso-bidi-font-style: normal">AFP</i></span></div>\r\n<input type="hidden" value="/c36s36/Thegioi/trang-1.htm" id="hidNextUsing" /></div>\r\n<p>&lt;</p>', '', '0000-00-00 00:00:00', '2012-11-15 11:31:02', 'Chưa duyệt', ',42,43,44,', 0, 0, '<p><img src="/unc/userfiles/image/Jellyfish.jpg" alt="" /></p>', 0, 0),
(9, ' Liverpool, Atletico Madrid thua thảm, Inter giành vé sớm ', '-Liverpool,-Atletico-Madrid-thua-tham,-Inter-gianh-ve-som-', '<p><a href="http://dantri.com.vn/c26/s26-660749/liverpool-atletico-madrid-thua-tham-inter-gianh-ve-som.htm"><img border="0" alt="" src="http://dantri21.vcmedia.vn/zoom/130_100/lnqpQeBYCt45LSBySE/Image/2012/11/1-7c556.jpg" /></a>(D&acirc;n tr&iacute;) - Th&ecirc;m một chiến thắng ấn tượng nữa đ&atilde; gi&uacute;p Inter Milan ch&iacute;nh thức gi&agrave;nh v&eacute; đi tiếp v&agrave;o v&ograve;ng knock-out tại Europa League năm nay. Trong khi đ&oacute;, những ứng vi&ecirc;n s&aacute;ng gi&aacute; kh&aacute;c l&agrave; Liverpool hay Atletico Madrid đều phải hứng chịu thất bại.</p>', '<div class="fon34 mt3 mr2 fon43">\r\n<p><b>Anzhi 1-0 Liverpool: Th&acirc;n nước Nga, hồn để ở&hellip;Stamford Bridge</b></p>\r\n<p>C&aacute;ch đ&acirc;y 2 tuần, d&ugrave; được thi đấu tại Anfield v&agrave; đ&atilde; tung ra một đội h&igrave;nh rất mạnh th&igrave; thầy tr&ograve; Brendan Rodgers cũng chỉ c&oacute; thể vượt qua &ldquo;g&atilde; nh&agrave; gi&agrave;u&rdquo; Anzhi với gi&acirc;y ph&uacute;t tỏa s&aacute;ng của Stewart Downing. Bởi vậy, khi phải h&agrave;nh qu&acirc;n đến nước Nga trong bối cảnh cất h&agrave;ng loạt trụ cột ở nh&agrave; để chuẩn bị cho trận đại chiến với Chelsea tại Stamford Brigde thứ Bảy tới, kh&ocirc;ng qu&aacute; ngạc nhi&ecirc;n trước việc <i>The Kop</i> bị Eto&rsquo;o c&ugrave;ng c&aacute;c đồng đội đả bại.</p>\r\n<p align="center"><span style="font-family: Tahoma; font-size: 10pt"><img alt="Đội h&igrave;nh gồm nhiều cầu thủ trẻ v&agrave; dự bị của Liverpool đ&atilde; thất bại tr&ecirc;n đất Nga" src="http://dantri4.vcmedia.vn/lnqpQeBYCt45LSBySE/Image/2012/11/1-7c556.jpg" /><br />\r\nĐội h&igrave;nh gồm nhiều cầu thủ trẻ v&agrave; dự bị của Liverpool đ&atilde; thất bại tr&ecirc;n đất Nga</span></p>\r\n<p>Trận thua tại nước Nga n&agrave;y khiến Liverpool đ&aacute;nh mất ng&ocirc;i đầu bảng A tại Europa League năm nay v&agrave;o tay Anzhi. C&ugrave;ng với chiến thắng ấn tượng của Young Boys trước Udinese ngay tại Friuli, cục diện bảng đấu vẫn c&ograve;n rất căng thẳng khi cả 4 đội đều c&ograve;n cơ hội đi tiếp. Điểm số lần lượt của Anzhi, Liverpool, Young Boys v&agrave; Udinese l&agrave; 7,6,6,4 điểm.</p>\r\n<p>Trở lại với diễn biến của cuộc so t&agrave;i diễn ra tại Moscow, Brendan Rodgers đ&atilde; thể hiện r&otilde; &yacute; định dồn to&agrave;n lực cho mặt trận Premier League của m&igrave;nh bằng việc tung ra một đội h&igrave;nh với 9 cầu thủ người Anh, trong đ&oacute; c&oacute; rất nhiều gương mặt trẻ v&agrave; độ tuổi trung b&igrave;nh chỉ chưa đầy 24.</p>\r\n<p>B&ecirc;n ph&iacute;a Anzhi, khi được thi đấu tr&ecirc;n s&acirc;n nh&agrave; Stadion Lokomotiv với đầy đủ c&aacute;c hảo thủ quan trọng như Samba, Zhirkov hay Eto&rsquo;o, ch&iacute;nh họ mới l&agrave; những người nắm giữ thế chủ động tr&ecirc;n s&acirc;n sau tiếng c&ograve;i khai cuộc.</p>\r\n<p>V&igrave; vậy, trong gần như to&agrave;n bộ hiệp một, c&aacute;c cầu thủ trẻ như Wisdom, Flanagan, Coady, Shelvey v&agrave; những &ldquo;dự bị chuy&ecirc;n nghiệp&rdquo; kiểu Joe Cole, Downing, Coates&hellip;chỉ biết căng m&igrave;nh l&ecirc;n chống đỡ c&aacute;c đợt h&atilde;m th&agrave;nh của Anzhi. Thi thoảng, c&aacute;c vị kh&aacute;ch cũng đưa ra được một v&agrave;i pha phản c&ocirc;ng song đều rất nhạt nh&ograve;a bởi chất lượng của những gương mặt tr&ecirc;n h&agrave;ng c&ocirc;ng.<br />\r\n<br />\r\nTrong khi đ&oacute;, khung th&agrave;nh của Brad Jones thường bị đặt dưới t&igrave;nh trạng b&aacute;o động cao khi m&agrave; những t&igrave;nh huống ăn b&agrave;n mười mươi như của Eto&rsquo;o ở ph&uacute;t 35 sau đường chuyền từ b&ecirc;n c&aacute;nh tr&aacute;i của Zhirkov l&agrave; kh&ocirc;ng hiếm.</p>\r\n<p>Tuy nhi&ecirc;n, cuối c&ugrave;ng th&igrave; điều cần đến cũng đ&atilde; phải đến, ph&uacute;t b&ugrave; giờ thứ nhất của hiệp 1, ch&acirc;n s&uacute;t trẻ tuổi Lacina Traore c&oacute; pha xử l&iacute; b&oacute;ng cực k&igrave; đẳng cấp để vượt qua sự truy cản của hai hậu vệ Liverpool. Trong t&igrave;nh huống đối mặt với Brad Jones, trung phong 22 tuổi người Bờ Biển Ng&agrave; nhanh ch&acirc;n tung ra c&uacute; lốp b&oacute;ng tinh tế, <b>mở tỉ số cho Anzhi</b>. Đ&acirc;y đ&atilde; l&agrave; b&agrave;n thắng thứ 4 trong 6 trận đấu gần nhất của t&agrave;i năng đang l&ecirc;n n&agrave;y.</p>\r\n<p align="center"><span style="font-family: Tahoma; font-size: 10pt"><img alt="Anzhi c&oacute; chiến thắng xứng đ&aacute;ng để leo l&ecirc;n ng&ocirc;i đầu bảng A" src="http://dantri4.vcmedia.vn/lnqpQeBYCt45LSBySE/Image/2012/11/2-7c556.jpg" /><br />\r\nAnzhi c&oacute; chiến thắng xứng đ&aacute;ng để leo l&ecirc;n ng&ocirc;i đầu bảng A</span></p>\r\n<p>Bước sang hiệp hai, &ldquo;lữ đo&agrave;n đỏ&rdquo; d&ugrave; bị dẫn nhưng cũng chẳng c&oacute; vẻ g&igrave; l&agrave; quyết t&acirc;m lật ngược thế cờ v&agrave; những sự thay đổi của Brendan Rodgers chỉ l&agrave; giải ph&aacute;p t&igrave;nh thế. Anzhi vẫn l&agrave; nắm ưu thế tr&ecirc;n s&acirc;n v&agrave; su&yacute;t ch&uacute;t nữa, Traore tạo n&ecirc;n một si&ecirc;u phẩm kh&aacute;c bằng c&uacute; &ldquo;n&atilde; đại b&aacute;c&rdquo; từ ngo&agrave;i v&ograve;ng cấm địa ở ph&uacute;t 60.</p>\r\n<p>Ph&uacute;t 76, Liverpool khiến 15.000 CĐV tại Stadion Lokomotiv giật m&igrave;nh khi tạo n&ecirc;n một t&igrave;nh huống s&oacute;ng gi&oacute; trước khung th&agrave;nh của Vladimir Gabulov. Suso đi b&oacute;ng rồi dứt điểm quyết đo&aacute;n từ cự li 25 m&eacute;t khiến thủ m&ocirc;n của Anzhi kh&ocirc;ng thể bắt d&iacute;nh b&oacute;ng. Tuy nhi&ecirc;n, Joe Cole lại đ&aacute; nối qu&aacute; hiền trong pha b&oacute;ng sau đ&oacute; v&agrave; để Gabulov cản ph&aacute; được.</p>\r\n<p>Anzhi đưa ra c&acirc;u trả lời ngay sau đ&oacute; 1 ph&uacute;t với t&igrave;nh huống Traore tho&aacute;t xuống mặt đối mặt với Brad Jones nhưng lần n&agrave;y, anh đ&atilde; dứt điểm sạt cột dọc. Những ph&uacute;t c&ograve;n lại, cả hai b&ecirc;n đều kh&ocirc;ng tạo n&ecirc;n được nhiều cơ hội đ&aacute;ng ch&uacute; &yacute; v&agrave; 1-0 l&agrave; tỉ số cuối c&ugrave;ng của trận đấu. 3 điểm xứng đ&aacute;ng d&agrave;nh cho thầy tr&ograve; Guus Hiddink c&ugrave;ng ng&ocirc;i đầu bảng A.</p>\r\n<p><b><i>Đội h&igrave;nh ra s&acirc;n:</i></b></p>\r\n<p><b>Anzhi Makhachkala:</b> Vladimir Gabulov, Logashov, Samba, Joao Carlos, Tagirbekov, Ahmedov (Carcela-Gonzalez 19) (Lakhiyalov 90), Jucilei, Zhirkov, Etoo, Boussoufa, Traore (Smolov 80).</p>\r\n<p><b>Liverpool: </b>Jones, Wisdom, Carragher, Coates, Flanagan, Henderson, Coady (Fernandez Saez 35), Cole (Assaidi 77), Shelvey, Downing, Morgan (Pacheco 61).</p>\r\n<p><b>Partizan Belgrade 1-3 Inter Milan: Nerazzurri gi&agrave;nh v&eacute; sớm</b></p>\r\n<p></p>\r\n<p>Phong độ tuyệt vời của thầy tr&ograve;<a name="OLE_LINK3">Andrea Stramaccioni </a>tiếp tục được nối d&agrave;i th&ecirc;m khi đả bại Partizan Belgrade <a name="OLE_LINK2"></a><a name="OLE_LINK1">ngay tr&ecirc;n đất Serbia. Như vậy, Inter Milan đ&atilde; c&oacute; trận thắng thứ 10 li&ecirc;n tiếp </a>của m&igrave;nh v&agrave; chuỗi ng&agrave;y tươi đẹp của họ vẫn chưa c&oacute; dấu hiệu dừng lại.</p>\r\n<p>Kh&ocirc;ng đưa ra đội h&igrave;nh mạnh nhất khi thiếu vắng nhưng &ldquo;&ocirc;ng lớn&rdquo; của Italia vẫn tỏ ra qu&aacute; mạnh so với Partizan Belgrade. Ph&uacute;t 19, Guarin thử vận may bằng c&uacute; s&uacute;t rất căng ở cự li 20 m&eacute;t nhưng kh&ocirc;ng tr&uacute;ng đ&iacute;ch.</p>\r\n<p align="center"><span style="font-family: Tahoma; font-size: 10pt"><img alt="Palacio - Guarin rực s&aacute;ng để mang về trận thắng thứ 10 cho Inter" src="http://dantri4.vcmedia.vn/lnqpQeBYCt45LSBySE/Image/2012/11/3-7c556.jpg" /><br />\r\nPalacio - Guarin rực s&aacute;ng để mang về trận thắng thứ 10 cho Inter</span></p>\r\n<p>Cơ hội đ&aacute;ng ch&uacute; &yacute; nhất của đội chủ nh&agrave; trong hiệp 1 thuộc về Ivanov với c&uacute; đ&aacute;nh đầu nguy hiểm ở ph&uacute;t 31 sau t&igrave;nh huống đ&aacute; phạt b&ecirc;n c&aacute;nh tr&aacute;i nhưng Handanovic đ&atilde; chơi tỉnh t&aacute;o v&agrave; h&oacute;a giải th&agrave;nh c&ocirc;ng.</p>\r\n<p>Bước sang hiệp hai, Inter Milan thực sự b&ugrave;ng nổ khi HLV Stramaccioni đưa Palacio v&agrave;o s&acirc;n để thay cho Nagatomo. Hiệu quả đến rất nhanh khi ở ph&uacute;t 51, ch&acirc;n s&uacute;t người Argentina dễ d&agrave;ng đưa b&oacute;ng v&agrave;o lưới trống sau đường chuyền dọn cỗ của Guarin.</p>\r\n<p>Chưa dừng lại ở đ&oacute;, vẫn l&agrave; bộ đ&ocirc;i Guarin - Palacio phối hợp với nhau trong một pha phản c&ocirc;ng nhanh ở ph&uacute;t 75. Palacio ph&aacute; bẫy việt vị tho&aacute;t xuống v&agrave; dứt điểm ch&eacute;o g&oacute;c ch&iacute;nh x&aacute;c n&acirc;ng tỉ số l&ecirc;n 2-0 cho c&aacute;c vị kh&aacute;ch.</p>\r\n<p>Sau đ&oacute;, đến lượt người chơi rất hay kể từ đầu trận Guarin c&oacute; b&agrave;n thắng cho ri&ecirc;ng c&aacute; nh&acirc;n anh ở ph&uacute;t 87. Partizan Belgrade chỉ gỡ lại được 1 b&agrave;n danh dự nhờ c&ocirc;ng của Tomic ở những ph&uacute;t b&ugrave; giờ. Với trận thắng n&agrave;y, <i>Nerazzurri</i> c&ugrave;ng với Rubin Kazan đ&atilde; ch&iacute;nh thức nắm tay nhau bước v&agrave;o v&ograve;ng knock-out với th&agrave;nh t&iacute;ch 10 điểm/4 trận trong khi Neftchi c&ugrave;ng Partizan Belgrade chỉ mới gi&agrave;nh được vẻn vẹn 1 điểm.</p>\r\n<p><b><i>Đội h&igrave;nh ra s&acirc;n:</i></b></p>\r\n<p><strong>Partizan Belgrade:</strong>Petrovic; Miljkovic, Ostojic, Ivanov, Lazevski; Medo (M Scepovic 55), Smiljanic; Tomic, S Ilic (Jojic 30), L Markovic; S Scepovic (Mitrovic 69)</p>\r\n<p><strong>Inter:</strong>Handanovic; Jonathan, Silvestre, Juan Jesus, Nagatomo (Palacio 46); Zanetti, Mudingayi (Gargano 13), Cambiasso, Guarin, Alvaro Pereira; Livaja (Cassano 77)</p>\r\n<p>Ở một v&agrave;i trận đấu đ&aacute;ng ch&uacute; &yacute; kh&aacute;c, ĐKVĐ Atletico Madrid đứt chuỗi 16 trận thắng li&ecirc;n tiếp tại trời &Acirc;u của m&igrave;nh khi th&uacute;c thủ 0-2 tr&ecirc;n s&acirc;n của Academica Coimbra . Napoli đ&egrave; bẹp Dnipro Dpetrovsk với tỉ số 4-2 trong một ng&agrave;y m&agrave; Edinson Cavani đ&atilde; lập một c&uacute; &ldquo;poker&rdquo; (4 b&agrave;n).<br />\r\n<br />\r\n<b><i>Kết quả c&aacute;c trận đấu hấp dẫn kh&aacute;c:</i></b></p>\r\n<p>Marseille 2 - 2 Monchengladbach</p>\r\n<p>Club Brugge 2 - 2 Newcastle</p>\r\n<p>Athletic Bilbao 2 - 3 Lyon</p>\r\n<p>Lazio 3 - 0 Panathinaikos</p>\r\n<p>Tottenham 3-1 Maribor</p>\r\n<p>Bayer Leverkusen 3 - 0 Rapid Vienna</p>\r\n<p align="right"><b>Nguyễn Huy</b></p>\r\n<input id="hidNextUsing" type="hidden" value="/c26s26/The-Thao/trang-1.htm" /></div>\r\n<p>&lt;</p>', 'pvtest', '0000-00-00 00:00:00', '2012-11-16 01:53:33', 'Công khai', ',16,18,', 0, 0, '<p><img alt="" src="/unc/userfiles/image/beautiful_lake_wallpaper_15e9c.jpg" /></p>', 0, 0),
(10, ' HLV Wenger quyết ngăn chặn sự “tháo chạy” của Walcott ', '-HLV-Wenger-quyet-ngan-chan-su-“thao-chay”-cua-Walcott-', ' (Dân trí) - Phát biểu trước báo giới, HLV Wenger khẳng định rằng ông sẽ làm mọi cách để giữ chân Walcott và không muốn lặp lại thêm những trường hợp của Fabregas hay Van Persie. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P>Hợp đồng giữa Walcott và Arsenal sẽ hết hạn vào tháng 6/2013, điều đó đồng nghĩa rằng nếu “Pháo thủ” không gia hạn hợp đồng với cầu thủ này trước tháng 1/2013, Walcott hoàn toàn có quyền tiếp cận với bất kỳ đội bóng nào (Arsenal sẽ không thu được khoản phí nào từ thương vụ này).</P>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=center src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/11/wenger-walcott-8077f.jpg" width=450 _fl="" alt="HLV Wenger không muốn lặp lại thêm những trường hợp của Fabregas hay Van Persie"></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">HLV Wenger không muốn lặp lại thêm những trường hợp của Fabregas hay Van Persie</SPAN></DIV>\r\n<DIV align=center>&nbsp;</DIV>\r\n<P>Tuy nhiên, tới lúc này, thương vụ gia hạn hợp đồng với tiền đạo người Anh của Arsenal vẫn đi vào bế tắc, như thường lệ, nguyên nhân chính vẫn nằm ở mức lương “chưa thỏa đáng”. Để trấn an dư luận, ngày hôm qua, HLV Wenger đã đăng đàn khẳng định Arsenal sẽ tìm mọi cách để giữ chân Walcott.</P>\r\n<P>Trên trang web chính thức của Arsenal, “giáo sư” khẳng định: “Tôi rất muốn Theo Walcott ở lại đội bóng và Arsenal sẽ tìm mọi cách để giữ chân cậu ấy và hy vọng rằng mọi chuyện sẽ diễn ra tốt đẹp.</P>\r\n<P>Chúng tôi sẽ sớm sắp xếp ổn thỏa mọi việc, chắc chắn, chuyện gia hạn hợp đồng với Theo Walcott sẽ diễn ra trước khi tháng 12 kết thúc. Lúc này, tôi không muốn bình luận thêm điều gì nhưng bạn hãy tin rằng chúng tôi sẽ làm mọi cách để giữ chân những cầu thủ tốt nhất”.</P>\r\n<P>Tính tới thời điểm này, Walcott mới chỉ ra sân đúng 1 lần trong khuôn khổ Premier League (trong trận mở màn với Sunderland), chính điều này đã khiến tiền đạo này tỏ ra không hài lòng và ngày hôm qua, anh đã lên tiếng đòi ra sân nhiều hơn. Mặc dù không đả động tới chuyện hợp đồng nhưng rõ ràng, đó là tín hiệu mà Walcott muốn gửi tới HLV Wenger cũng như BLĐ Arsenal.</P>\r\n<P align=right><STRONG>H.Long</STRONG></P><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0),
(11, ' Từ trận thua Celtic: Barcelona đang cạn ý tưởng? ', '-Tu-tran-thua-Celtic:-Barcelona-dang-can-ý-tuong?-', ' (Dân trí) - Không có một “người hùng” Jordi Alba như ở trận lượt đi với Celtic, Barcelona buộc phải chịu khuất phục trước đội bóng Scotland trong trận tái đấu dù cho họ đã kiểm soát tới 84% thời lượng trận đấu. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P>Nếu không tính trận thua trước Real Madrid trong trận tranh Siêu Cup, thất bại trước Celtic trên “thánh địa” Celtic Park ngày hôm qua mới là lần đầu tiên trong mùa giải này, Barcelona “trắng tay” rời khỏi cuộc chơi.</P>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=center src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/11/barcelona-celtic1-75e66.JPG" width=450 _fl="" alt="Barcelona đã thi đấu quá bế tắc trước Celtic"></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Barcelona đã thi đấu quá bế tắc trước Celtic</SPAN></DIV>\r\n<DIV align=center>&nbsp;</DIV>\r\n<P>Tất nhiên, thất bại này chẳng ảnh hưởng quá nhiều tới tình hình của Barcelona bởi họ vẫn là đội bóng dẫn đầu bảng với 9 điểm sau 4 trận đấu và chỉ cần giành chiến thắng trong trận đấu với Spartak Moskva, thày trò Tito Vilanova sẽ nghiễm nhiên cầm vé vào vòng sau.</P>\r\n<P>Tuy nhiên, ẩn đằng sau trận thua “không đáng kể” này là đầy rẫy vấn đề đang tồn tại trong “cỗ máy chiến thắng” Barcelona. Trong khi hàng thủ (vốn không được đánh giá cao) của họ phản ứng quá chậm chạp trước những “cú đánh” vỗ mặt từ đối thủ thì sự bế tắc của hàng công đã mang lại nỗi lo lớn cho những người Barcelona.</P>\r\n<P>Đơn giản, trong những năm qua, tấn công chính là “hơi thở”, “kim chỉ nam” đưa Barcelona tới chiến thắng với sự nhịp nhàng tới từng chi tiết của lối chơi tiqui – taka đầy ma quái. Thế nhưng, như một điều tất yếu của quy luật phát triển, khi Barcelona đã lên tới đỉnh cao, ắt hẳn những đối thủ của họ sẽ tìm ra cách chế ngự họ và nếu không thay đổi, Barcelona có thể sẽ đối diện với “cái chết”.</P>\r\n<P>Còn quá sớm để nói Barcelona sẽ “chết” trong mùa giải này nhưng thất bại trước Chelsea (mùa giải 2011/12) và Inter (2009/10) hay trong những cuộc đối đầu với Real Madrid gần đây đã chỉ ra rằng tiqui – taka hoàn toàn có thể bị át chế.</P>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=center src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/11/barcelona-celtic2-75e66.jpg" width=450 _fl="" alt="Dường như những ý tưởng tấn công của Barcelona đang cạn dần"></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Dường như những ý tưởng tấn công của Barcelona đang cạn dần</SPAN></DIV>\r\n<DIV align=center>&nbsp;</DIV>\r\n<P>Trong ngày hôm qua (và cả trận lượt đi), Celtic càng chứng minh rõ hơn điều này. Như thường lệ, Barcelona vẫn kiểm soát hoàn toàn thế trận (84%) và triển khai nhịp nhàng lối chơi tiqui – taka quen thuộc (chuyền bóng 955 lần so với 166 lần của Celtic), thế nhưng, trước hàng thủ dày đặc, có tổ chức cao của đội chủ sân Celtic Park, những Messi, Sanchez, Xavi, Iniesta… và sau đó là D.Villa, Fabregas đều tỏ ra bất lực trong việc tìm đường vào khung thành Celtic.</P>\r\n<P>Chính điều này đã khiến Barcelona phải sử dụng nhiều hơn những tình huống dứt điểm từ xa trong trận đấu này. Tuy nhiên, tất cả đều diễn ra trong vô vọng, tính ra trong 25 cứ dứt điểm của những cầu thủ Barcelona trong trận đấu này, chỉ có 8 lần bóng đi trúng khung gỗ (10 lần đi ra ngoài, 7 lần bị cản phá bởi những hậu vệ Celtic, 2 lần trúng khung gỗ).</P>\r\n<P>Có một con số thống kê đáng chú ý về Barcelona ở mùa giải này đó là khoảng thời gian “nổ súng” nhiều nhất của các cầu thủ đội bóng này ở La Liga lại đến trong khoảng 15 phút cuối với 13 bàn, vượt xa đội đứng thứ 2, Betis (6 bàn).</P>\r\n<DIV>&nbsp;</DIV>\r\n<DIV align=center><IMG style="MARGIN: 5px" align=center src="http://dantri4.vcmedia.vn/uajiKupQ6reCuKKDlVlG/Image/2012/11/barcelona-celtic3-75e66.JPG" width=450 _fl="" alt="Trong mùa giải này, Barcelona thường xuyên ghi bàn trong những phút cuối"></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Trong mùa giải này, Barcelona thường xuyên ghi bàn trong những phút cuối</SPAN></DIV>\r\n<DIV align=center>&nbsp;</DIV>\r\n<P>Trong khi đó, tình hình của đội bóng này ở Champions League cũng diễn ra tương tự. Trong tổng số 8 bàn của Barcelona tính tới thời điểm này, có tới 4 bàn được thực hiện trong khoảng 20 phút cuối trận (3 bàn trong 10 phút cuối trận).</P>\r\n<P>Điều đó đã cho thấy được sự khó khăn của các cầu thủ Barcelona trong việc tiếp cận đối phương. Khi những ý tưởng tới khung thành đối phương cạn dần, Tito Vilanova chỉ biết trông chờ vào những phút lóe sáng của học trò ( như Jordi Alba trong trận lượt đi) nhưng rõ ràng, đó là điều không thể lúc nào cũng xảy ra, đặc biệt khi những đối thủ của Barcelona càng thi đấu khó chịu và tập trung hơn. Rất có thể, đây sẽ là một mùa giải đầy “giông bão” nữa cho thày trò Tito Vilanova.</P>\r\n<P align=right><STRONG>H.Long</STRONG></P><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0),
(12, ' Maria Sharapova gác vợt đi... bán kẹo? ', '-Maria-Sharapova-gac-vot-di...-ban-keo?-', ' Tuy không còn ở trên đỉnh vinh quang nhưng "Nữ hoàng quần vợt" người Nga Maria Sharapova vẫn là cái tên đáng chú ý khi 7 năm liền dẫn đầu danh sách các nữ VĐV kiếm tiền giỏi nhất thế giới. ', '<div class="fon34 mt3 mr2 fon43">\r\n Ngoài khoản tiền lương kếch xù của nghiệp cầm vợt, Masha (tên viết tắt của Maria Sharapova) còn rất đắt show quảng cáo, đặc biệt, năm nay cô còn hợp tác cùng hãng sản xuất đồ ăn Henri Bendel để cho ra đời thương hiệu kẹo độc quyền do chính cô làm chủ sở hữu có tên gọi Sugarpova. <BR><BR>Nữ vận động viên xinh đẹp của làng quần vợt thế giới&nbsp;đã dành toàn bộ thời gian và tâm huyết của mình&nbsp;để tạo nên sản phẩm kẹo ngọt dễ thương này, từ khâu đầu tư vốn ban đầu, lựa chọn nhân công, tiến hành sản xuất cho&nbsp;đến việc thiết kế bao bì. <BR><BR>Sugarpova&nbsp;gồm 12 loại kẹo dẻo, mềm, cứng mang hương vị khác nhau và được đặt những cái tên vô cũng dễ thương như “tán tỉnh”, “táo bạo", “thể thao”, “ngại ngùng”… Trên vỏ ngoài mỗi chiếc kẹo, khách hàng có thể thấy hình đôi môi của nữ chủ nhân xinh&nbsp;đẹp&nbsp;được thiết kế cách điệu, hấp dẫn và vui nhộn.<BR><BR>\r\n<DIV align=center>\r\n<DIV><IMG style="MARGIN: 5px" align=center src="http://dantri4.vcmedia.vn/Ic3EyFHpPWFvMJOJFocc/Image/2012/11/ma1-1f229.jpg" width=450 _fl="" alt="Bao bì của những&nbsp;viên kẹo ngọt mang tên Maria Sharapova"><BR><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Bao bì của những&nbsp;"viên kẹo ngọt" mang tên Maria Sharapova</SPAN><BR><BR></DIV></DIV><EM></EM>\r\n<DIV align=right>Theo <STRONG>Ngọc Linh</STRONG></DIV>\r\n<DIV align=right><EM>VnMedia</EM></DIV><input type="hidden" value="/c26s26/The-Thao/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0),
(13, ' Nghề giáo ngày càng áp lực ', '-Nghe-giao-ngay-cang-ap-luc-', ' <a href = "http://dantri.com.vn/c25/s25-660703/nghe-giao-ngay-cang-ap-luc.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/7MPEnE5WMWukgZZut8l/Image/2012/11/toadam2-699a4.jpg" /></a>(Dân trí) - Bàn về chế độ cho nhà giáo, hầu hết các đại biểu đều trăn trở về điều này. Thầy cô không đủ sống, không trợ giúp được gia đình với mức lương của mình nên phải tìm mọi cách để bươn chải dẫn đến những cái nhìn không hay từ xã hội. ', '<div class="fon34 mt3 mr2 fon43">\r\n <DIV>Trong thời kỳ hội nhập, yêu cầu của xã hội với người giáo viên về năng lực, nhân cách và phẩm chất ngày càng cao. Người thầy phải luôn có ý thức nâng cao trình độ, tiếp cận với khoa học và công nghệ hiện đại để nâng cao chuyên môn từng ngày. Đó là ý kiến chia sẻ chung tại tọa đàm “Người thầy - nhân tố quan trọng trong sự nghiệp giáo dục” do&nbsp;Ban tuyên giáo Thành ủy TPHCM tổ chức ngày 8/11 nhân kỉ nhiệm 30 năm ngày Nhà giáo Việt Nam với sự góp mặt của hơn 300 đại biểu. Tọa đàm tập trung bàn luận các vấn đề như chuẩn mực của người thầy trong xã hội hiện nay, chính sách để thu hút người tài theo nghề sư phạm, công tác chăm lo hỗ trợ đời sống của đội ngũ nhà giáo…</DIV>\r\n<P style="TEXT-ALIGN: center"></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="Khoảng 300 đại biểu dự tọa đàm Người thầy - nhân tố quan trọng trong sự nghiệp giáo dục.&nbsp;" align=center src="http://dantri4.vcmedia.vn/7MPEnE5WMWukgZZut8l/Image/2012/11/toadam1-699a4.jpg" width=449 _fl=""></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Khoảng 300 đại biểu dự tọa đàm "Người thầy - nhân tố quan trọng trong sự nghiệp giáo dục".&nbsp;</SPAN></DIV><BR>\r\n<P></P>\r\n<P><B>Chuẩn mực người thầy hiện đại </B></P>\r\n<P>Nói về động lực chính thúc đẩy người thầy say mê với bục giảng, PGS.TS Nguyễn Tấn Phát - nguyên Thứ trưởng Bộ GD-ĐT gói gọn trong dòng chữ: “Tin&nbsp;- yêu&nbsp;- tinh thần trách nhiệm”. Để có được điều này, điều cần thiết nhất là khi đến với nghề không thể mang theo sự toan tính, không thật tâm khám phá và muốn cống hiến cho công việc. Ngành nghề nào cũng đòi hỏi điều này, nhưng với nghề giáo càng đúng hơn. </P>\r\n<P>Tuy nhiên, ngoài yếu tố cá nhân, ông Phát cho rằng động lực thôi thúc người thầy cống hiến cho công việc còn phụ thuộc rất lớn vào nhận thức của các cấp lãnh đạo; các chủ trương, chính sách về giáo dục. Khi trường không ra trường, lớp không ra lớp thì người thầy cũng khó hoàn thành chức trách. </P>\r\n<P>Ông Lê Hồng Sơn - giám đốc Sở GD-ĐT TPHCM chia sẻ dạy học là một hình thức lao động đặc biệt nên phẩm chất và nhân cách nhà giáo được quy định nhiều yếu tố, nhưng cốt lõi vẫn là tri thức và tình yêu thương học trò. </P>\r\n<DIV>“Đối với nhà giáo thâm niên hay người mới vào nghề, để tồn tại và phát triển được nghề nghiệp thì buộc họ luôn phải có ý thức gia tăng hàm lượng tri thức trong tư duy và bồi đắp tình yêu thương, trách nhiệm trong giáo dục với thế hệ trẻ”, ông Sơn bộc bạch. </DIV>\r\n<DIV>\r\n<P>Các đại biểu nhấn mạnh, bất kể thời kỳ nào xã hội cũng đòi hỏi năng lực, nhân cách và phẩm chất của người thầy trong cuộc sống và nghề nghiệp. Trong thời kỳ hội nhập, áp lực này càng cao và xã hội yêu cầu thêm người thầy về phẩm chất là phải luôn có ý thức nâng cao trình độ, tiếp cận với khoa học và công nghệ hiện đại để nâng cao chuyên môn từng ngày. </P>\r\n<P>PGS.TS Trần Chí Đáo -&nbsp;nguyên Thứ trưởng Bộ GD-ĐT cho hay đội ngũ nhà giáo là yếu tố hàng đầu quyết định đến chất lượng giáo dục. Sau giải phóng 1975, do thiếu đội ngũ GV phổ thông nên chúng ta đào tạo gấp rút cũng là nguyên nhân ảnh hưởng đến chất lượng giáo dục.</P>\r\n<P>Muốn làm cuộc cải cách giáo dục “căn bản và toàn diện”, theo ông Đáo phải lấy mục tiêu người thầy là hàng đầu. Từ thầy kém sẽ có một lớp học trò kém kế tiếp. Muốn có thầy giỏi thì phải có nhiều chủ trương, chính sách, chế độ nhưng cần nhất là quan điểm giáo dục đúng đắn hay còn gọi là tư duy giáo dục đúng đắn.</P>\r\n<P>Còn hiện nay, ông đánh giá chúng ta đang thiếu dân chủ trong giáo dục, thiếu dân chủ với người dạy và cả người học. Thế nên họ chưa được phát huy được hết sự sáng tạo, khả năng của mình mà vẫn bị gò bó, ép buộc với các tiêu chí thi đua, thành tích. </P></DIV>\r\n<P style="TEXT-ALIGN: center"></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="Khoảng 300 đại biểu dự tọa đàm Người thầy - nhân tố quan trọng trong sự nghiệp giáo dục.&nbsp;" align=center src="http://dantri4.vcmedia.vn/7MPEnE5WMWukgZZut8l/Image/2012/11/toadam-980a1.jpg" width=419 _fl=""></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">PGS.TS Trần Chí Đáo cho rằng cần dân chủ trong giáo dục để người dạy và người học phát huy được tính sáng tạo.</SPAN></DIV><BR>\r\n<P></P>\r\n<P><B>Trăn trở chế độ cho nhà giáo</B></P>\r\n<P>Trong nội dung chia sẻ, hầu hết các đại biểu tại buổi tạo đàm đều trăn trở về chế độ đối với nhà giáo hiện nay quá thấp. Thầy cô không đủ sống, không trợ giúp được gia đình với mức lương của mình nên phải tìm mọi cách để bươn chải dẫn đến những cái nhìn không hay từ xã hội. </P>\r\n<P>Sau khi đưa ra những con số so sánh về mức lương của giáo viên (GV) mầm non thấp hơn hoặc chỉ bằng nhân viên lái xe hay đánh máy tính, rồi giảng viên cũng có mức lương bèo bọt, TS Hồ Thiệu Hùng&nbsp;- nguyên Phó trưởng ban Tư tưởng Văn hóa Thành ủy TPHCM đặt câu hỏi: “Với đồng lương nhận được đủ nuôi gia đình nhà giáo sống tại đô thị trong bao lâu? Một tuần hay nửa tháng?”. </P>\r\n<P>“Khi họ muốn sống bằng công việc chuyên môn của mình thì bị xem là hành vi tiêu cực, bị làm khó đủ đường. Nên có những GV phải tính đến việc một buổi đi dạy, một buổi đi chợ bán hành tỏi; buổi tối xin đi phục vụ ở nhà hàng hoặc chạy xe ôm… “, ông Hùng nói thêm. </P>\r\n<P>Ông Hùng đưa ra một so sách ví von: “Nhà giáo là thủy thủ làm nhiệm vụ trên con tàu giáo dục. Lòng tự hào, vị thế của thủy thủ sẽ được nâng lên nếu được phục vụ không phải trên một con tàu cũ, thiết bị lạc hậu, chạy chậm mà lại đang chạy lạc lối. Hãy để rồi đây mọi người không còn phải chê bai “chuột chạy cùng sào cũng… không vào sư phạm”. </P>\r\n<P>Cho rằng bài toán nan giải nhất để GV tận tâm với bục giảng hiện nay là mức lương, PGS.TS Nguyễn Tấn Phát đồng tình với việc để nhà giáo và cán bộ quản lý giáo dục được xếp bậc lương cao nhất trong hệ thống bảng lương viên chức, công chức nhà nước. Điều đó không chỉ đảm bảo mức sống để người thầy cống hiến cho nghề mà quan trọng nhất là thể hiện sự tôn vinh của xã hội đối với lĩnh vực được coi là quốc sách hàng đầu của đất nước. </P>\r\n<DIV>“Chúng ta phải chăm lo đời sống tinh thần, vật chất cho đội ngũ công viên chức giáo dục như là nhiệm vụ chính trị. Không thể phó mặc cho nhà giáo tự bươn chải vật lộn với đồng lương không đủ sống vì làm như vậy là họ đang bị tổn thương”, nguyên Thứ trưởng Bộ GD-ĐT nói.</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV>\r\n<DIV align=center>\r\n<TABLE style="BORDER-BOTTOM: medium none; BORDER-LEFT: medium none; WIDTH: 80.46%; BORDER-COLLAPSE: collapse; BACKGROUND: whitesmoke; BORDER-TOP: medium none; BORDER-RIGHT: medium none; mso-border-alt: outset steelblue .75pt" class=MsoNormalTable border=1 cellSpacing=0 cellPadding=0 width="80%">\r\n<TBODY>\r\n<TR style="HEIGHT: 131.75pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes; mso-yfti-lastrow: yes">\r\n<TD style="BORDER-BOTTOM: steelblue 1pt inset; BORDER-LEFT: steelblue 1pt inset; PADDING-BOTTOM: 0.75pt; BACKGROUND-COLOR: transparent; PADDING-LEFT: 0.75pt; PADDING-RIGHT: 0.75pt; HEIGHT: 131.75pt; BORDER-TOP: steelblue 1pt inset; BORDER-RIGHT: steelblue 1pt inset; PADDING-TOP: 0.75pt; mso-border-alt: inset steelblue .75pt">\r\n<P><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Chia sẻ bên lề buổi tọa đàm, ông Lê Hồng Sơn - Giám đốc Sở GD-ĐT TPHCM cho hay trong thời gian qua, TPHCM đã từng bước thực hiện chế độ trợ cấp, phụ cấp theo từng đối tượng GV. Đối với GV, các bộ quản lý giáo dục ở các phường xã khó khăn được trợ cấp 700.000 đồng/tháng, riêng huyện Cần Giờ là 750.000 đồng/tháng; thực hiện giải quyết tiền vượt giờ cho GV mầm non 200 tiết/năm; trợ cấp 300.000 đồng đối với công chức, viên chức có hệ số lương từ 3,0 đổ xuống. Ngoài ra, trợ cấp giảng dạy đối với GV dạy hòa nhập, dạy giáo dục công dân, pháp luật… <?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Ông Sơn đánh giá, thu nhập GV ở TPHCM không thấp nhưng họ chịu áp lực giá cả đắt đỏ ở thành phố. Thời gian tới, thành phố sẽ tiếp tục nghiên cứu chính sách nhằm cải thiện đời sống GV như hỗ trợ nhà công vụ, hỗ trợ GV mua nhà ở xã hội, nhà cho người thu nhập thấp và kiến nghị Trung ương có chính sách trợ cấp “đắt đỏ” cho GV công tác tại các thành phố lớn. <o:p></o:p></SPAN></P></TD></TR></TBODY></TABLE></DIV></DIV>\r\n<P></P>\r\n<DIV align=right><B></B>&nbsp;</DIV>\r\n<DIV align=right><B></B>&nbsp;</DIV>\r\n<DIV align=right><B>Hoài Nam</B></DIV><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0),
(14, ' Cảm động chuyện kể của “nữ sinh được bố cõng đi thi” ', '-Cam-dong-chuyen-ke-cua-“nu-sinh-duoc-bo-cong-di-thi”-', ' (Dân trí) - Có mặt trong phần giao lưu của chương trình “Ngày hội Thầy và trò” diễn ra ngày 8/11 tại Hà Nội, cô tân sinh viên khuyết tật Nguyễn Phương Linh đã khiến mọi người xúc động bởi câu chuyện về cô giáo chủ nhiệm hồi cấp ba của mình.<br><a href="http://dantri.com.vn/c25/s25-632826/iem-vui-moi-cua-nu-sinh-duoc-bo-cong-di-thi.htm"><b>&nbsp;>>&nbsp;  Niềm vui mới của nữ sinh được bố cõng đi thi</b></a><br><a href="http://dantri.com.vn/c25/s25-603699/Doc-gia-dong-vien-chia-se-voi-nu-sinh-duoc-bo-cong-di-thi.htm"><b>&nbsp;>>&nbsp;  Độc giả động viên, chia sẻ với nữ sinh được bố cõng đi thi </b></a> ', '<div class="fon34 mt3 mr2 fon43">\r\n <P>“Với em, cô là người mẹ thứ hai đã dìu dắt và cho em được như ngày hôm nay” - đó là những chia sẻ của Linh về cô giáo Nguyễn Thúy Nga, hiện giảng dạy tại Trường THPT Trương Định (Hà Nội). Ba năm học dưới mái trường phổ thông tại lớp&nbsp;cô Nga chủ nhiệm, với Linh đó là quãng thời gian đẹp và ý nghĩa. </P>\r\n<P>Nhớ lại những ngày đầu mới bước chân vào Trường THPT Trương Định, Linh tâm sự: “Từ nhỏ em đã bị tật nguyền và phải ngồi xe lăn nên cũng mặc cảm nhiều lắm. Lên đến lớp 10 thì em lại càng lo hơn bởi suy nghĩ lớn hơn rồi phải học nhiều và tham gia các hoạt động nên việc di chuyển cũng sẽ nhiều hơn mà điều này với em thì không dễ dàng gì cả. Tuy nhiên, khi được xếp vào lớp cô Nga chủ nhiệm thì mọi nỗi lo đó cũng em đều không còn nữa vì cô luôn tạo điều kiện tốt nhất cho em yên tâm học hành”.</P>\r\n<DIV align=center><IMG alt="Kể chuyện cô giáo chủ nhiệm, Nguyễn Phương Linh vô cùng xúc động." align=middle src="http://dantri4.vcmedia.vn/OiXSkesLXcY7767ccccc/Image/2012/11/linh-1-0e87d.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Kể chuyện cô giáo chủ nhiệm, Nguyễn Phương Linh vô cùng xúc động.</SPAN></DIV>\r\n<P>Cô Thúy Nga rất tâm lí và thương Linh nên cô luôn ở bên cạnh hỏi han, quan tâm và động viên em học tập. Đối với những hoạt động ngoại khóa của lớp tổ chức, bao giờ cô cũng hướng dẫn các bạn chọn địa điểm nào phù hợp mà Linh có thể tham gia được. Hay những dịp nhà trường tổ chức cắm trại và làm các chương trình, cô Nga lại ân cần giao các việc mà Linh có thể làm như ngồi kết hoa, vẽ báo tường và ở khâu chuẩn bị để em được hòa nhập với các bạn. Với cô, Linh không chỉ là một học sinh mà còn là một đứa con nhỏ đã phải chịu nhiều thiệt thòi nên cô thương và yêu quý lắm.</P>\r\n<P>Ba năm - khoảng thời gian không quá dài so với cuộc đời một con người nhưng bằng đó là đủ để cho cô học trò nhỏ ngấm và hiểu dần những lời cô Thúy Nga dạy. Với cô Nga, là cô giáo chủ nhiệm lại trực tiếp dạy Linh bộ môn tiếng Anh nên cô có nhiều thời gian gần gũi và hiểu học trò hơn. Những hôm Linh đau ốm phải nghỉ học, cô không quản ngại mang vở đến tận nhà cho em chép và giảng lại bài học ngày hôm đó. Đối với những môn học khác, chính cô Nga lại làm nhịp cầu để Linh có điều kiện được học hỏi ngoài giờ lên lớp. Có việc gì khó cô đều ở bên cạnh Linh giúp đỡ và giảng giải cho em hiểu cách làm nào tốt và phù hợp với em nhất. </P>\r\n<P>Trong tâm trí của Linh, em nhớ đã có lần mình biếng học và không cố gắng nhưng những lời cô Nga phân tích: “Bố mẹ sẽ không ở lại với con cả cuộc đời được, vì thế bản thân con phải tự cứu lấy con bằng cách học hành để có một công ăn việc làm ổn định nuôi sống bản thân mình sau đó giúp đỡ những người khác” khiến em ngộ ra nhiều điều và thay đổi suy nghĩ. Từ đó, em càng cố gắng chăm chỉ học tập hơn và xác định được mục tiêu phấn đấu cho tương lai. </P>\r\n<DIV align=center><IMG alt="Linh và cô giáo chủ nhiệm Nguyễn Thúy Nga có mặt trong buổi lễ giao lưu." align=middle src="http://dantri4.vcmedia.vn/OiXSkesLXcY7767ccccc/Image/2012/11/linh-2-0e87d.jpg" width=450 _fl=""></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Phương Linh và cô giáo chủ nhiệm Nguyễn Thúy Nga có mặt trong <A href="http://dantri.com.vn/c25/s25-660477/nao-nuc-chuong-trinh-ngay-hoi-thay-va-tro.htm">buổi lễ giao lưu</A>&nbsp;tại "Ngày hội Thầy và trò".</SPAN></DIV>\r\n<P>Thương Linh, cô Nga luôn dõi theo cô học trò nhưng không vì thế mà cô xem nhẹ những lỗi Linh mắc phải. Nghiêm khắc xử phạt cũng như thẳng thắn góp ý, cô muốn Linh luôn xác định rõ ràng em cũng như các bạn khác, không vì nghĩ mình khuyết tật mà được đặc cách. Các bạn học thì em cũng học, các bạn lao động hay vui đùa cũng luôn có Linh bên cạnh cổ vũ động viên tinh thần. Cũng nhờ cách dạy đó của cô Nga mà cô học trò nhút nhát đã mạnh dạn và tự tin hơn vào bản thân rất nhiều khiến em không thấy giữa mình và mọi người có khoảng cách. Tất cả mọi việc đều diễn ra bình thường ngay cả khi em phải ngồi xe lăn để di chuyển. </P>\r\n<P>Hiện tại Linh là sinh viên năm nhất khoa Luật của Trường ĐH Công đoàn nhưng những kỉ niệm về cô giáo chủ nhiệm Nguyễn Thúy Nga vẫn theo sát em trên giảng đường. Hình ảnh người cô ngày ngày cặm cụi với phấn trắng bảng đen dạy học trò luôn hiển hiện trong em. Câu nói vô tình của ai đó&nbsp;rằng “Cuộc đời giáo viên như người lái đò chở những thế hệ học sinh qua sông nhưng nhiều người cập bến rồi cũng đã quên mất” đã khiến Linh bật khóc. Qua cuộc trò chuyện giao lưu, Linh cũng muốn nhắn nhủ đến các thế hệ học trò hãy nhớ và gửi lời tri ân đến những người thầy cô đã và đang cần mẫn với công việc trồng người. </P>\r\n<P align=right><B>Phạm Oanh</B></P><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0);
INSERT INTO `unc_news` (`news_id`, `news_title`, `alias`, `news_summary`, `news_content`, `news_author`, `news_post_date`, `news_modified_date`, `news_status`, `category_id`, `is_hot`, `viewer_number`, `news_avatar`, `review_id`, `user_id`) VALUES
(15, ' Gặp nữ sinh đa tài đam mê CNTT  ', '-Gap-nu-sinh-da-tai-dam-me-CNTT--', ' (Dân trí) - Đa tài, năng động, có nhiều thành tích học tập đặc biệt trong lĩnh vực công nghệ thông tin, Nguyễn Thu Quỳnh - SV năm 3 ĐH Duy Tân (Đà Nẵng) vừa được TW Đoàn TNCS Hồ Chí Minh xét chọn là một trong 20 nữ sinh viên CNTT tiêu biểu toàn quốc. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P class=MsoNormal><SPAN style="TEXT-ALIGN: justify"><SPAN style="FONT-SIZE: 10pt"><SPAN style="FONT-FAMILY: Tahoma"></SPAN></SPAN></SPAN></P>\r\n<DIV style="TEXT-ALIGN: center"><IMG style="MARGIN: 5px" alt="Gặp nữ sinh viên CNTT tiêu biểu ở Đà Nẵng" align=center src="http://dantri4.vcmedia.vn/ysuJrh3MO0KMq51dxOuJ/Image/2012/11/a1-05ea5.JPG" width=450 _fl=""></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Nguyễn Thu Quỳnh - SV năm 3 ĐH Duy Tân (Đà Nẵng) vừa được TW Đoàn xét chọn trao giải thưởng Nữ Sinh viên tiêu biểu trong lĩnh vực CNTT.</SPAN></DIV>\r\n<P></P>\r\n<P class=MsoNormal align=left><B>Cô bạn đa tài </B></P>\r\n<P class=MsoNormal align=left>Trở thành một trong 20 nữ sinh viên (SV)&nbsp;tiêu biểu toàn quốc trong lĩnh vực CNTT, tin vui nhưng không mấy bất ngờ với những ai biết đến cô SV năm 3, chuyên ngành Hệ thống thông tin CMU của ĐH Duy Tân - Nguyễn Thu Quỳnh. Vừa bước qua năm thứ 3 ĐH, song Quỳnh đã sở hữu một bộ sưu tập thành tích kha khá trong lĩnh vực CNTT. </P>\r\n<P class=MsoNormal align=left>Không dừng lại ở thành tích SV xuất sắc, có điểm tích lũy cao nhất toàn khóa 2 năm liền, được nhận học bổng của học viện đào tạo chuyên viên CNTT quốc tế NIIT Duy Tân trị giá hơn 10 triệu đồng, được xét trao Học bổng của hãng máy bay Boeing (Mỹ) trị giá 1.000 USD, cô SV chuyên ngành Hệ thống thông tin CMU còn tham gia nhiều hoạt động trong lĩnh vực CNTT bên ngoài nhà trường. Quỳnh từng tham gia viết website bằng ngôn ngữ ASP.net, biên tập video cho AIESEC (Tổ chức phi lợi nhuận dành cho SV lớn nhất thế giới) tại Đà Nẵng, biên tập video tuyên truyền cho CLB hoạt động bảo vệ môi trường GoGreen, làm trưởng nhóm sáng tạo tác phẩm “Đà Nẵng - con thuyền của sức mạnh” đoạt giải Ba cuộc thi trình diễn pháo hoa trên máy tính của Đà Nẵng năm 2012.</P>\r\n<P class=MsoNormal align=left>Chia sẻ đam mê lĩnh vực CNTT từ những ngày học phổ thông, Quỳnh thể hiện là cô bạn khá đa tài. Các giảng viên ở ĐH Duy Tân nhận xét Quỳnh không chỉ xuất sắc trong chuyên ngành CNTT, mà còn là một trong những SV giỏi ngoại ngữ nhất khóa. Ngoài tiếng Anh, Quỳnh còn học thêm tiếng Nhật. Cô bạn chia sẻ: “Trau dồi vốn Anh ngữ giúp em rất nhiều trong việc nghiên cứu các tài liệu chuyên ngành. Song song đó, theo em, khách hàng của các sản phẩm phầm mềm đến từ Nhật rất nhiều nên em học tiếng Nhật để chuẩn bị cho tương lai”. </P>\r\n<P class=MsoNormal align=left>Sở thích học tập và nghiên cứu CNTT, cô bạn thư giãn sau giờ học với “tài lẻ” vừa hát vừa đàn piano và tham gia các hoạt động thiện nguyện, hoạt động Đoàn do trường, lớp phối hợp với địa phương, hay các đơn vị ngoài trường học tổ chức như chăm sóc người già ở mái ấm tình thương, mang “Tết nhân ái” và “Đêm rằm Trung thu” đến với các em nhỏ ở trung tâm nuôi dưỡng trẻ mồ côi và các em nhỏ ở vùng sâu, vùng xa…</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal align=left><B>“Không có giới hạn nào trong tư duy con người</B>…”</P>\r\n<P class=MsoNormal align=left>Gặp Quỳnh, đối diện với chúng tôi là một cô gái nhỏ nhắn, xinh xắn nhưng rất tự tin với nụ cười tươi. Hỏi Quỳnh rằng con gái mà học về chuyên ngành CNTT thì có khó khăn không, Quỳnh nói ngay: “Đã mê lĩnh vực này rồi thì khó dễ gì em cũng theo đuổi chuyên ngành em đang học đến cùng”. Lý lịch “tích ngang” trong hồ sơ dự tuyển giải thưởng nữ sinh viên CNTT tiêu biểu toàn quốc, Quỳnh tự tin trình bày quan điểm sống: “Không có giới hạn nào trong tư duy con người ngoài những giới hạn do chính con người đặt ra”.</P>\r\n<P style="TEXT-ALIGN: justify" class=MsoNormal></P>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt"><IMG style="MARGIN: 5px" alt="Gặp nữ sinh viên CNTT tiêu biểu ở Đà Nẵng" align=center src="http://dantri4.vcmedia.vn/ysuJrh3MO0KMq51dxOuJ/Image/2012/11/a2-05ea5.JPG" width=400 _fl=""></SPAN></DIV>\r\n<DIV style="TEXT-ALIGN: center"><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Quỳnh tâm đắc với quan điểm sống:&nbsp;“Không có giới hạn nào trong tư duy con người ngoài những giới hạn do chính con người đặt ra”.</SPAN></DIV>\r\n<P></P>\r\n<P class=MsoNormal align=left>Vừa học xong năm thứ 2 ĐH, Quỳnh đã tự tin dự tuyển thực tập tại công ty Logigear Đà Nẵng vốn có những yêu cầu cao và thường chỉ dành cho SV năm cuối. Cô bạn là một trong 2 SV của Trường ĐH Duy Tân&nbsp;trúng tuyển và dành cả mùa hè trước khi bước vào năm thứ 3 ĐH để thực tập. Cái được của Quỳnh sau 2 tháng thực tập ở đây là: “Em cảm thấy chương trình học ở trường nhẹ nhàng hơn rất nhiều sau khi được trải nghiệm áp lực công việc đòi hỏi trình độ CNTT cao. Hầu như toàn bộ các tài liệu nghiên cứu cho công việc đều bằng tiếng Anh nên qua đó em còn được trau dồi kỹ năng Anh ngữ tốt hơn”.</P>\r\n<P class=MsoNormal align=left>Ước mơ của Quỳnh không nằm ngoài lĩnh vực CNTT. Dự tính gần nhất của Quỳnh là sẽ thử làm một phần mềm website học tiếng Anh trực tuyến có cả các bài tập và video minh họa sinh động. Mục tiêu của Quỳnh sau khi tốt nghiệp ĐH là thành lập công ty gia công phần mềm.</P>\r\n<P style="TEXT-ALIGN: right" class=MsoNormal><B style="TEXT-ALIGN: right">Khánh Hiền</B></P><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0),
(16, ' Vừa học vừa bán vé số nuôi mẹ bệnh tật ', '-Vua-hoc-vua-ban-ve-so-nuoi-me-benh-tat-', ' Trương Thế An mất cha khi còn nhỏ, sống cùng mẹ bị bệnh tâm thần trong căn nhà nhỏ vài chục m2 ở số 17Q/3, tổ 3, khu vực 3, P.An Khánh (Ninh Kiều, Cần Thơ). Hằng ngày, An vừa đi học ở Trường ĐH Võ Trường Toản (tỉnh Hậu Giang), vừa bán vé số dạo nuôi mẹ. ', '<div class="fon34 mt3 mr2 fon43">\r\n <P>Sinh năm 1992, từ nhỏ An đã phải bán vé số dạo cùng mẹ để kiếm sống. Cuối năm 2010, mẹ của An đột nhiên phát bệnh tâm thần, lại bị tai biến. Cuộc sống của An càng nặng nề hơn, vừa đi học vừa đi bán vé số dạo; còn phải chăm sóc mẹ từ cơm nước đến vệ sinh, giặt giũ.</P>\r\n<P>Mẹ của An, bà Trương Ngọc Cẩm, 61 tuổi lúc tỉnh táo nghẹn ngào nói: “Thấy con quá cực khổ, vất vả mà chảy nước mắt, khổ nỗi tôi không còn làm gì được”.</P>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">\r\n<DIV><IMG style="MARGIN: 5px" align=center src="http://dantri4.vcmedia.vn/vtfPRccccccccccccodZ/Image/2012/11/em-An-94055.jpg" width=400 _fl="" alt="Trương Thế An và mẹ. (Ảnh: Tùng Huyên)"></DIV></SPAN></DIV>\r\n<DIV align=center><SPAN style="FONT-FAMILY: Tahoma; FONT-SIZE: 10pt">Trương Thế An và mẹ. (Ảnh: Tùng Huyên)</SPAN></DIV>\r\n<P>Với dáng người gầy yếu, da xanh xao, nụ cười thật hiền, An cho biết: Mỗi ngày đi bán vé số cố gắng kiếm từ 50.000 đồng trở lên, mới đủ chi tiêu dè sẻn. Ngày nào An cũng phải lội khắp ngõ ngách thành phố để bán vé số. </P>\r\n<P>Kỳ thi đại học năm 2012- 2013, An đỗ Trường ĐH Võ Trường Toản, Ngành Quản trị dịch vụ Du lịch - Lữ hành và Trường ĐH An Giang, Khoa Công nghệ Kỹ thuật Môi Trường. Nhưng thấy Trường ĐH Võ Trường Toản học phí cao, An chọn Trường ĐH An Giang vì học phí thấp hơn.</P>\r\n<P>Tính toán của mẹ con An, cho mướn căn nhà nhỏ ở Cần Thơ, lấy tiền lên An Giang mướn nhà ở. Thế là An đưa mẹ lên An Giang. Khổ nỗi, mướn nhà trọ không được vì người ta thấy mẹ An bệnh liệt. Cuối cùng, An xin cho mẹ vào Trung tâm Nuôi dưỡng Người già, còn An ở ký túc xá.</P>\r\n<P>Việc ăn ở và học hành tạm ổn nhưng lại không thể chăm sóc mẹ. An lại phải đưa mẹ về Cần Thơ và xin vào Trường ĐH Võ Trường Toản. Hiện nay, Trường ĐH Võ Trường Toản chấp thuận cho An học ngành kế toán, khóa 5.</P>\r\n<P>Buổi tối, An đi bán vé số đến gần nửa đêm, ngày nào học một buổi, một buổi An cũng đi bán vé số. An mới hai mươi mà tóc đã có nhiều sợi bạc nên hớt ngắn để “cho dễ coi” như An nói, nhưng trông An thật khắc khổ.</P>\r\n<P>Mong các nhà hảo tâm giúp đỡ chàng sinh viên hiếu thảo trên hành trình tìm kiếm tương lai tốt đẹp.</P>\r\n<DIV align=right>Theo <B>Tùng Huyên</B></DIV>\r\n<DIV align=right><I>Tiền Phong</I></DIV><input type="hidden" value="/c25s25/giaoduc-khuyenhoc/trang-1.htm" id="hidNextUsing"/>\r\n \r\n </div>\r\n<', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 'Chưa duyệt', '0', 0, 0, '', 0, 0),
(17, ' Cảnh khốn cùng của cô giáo làng mang bệnh hiểm nghèo ', '-Canh-khon-cung-cua-co-giao-lang-mang-benh-hiem-ngheo-', ' <a href = "http://dantri.com.vn/c167/s167-660726/canh-khon-cung-cua-co-giao-lang-mang-benh-hiem-ngheo.htm" ><img border="0" src="http://dantri21.vcmedia.vn/zoom/130_100/EuEav3spzYNlEeSShBoZ/Image/2012/11/IMG_0090-15058.JPG" /></a>(Dân trí) – Vẫn là đôi mắt trìu mến và giọng nói nhẹ nhàng đã dìu dắt bao thế hệ học trò, nhưng căn bệnh ung thư khiến cô Nhung trở nên tiều tụy. Nỗi thèm khát được trở lại bục giảng và tận tay chăm sóc hai đứa con thơ của cô đang xa dần. ', '<div class="fon34 mt3 mr2 fon43">\r\n <p class="MsoNormal"><span style="font-family: "Times New Roman", serif; font-size: 12pt; line-height: 115%; ">Chiều cuối tuần, những\r\nbệnh nhân ở gần về thăm nhà khiến phòng bệnh trở nên mênh mông, cô quạnh. Trên\r\nchiếc giường sát với hành lang người phụ nữ đầu chỉ còn ít cọng tóc lún phún nhẹ\r\nnhàng lật từng trang sách. Người phụ nữ ấy là cô Nguyễn Thị Nhung (45 tuổi, ngụ\r\ntại Quảng Nam).</span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Ngày nào cũng vậy,\r\nkhông sách thì báo, mệt đến mấy cô cũng tranh thủ đọc. Cô đọc để đỡ nhớ trường\r\nnhớ lớp với những ánh mắt và gương mặt ngây thơ của học trò. Nhưng nghịch lý cuộc\r\nsống khiến cái mà người ta muốn quên lại càng trở thành nỗi nhớ day dứt khôn\r\nnguôi. Có người để chia sẻ, cô như trút được gánh nặng trong lòng bao nhiêu\r\nthăng trầm trong cuộc sống bỗng chốc trở thành bản bi ca não nề về cuộc đời cô\r\ngiáo làng.<o:p></o:p></span></p><p class="MsoNormal" style="text-align: center;"><span style="line-height: 115%; "><span style="font-family: Tahoma; font-size: 10pt; "></span></span></p><div align="center"><img _fl="" style="margin: 5px;" src="http://dantri4.vcmedia.vn/EuEav3spzYNlEeSShBoZ/Image/2012/11/IMG_0095-15058.JPG" align="middle" width="448" alt="Người bệnh cùng phòng trở thành chỗ dựa của cô Nhung"></div><div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Người bệnh cùng phòng trở thành chỗ dựa của cô Nhung</span></div><p></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Sinh ra trên vùng đất\r\n“chưa mưa đã thấm” cha mẹ quanh năm “bán mặt cho đất bán lưng cho trời” nhưng\r\ngánh nặng cơm áo luôn đè nặng. Học hết Trung cấp Sư phạm cô Nhung trở thành\r\ngiáo viện cấp I của huyện miền núi Trà My. Thoát khỏi cảnh chân lấm tay bùn của\r\nmẹ cha nhưng đời sống giáo viên những năm 80 của thế kỷ trước cũng chẳng dễ thở.\r\nSong bằng lòng yêu nghề, cô đã vượt qua mọi khó khăn gắn bó 4 năm với học sinh\r\nnơi đây rồi lại xuôi về vùng biển Tam Tiến của huyện Núi Thành giảng dạy hơn 3\r\nnăm.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Sau khi chuyện tình\r\nđẹp giữa cô giáo trẻ với người công nhân địa chất Ngô Công Sơn thành đôi thành\r\ncặp, cô Dung chuyển về dạy học tại trường làng Nguyễn Văn Trỗi thuộc xã Tam Xuận\r\n2. Nhưng hạnh phúc của hai vợ chồng họ cũng đầy sóng gió, 3 năm sau ngày cưới\r\ncô Nhung vẫn không có thai, đến bệnh viện kiểm tra bác sĩ cho biết cô mắc chứng\r\nbệnh xơ tử cung nên rất khó có con. <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Nhiều năm sau, nỗi\r\nkhát thèm có một đứa con cho vui cửa vui nhà của vợ chồng cô ngày thêm cháy bỏng.\r\nVét hết mọi tài sản tích cóp được hai vợ chồng đi khắp nơi trị bệnh hiếm muộn,\r\n12 năm sau ngày cưới nhờ sự can thiệp của bệnh viện Từ Dũ, mơ ước của vợ chồng\r\ncô mới trọn ven khi đón đứa con trai đầu lòng Ngô Công Hoài Bảo (SN: 2003) chào\r\nđời. Bốn năm sau họ có đủ nếp đủ tẻ khi sinh thêm bé gái Ngô Thị Hoài Thương. Nhưng\r\ncuộc sống lại trở nên khốn khó vì công ty địa chất giải thể khiến người chồng mất\r\nviệc phải quay về với đồng ruộng, cô Nhung bất đắc dĩ trở thành lao động chính\r\nnuôi chồng con.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Những tưởng cuộc sống\r\nsẽ bình lặng trôi đi nhưng tai họa dồn dập ập đến với gia đình cô. Cuối năm\r\n2011, ông Sơn bị xe container gây tai nạn. May mắn giữ được tính mạng nhưng ông\r\nbị đa chấn thương, và gãy một chân. Chồng chưa kịp bình phục thì đầu năm 2012\r\ncô Nhung đổ bệnh, ban đầu là những triệu chứng mệt mỏi, sốt cao, sụt ký… Cô đã\r\nđi nhiều bệnh viện nhưng không tìm ra bệnh. Tháng 3/2012 đến bệnh viện Ung Bướu,\r\nTPHCM kiểm tra cô chết lặng khi bác sĩ cho biết mình bị ung thư vú giai đoạn 2.</span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">“Thời gian đầu khi\r\nmới phát hiện bệnh, tôi bị sốc nặng, nhưng nghĩ đến hai đứa con nhỏ cùng học\r\ntrò trên lớp đang chờ đợi nên tôi đã cố gắng. Song giờ đây những cố gắng của cô\r\nNhung đang đi vào ngõ cụt bởi tai nạn chồng của chồng đã khiến gia đình lâm vào\r\ncảnh khánh kiệt, dù đã xuất viện nhưng chồng cô không thể gánh vác được công việc\r\ngia đình, hai đứa con còn quá nhỏ. <o:p></o:p></span></p><p class="MsoNormal" style="text-align: center;"><span style="line-height: 115%; "><span style="font-family: Tahoma; font-size: 10pt; "></span></span></p><div align="center"><img _fl="" style="margin: 5px;" src="http://dantri4.vcmedia.vn/EuEav3spzYNlEeSShBoZ/Image/2012/11/IMG_0090-15058.JPG" align="middle" width="448" alt="Cô giáo làng đang rất cần sự chia sẻ để tiếp tục chiến đấu với bệnh tật"></div><div align="center"><span style="font-family: Tahoma; font-size: 10pt;">Cô giáo làng đang rất cần sự chia sẻ để tiếp tục chiến đấu với bệnh tật</span></div><p></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Để có tiền chạy chữa\r\ntrong thời gian hơn nửa năm qua, vợ chồng cô Nhung buộc phải cầm cố đất đai vay\r\nmượn ngân hàng hơn 40 triệu đồng và vay mượn đồng nghiệp, bà con lối xóm hơn 20\r\ntriệu đồng. Song những khoản tiền ấy chẳng khác nào “gió vào nhà trống” bởi việc\r\nđiều trị quá tốn kém. Khi những nơi có thể vay được đều đã gõ cửa thì “con trâu\r\nlà đầu cơ nghiệp” cũng phải bán nốt để lo viện phí cho vợ. <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">Hiện cô Nhung đang\r\ntrong giai đoạn xạ trị và còn phải trải qua hơn 5 năm theo dõi điều trị tại bệnh\r\nviện Ung Bướu. Nhưng mọi kinh phí có được đến nay đã cạn, quá trình điều trị của\r\ncô có thể sẽ phải chấm dứt trước khi bệnh ung thư được đẩy lùi. Nằm trên giường\r\nbệnh cô nghẹn ngào: “Tôi chỉ sợ khi mình không còn nữa, hai đứa con thơ sẽ phải\r\nsống trong cảnh bơ vơ, thất học.” <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal"><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; ">DS Hà Thu Điểm, Trưởng\r\nban Công tác Xã hội bệnh viện Ung Bướu cho biết: “Cô Nhung là trường hợp đặc biệt\r\nđang được điều trị tại khoa Xạ 4, chồng bị tai nạn, hai con còn quá nhỏ chưa thể\r\ntự lo cho mình. Cô đang bước vào giai đoạn điều trị mang tính quyết định đến\r\nthành công của cả quá trình chữa trị, nhưng hoàn cảnh gia đình quá khó khăn nên\r\nrất cần đến sự chung tay giúp sức của cộng đồng xã hội.”<o:p></o:p></span></p><span style="font-size: 12pt; line-height: 115%; font-family: "Times New Roman", serif; "><!--[if gte mso 9]><xml>\r\n <w:WordDocument>\r\n <w:View>Normal</w:View>\r\n <w:Zoom>0</w:Zoom>\r\n <w:PunctuationKerning/>\r\n <w:ValidateAgainstSchemas/>\r\n <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n <w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n <w:Compatibility>\r\n  <w:BreakWrappedTables/>\r\n  <w:SnapToGridInCell/>\r\n  <w:WrapTextWithPunct/>\r\n  <w:UseAsianBreakRules/>\r\n  <w:DontGrowAutofit/>\r\n </w:Compatibility>\r\n <w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel>\r\n </w:WordDocument>\r\n</xml><![endif]-->\r\n\r\n</span><div align="center">\r\n\r\n<table class="MsoNormalTable" style="width:90.0%;background:whitesmoke;border-collapse:collapse;border:none;\r\n mso-border-alt:outset steelblue .75pt" border="1" cellpadding="0" cellspacing="0" width="90%">\r\n <tbody><tr style="mso-yfti-irow:0;mso-yfti-firstrow:yes;mso-yfti-lastrow:yes">\r\n <td style="border:inset steelblue 1.0pt;mso-border-alt:inset steelblue .75pt;\r\n padding:.75pt .75pt .75pt .75pt">\r\n <p style="text-align:center" align="center"><b><span style="font-size:10.0pt;\r\n font-family:Tahoma;color:black">Mọi đóng góp hảo tâm xin gửi về:</span></b></p>\r\n <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto"><b style="mso-bidi-font-weight:normal"><span style="font-size:10.0pt;font-family:\r\n Tahoma">1.</span></b><span style="font-size:10.0pt;font-family:Tahoma"> <b>Mã\r\n số 796: </b></span><span style="font-size:10.0pt;font-family:Tahoma;\r\n mso-fareast-font-family:&quot;Times New Roman&quot;;mso-fareast-language:EN-US;\r\n mso-bidi-font-weight:bold">Cô <b>Nguyễn Thị Nhung, </b></span><span style="font-size:10.0pt;font-family:Tahoma;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n mso-fareast-language:EN-US">Phòng 302, khoa Xạ 4, bệnh viện Ung Bướu, TPHCM</span></p>\r\n <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto"><span style="font-size:10.0pt;font-family:Tahoma;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n mso-fareast-language:EN-US">ĐT: 01238.990.391</span></p>\r\n <p><b><span style="font-size:10.0pt;font-family:Tahoma;color:black">2. Quỹ\r\n Nhân ái - Báo Khuyến học &amp; Dân trí - Báo điện tử Dân trí. </span></b></p>\r\n <p><span style="font-size:10.0pt;font-family:Tahoma;color:black">Ngõ 2 nhà số\r\n 48 Giảng Võ, Đống Đa, Hà Nội (Cạnh cây xăng Kim Mã)</span></p>\r\n <p><span style="font-size:10.0pt;font-family:Tahoma;color:black">Tel: 04. 3.\r\n 7366.491/ Fax: 04. 3. 7366.490 </span></p>\r\n <p><span style="font-size:10.0pt;font-family:Tahoma;color:black">Email: </span><a href="mailto:quynhanai@dantri.com.vn"><span style="font-size:10.0pt;\r\n font-family:Tahoma;color:#00005C;background:#F7FAFA;text-decoration:none;\r\n text-underline:none">quynhanai@dantri.com.vn</span></a><span style="font-size:10.0pt;font-family:Tahoma;color:black"> </span></p>\r\n <p><b><i><u><span style="font-size:10.0pt;font-family:Tahoma;color:black">Bạn\r\n đọc ủng hộ qua các tài khoản sau:</span></u></i></b></p>\r\n <p><b><i><span style="font-size:10.0pt;font-family:Tahoma;color:black">* Tài\r\n khoản VNĐ tại VietComBank: <br>\r\n </span></i></b><span style="font-size:10.0pt;font-family:Tahoma;color:black">Tên\r\n TK: <b>Báo Khuyến học &amp; Dân trí <br>\r\n </b>Số TK: 045 100 194 4487 <br>\r\n Tại: Ngân Hàng TMCP Ngoại thương Việt Nam - Chi nhánh Thành Công - Hà\r\n Nội.</span></p>\r\n <p><b><i><span style="font-size:10.0pt;font-family:Tahoma;color:black">* Tài\r\n khoản USD tại VietComBank: <br>\r\n </span></i></b><span style="font-size:10.0pt;font-family:Tahoma;color:black">Account\r\n Name: <b>Bao Khuyen hoc &amp; Dan tri</b></span></p>\r\n <p><span style="font-size:10.0pt;font-family:Tahoma;color:black">Account\r\n Number: 045 137 195 6482</span></p>\r\n <p><span style="font-size:10.0pt;font-family:Tahoma;color:black">Swift Code:\r\n BFTVVNVX</span></p>\r\n <p><span style="font-size:10.0pt;font-family:Tahoma;color:black">Bank Name:\r\n THE BANK FOR FOREIGN TRADE OF VIETNAM\r\n (VietComBank)</span></p>\r\n <p><b><i><span style="font-size:10.0pt;font-family:Tahoma;color:black">* Tài\r\n khoản VNĐ tại VietinBank: <br>\r\n </span></i></b><span style="font-size:10.0pt;font-family:Tahoma;color:black">Tên\r\n TK: <b>Báo Khuyến học &amp; Dân trí <br>\r\n </b>Số TK: 10 201 0000 220 639 <br>\r\n Tại: Ngân h