INSERT INTO `n_gioithieu` (`id`, `tieude`, `tieudee`, `noidung`, `noidunge`) VALUES
(1, 'Lời giới thiệu', 'Introduction', '<p>\n	&nbsp;</p>\n<div>\n	<p class="content" id="content1" style="text-align: justify; text-indent: 1cm; margin-bottom: 10px;">\n		K&iacute;nh gửi qu&yacute; kh&aacute;ch h&agrave;ng. Trước hết ch&uacute;c qu&yacute; kh&aacute;ch h&agrave;ng lu&ocirc;n đạt được những thắng lợi v&agrave; ph&aacute;t triển bền vững. Ch&uacute;ng t&ocirc;i rất lấy l&agrave;m vinh dự khi ch&uacute;ng t&ocirc;i, c&ocirc;ng ty TNHH C&ocirc;ng nghệ NIW được đồng h&agrave;nh c&ugrave;ng qu&yacute; kh&aacute;ch h&agrave;ng suốt trong thời gian qua. NIW với một đội ngũ kỹ sư trẻ, t&agrave;i năng, quy tr&igrave;nh quản l&yacute; chất lượng quy chuẩn b&ecirc;n cạnh những dịch vụ lu&ocirc;n hướng tới kh&aacute;ch h&agrave;ng. NIW ch&uacute;ng tối tự h&agrave;o cung cấp đến kh&aacute;ch h&agrave;ng những sản phẩm chất lượng: Thiết kế xấy dựng website chuy&ecirc;n nghiệp, thực hiện ph&aacute;t triển phần mềm ứng dụng như phần mềm kế to&aacute;n, ERP, phần mềm nhận dạng... Trong thời gian tới, NIW cam kết với c&aacute;c qu&yacute; kh&aacute;ch h&agrave;ng tiếp tục n&acirc;ng cao cải tiến năng lực l&agrave;m việc để phục vụ tốt hơn qu&yacute; kh&aacute;ch h&agrave;ng. NIW lu&ocirc;n mong muốn nhận được sự hợp t&aacute;c từ qu&yacute; kh&aacute;ch h&agrave;ng cũng như c&aacute;c đối t&aacute;c, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng cung cấp dịch vụ khi qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; y&ecirc;u cầu. Với kh&aacute;t vọng hợp t&aacute;c v&agrave; th&agrave;nh c&ocirc;ng. BGĐ C&ocirc;ng ty TNHH C&ocirc;ng nghệ NIW &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\n</div>\n<p>\n	&nbsp;</p>', '<p>\n	NIW is a new Viet Nam company in the information technology. NIW has the young strength, good experiences engineers, ambition of innovation and the development. We develop softwares, websites and mobie applications with the wolrd class quality. NIW want to thanks all customers, who believed and used our services. With best regards, NIW boards of directors.</p>\n');
