			 	$('#counter').countdown('option', {until: 7});
			 	$('#counter').countdown('pause'); 
			   clearInterval(timeId1);
			   clearInterval(timeId2);
			   clearInterval(timeId3);
			   $('#counter').addClass('colorred');
			 }).mouseout(function(){
			 	$('#counter').removeClass('colorred');
